INSERT INTO [t4182] VALUES (123,'عامر بن إبراهيم بن واقد بن عبد الله ذكر محمد بن إبراهيم بن عامر قال: خرج جدي إلى يعقوب القمي , فأقام عنده في داره شهرا , فكتب عنه الكتب , وتوفي سنة إحدى أو اثنتين ومائتين , وقال: شاذة بن عطاء: قلت لعامر بن إبراهيم: كيف لم تكتب كتب النعمان؟ قال:','429','100','0');
