INSERT INTO [t4182] VALUES (189,'أبو مسعود أحمد بن الفرات بن خالد الرازي توفي سنة ثمان وخمسين ومائتين , صلى عليه إبراهيم بن أحمد الخطابي , يروي عنه عبد الرزاق حدثنا عنه الفريابي , وأبو خليفة , من الحفاظ الكبار , وصنف المسند , والكتب الكثيرة , قدم أصبهان قديما قبل أن يخرج إلى العراق أيام','655','188','0');
INSERT INTO [t4182] VALUES (190,'أبو جعفر محمد بن عاصم بن عبد الله الثقفي توفي في صفر سنة اثنتين وستين في آخره حكى إبراهيم بن أورمة قال: ما رأيت مثل محمد بن عاصم , ولا رأى محمد بن عاصم مثل نفسه','667','188','0');
INSERT INTO [t4182] VALUES (191,'إسماعيل بن يزيد بن مردانبه أبو أحمد القطان , توفي قبل الستين والمائتين , يروي عن ابن عيينة , وسمع منه , وسمع من الحميدي , عن ابن عيينة , فاختلط حديثه ولم يتعمد الكذب , وروى عن وكيع , والوليد بن مسلم , ومعن , والناس , وكان خيرا فاضلا كثير الفوائد والغرائب','675','188','0');
INSERT INTO [t4182] VALUES (192,'إبراهيم بن عامر الأشعري توفي سنة ستين ومائتين , حكى عامر عن أبيه قال: كنت آتي مسددا لأسمع منه , وكنت إذا أبطأت عليه يغطي رأسه فيقول: جاء إبراهيم؟ فإذا قالوا ذلك رفع رأسه , قال: وقال لي مسدد: قال يحيى القطان: ثنا مسدد: لو علمت وقت مجيئك لجئتك , ثنا','680','188','0');
INSERT INTO [t4182] VALUES (193,'محمد بن عامر عن أبي داود , عنده غرائب , ومات سنة ست وستين ومائتين , وكان لإبراهيم بن عامر بنون يحدثون , منهم: عامر , ومحمد , كتبنا عنهما جميعا , وصلى على إبراهيم بن عامر أخوه محمد في خلافة المعتمد , ومن غرائب حديثه','681','188','0');
INSERT INTO [t4182] VALUES (194,'إبراهيم بن عون بن راشد السعدي روى عن ابن عيينة , ووكيع , وغيرهما , وكان فاضلا خيرا , ولم يخرج له كثير حديث','683','188','0');
INSERT INTO [t4182] VALUES (195,'إبراهيم بن عبد العزيز بن الضحاك المديني كان يقول له: شاذة بن عبد كويه يحدث عن ابن علية , وغيره , روى عنه يونس بن حبيب في المسند','686','188','0');
INSERT INTO [t4182] VALUES (196,'إسحاق بن الفيض بن محمد بن سليمان أبو يعقوب , مولى عتاب بن أسيد بن أبي الفيض , روى عن الوليد بن مسلم , وأبي زهير عبد الرحمن بن مغراء , ومهران الرازي , وغيرهم , حكي عنه أنه قال: عندي عن عبد الرحمن بن مغراء ثلاثون ألف حديث , توفي بعد الخمسين ومائتين ,','689','188','0');
INSERT INTO [t4182] VALUES (197,'محمد بن معروف العطار يعرف بمهوله , وكان من العباد والورعين , وكان إمام مسجد الجامع , يحدث عن يحيى بن سعيد القطان , ويزيد بن هارون , وأبي عبيدة النمري , لم يخرج حديثه إلى الناس إلا القليل','692','188','0');
