INSERT INTO [t5372] VALUES (89,'٨١ قال: فقال جعفر: "يا أمير المؤمنين! ما هذا هو الحكيم.','12','82','0');
INSERT INTO [t5372] VALUES (90,'"قال: فقال الرشيد: "وكيف!؟ " قال: "يذكر أمير المؤمنين، ما كان اختياره وقع عليه،','12','82','0');
INSERT INTO [t5372] VALUES (91,'٨٢ قال الأصمعي: "قلت أما تشبيهه مرض الطرف فحسن إلا أنه قد هجنه بذكر العلة،','12','82','0');
