INSERT INTO [t5372] VALUES (97,'٨٧ فقال جعفر متمثلا: "ليت قليلا يلحق الهيجا حمل" يعرض بأنه قد يجوز أن يدرك هو،','13','82','0');
INSERT INTO [t5372] VALUES (98,'وقول عدي بن الرقاع كامل:','13','82','0');
INSERT INTO [t5372] VALUES (99,'٨٨ قال الأصمعي: "هذا كله حسن بارع، وغيره أحسن منه.','13','82','0');
INSERT INTO [t5372] VALUES (100,'٨٩ قال الأصمعي: فكأني والله ألقمت جعفرا حجرا، فاهتز الرشيد فوق سريره سرورا،','14','82','0');
INSERT INTO [t5372] VALUES (101,'٩٠ فالتفت إلى يحيى، فقال: "أوجب؟ " قال: "وجب! " قال: "أفنزيدك؟ "قال: "وأي، هو','14','82','0');
INSERT INTO [t5372] VALUES (102,'ثم التفت إلى الفضل، فقال: "أوجب؟ " قال: وجب! " قال: "أزيدك؟ " قال: "ذاك إلى أمير','14','82','0');
INSERT INTO [t5372] VALUES (103,'٩١ فقلت: "يا أمير المؤمنين، هذا بيت حسد عليه عديا، جرير! "فقال: "وكيف ذاك! "','14','82','0');
INSERT INTO [t5372] VALUES (104,'٩٢ قال: "لله درك يا أصمعي، "ثم أطرق، ورفع رأسه، وقال: "أتراك تغنيني عن عقلي','14','82','0');
INSERT INTO [t5372] VALUES (105,'٩٣ قال أبو علي: وأخبرنا محمد بن عبد الواحد، عن أحمد بن يحيى، عن أبي نصر، عن','15','82','0');
