INSERT INTO [t5390] VALUES (24,'الباب الرابع والعشرون في الجوارى ذات الألحان قال الثعالبي في تحفة الأرواح وموائد السرور والأفراح إن كان أجود منه وذلك مع الروية وقال أفلاطون: غناء الملاح تحرك فيه الشهوة والطرب وغناء القباح يحرك فيه الطرب لا الشهوة وقد قيل أحسن الناس غناء من تشبه بالنساء من الرجال ومن تشبه بالرجال من النساء وما أحسن قول القائل: جائت بوجه كأنه قمر ... على قوم كأنه غصن','127','0','0');
