INSERT INTO [t550] VALUES (1,'المقدمة','1','0','0');
INSERT INTO [t550] VALUES (2,'تفسير قوله تعالى: الحمد لله الذي أنزل على عبده الكتاب ولم يجعل له عوجا','3','0','0');
INSERT INTO [t550] VALUES (3,'تفسير قوله تعالى: قيما لينذر بأسا شديدا من لدنه ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا حسنا','5','0','0');
INSERT INTO [t550] VALUES (4,'تفسير قوله تعالى: ماكثين فيه أبدا','8','0','0');
INSERT INTO [t550] VALUES (5,'تفسير قوله تعالى: وينذر الذين قالوا اتخذ الله ولدا','9','0','0');
INSERT INTO [t550] VALUES (6,'تفسير قوله تعالى: ما لهم به من علم ولا لآبائهم كبرت كلمة تخرج من أفواههم إن يقولون إلا كذبا','9','0','0');
INSERT INTO [t550] VALUES (7,'تفسير قوله تعالى: فلعلك باخع نفسك على آثارهم إن لم يؤمنوا بهذا الحديث أسفا','12','0','0');
INSERT INTO [t550] VALUES (8,'تفسير قوله تعالى: إنا جعلنا ما على الأرض زينة لها لنبلوهم أيهم أحسن عملا','13','0','0');
INSERT INTO [t550] VALUES (9,'تفسير قوله تعالى: وإنا لجاعلون ما عليها صعيدا جرزا','16','0','0');
INSERT INTO [t550] VALUES (10,'تفسير قوله تعالى: أم حسبت أن أصحاب الكهف والرقيم كانوا من آياتنا عجبا','17','0','0');
INSERT INTO [t550] VALUES (11,'تفسير قوله تعالى: إذ أوى الفتية إلى الكهف فقالوا ربنا آتنا من لدنك رحمة وهيئ لنا من أمرنا رشدا','18','0','0');
INSERT INTO [t550] VALUES (12,'تفسير قوله تعالى: فضربنا على آذانهم في الكهف سنين عددا','19','0','0');
INSERT INTO [t550] VALUES (13,'تفسير قوله تعالى: ثم بعثناهم لنعلم أي الحزبين أحصى لما لبثوا أمدا','20','0','0');
INSERT INTO [t550] VALUES (14,'تفسير قوله تعالى: نحن نقص عليك نبأهم بالحق إنهم فتية آمنوا بربهم وزدناهم هدى','21','0','0');
INSERT INTO [t550] VALUES (15,'تفسير قوله تعالى: وربطنا على قلوبهم إذ قاموا فقالوا ربنا رب السماوات والأرض لن ندعو من دونه إلها لقد قلنا إذا شططا','23','0','0');
INSERT INTO [t550] VALUES (16,'تفسير قوله تعالى: هؤلاء قومنا اتخذوا من دونه آلهة لولا يأتون عليهم بسلطان بين فمن أظلم ممن افترى على الله كذبا','24','0','0');
INSERT INTO [t550] VALUES (17,'تفسير قوله تعالى: وإذ اعتزلتموهم وما يعبدون إلا الله فأووا إلى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من أمركم مرفقا','26','0','0');
INSERT INTO [t550] VALUES (18,'تفسير قوله تعالى: وترى الشمس إذا طلعت تزاور عن كهفهم ذات اليمين وإذا غربت تقرضهم ذات الشمال وهم في فجوة منه ذلك من آيات الله من يهد الله فهو المهتد ومن يضلل فلن تجد له وليا مرشدا','28','0','0');
INSERT INTO [t550] VALUES (19,'تفسير قوله تعالى: وتحسبهم أيقاظا وهم رقود ونقلبهم ذات اليمين وذات الشمال وكلبهم باسط ذراعيه بالوصيد لو اطلعت عليهم لوليت منهم فرارا ولملئت منهم رعبا','31','0','0');
INSERT INTO [t550] VALUES (20,'تفسير قوله تعالى: وكذلك بعثناهم ليتساءلوا بينهم قال قائل منهم كم لبثتم قالوا لبثنا يوما أو بعض يوم قالوا ربكم أعلم بما لبثتم فابعثوا أحدكم بورقكم هذه إلى المدينة فلينظر أيها أزكى طعاما فليأتكم برزق منه وليتلطف ولا يشعرن بكم أحدا','33','0','0');
INSERT INTO [t550] VALUES (21,'تفسير قوله تعالى: إنهم إن يظهروا عليكم يرجموكم أو يعيدوكم في ملتهم ولن تفلحوا إذا أبدا','36','0','0');
INSERT INTO [t550] VALUES (22,'تفسير قوله تعالى: وكذلك أعثرنا عليهم ليعلموا أن وعد الله حق وأن الساعة لا ريب فيها إذ يتنازعون بينهم أمرهم فقالوا ابنوا عليهم بنيانا ربهم أعلم بهم قال الذين غلبوا على أمرهم لنتخذن عليهم مسجدا','36','0','0');
INSERT INTO [t550] VALUES (23,'تفسير قوله تعالى: سيقولون ثلاثة رابعهم كلبهم ويقولون خمسة سادسهم كلبهم رجما بالغيب ويقولون سبعة وثامنهم كلبهم قل ربي أعلم بعدتهم ما يعلمهم إلا قليل فلا تمار فيهم إلا مراء ظاهرا ولا تستفت فيهم منهم أحدا','38','0','0');
INSERT INTO [t550] VALUES (24,'تفسير قوله تعالى: ولا تقولن لشيء إني فاعل ذلك غدا','40','0','0');
INSERT INTO [t550] VALUES (25,'تفسير قوله تعالى: إلا أن يشاء الله واذكر ربك إذا نسيت وقل عسى أن يهدين ربي لأقرب من هذا رشدا','40','0','0');
INSERT INTO [t550] VALUES (26,'تفسير قوله تعالى: ولبثوا في كهفهم ثلاث مائة سنين وازدادوا تسعا','45','0','0');
INSERT INTO [t550] VALUES (27,'تفسير قوله تعالى: قل الله أعلم بما لبثوا له غيب السماوات والأرض أبصر به وأسمع ما لهم من دونه من ولي ولا يشرك في حكمه أحدا','46','0','0');
INSERT INTO [t550] VALUES (28,'تفسير قوله تعالى: واتل ما أوحي إليك من كتاب ربك لا مبدل لكلماته ولن تجد من دونه ملتحدا','51','0','0');
INSERT INTO [t550] VALUES (29,'تفسير قوله تعالى: واصبر نفسك مع الذين يدعون ربهم بالغداة والعشي يريدون وجهه ولا تعد عيناك عنهم تريد زينة الحياة الدنيا ولا تطع من أغفلنا قلبه عن ذكرنا واتبع هواه وكان أمره فرطا','53','0','0');
INSERT INTO [t550] VALUES (30,'تفسير قوله تعالى: وقل الحق من ربكم فمن شاء فليؤمن ومن شاء فليكفر إنا أعتدنا للظالمين نارا أحاط بهم سرادقها وإن يستغيثوا يغاثوا بماء كالمهل يشوي الوجوه بئس الشراب وساءت مرتفقا','58','0','0');
INSERT INTO [t550] VALUES (31,'تفسير قوله تعالى: إن الذين آمنوا وعملوا الصالحات إنا لا نضيع أجر من أحسن عملا','60','0','0');
INSERT INTO [t550] VALUES (32,'تفسير قوله تعالى: أولئك لهم جنات عدن تجري من تحتهم الأنهار يحلون فيها من أساور من ذهب ويلبسون ثيابا خضرا من سندس وإستبرق متكئين فيها على الأرائك نعم الثواب وحسنت مرتفقا','61','0','0');
INSERT INTO [t550] VALUES (33,'تفسير قوله تعالى: واضرب لهم مثلا رجلين جعلنا لأحدهما جنتين من أعناب وحففناهما بنخل وجعلنا بينهما زرعا','63','0','0');
INSERT INTO [t550] VALUES (34,'تفسير قوله تعالى: كلتا الجنتين آتت أكلها ولم تظلم منه شيئا وفجرنا خلالهما نهرا','64','0','0');
INSERT INTO [t550] VALUES (35,'تفسير قوله تعالى: وكان له ثمر فقال لصاحبه وهو يحاوره أنا أكثر منك مالا وأعز نفرا','64','0','0');
INSERT INTO [t550] VALUES (36,'تفسير قوله تعالى: ودخل جنته وهو ظالم لنفسه قال ما أظن أن تبيد هذه أبدا','65','0','0');
INSERT INTO [t550] VALUES (37,'تفسير قوله تعالى: وما أظن الساعة قائمة ولئن رددت إلى ربي لأجدن خيرا منها منقلبا','66','0','0');
INSERT INTO [t550] VALUES (38,'تفسير قوله تعالى: قال له صاحبه وهو يحاوره أكفرت بالذي خلقك من تراب ثم من نطفة ثم سواك رجلا','66','0','0');
INSERT INTO [t550] VALUES (39,'تفسير قوله تعالى: لكنا هو الله ربي ولا أشرك بربي أحدا','67','0','0');
INSERT INTO [t550] VALUES (40,'تفسير قوله تعالى: ولولا إذ دخلت جنتك قلت ما شاء الله لا قوة إلا بالله إن ترن أنا أقل منك مالا وولدا','68','0','0');
INSERT INTO [t550] VALUES (41,'تفسير قوله تعالى: فعسى ربي أن يؤتين خيرا من جنتك ويرسل عليها حسبانا من السماء فتصبح صعيدا زلقا','70','0','0');
INSERT INTO [t550] VALUES (42,'تفسير قوله تعالى: أو يصبح ماؤها غورا فلن تستطيع له طلبا','71','0','0');
INSERT INTO [t550] VALUES (43,'تفسير قوله تعالى: وأحيط بثمره فأصبح يقلب كفيه على ما أنفق فيها وهي خاوية على عروشها ويقول يا ليتني لم أشرك بربي أحدا','72','0','0');
INSERT INTO [t550] VALUES (44,'تفسير قوله تعالى: ولم تكن له فئة ينصرونه من دون الله وما كان منتصرا','72','0','0');
INSERT INTO [t550] VALUES (45,'تفسير قوله تعالى: هنالك الولاية لله الحق هو خير ثوابا وخير عقبا','73','0','0');
INSERT INTO [t550] VALUES (46,'تفسير قوله تعالى: واضرب لهم مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض فأصبح هشيما تذروه الرياح وكان الله على كل شيء مقتدرا','74','0','0');
INSERT INTO [t550] VALUES (47,'تفسير قوله تعالى: المال والبنون زينة الحياة الدنيا والباقيات الصالحات خير عند ربك ثوابا وخير أملا','75','0','0');
INSERT INTO [t550] VALUES (48,'تفسير قوله تعالى: ويوم نسير الجبال وترى الأرض بارزة وحشرناهم فلم نغادر منهم أحدا','77','0','0');
INSERT INTO [t550] VALUES (49,'تفسير قوله تعالى: وعرضوا على ربك صفا لقد جئتمونا كما خلقناكم أول مرة بل زعمتم ألن نجعل لكم موعدا','79','0','0');
INSERT INTO [t550] VALUES (50,'تفسير قوله تعالى: ووضع الكتاب فترى المجرمين مشفقين مما فيه ويقولون يا ويلتنا مال هذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها ووجدوا ما عملوا حاضرا ولا يظلم ربك أحدا','81','0','0');
INSERT INTO [t550] VALUES (51,'تفسير قوله تعالى: وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس كان من الجن ففسق عن أمر ربه أفتتخذونه وذريته أولياء من دوني وهم لكم عدو بئس للظالمين بدلا','84','0','0');
INSERT INTO [t550] VALUES (52,'تفسير قوله تعالى: ما أشهدتهم خلق السماوات والأرض ولا خلق أنفسهم وما كنت متخذ المضلين عضدا','90','0','0');
INSERT INTO [t550] VALUES (53,'تفسير قوله تعالى: ويوم يقول نادوا شركائي الذين زعمتم فدعوهم فلم يستجيبوا لهم وجعلنا بينهم موبقا','91','0','0');
INSERT INTO [t550] VALUES (54,'تفسير قوله تعالى: ورأى المجرمون النار فظنوا أنهم مواقعوها ولم يجدوا عنها مصرفا','92','0','0');
INSERT INTO [t550] VALUES (55,'تفسير قوله تعالى: ولقد صرفنا في هذا القرآن للناس من كل مثل وكان الإنسان أكثر شيء جدلا','93','0','0');
INSERT INTO [t550] VALUES (56,'تفسير قوله تعالى: وما منع الناس أن يؤمنوا إذ جاءهم الهدى ويستغفروا ربهم إلا أن تأتيهم سنة الأولين أو يأتيهم العذاب قبلا','95','0','0');
INSERT INTO [t550] VALUES (57,'تفسير قوله تعالى: وما نرسل المرسلين إلا مبشرين ومنذرين ويجادل الذين كفروا بالباطل ليدحضوا به الحق واتخذوا آياتي وما أنذروا هزوا','97','0','0');
INSERT INTO [t550] VALUES (58,'تفسير قوله تعالى: ومن أظلم ممن ذكر بآيات ربه فأعرض عنها ونسي ما قدمت يداه إنا جعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإن تدعهم إلى الهدى فلن يهتدوا إذا أبدا','99','0','0');
INSERT INTO [t550] VALUES (59,'تفسير قوله تعالى: وربك الغفور ذو الرحمة لو يؤاخذهم بما كسبوا لعجل لهم العذاب بل لهم موعد لن يجدوا من دونه موئلا','102','0','0');
INSERT INTO [t550] VALUES (60,'تفسير قوله تعالى: وتلك القرى أهلكناهم لما ظلموا وجعلنا لمهلكهم موعدا','103','0','0');
INSERT INTO [t550] VALUES (61,'تفسير قوله تعالى: وإذ قال موسى لفتاه لا أبرح حتى أبلغ مجمع البحرين أو أمضي حقبا','104','0','0');
INSERT INTO [t550] VALUES (62,'تفسير قوله تعالى: فلما بلغا مجمع بينهما نسيا حوتهما فاتخذ سبيله في البحر سربا','106','0','0');
INSERT INTO [t550] VALUES (63,'تفسير قوله تعالى: فلما جاوزا قال لفتاه آتنا غداءنا لقد لقينا من سفرنا هذا نصبا','107','0','0');
INSERT INTO [t550] VALUES (64,'تفسير قوله تعالى: قال أرأيت إذ أوينا إلى الصخرة فإني نسيت الحوت وما أنسانيه إلا الشيطان أن أذكره واتخذ سبيله في البحر عجبا','107','0','0');
INSERT INTO [t550] VALUES (65,'تفسير قوله تعالى: قال ذلك ما كنا نبغ فارتدا على آثارهما قصصا','108','0','0');
INSERT INTO [t550] VALUES (66,'تفسير قوله تعالى: فوجدا عبدا من عبادنا آتيناه رحمة من عندنا وعلمناه من لدنا علما','108','0','0');
INSERT INTO [t550] VALUES (67,'تفسير قوله تعالى: قال له موسى هل أتبعك على أن تعلمن مما علمت رشدا','109','0','0');
INSERT INTO [t550] VALUES (68,'تفسير قوله تعالى: قال إنك لن تستطيع معي صبرا','109','0','0');
INSERT INTO [t550] VALUES (69,'تفسير قوله تعالى: وكيف تصبر على ما لم تحط به خبرا','109','0','0');
INSERT INTO [t550] VALUES (70,'تفسير قوله تعالى: قال ستجدني إن شاء الله صابرا ولا أعصي لك أمرا','110','0','0');
INSERT INTO [t550] VALUES (71,'تفسير قوله تعالى: قال فإن اتبعتني فلا تسألني عن شيء حتى أحدث لك منه ذكرا','110','0','0');
INSERT INTO [t550] VALUES (72,'تفسير قوله تعالى: فانطلقا حتى إذا ركبا في السفينة خرقها قال أخرقتها لتغرق أهلها لقد جئت شيئا إمرا','111','0','0');
INSERT INTO [t550] VALUES (73,'تفسير قوله تعالى: قال ألم أقل إنك لن تستطيع معي صبرا','113','0','0');
INSERT INTO [t550] VALUES (74,'تفسير قوله تعالى: قال لا تؤاخذني بما نسيت ولا ترهقني من أمري عسرا','113','0','0');
INSERT INTO [t550] VALUES (75,'تفسير قوله تعالى: فانطلقا حتى إذا لقيا غلاما فقتله قال أقتلت نفسا زكية بغير نفس لقد جئت شيئا نكرا','113','0','0');
INSERT INTO [t550] VALUES (76,'تفسير قوله تعالى: قال ألم أقل لك إنك لن تستطيع معي صبرا','114','0','0');
INSERT INTO [t550] VALUES (77,'تفسير قوله تعالى: قال إن سألتك عن شيء بعدها فلا تصاحبني قد بلغت من لدني عذرا','115','0','0');
INSERT INTO [t550] VALUES (78,'تفسير قوله تعالى: فانطلقا حتى إذا أتيا أهل قرية استطعما أهلها فأبوا أن يضيفوهما فوجدا فيها جدارا يريد أن ينقض فأقامه قال لو شئت لاتخذت عليه أجرا','115','0','0');
INSERT INTO [t550] VALUES (79,'تفسير قوله تعالى: قال هذا فراق بيني وبينك سأنبئك بتأويل ما لم تستطع عليه صبرا','117','0','0');
INSERT INTO [t550] VALUES (80,'تفسير قوله تعالى: أما السفينة فكانت لمساكين يعملون في البحر فأردت أن أعيبها وكان وراءهم ملك يأخذ كل سفينة غصبا','117','0','0');
INSERT INTO [t550] VALUES (81,'تفسير قوله تعالى: وأما الغلام فكان أبواه مؤمنين فخشينا أن يرهقهما طغيانا وكفرا','118','0','0');
INSERT INTO [t550] VALUES (82,'تفسير قوله تعالى: فأردنا أن يبدلهما ربهما خيرا منه زكاة وأقرب رحما','118','0','0');
INSERT INTO [t550] VALUES (83,'تفسير قوله تعالى: وأما الجدار فكان لغلامين يتيمين في المدينة وكان تحته كنز لهما وكان أبوهما صالحا فأراد ربك أن يبلغا أشدهما ويستخرجا كنزهما رحمة من ربك وما فعلته عن أمري ذلك تأويل ما لم تسطع عليه صبرا','119','0','0');
INSERT INTO [t550] VALUES (84,'تفسير قوله تعالى: ويسألونك عن ذي القرنين قل سأتلو عليكم منه ذكرا','120','0','0');
INSERT INTO [t550] VALUES (85,'تفسير قوله تعالى: إنا مكنا له في الأرض وآتيناه من كل شيء سببا','122','0','0');
INSERT INTO [t550] VALUES (86,'تفسير قوله تعالى: فأتبع سببا','122','0','0');
INSERT INTO [t550] VALUES (87,'تفسير قوله تعالى: حتى إذا بلغ مغرب الشمس وجدها تغرب في عين حمئة ووجد عندها قوما قلنا يا ذا القرنين إما أن تعذب وإما أن تتخذ فيهم حسنا','123','0','0');
INSERT INTO [t550] VALUES (88,'تفسير قوله تعالى: قال أما من ظلم فسوف نعذبه ثم يرد إلى ربه فيعذبه عذابا نكرا (٨٧) وأما من آمن وعمل صالحا فله جزاء الحسنى وسنقول له من أمرنا يسرا','123','0','0');
INSERT INTO [t550] VALUES (89,'تفسير قوله تعالى: ثم أتبع سببا (٨٩) حتى إذا بلغ مطلع الشمس وجدها تطلع على قوم لم نجعل لهم من دونها سترا','125','0','0');
INSERT INTO [t550] VALUES (90,'تفسير قوله تعالى: كذلك وقد أحطنا بما لديه خبرا','126','0','0');
INSERT INTO [t550] VALUES (91,'تفسير قوله تعالى: ثم أتبع سببا (٩٢) حتى إذا بلغ بين السدين وجد من دونهما قوما لا يكادون يفقهون قولا','126','0','0');
INSERT INTO [t550] VALUES (92,'تفسير قوله تعالى: قالوا يا ذا القرنين إن يأجوج ومأجوج مفسدون في الأرض فهل نجعل لك خرجا على أن تجعل بيننا وبينهم سدا','127','0','0');
INSERT INTO [t550] VALUES (93,'تفسير قوله تعالى: قال ما مكني فيه ربي خير فأعينوني بقوة أجعل بينكم وبينهم ردما','129','0','0');
INSERT INTO [t550] VALUES (94,'تفسير قوله تعالى: آتوني زبر الحديد حتى إذا ساوى بين الصدفين قال انفخوا حتى إذا جعله نارا قال آتوني أفرغ عليه قطرا','130','0','0');
INSERT INTO [t550] VALUES (95,'تفسير قوله تعالى: فما اسطاعوا أن يظهروه وما استطاعوا له نقبا','131','0','0');
INSERT INTO [t550] VALUES (96,'تفسير قوله تعالى: قال هذا رحمة من ربي فإذا جاء وعد ربي جعله دكاء وكان وعد ربي حقا','131','0','0');
INSERT INTO [t550] VALUES (97,'تفسير قوله تعالى: وتركنا بعضهم يومئذ يموج في بعض ونفخ في الصور فجمعناهم جمعا','133','0','0');
INSERT INTO [t550] VALUES (98,'تفسير قوله تعالى: وعرضنا جهنم يومئذ للكافرين عرضا','135','0','0');
INSERT INTO [t550] VALUES (99,'تفسير قوله تعالى: الذين كانت أعينهم في غطاء عن ذكري وكانوا لا يستطيعون سمعا','136','0','0');
INSERT INTO [t550] VALUES (100,'تفسير قوله تعالى: أفحسب الذين كفروا أن يتخذوا عبادي من دوني أولياء إنا أعتدنا جهنم للكافرين نزلا','137','0','0');
INSERT INTO [t550] VALUES (101,'تفسير قوله تعالى: قل هل ننبئكم بالأخسرين أعمالا','138','0','0');
INSERT INTO [t550] VALUES (102,'تفسير قوله تعالى: الذين ضل سعيهم في الحياة الدنيا وهم يحسبون أنهم يحسنون صنعا','138','0','0');
INSERT INTO [t550] VALUES (103,'تفسير قوله تعالى: أولئك الذين كفروا بآيات ربهم ولقائه فحبطت أعمالهم فلا نقيم لهم يوم القيامة وزنا','139','0','0');
INSERT INTO [t550] VALUES (104,'تفسير قوله تعالى: ذلك جزاؤهم جهنم بما كفروا واتخذوا آياتي ورسلي هزوا','142','0','0');
INSERT INTO [t550] VALUES (105,'تفسير قوله تعالى: إن الذين آمنوا وعملوا الصالحات كانت لهم جنات الفردوس نزلا','143','0','0');
INSERT INTO [t550] VALUES (106,'تفسير قوله تعالى: خالدين فيها لا يبغون عنها حولا','145','0','0');
INSERT INTO [t550] VALUES (107,'تفسير قوله تعالى: قل لو كان البحر مدادا لكلمات ربي لنفد البحر قبل أن تنفد كلمات ربي ولو جئنا بمثله مددا','146','0','0');
INSERT INTO [t550] VALUES (108,'تفسير قوله تعالى: قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد فمن كان يرجو لقاء ربه فليعمل عملا صالحا ولا يشرك بعبادة ربه أحدا','147','0','0');
