INSERT INTO [t5597] VALUES (1,'تتمة مسند عبد الرحمن بن عوف رضي الله عنه','1','0','0');
INSERT INTO [t5597] VALUES (2,'لم يشهد حلف المطيبين ولا أدركه وإنما شهد حلف الفضول الذي عقد في دار عبد الله بن جدعان التيمي الذي روي عن رسول الله','2','1','0');
INSERT INTO [t5597] VALUES (3,'قبل أن يبعث نبيا وهو الحلف الذي تعاقده بنو هاشم وبنو المطلب وبنو أسد بن عبد العزى وبنو زهرة وبنو تيم بن مرة على أن لا يدعوا بمكة مظلمة إلا ردوها قالوا وأما حلف المطيبين فإنه جرى بين بني مخزوم وجمح وسهم وعدي وبني عبد','2','1','0');
INSERT INTO [t5597] VALUES (4,'في الإسلام أنه لو دعي إلى ذلك لأجاب','3','1','0');
INSERT INTO [t5597] VALUES (5,'ذكر من روى هذا الخبر عن جبير بن مطعم فلم يجعل بينه وبين رسول الله','4','1','0');
INSERT INTO [t5597] VALUES (6,'قال لا حلف في الإسلام وأيما حلف كان في الجاهلية فلم يزده الإسلام إلا شدة','4','1','0');
INSERT INTO [t5597] VALUES (7,'ذكر الخبر الوارد عن رسول الله','5','1','0');
INSERT INTO [t5597] VALUES (8,'لقد شهدت في دار عبد الله بن جدعان حلفا هو أحب إلي من حمر النعم ولو دعيت إليه اليوم في الإسلام لأجبت','5','1','0');
INSERT INTO [t5597] VALUES (9,'حلف الفضول','6','1','0');
INSERT INTO [t5597] VALUES (10,'ثم لأدعون بحلف الفضول فقال عبد الله بن الزبير وهو عبد الوليد حين قال الحسين ما قال وأنا أحلف بالله لئن دعا به لآخذن سيفي ثم لأقومن معه حتى ينصف من حقه أو نموت جميعا فبلغت المسور بن مخرمة بن نوفل الزهري فقال مثل ذلك وبلغت','7','1','0');
INSERT INTO [t5597] VALUES (11,'والذي فيه من ذلك الدليل على أن كل حلف كان عقد في الجاهلية قبل الإسلام أن على أهله الوفاء به وذلك أن النبي','7','1','0');
INSERT INTO [t5597] VALUES (12,'أنه قال لا حلف في الإسلام وما كان من حلف في الجاهلية فلم يزده الإسلام إلا شدة','7','1','0');
INSERT INTO [t5597] VALUES (13,'ذكر الأخبار الواردة بذلك عنه','8','1','0');
INSERT INTO [t5597] VALUES (14,'لا حلف في الإسلام وما كان في الجاهلية فلم يزده الإسلام إلا شدة','8','1','0');
INSERT INTO [t5597] VALUES (15,'لا حلف في الإسلام وكل حلف كان في الجاهلية فلم يزده الإسلام إلا شدة وما يسرني أن لي حمر النعم وأني أنقض الحلف الذي كان في دار الندوة','8','1','0');
INSERT INTO [t5597] VALUES (16,'عن الحلف قال فقال ما كان من حلف في الجاهلية فتمسكوا به ولا حلف في الإسلام','9','1','0');
INSERT INTO [t5597] VALUES (17,'عن الحلف فقال لا حلف في الإسلام ولكن تمسكوا بحلف الجاهلية','10','1','0');
INSERT INTO [t5597] VALUES (18,'قال لا حلف في الإسلام وما كان من حلف في الجاهلية لم يزده الإسلام إلا شدة فإن قال لنا قائل فإن كان الأمر في الحلف في الإسلام كما قلت من أنه غير جائز عقده فما أنت قائل فيما','10','1','0');
INSERT INTO [t5597] VALUES (19,'آخى بين المهاجرين والأنصار وكانوا يتوارثون بذلك العقد وكانت الجاهلية في جاهليتها تفعل ذلك فنسخ الله تعالى ذكره ذلك بقوله وأولوا الأرحام بعضهم أولى ببعض في كتاب الله ورد المواريث إلى القرابات بالأرحام والحرمة بقوله','11','1','0');
INSERT INTO [t5597] VALUES (20,'حينئذ أوفوا بحلف الجاهلية فإنه لم يزده الإسلام إلا شدة','12','1','0');
INSERT INTO [t5597] VALUES (21,'قال في خطبة يوم فتح مكة أوفوا بحلف الجاهلية فإنه لا يزيده الإسلام إلا شدة ولا تحدثوا حلفا في الإسلام','12','1','0');
INSERT INTO [t5597] VALUES (22,'مكة عام الفتح قام خطيبا في الناس فقال يا أيها الناس ما كان من حلف في الجاهلية فإن الإسلام لم يزده إلا شدة ولا حلف في الإسلام','13','1','0');
INSERT INTO [t5597] VALUES (23,'بنحوه فإن قال لنا أو جائز في الحلف الذي أمر النبي','14','1','0');
INSERT INTO [t5597] VALUES (24,'بالوفاء به من ذلك هو ما لم يفسخه الإسلام ولم يبطله حكم القرآن وهو التعاون على الحق والنصرة على الأخذ على يد الظالم الباغي فإن قال فإن هذا حق لكل مسلم على كل مسلم فما المعنى الذي خص به في الجاهلية حتى وجب من أجله الوفاء به ونهى عن','15','1','0');
INSERT INTO [t5597] VALUES (25,'أنه قال من تعزى بعزاء الجاهلية فأعضوه بهن أبيه ولا تكنوا','15','1','0');
INSERT INTO [t5597] VALUES (26,'المظلوم عن الاعتزاء بما ذكرنا وأمر من ظلم فاستصرخ على ظالمه أن يقول يا لعباد الله ويا للمسلمين ثم أطلق له من الاستصراخ بحليفه مما نهى عن الاستصراخ بمثله بنسيبه من قبيلته وعشيرته فأجاز للرجل من أهل حلف الفضول أو غيره أن يقول','16','1','0');
INSERT INTO [t5597] VALUES (27,'أنه قال شهدت مع عمومتي حلف المطيبين أحد من أهل النقل في روايته قيل أما بإسناد متصل فلا نعلمه ولكن','17','1','0');
INSERT INTO [t5597] VALUES (28,'قال وهو على المنبر ما شهدت لقريش قسامة إلا حلف المطيبين وما يسرني أن لي حمر النعم وأني نكثته','17','1','0');
INSERT INTO [t5597] VALUES (29,'ذكر ما صح عندنا من خبر ابن عباس عن عبد الرحمن بن عوف عن النبي','18','1','0');
INSERT INTO [t5597] VALUES (30,'في الرجل إذا أوهم في صلاته فلا يدري أزاد أو نقص قلت لا فأتى علينا عبد الرحمن بن عوف فقال فيم أنتما فقال عمر سألته هل سمعتم فيما سمعتم عن النبي','18','1','0');
INSERT INTO [t5597] VALUES (31,'يقول إذا شك أحدكم في صلاته فلا يدري أصلى اثنتين أو واحدة فليجعلها واحدة وإذا شك في الثنتين والثلاث فليجعلها ثنتين وإذا شك في الثلاث والأربع فليجعلها ثلاثا حتى يكون الشك في الزيادة','18','1','0');
INSERT INTO [t5597] VALUES (32,'قال إذا صلى أحدكم فشك في صلاته فإن شك في الواحدة والثنتين فليجعلها واحدة وإن شك في الثنتين والثلاث فليجعلها ثنتين وإن شك في الثلاث والأربع فليجعلها ثلاثا حتى يكون الوهم في الزيادة ثم يسجد سجدتين قبل أن يسلم ثم يسلم','19','1','0');
INSERT INTO [t5597] VALUES (33,'يقول هذا الحديث','20','1','0');
INSERT INTO [t5597] VALUES (34,'في الرجل إذا نسي من صلاته فلا يدري أزاد أم نقص ما أمر به فيه قال قلت أما سمعت أنت يا أمير المؤمنين من رسول الله','20','1','0');
INSERT INTO [t5597] VALUES (35,'فقال عمر فأنت عندنا العدل الرضا فماذا سمعت قال سمعت النبي','20','1','0');
INSERT INTO [t5597] VALUES (36,'يقول إذا سها أحدكم في صلاته فلم يدر واحدة صلى أو اثنتين فليبن على واحدة فإن لم يدر ثنتين صلى أو ثلاثا فليبن على ثنتين فإن لم يدر ثلاثا صلى أو أربعا فليبن على ثلاث وليسجد سجدتين قبل أن يسلم','21','1','0');
INSERT INTO [t5597] VALUES (37,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب','21','1','0');
INSERT INTO [t5597] VALUES (38,'مرسلا وبعضهم يقول عن ابن إسحاق عن حسين بن عبد الله عن مكحول عن كريب عن ابن عباس والثانية أن حسين بن عبد الله عندهم ممن لا يجوز الاحتجاج بنقله في الدين والثالثة أن محمد بن إسحاق عندهم غير مرتضى','22','1','0');
INSERT INTO [t5597] VALUES (39,'والذي فيه من ذلك البيان عن صحة قول القائلين في الشاك فيما صلى من صلاة هو فيها من عددها أنه يبني على ما استيقن أنه قد صلى منها ويتم على ذلك باقي صلاته وأن عليه إذا فعل ذلك سجدتي السهو إذ كان ممكنا أن يكون قد ألحق في صلاته ما ليس','22','1','0');
INSERT INTO [t5597] VALUES (40,'نذكر ما صح من ذلك عندنا بسنده ثم نتبع جميعه البيان إن شاء الله','22','1','0');
INSERT INTO [t5597] VALUES (41,'ذكر ذلك','23','1','0');
INSERT INTO [t5597] VALUES (42,'قال إذا شك أحدكم فلم يدر كم صلى ثلاثا أم أربعا فليصل ركعة تامة ثم ليسجد سجدتين وهو جالس فإن كانت تلك الركعة خامسة شفع بها السجدتين وإن كانت رابعة كانت ترغيما للشيطان','23','1','0');
INSERT INTO [t5597] VALUES (43,'أنه قال إذا صلى أحدكم فلم يدر أثلاثا صلى أم أربعا فليبن على اليقين ويدع الشك ثم يسجد سجدتين فإن كانت صلاته نقصت فقد أتمها وكانت السجدتان ترغيما للشيطان وإن كانت صلاته تامة كان ما زاد والسجدتان نافلة','24','1','0');
INSERT INTO [t5597] VALUES (44,'قال إذا لم يدر أحدكم ثلاثا صلى أو أربعا فليصل ركعة ثم يسجد سجدتين وهو جالس فإن كان صلى خمسا شفعتا له صلاته وإن كان صلى أربعا كانتا ترغيما للشيطان','25','1','0');
INSERT INTO [t5597] VALUES (45,'قال إذا صلى أحدكم فلا يدري كم صلى ثلاثا أم أربعا فليركع ركعتين يحسن ركوعهما وسجودهما ثم يسجد سجدتين فإن قال قائل فما أنت قائل إن كانت هذه الأخبار التي ذكرتها','25','1','0');
INSERT INTO [t5597] VALUES (46,'عندك صحاحا فيما','26','1','0');
INSERT INTO [t5597] VALUES (47,'إذا سها أحدكم في صلاته فليتحر وليسجد سجدتين','26','1','0');
INSERT INTO [t5597] VALUES (48,'قال إذا شك أحدكم في صلاته فليتحر وليسجد سجدتين','27','1','0');
INSERT INTO [t5597] VALUES (49,'إنما أنا بشر أنسى كما تنسون فأيكم شك في صلاته فلينظر أحرى ذلك للصواب فليتم عليه ثم ليسجد سجدتين','27','1','0');
INSERT INTO [t5597] VALUES (50,'قال إذا شك أحدكم في صلاته فليتحر وليسجد سجدتين','27','1','0');
INSERT INTO [t5597] VALUES (51,'فذكر نحو حديث ابن بشار','28','1','0');
INSERT INTO [t5597] VALUES (52,'فقال عمرو عن النبي','28','1','0');
INSERT INTO [t5597] VALUES (53,'من أنه إذا شك الرجل في صلاته فلم يدر كم صلى بنى على اليقين وقال بعضهم يتحرى فيبني على الأغلب عنده وقال بعضهم يستقبل صلاته وقال بعضهم إن كان ذلك في تطوع تحرى فبنى على الأغلب','28','1','0');
INSERT INTO [t5597] VALUES (54,'ذكر من قال يبني على اليقين ويتم صلاته','29','1','0');
INSERT INTO [t5597] VALUES (55,'ذكر من قال يتحرى فيبني على الأغلب عنده','32','1','0');
INSERT INTO [t5597] VALUES (56,'ذكر من قال يأخذ في ذلك بالأكثر والأتم ثم يسجد سجدتين في آخر صلاته','34','1','0');
INSERT INTO [t5597] VALUES (57,'قال إذا نودي بالأذان أدبر الشيطان له ضراط حتى لا يسمع الأذان فإذا','36','1','0');
INSERT INTO [t5597] VALUES (58,'إذا لم يدر أحدكم أثلاثا صلى أم أربعا فليسجد سجدتين وهو جالس','37','1','0');
INSERT INTO [t5597] VALUES (59,'إذا نادى المنادي أدبر الشيطان وله ضراط فإذا قضى أقبل فإذا ثوب بها أدبر فإذا قضى أقبل حتى يخطر','37','1','0');
INSERT INTO [t5597] VALUES (60,'إذا لبس الشيطان على أحدكم صلاته فلا يدري أثلاثا صلى أم أربعا فليسجد سجدتين وهو جالس','38','1','0');
INSERT INTO [t5597] VALUES (61,'إذا سها أحدكم فلم يدر أزاد أو نقص فليسجد سجدتين وهو جالس','38','1','0');
INSERT INTO [t5597] VALUES (62,'إذا أذن بالصلاة أدبر الشيطان له ضراط حتى لا يسمع التأذين فإذا سكت المؤذن','38','1','0');
INSERT INTO [t5597] VALUES (63,'قال إن الشيطان يأتي أحدكم في صلاته فيلبس عليه حتى لا يدري كم صلى فإذا وجد أحدكم من ذلك شيئا فليسجد سجدتين وهو جالس','39','1','0');
INSERT INTO [t5597] VALUES (64,'نحوه','39','1','0');
INSERT INTO [t5597] VALUES (65,'يأتي','39','1','0');
INSERT INTO [t5597] VALUES (66,'يأتي أحدكم الشيطان في صلاته فيلبس عليه حتى لا يدري كم صلى فإذا وجد ذلك فليسجد سجدتين وهو جالس','40','1','0');
INSERT INTO [t5597] VALUES (67,'قال إذا قام أحدكم يصلي جاءه الشيطان فلبس عليه صلاته حتى لا يدري كم صلى فإذا وجد ذلك أحدكم فليسجد سجدتين وهو جالس','40','1','0');
INSERT INTO [t5597] VALUES (68,'إذا صلى أحدكم فلم يدر أزاد أم نقص فليسجد سجدتين وهو جالس ثم','40','1','0');
INSERT INTO [t5597] VALUES (69,'إذا صلى أحدكم فلم يدر أثلاثا صلى أم ثنتين فليسجد وهو جالس','41','1','0');
INSERT INTO [t5597] VALUES (70,'إذا أذن المؤذن خرج الشيطان من المسجد وله حصاص فإذا سكت رجع حتى يأتي المرء المسلم في صلاته فيدخل بينه وبين نفسه حتى لا يدري أزاد في صلاته أم نقص فإذا وجد ذلك أحدكم فليسجد سجدتين وهو جالس قبل أن يسلم ثم يسلم','41','1','0');
INSERT INTO [t5597] VALUES (71,'قال إن الشيطان إذا ثوب بالصلاة ولى وله ضراط فإذا فرغ منها رجع يلتمس الخلاط ومناه ومناه وذكره من حاجاته ما لم يكن يذكر حتى لا يدري كم صلى فإذا وجد ذلك أحدكم فليسجد سجدتين وهو جالس أو نحو هذا من الكلام','42','1','0');
INSERT INTO [t5597] VALUES (72,'إذا سها أحدكم في صلاته فلا يدري أزاد أو نقص فليسجد سجدتين وهو جالس','42','1','0');
INSERT INTO [t5597] VALUES (73,'فأتاه رجل فسلم عليه ثم قال يا نبي الله إني صليت فلم أدر أشفعت أم أوترت ثلاث مرات فأجابه النبي','43','1','0');
INSERT INTO [t5597] VALUES (74,'قال من نسي شيئا من صلاته فليسجد سجدتين وهو جالس','44','1','0');
INSERT INTO [t5597] VALUES (75,'قال قال ابن أبي زياد في حديثه من شك في صلاته وقال ابن إسحاق الناقد في حديثه من نسي شيئا من صلاته فليسجد سجدتين وهو جالس','45','1','0');
INSERT INTO [t5597] VALUES (76,'سجدتا السهو تجزئان من كل زيادة ونقصان','45','1','0');
INSERT INTO [t5597] VALUES (77,'من أمره الشاك فيما صلى من عدد ركعات صلاته فإنه أمر منه','46','1','0');
INSERT INTO [t5597] VALUES (78,'من أمره إياه بالبناء على اليقين والسجود لسهوه بعد فراغه من صلاته فهو أحب إلينا وأفضل وعمل بالأحوط لدينه والأسلم وإن هو بنى على أكثر رأيه متحريا في ذلك الأغلب عليه في نفسه أنه قد صلى على ما روى عبد الله بن مسعود ومن روى ذلك عنه عن','46','1','0');
INSERT INTO [t5597] VALUES (79,'بالصلاة في السترة الطاهرة ومتطهرين بالمياه الطاهرة إذا وجدوها وغير ذلك من الأمور التي يكثر عددها وعليهم في كل ذلك من أداء الواجب عليهم فيه مثل الذي عليهم من فرض عدد ركعات الصلاة ولا خلاف بين الجميع من سلف علماء الأمة وخلفها','47','1','0');
INSERT INTO [t5597] VALUES (80,'في الشاك فيما صلى من عدد ركعات صلاة هو فيها على ما رويناها عنه الدلالة الواضحة على سبيل العمل في كل ما شك فيه شاك من الفرائض الواجبة عليه لله هل أداه أم لا وذلك كالشاك من رماة الجمار من الحاج في عدد ما رماها به من الحصى والشاك من','48','1','0');
INSERT INTO [t5597] VALUES (81,'الواردة عنه بالمعاني الثلاثة في حكم الشاك فيما قضى وفيما بقي عليه من عدد صلاة هو فيها على ما رويناها عنه فإن قال قائل فما أنت قائل فيما على من شك في عدد صلاة هو فيها فبنى على اليقين أو تحرى فبنى على أكثر رأيه فيها أو بنى على','49','1','0');
INSERT INTO [t5597] VALUES (82,'أمر بالسجود في ذلك بعد السلام قيل القول في ذلك عندنا نظير القول فيما بينا من أمر الشاك في صلاته على ما قد بينا وهو أن ذلك إعلام من رسول الله','49','1','0');
INSERT INTO [t5597] VALUES (83,'الأمران كلاهما أعني سجوده في السهو في الصلاة في الزيادة بعد السلام في حال وأمره بالسجود فيها في الزيادة بعد السلام وقبله في أخرى علم','49','1','0');
INSERT INTO [t5597] VALUES (84,'ذكر ذلك','50','1','0');
INSERT INTO [t5597] VALUES (85,'ذكر من فعل ذلك أو قاله','50','1','0');
INSERT INTO [t5597] VALUES (86,'الذي ذكرناه قبل وقال آخرون إذا بنى على اليقين في ذلك فإنه يسجد فيها بعد السلام','51','1','0');
INSERT INTO [t5597] VALUES (87,'أنه سجد يوم ذي اليدين بعدما سلم قالوا وكان تسليم النبي','52','1','0');
INSERT INTO [t5597] VALUES (88,'ذكر من قال ذلك','53','1','0');
INSERT INTO [t5597] VALUES (89,'القول في البيان عما في هذه الأخبار من الغريب','53','1','0');
INSERT INTO [t5597] VALUES (90,'في الرجل إذا أوهم في صلاته يعني بقوله أوهم أسقط يقال منه أوهم الرجل في الحساب إذا أسقط منه شيئا وأوهم في صلاته إذا أسقط منها ركعة أو أكثر أو أقل فهو موهم إيهاما فأما قولهم وهمت بفتح الواو وكسر الهاء فإنه معنى غير','53','1','0');
INSERT INTO [t5597] VALUES (91,'في الحديث الذي ذكرناه عن ابن مسعود عنه أنه قال إذا شك أحدكم في صلاته فليتحر وليسجد سجدتين فإنه يعني بقوله فليتحر فليتعمد ومنه قول امرئ القيس بن حجر ديمة هطلاء فيها وطف طبق الأرض تحرى وتدر يعني بقوله تحرى','54','1','0');
INSERT INTO [t5597] VALUES (92,'أراد بذلك أن كثرة ما يكون منه ذلك يستفرغ ما في جوفه منه كالرأس المنحص شعره وأما قوله','54','1','0');
INSERT INTO [t5597] VALUES (93,'ذكر خبر آخر من أخبار ابن عباس عن عبد الرحمن بن عوف عن النبي','55','1','0');
INSERT INTO [t5597] VALUES (94,'يقول إذا سمعتم به يعني الطاعون في أرض فلا تقدموا عليه وإذا وقع بأرض وأنتم','55','1','0');
INSERT INTO [t5597] VALUES (95,'القول في علل هذا الخبر','56','1','0');
INSERT INTO [t5597] VALUES (96,'ومن قائل يقول فيه عن الزهري عن سالم عن أبيه عن عبد الرحمن عن النبي','56','1','0');
INSERT INTO [t5597] VALUES (97,'والثانية أنه قد روي عن عمر بن الخطاب أنه كان يستغفر الله من رجوعه بالناس من سرغ إذ وقع الطاعون بالشام قالوا ولو كان عبد الرحمن حدثه عن رسول الله','57','1','0');
INSERT INTO [t5597] VALUES (98,'أنه قال الفار من الطاعون كالفار من الزحف قالوا ولم يكن عمر يفر من أمر يكون الفرار منه كالفرار من الزحف','57','1','0');
INSERT INTO [t5597] VALUES (99,'يقول إذا سمعتم به بأرض فلا تدخلوا عليه وإذا وقع وأنتم بأرض فلا تخرجوا فرارا منه فرجع عمر عن حديث عبد الرحمن بن عوف','58','1','0');
INSERT INTO [t5597] VALUES (100,'قال إن كان الوباء ببلد وأنتم به فلا تخرجوا منه وإن كان ببلد ولستم فيه فلا تدخلوه','58','1','0');
INSERT INTO [t5597] VALUES (101,'ذكر من حدث هذا الحديث فقال فيه عن الزهري عن عامر بن سعد عن أسامة عن النبي','59','1','0');
INSERT INTO [t5597] VALUES (102,'قال إن هذا الوجع أو السقم رجز عذب به بعض الأمم قبلكم ثم بقي بعد في الأرض فيذهب المرة ويأتي الأخرى فمن سمع به بأرض فلا يقدمن عليه وإن وقع بأرض وهو بها فلا يخرجنه الفرار منه','59','1','0');
INSERT INTO [t5597] VALUES (103,'ذكر الخبر عن رسول الله','61','1','0');
INSERT INTO [t5597] VALUES (104,'أنه قال الصابر في الطاعون كالصابر يوم الزحف والفار منه كالفار يوم الزحف','61','1','0');
INSERT INTO [t5597] VALUES (105,'يقول في الطاعون الفار منه كالفار يوم الزحف ومن صبر فيه كان له أجر شهيد وقد وافق عبد الرحمن في روايته هذا الخبر عن رسول الله','61','1','0');
INSERT INTO [t5597] VALUES (106,'يقول إن هذا الطاعون عذاب أو رجز أرسل على أناس أو طائفة من بني إسرائيل يجيء أحيانا ويذهب أحيانا فإذا سمعتم به بأرض فلا تدخلوها عليه وإذا وقع بأرض وأنتم بها فلا تخرجوا فرارا منه','62','1','0');
INSERT INTO [t5597] VALUES (107,'قال إذا كنت بأرض قد وقع بها فلا تخرج منها وإذا بلغك أنه بأرض فلا تدخلها قال قلت عن من قالوا عن عامر بن سعد يحدث به قال فأتيته فقالوا غائب فلقيت أخاه إبراهيم بن سعد فسألته فقال شهدت أسامة بن زيد يحدث سعدا','62','1','0');
INSERT INTO [t5597] VALUES (108,'إن هذا الطاعون رجز وعذاب عذب به من كان قبلكم فإذا كان بأرض فلا تدخلوها وإذا وقع بأرض وأنتم فيها فلا تخرجوا منها','63','1','0');
INSERT INTO [t5597] VALUES (109,'إن الطاعون بقية عذاب عذب به قوم من قبلكم فإذا وقع في أرض وأنتم بها فلا تخرجوا منها وإذا سمعتم به في أرض فلا تدخلوها','63','1','0');
INSERT INTO [t5597] VALUES (110,'إن الطاعون رجز','63','1','0');
INSERT INTO [t5597] VALUES (111,'قال ذكر الطاعون عنده فقال إنه رجز أو رجس عذبت به أمة من الأمم وقد بقيت منه بقايا','64','1','0');
INSERT INTO [t5597] VALUES (112,'عن الطاعون أنه قال إنه رجز وعذاب عذبت به الأمم قبلكم فلا تدخلوا عليه إذا وقع بأرض ولا تخرجوا فرارا منه لا يخرجكم إلا ذلك','65','1','0');
INSERT INTO [t5597] VALUES (113,'قال إذا','65','1','0');
INSERT INTO [t5597] VALUES (114,'قال في الطاعون إذا كان بأرض وأنتم بها فلا تخرجوا منها وإذا كان بأرض ولستم بها فلا تدخلوها','66','1','0');
INSERT INTO [t5597] VALUES (115,'قال في غزوة تبوك إذا كان الطاعون بأرض وأنتم بها فلا تخرجوا منها وإن لم تكونوا بها فلا تقدموها','67','1','0');
INSERT INTO [t5597] VALUES (116,'إذا كان الطاعون بأرض ولست بها فلا تحضرنها وإن كان بأرض وأنت بها فلا تفرن منه','68','1','0');
INSERT INTO [t5597] VALUES (117,'يقول إذا سمعتم بالطاعون في أرض فلا تهبطوا عليه وإذا وقع بأرض وأنتم بها فلا تخرجوا منها','68','1','0');
INSERT INTO [t5597] VALUES (118,'يقول إذا وقع الطاعون بأرض فلا تدخلوها وإن كنتم بها فلا تخرجوا منها فرجع عمر ولم يدخلها وقال مكر ابن أبي سفيان','69','1','0');
INSERT INTO [t5597] VALUES (119,'والذي فيها من ذلك الدلالة على أن على المرء توقي المكاره قبل وقوعها وتجنب الأشياء المخوفة قبل هجومها وأن عليه الصبر بعد نزولها وترك الجزع بعد وقوعها وذلك أن النبي','69','1','0');
INSERT INTO [t5597] VALUES (120,'في أمره على سبيل ما رويناها عنه وهذا الذي روي عن رسول الله','69','1','0');
INSERT INTO [t5597] VALUES (121,'في لقاء العدو لا تتمنوا لقاء العدو وسلوا الله العافية وإذا لقيتموهم فلا تفروا فإن قال قائل فإن كان الأمر في ذلك كالذي ذكرت فما أنت قائل فيما','70','1','0');
INSERT INTO [t5597] VALUES (122,'فإن قال فاذكر لنا من خالفهم','73','1','0');
INSERT INTO [t5597] VALUES (123,'وعمرو أضل من بعير أهله إنه دعوة نبيكم','73','1','0');
INSERT INTO [t5597] VALUES (124,'اللهم أعط معاذا وأهله نصيبهم من رحمتك فطعن في كفه قال أبو قلابة فكنت أقول لقد عرفت الشهادة والرحمة وكنت لا أدري ما دعوة نبيكم','74','1','0');
INSERT INTO [t5597] VALUES (125,'كان يقول في صلاته ذات ليلة فحمى إذا وطاعونا فحمى إذا وطاعونا قالها مرتين فقال رجل يا رسول الله لقد سمعتك البارحة تدعو بدعاء ما سمعته منك قط فقال أوسمعته قال إني استجرت الله لأمتي ألا يهلكهم بسنة بعامة فأعطانيها','75','1','0');
INSERT INTO [t5597] VALUES (126,'وقبض الصالحين قبلكم ولكن أخاف عليكم سوى ذلك أخاف أن يغدو الرجل منكم من بيته لا يدري أمؤمن هو أو منافق','76','1','0');
INSERT INTO [t5597] VALUES (127,'وميتة الصالحين قبلكم اللهم اقسم لآل معاذ نصيبهم الأوفى قال فلما نزل أتاه آت فقال إن عبد الرحمن قد أصيب قال فأتاه فقال يا بني الحق من ربك فلا تكونن من الممترين فقال ستجدني إن شاء الله من الصابرين فمات','76','1','0');
INSERT INTO [t5597] VALUES (128,'وموت الصالحين قبلكم اللهم اقسم لآل معاذ نصيبهم الأوفى منه ثم ذكر نحوه','77','1','0');
INSERT INTO [t5597] VALUES (129,'وقبض الصالحين قبلكم اللهم أعط آل معاذ حظهم من هذا الأمر فنزل فوجد ابنه بالموت فقال يا أبت الحق من ربك فلا تكونن من الممترين فقال معاذ ستجدني إن شاء الله من الصابرين','78','1','0');
INSERT INTO [t5597] VALUES (130,'ذكر ما حضرنا ذكره من الأخبار الواردة عنهم بذلك','79','1','0');
INSERT INTO [t5597] VALUES (131,'ارجع بالناس ولا تقدمهم على هذا الوباء فأمرهم أن يرتفعوا ثم قال ادع لي الأنصار فدعوتهم فاستشارهم فسلكوا سبيل المهاجرين واختلفوا كاختلافهم فأمرهم أن يرتفعوا ثم قال ادع لي من كان ها هنا من مشيخة مهاجرة الفتح فدعوتهم','79','1','0');
INSERT INTO [t5597] VALUES (132,'يقول إذا سمعتم به في أرض فلا تقدموا عليه وإذا وقع بأرض وأنتم فيها فلا يخرجكم منها فرار منه قال فكبر عمر وحمد الله وانصرف أحدهما يزيد الكلمة','80','1','0');
INSERT INTO [t5597] VALUES (133,'تدخلهم أرضا بها الطاعون الذين هم أئمة يقتدى بهم قال فقال له عمر يا أبا عبيدة شككت فقال يا أمير المؤمنين أشاكا كان يعقوب صلوات الله عليه إذ قال لبنيه لا تدخلوا من باب واحد وادخلوا من أبواب متفرقة قال فقال عمر','81','1','0');
INSERT INTO [t5597] VALUES (134,'إذ قالوا لو أطاعونا في الجلوس عن القتال ما قتلوا فكره رسول الله','82','1','0');
INSERT INTO [t5597] VALUES (135,'نظير نهيه عن الدنو من المجذوم وقوله فر من المجذوم فرارك من الأسد مع إعلامه','82','1','0');
INSERT INTO [t5597] VALUES (136,'أمته ألا عدوى ولا صفر وقد تقدم بيان ذلك كله قبل فيما مضى من كتابنا هذا','83','1','0');
INSERT INTO [t5597] VALUES (137,'فمن ذلك قول رسول الله','83','1','0');
INSERT INTO [t5597] VALUES (138,'رجز عذاب ومنه قول الله تعالى ذكره أولئك لهم عذاب من رجز أليم يقال هو رجز ورجس بالزاي والسين ومنه قول رؤبة بن العجاج كم رامنا من ذي عديد مبز حتى وقمنا كيده بالرجز يعني بقوله وقمنا كيده','83','1','0');
INSERT INTO [t5597] VALUES (139,'إذ قال فناء أمتي بالطعن والطاعون فقيل أما الطعن فقد عرفناه فما الطاعون فقال وخز أعدائكم من الجن ومنه قول رؤبة بن العجاج والحرب عسراء اللقاح المغزي بالمشرفيات وطعن وخز وأما قول عمرو بن العاص فتفرقوا','85','1','0');
INSERT INTO [t5597] VALUES (140,'وسألته ألا يلبسهم شيعا فإنه عنى بقوله شيعا فرقا يقول سألته ألا يجعلهم متفرقي الأهواء ومنه قول الله تعالى ذكره إن الذين فارقوا دينهم وكانوا شيعا يعني فرقا ومنه قول رؤبة لو أن يأجوج ومأجوج معا والناس','86','1','0');
INSERT INTO [t5597] VALUES (141,'ذكر خبر آخر من أخبار عبد الرحمن بن عوف عن النبي','87','1','0');
INSERT INTO [t5597] VALUES (142,'قال إذا أقيم على السارق الحد فلا غرم عليه','87','1','0');
INSERT INTO [t5597] VALUES (143,'القول في علل هذا الخبر','88','1','0');
INSERT INTO [t5597] VALUES (144,'إلا من هذا الوجه والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه والثانية أن المعروف من هذا الخبر عن رواته عن مفضل بن فضالة عن يونس بن يزيد عن سعد بن إبراهيم عن أخيه المسور بن إبراهيم عن عبد الرحمن بن عوف عن النبي','88','1','0');
INSERT INTO [t5597] VALUES (145,'ذكر من حدث هذا الحديث عن مفضل فلم يجعل فيه بين المسور بن إبراهيم وعبد الرحمن بن عوف أحدا','89','1','0');
INSERT INTO [t5597] VALUES (146,'قال لا يغرم السارق إذا أقيم عليه الحد','89','1','0');
INSERT INTO [t5597] VALUES (147,'أنه قال لا يغرم صاحب السرقة إذا أقيم عليه الحد','89','1','0');
INSERT INTO [t5597] VALUES (148,'ذكر من روى هذا الحديث عن ابن عفير فوافق في روايته إياه عنه سائر من ذكرنا ممن حدث به عن مفضل بن فضالة','90','1','0');
INSERT INTO [t5597] VALUES (149,'قال إذا أقيم على السارق الحد فلا غرم عليه','90','1','0');
INSERT INTO [t5597] VALUES (150,'والذي فيه من ذلك البيان البين عن صحة قول القائلين إن السارق إذا أخذ وقد استهلك ما سرق فقطعت يده أنه لا سبيل للمسروق منه عليه بسبب السرقة التي سرقها منه في اتباعه بغرم قيمة ذلك وفساد قول من زعم أن عليه مع القطع ضمان قيمة ما استهلك','90','1','0');
INSERT INTO [t5597] VALUES (151,'ذكر من قال القول الذي رويناه عن رسول الله','91','1','0');
INSERT INTO [t5597] VALUES (152,'ذكر من قال ذلك','94','1','0');
INSERT INTO [t5597] VALUES (153,'ذكر من قال ذلك','95','1','0');
INSERT INTO [t5597] VALUES (154,'وفي تركه تعريفهم وجوب ذلك عليه ببعض ما ذكرنا الدلالة الواضحة على أن ذلك عليه غير واجب فإن ظن ظان أن في بيان الله تعالى ذكره على لسان رسوله','96','1','0');
INSERT INTO [t5597] VALUES (155,'قال إذا أقيم على السارق الحد فلا غرم عليه والغرم إنما هو غرم ما استهلكه أو قيمته وذلك أنه لا يقال غرم فلان لفلان شيئا بمعنى رد عليه ما أخذه منه بعينه وإنما يقال غرم له ما استهلكه عليه فإذ كان','98','1','0');
INSERT INTO [t5597] VALUES (156,'ذكر بعض من حضرنا ذكره ممن قال ذلك من السلف','98','1','0');
INSERT INTO [t5597] VALUES (157,'قال إذا أقيم على السارق الحد فلا غرم عليه فأزال عنه غرم ما استهلكه بالحد الذي يقام عليه فإذا لم يقم عليه الحد ولم يكن عليه واجبا فلا شك أن عليه الغرم وبذلك من القول قال الجميع من سلف علماء هذه الأمة وخلفها وفي هذا الخبر','99','1','0');
INSERT INTO [t5597] VALUES (158,'السارق إذا أقيم عليه الحد فلا غرم عليه كل سارق البيان البين أن ذلك حكم المحارب وغيره من السراق','103','1','0');
INSERT INTO [t5597] VALUES (159,'ذكر خبر آخر من أخبار عبد الرحمن بن عوف عن النبي','105','1','0');
INSERT INTO [t5597] VALUES (160,'ولم يشبع هو وأهل بيته من خبز الشعير فلا أرانا أخرنا لهذا لما هو خير لنا','105','1','0');
INSERT INTO [t5597] VALUES (161,'القول في علل هذا الخبر','106','1','0');
INSERT INTO [t5597] VALUES (162,'إلا من هذا الوجه والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه وهذا خبر قد مضى قبل في كتابنا هذا نظائره وبيان ما في ذلك كله من الفقه والمعاني فكرهنا إعادته','106','1','0');
INSERT INTO [t5597] VALUES (163,'يقول قال الله تبارك وتعالى أنا الرحمن وأنا خلقت الرحم واشتققت لها من اسمي فمن وصلها وصلته ومن قطعها بتته','107','1','0');
INSERT INTO [t5597] VALUES (164,'القول في علل هذا الخبر','108','1','0');
INSERT INTO [t5597] VALUES (165,'إلا من هذا الوجه والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه والثانية أنه خبر قد حدث به عن الزهري عن أبي سلمة بن عبد الرحمن جماعة فلم يدخلوا بينه وبين أبيه أبا الرداد وجعلوا الخبر مرسلا عنه عن أبيه','108','1','0');
INSERT INTO [t5597] VALUES (166,'ذكر من روى هذا الحديث عن الزهري عن أبي سلمة فأرسله عنه عن أبيه ولم يجعل بينه وبين أبيه أبا الرداد','109','1','0');
INSERT INTO [t5597] VALUES (167,'يقول قال الله تبارك وتعالى أنا الله وأنا الرحمن خلقت الرحم وشققت لها من اسمي فمن وصلها وصلته ومن قطعها قطعته','109','1','0');
INSERT INTO [t5597] VALUES (168,'يقول قال الله عز وجل إني أنا الله الذي خلقت الرحم وشققت لها شعبة أو شقة قال أبو جعفر أنا أشك من اسمي وأنا الرحمن وهي','109','1','0');
INSERT INTO [t5597] VALUES (169,'ذكر من روى هذا الخبر عن أبي سلمة فقال فيه عنه عن أبي هريرة عن النبي','110','1','0');
INSERT INTO [t5597] VALUES (170,'قال الله تبارك وتعالى أنا الرحمن وهي الرحم شققت لها من اسمي فمن يصلها أصله ومن يقطعها أقطعه فأبته','110','1','0');
INSERT INTO [t5597] VALUES (171,'يقول قال الله تبارك وتعالى إني أنا الرحمن وهي الرحم وشققت لها من اسمي فمن يصلها أصله ومن يقطعها أقطعه ومن يبتتها أبتته','111','1','0');
INSERT INTO [t5597] VALUES (172,'يقول قال الله تبارك وتعالى أنا الرحمن خلقت الرحم واشتققت لها من اسمي فمن يصلها أصله ومن يقطعها أقطعه وقد روي هذا الحديث عن عبد الرحمن بن عوف عن النبي','112','1','0');
INSERT INTO [t5597] VALUES (173,'ذكر ذلك','112','1','0');
INSERT INTO [t5597] VALUES (174,'ثلاثة تحت العرش يوم القيامة','112','1','0');
INSERT INTO [t5597] VALUES (175,'جماعة من أصحابه نذكر ما حضرنا من ذلك ذكره مما صح عندنا سنده','114','1','0');
INSERT INTO [t5597] VALUES (176,'الرحم شجنة آخذة بحجزة الرحمن يوم القيامة لها لسان ذلق تقول يا رب صل من وصلني واقطع من قطعني','114','1','0');
INSERT INTO [t5597] VALUES (177,'أنه كان يقول إن هذه الرحم شجنة من الرحمن فمن قطعها حرم الله عليه الجنة','115','1','0');
INSERT INTO [t5597] VALUES (178,'الرحم شجنة من الرحمن فمن وصلها وصله ومن قطعها قطعه','115','1','0');
INSERT INTO [t5597] VALUES (179,'الرحم معلقة بالعرش تقول من وصلني وصله الله ومن قطعني قطعه الله','116','1','0');
INSERT INTO [t5597] VALUES (180,'قال الرحم شجنة من الله من وصلها وصله الله ومن قطعها قطعه','116','1','0');
INSERT INTO [t5597] VALUES (181,'أنه قال خلق الله الخلق فلما فرغ منهم تعلقت الرحم بحقوي الرحمن فقال مه فقالت هذا مقام عائذ بك من القطعية قال فما ترضين أن أقطع من قطعك وأصل من وصلك قالت بلى قال فذلك لك قال سليمان في حديثه قال أبو هريرة','117','1','0');
INSERT INTO [t5597] VALUES (182,'قال إن الله جل وعز خلق الخلق حتى فرغ من خلقه فقامت الرحم فأخذت بحقوي الرحمن فقال مه قالت هذا مقام العائذ من القطيعة قال نعم','117','1','0');
INSERT INTO [t5597] VALUES (183,'إن الله حين خلق الخلق قامت الرحم فقالت هذا مقام العائذ بك من القطعية فقال تبارك وتعالى ترضين أن أقطع من قطعك وأصل من وصلك قالت نعم واقرؤا إن شئتم فهل عسيتم إن توليتم أن تفسدوا في الأرض وتقطعوا أرحامكم','118','1','0');
INSERT INTO [t5597] VALUES (184,'قال خلق الله الخلق فلما فرغ منه قامت الرحم فقال الله مه فقالت هذا مقام العائذ من القطعية فقال نعم ألا ترضين أن أصل من وصلك وأقطع من قطعك قالت بلى قال فذلك لك ثم قال أبو هريرة فاقرؤوا إن شئتم فهل','118','1','0');
INSERT INTO [t5597] VALUES (185,'يقول الرحم شجنة آخذة بحقو الرحمن يوم القيامة تقول اللهم صل من وصلني واقطع من قطعني','119','1','0');
INSERT INTO [t5597] VALUES (186,'إن الرحم شجنة من الله تقول رب إني قطعت إني أسيء إلي فيقول أما ترضين أن أصل من وصلك وأقطع من قطعك','119','1','0');
INSERT INTO [t5597] VALUES (187,'فذكر نحوه','120','1','0');
INSERT INTO [t5597] VALUES (188,'الرحم شجنة من الرحمن تعلقت بحقوي الرحمن تقول اللهم صل من وصلني واقطع من قطعني','120','1','0');
INSERT INTO [t5597] VALUES (189,'قال الراحمون يرحمهم الرحمن ارحموا أهل الأرض يرحمكم أهل السماء الرحم شجنة من الرحمن من وصلها وصلته ومن قطعها بتته','121','1','0');
INSERT INTO [t5597] VALUES (190,'قال الرحم شجنة من الرحمن','122','1','0');
INSERT INTO [t5597] VALUES (191,'قال الرحم معلقة بالعرش وليس الواصل بالمكافئ ولكن الواصل الذي إذا قطعت رحمه وصلها','122','1','0');
INSERT INTO [t5597] VALUES (192,'الرحم معلقة بالعرش وليس الواصل بالمكافئ ولكن الواصل الذي إذا قطعت رحمه وصلها','123','1','0');
INSERT INTO [t5597] VALUES (193,'بنحوه إلا أنه قال في حديثه عن حسين ولكن الواصل الذي إذا انقطعت رحمه وصلها','123','1','0');
INSERT INTO [t5597] VALUES (194,'إن الرحم معلقة بالعرش لها لسان ذلق تقول اللهم صل من وصلني واقطع من قطعني','125','1','0');
INSERT INTO [t5597] VALUES (195,'إن قال لنا قائل ما معنى هذه الأخبار التي رويتها عن رسول الله','125','1','0');
INSERT INTO [t5597] VALUES (196,'فيما مضى من كتابنا هذا أنه قال من سره أن ينسأ في أجله ويوسع عليه في رزقه فليصل رحمه وأنه قال إن صلة الرحم محبة في الأهل مثراة للمال منسأة في الأثر وإن كان من أهل الكفر به والمعصية له ففي عاجل دنياه','126','1','0');
INSERT INTO [t5597] VALUES (197,'فيما مضى من كتابنا هذا أنه قال إن الله تبارك وتعالى ليعمر بالقوم الديار ويكثر لهم في الأموال وما نظر إليهم منذ خلقهم بغضا لهم قيل وكيف ذاك يا رسول الله قال بصلتهم أرحامهم وأنه','127','1','0');
INSERT INTO [t5597] VALUES (198,'بلوا أرحامكم ولو بالسلام','129','1','0');
INSERT INTO [t5597] VALUES (199,'بلوا أرحامكم ولو بالسلام فأعلم','130','1','0');
INSERT INTO [t5597] VALUES (200,'لا يدخل الجنة قاطع','131','1','0');
INSERT INTO [t5597] VALUES (201,'قال لا يدخل الجنة قاطع','131','1','0');
INSERT INTO [t5597] VALUES (202,'يقول لا يدخل الجنة قاطع','132','1','0');
INSERT INTO [t5597] VALUES (203,'يقول لا يدخل الجنة قاطع','132','1','0');
INSERT INTO [t5597] VALUES (204,'يقول لا يدخل الجنة قاطع','132','1','0');
INSERT INTO [t5597] VALUES (205,'يقول لا يدخل الجنة قاطع قال يريد الرحم','133','1','0');
INSERT INTO [t5597] VALUES (206,'لا يدخل الجنة قاطع وقال فيه العلماء القول الذي','133','1','0');
INSERT INTO [t5597] VALUES (207,'وإن كان في إسناده نظر','134','1','0');
INSERT INTO [t5597] VALUES (208,'جالسا عشية عرفة في حلقة فقال لا يحل لمن أمسى قاطع رحم إلا قام عنا','134','1','0');
INSERT INTO [t5597] VALUES (209,'ما لك لم يقم أحد غيرك قال كانت لي خالة مصارمتي فلما سمعت الذي قلت أتيتها فقالت ما الذي جاء بك ما هذا عن أمرك فأخبرتها الذي قلت فاستغفرت لي واستغفرت لها قال أحسنت إجلس ألا إن الرحمة لا تنزل على قوم فيهم قاطع','135','1','0');
INSERT INTO [t5597] VALUES (210,'تعرض الأعمال عشية الخميس ليلة الجمعة فيغفر لكل أحد إلا لقاطع رحم','137','1','0');
INSERT INTO [t5597] VALUES (211,'وذلك هو الذي تصرم ذوي رحمه على وجه العداوة لهم كما ذكر عن الفتى الذي وصف خبره في الأخبار التي رويناها من مصارمته عمته أو خالته فكان خروجه من قطعه رحمها عنده وعند من ذكرنا مراجعته وصالتها بالكلام دون بذل فضل ماله لها ونصرته','138','1','0');
INSERT INTO [t5597] VALUES (212,'فمن ذلك قول رسول الله','138','1','0');
INSERT INTO [t5597] VALUES (213,'في القرآن إنه يحاج العباد يوم القيامة له ظهر وبطن فإنه يختلف في معناه وقد بينت','138','1','0');
INSERT INTO [t5597] VALUES (214,'الرحم شجنة فإن الشجنة الفعلة من قولهم شجن فلان على فلان إذا حزن عليه فهو يشجن عليه شجنا ومنه قول زهير بن أبي سلمى فقلت والدار أحيانا يشطط بها صرف الأمير على من كان ذا شجن يعني بقوله على من كان ذا شجن على','140','1','0');
INSERT INTO [t5597] VALUES (215,'في الرحم أنها آخذة بحقوي الرحمن فإن الحقو في كلام العرب الإزار يجمع حقيا ومنه خبر أم عطية عن رسول الله','140','1','0');
INSERT INTO [t5597] VALUES (216,'إني آخذ بحجزتكم عن النار وأنتم تتقاحمون فيها وأما قوله','141','1','0');
INSERT INTO [t5597] VALUES (217,'توضع الرحم يوم القيامة لها حجنة فإنه يعني بالحجنة العطفة وكل شيء معطوف الرأس مثل الصولجان فإن العرب تسميه محجنا ومن ذلك قيل للرجل إذا أمال الشيء إلى نفسه حجنه واحتجنه ومنه قيل للصولجان محجن ومنه قول الطرماح بن حكيم','141','1','0');
INSERT INTO [t5597] VALUES (218,'صلة الرحم بالمعروف ولو بالشيء البائس يندى فيرطب وذلك أن العرب تصف الرجل إذا','141','1','0');
INSERT INTO [t5597] VALUES (219,'لبني عبد المطلب إذ أنزلت عليه وأنذر عشيرتك الأقربين يا بني عبد المطلب إني لا أغني عنكم من الله شيئا غير أن لكم رحما سأبلها ببلالها ومنه قول أعشى بن ثعلبة بالخيل شعثا ما تزال جيادها حسرى تغادر بالطريق سخالها','142','1','0');
INSERT INTO [t5597] VALUES (220,'ذكر خبر آخر من أخبار عبد الرحمن بن عوف عن النبي','144','1','0');
INSERT INTO [t5597] VALUES (221,'مكة انصرف إلى الطائف فحاصرهم سبع عشرة أو ثماني عشرة فلم يفتتحها ثم أغار غدوة أو روحة ثم نزل ثم هجر','144','1','0');
INSERT INTO [t5597] VALUES (222,'القول في علل هذا الخبر','145','1','0');
INSERT INTO [t5597] VALUES (223,'ذكر خبر آخر من أخبار عبد الرحمن بن عوف عن رسول الله','146','1','0');
INSERT INTO [t5597] VALUES (224,'إذا صلى على جنازة قال اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وصغيرنا وكبيرنا وذكرنا وأنثانا اللهم من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان','146','1','0');
INSERT INTO [t5597] VALUES (225,'إذا صلى على جنازة قال اللهم اغفر لأحيائنا وأمواتنا ولصغيرنا وكبيرنا ولذكرنا ولأنثانا اللهم من أحييته منا فأحيه على الإيمان ومن توفيته فتوفه على الإسلام','147','1','0');
INSERT INTO [t5597] VALUES (226,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلل إحداها أنه خبر لا يعرف له مخرج عن عبد الرحمن بن عوف عن رسول الله','147','1','0');
INSERT INTO [t5597] VALUES (227,'من أصحابه جماعة نذكر ما صح عندنا من ذلك سنده ثم نتبع جميعه البيان إن شاء الله','147','1','0');
INSERT INTO [t5597] VALUES (228,'ذكر ذلك','148','1','0');
INSERT INTO [t5597] VALUES (229,'يقول في الصلاة على الميت اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وصغيرنا وكبيرنا وذكرنا وأنثانا اللهم من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان','148','1','0');
INSERT INTO [t5597] VALUES (230,'في الصلاة على الجنازة اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وصغيرنا وكبيرنا اللهم من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان','148','1','0');
INSERT INTO [t5597] VALUES (231,'إذا صلى على الجنازة يقول اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وصغيرنا وكبيرنا','148','1','0');
INSERT INTO [t5597] VALUES (232,'كان يدعو للميت يقول اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وذكرنا وأنثانا وصغيرنا وكبيرنا من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان','149','1','0');
INSERT INTO [t5597] VALUES (233,'على الميت قالت كان يقول اللهم اغفر لحينا وميتنا ولصغيرنا وكبيرنا وذكرنا وأنثانا ولغائبنا وشاهدنا اللهم من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان','149','1','0');
INSERT INTO [t5597] VALUES (234,'يصلي على جنازة يقول اللهم اغفر لأولنا وآخرنا وحينا وميتنا وذكرنا وأنثانا وصغيرنا وكبيرنا وشاهدنا وغائبنا اللهم لا تحرمنا أجره ولا تضلنا بعده قال يحيى وحدثني أبو سلمة عن النبي','150','1','0');
INSERT INTO [t5597] VALUES (235,'يقول في الصلاة على الميت اللهم اغفر لحينا وميتنا وغائبنا وشاهدنا وذكرنا وأنثانا وصغيرنا وكبيرنا قال وحدثني يحيى بن أبي كثير عن أبي سلمة بن عبد الرحمن بهذا الحديث قال ومن أحييته منا فأحيه على الإسلام ومن توفيته منا','150','1','0');
INSERT INTO [t5597] VALUES (236,'يقول في الصلاة على الميت اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وذكرنا وأنثانا وصغيرنا وكبيرنا','151','1','0');
INSERT INTO [t5597] VALUES (237,'يقول في الصلاة على الميت اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وذكرنا وأنثانا وصغيرنا وكبيرنا','151','1','0');
INSERT INTO [t5597] VALUES (238,'أنه كان إذا صلى على الميت قال اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وصغيرنا وكبيرنا اللهم من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان اللهم عفوك عفوك','151','1','0');
INSERT INTO [t5597] VALUES (239,'علمهم الصلاة على الميت فقال اللهم اغفر لأحيائنا وأمواتنا وأصلح ذات بيننا اللهم هذا عبدك فلان بن فلان لا نعلم إلا خيرا وأنت أعلم به فاغفر لنا وله فقلت وأنا أصغر القوم فإن لم أعلم خيرا قال فلا تقل إلا ما','152','1','0');
INSERT INTO [t5597] VALUES (240,'كان إذا صلى على ميت قال اللهم اغفر لحينا وميتنا وشاهدنا وغائبنا وصغيرنا وكبيرنا وذكرنا وأنثانا قال يحيى وحدثني بهن أبو سلمة بن عبد الرحمن وزاد مع هؤلاء الثماني الكلمات كلمتين أخريين من أحييته منا فأحيه على','153','1','0');
INSERT INTO [t5597] VALUES (241,'إن قال لنا قائل إنك قلت إن هذه الأخبار التي رويتها عن رسول الله','153','1','0');
INSERT INTO [t5597] VALUES (242,'يصلي على ميت ففهمت من صلاته عليه اللهم اغفر له وارحمه واغسله بالبرد واغسله كما يغسل الثوب','153','1','0');
INSERT INTO [t5597] VALUES (243,'على جنازة فسمعته يقول اللهم اغفر له وارحمه واعف عنه وعافه وأكرم نزله وأوسع مدخله واغسله بماء وثلج وبرد ونقه من الخطايا كما ينقى الثوب من الدنس وأبدله دارا خيرا من داره وأهلا خيرا من أهله وزوجا خيرا من زوجه','154','1','0');
INSERT INTO [t5597] VALUES (244,'بنحو ذلك أيضا','154','1','0');
INSERT INTO [t5597] VALUES (245,'وصلى على جنازة ثم ذكر نحوه إلا أنه قال كما ينقى الثوب الأبيض من الدنس وزاد أيضا قال عوف فتمنيت أني لو كنت ذلك الميت لدعاء رسول الله','155','1','0');
INSERT INTO [t5597] VALUES (246,'على جنازة رجل من الأنصار فكان مما حفظت من دعائه في الصلاة اللهم اغفر له وارحمه واعف عنه وعافه وأكرم نزله ووسع مدخله وغسله بماء وثلج وبرد ونقه من الخطايا كما ينقى الثوب الأبيض من الدنس اللهم أبدله دارا خيرا من داره','155','1','0');
INSERT INTO [t5597] VALUES (247,'على جنازة رجل من الأنصار فسمعته يقول اللهم صل عليه واغفر له وارحمه وعافه واعف عنه وأكرم نزله ومنقلبه ونقه من الخطايا كما ينقى الثوب الأبيض من الدنس وأبدله دارا خيرا من داره وأهلا خيرا من أهله وقه فتنة القبر وعذاب','156','1','0');
INSERT INTO [t5597] VALUES (248,'يصلي على الجنازة فقال اللهم أنت خلقتها وأنت هديتها للإسلام وأنت قبضت روحها تعلم سرها وعلانيتها فاغفر لها','156','1','0');
INSERT INTO [t5597] VALUES (249,'يقول إذا صلى على الجنازة فذكر عن النبي','157','1','0');
INSERT INTO [t5597] VALUES (250,'يصلي على الجنازة قال قال خلقتها أو قال أنت خلقتها شعبة الذي شك وهديتها إلى الإسلام وأنت قبضت روحها تعلم سرها وعلانيتها جئنا شفعاء فاغفر لها','157','1','0');
INSERT INTO [t5597] VALUES (251,'يصلي على الجنازة فذكر نحوه','158','1','0');
INSERT INTO [t5597] VALUES (252,'أنه كان إذا صلى على جنازة قال اللهم أنت خلقته وأنت هديته للإسلام وأنت قبضت روحه وأنت أعلم بسره وعلانيته جئنا نشفع له فاغفر له','158','1','0');
INSERT INTO [t5597] VALUES (253,'في القول على الميت اللهم عبدك أنت خلقته وأنت قبضت روحه وأنت هديته للإسلام وأنت أعلم بسره وعلانيته وجئنا نشفع له فاغفر له','159','1','0');
INSERT INTO [t5597] VALUES (254,'فسمعته لما كبر الثالثة دعا للميت وقال اللهم أعظم أجره وأتمم له نوره وأضيء له في قبره وألحقه بنبيه واغفر لنا وله ثم كبر الرابعة واستغفر للمؤمنين والمؤمنات ثم سلم','159','1','0');
INSERT INTO [t5597] VALUES (255,'على رجل من المسلمين فأسمعه قال اللهم إن فلان بن فلان في ذمتك وحبل جوارك فأعذه من فتنة القبر وعذاب النار وأنت أهل الوفاء والحق اللهم فاغفر له وارحمه إنك أنت الغفور الرحيم قيل أما أسانيد بعضها فصحاح وفي بعضها','160','1','0');
INSERT INTO [t5597] VALUES (256,'قد كان فعله في بعض الأحوال إذ كانت الصلاة على الجنائز دعاء للميت واستغفارا له ولا شيء في ذلك من الدعاء مؤقت لا يجوز للمصلي تجاوزه فأي نوع من الدعاء الذي روينا عن رسول الله','161','1','0');
INSERT INTO [t5597] VALUES (257,'صحيحة لأنه','161','1','0');
INSERT INTO [t5597] VALUES (258,'ذكر من قال في ذلك نحو الذي قلنا فيه وعمل بالذي قلنا إنهم عملوا به فيه','161','1','0');
INSERT INTO [t5597] VALUES (259,'ذكر ما حضرنا ذكره من اختلاف من ذكرنا أنهم اختلفوا فيه من الدعاء على الميت','165','1','0');
INSERT INTO [t5597] VALUES (260,'ذكر ما روي في ذلك عن عمر بن الخطاب رحمة الله عليه','165','1','0');
INSERT INTO [t5597] VALUES (261,'ذكر ما روي عن علي بن أبي طالب رضوان الله عليه في ذلك','168','1','0');
INSERT INTO [t5597] VALUES (262,'ذكر ما روي في ذلك عن عبد الله بن مسعود','170','1','0');
INSERT INTO [t5597] VALUES (263,'ذكر ما روي في ذلك عن سائر الصحابة والتابعين','172','1','0');
INSERT INTO [t5597] VALUES (264,'ودعاء كثيرا لم يحفظه نافع','172','1','0');
INSERT INTO [t5597] VALUES (265,'في دعاء خفي علي','173','1','0');
INSERT INTO [t5597] VALUES (266,'ثم أقول اللهم عبدك أو أمتك كان يعبدك لا يشرك بك شيئا وأنت أعلم به إن كان محسنا فزد في إحسانه وإن كان مخطئا فتجاوز عنه اللهم لا تحرمنا أجره ولا تضلنا بعده','174','1','0');
INSERT INTO [t5597] VALUES (267,'ثم أقول اللهم هذا عبدك أو أمتك كان يعبدك لا يشرك بك شيئا ثم ذكر نحوه إلا أنه قال ولا تفتنا أو لا تضلنا بعده','174','1','0');
INSERT INTO [t5597] VALUES (268,'والتابعين لهم بإحسان والخالفين بعدهم في الصلاة على الجنازة واختلافهم في الدعاء فيها عليه أن رسول الله','179','1','0');
INSERT INTO [t5597] VALUES (269,'أيام حياته في ذلك على النحو الذي روي عنه من الدعاء على قدر ما كان يحضره وعلى ذلك من منهاجه في ذلك مضى الخيار من أمته وقد روينا عنه','179','1','0');
INSERT INTO [t5597] VALUES (270,'ذكر الرواية الواردة بذلك عنه','179','1','0');
INSERT INTO [t5597] VALUES (271,'قال إذا صليتم على الجنازة فأخلصوا له الدعاء','179','1','0');
INSERT INTO [t5597] VALUES (272,'يقول إذا صليتم على الجنازة فأخلصوا له الدعاء فالذي ينبغي لكل مصل صلى على جنازة أن يخلص للميت الدعاء بأحسن ما حضره مما قد ذكرناه فإن تعدى ذلك إلى بعض ما روي فيه عن بعض الصحابة والتابعين أو دعا بغير ذلك مما حضره من الدعاء','180','1','0');
INSERT INTO [t5597] VALUES (273,'فمن ذلك قول خالد البجلي سألت سعيد بن المسيب عن الصلاة على الجنازة فزبرني يعني بقوله فزبرني فانتهرني وأصله الضرب','180','1','0');
INSERT INTO [t5597] VALUES (274,'ذكر خبر آخر من أخبار عبد الرحمن بن عوف عن النبي','182','1','0');
INSERT INTO [t5597] VALUES (275,'قال ليس على مختلس قطع','182','1','0');
INSERT INTO [t5597] VALUES (276,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين إحداهما أنه خبر لا يعرف له مخرج عن عبد الرحمن بن عوف عن النبي','182','1','0');
INSERT INTO [t5597] VALUES (277,'بعض أصحابه نذكر ما صح عندنا من ذلك بسنده','183','1','0');
INSERT INTO [t5597] VALUES (278,'ليس على منتهب قطع ولا على الخائن ولا على المختلس قطع','183','1','0');
INSERT INTO [t5597] VALUES (279,'قال ليس على المختلس ولا على المنتهب','183','1','0');
INSERT INTO [t5597] VALUES (280,'القول في البيان عما في هذا الخبر من الفقه','184','1','0');
INSERT INTO [t5597] VALUES (281,'ذكر من قال من السلف لا قطع على مختلس','184','1','0');
INSERT INTO [t5597] VALUES (282,'وأن المختلس معناه معنى الغاصب ولا قطع على غاصب في قول أحد من أهل العلم وقال آخرون على المختلس ما يجب في مثله القطع القطع','188','1','0');
INSERT INTO [t5597] VALUES (283,'القول في البيان عما في هذه الأخبار من الغريب','189','1','0');
INSERT INTO [t5597] VALUES (284,'أنه قال للنساء لا تعذبن أولادكن بالدغر يعني','189','1','0');
INSERT INTO [t5597] VALUES (285,'مسند طلحة بن عبيد الله رضي الله عنه','190','1','0');
INSERT INTO [t5597] VALUES (286,'ذكر ما صح عندنا سنده من أخبار طلحة بن عبيد الله عن النبي','191','1','0');
INSERT INTO [t5597] VALUES (287,'فقال سمعت الله يقول إن الله وملائكته يصلون على النبي يا أيها الذين آمنوا صلوا عليه وسلموا تسليما فكيف الصلاة عليك فقال قل اللهم صل على محمد وعلى آل محمد كما صليت على إبراهيم إنك حميد مجيد وبارك على محمد وعلى آل محمد','192','1','0');
INSERT INTO [t5597] VALUES (288,'إلا من هذا الوجه والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه والثانية أنه خبر قد حدث به عن موسى بن طلحة غير عثمان بن موهب فقال فيه عن موسى بن طلحة عن زيد بن خارجة الأنصاري عن النبي','193','1','0');
INSERT INTO [t5597] VALUES (289,'ذكر من روى هذا الخبر عن موسى بن طلحة فقال فيه عنه عن زيد بن خارجة','193','1','0');
INSERT INTO [t5597] VALUES (290,'فقال سأله زيد بن خارجة الأنصاري فقال سألت رسول الله','193','1','0');
INSERT INTO [t5597] VALUES (291,'وقولوا اللهم بارك على محمد كما باركت على إبراهيم إنك حميد مجيد وقد شارك في رواية هذا الخبر عن رسول الله','194','1','0');
INSERT INTO [t5597] VALUES (292,'ذكر ذلك','195','1','0');
INSERT INTO [t5597] VALUES (293,'فقلنا قد عرفنا كيف نسلم عليك فكيف نصلي عليك قال قولوا اللهم صل على محمد وعلى آل محمد كما صليت على آل إبراهيم إنك حميد مجيد اللهم بارك على محمد وعلى آل محمد كما باركت على آل إبراهيم إنك حميد مجيد','196','1','0');
INSERT INTO [t5597] VALUES (294,'فقال يا رسول الله هذا السلام عليك قد عرفناه فكيف الصلاة عليك قال تقول اللهم صل على محمد وعلى آل محمد كما باركت على آل إبراهيم','197','1','0');
INSERT INTO [t5597] VALUES (295,'قالوا يا رسول الله هذا السلام عليك قد','198','1','0');
INSERT INTO [t5597] VALUES (296,'رجل فقال يا رسول الله أما السلام عليك فقد عرفناه فكيف الصلاة عليك قال فغضب رسول الله حتى وددنا أن الرجل الذي سأله لم يسأله قال إذا صليتم علي فقولوا اللهم صل على محمد النبي الأمي وعلى آل محمد كما صليت على إبراهيم','200','1','0');
INSERT INTO [t5597] VALUES (297,'حتى جلس بين يديه فقال يا رسول الله أما السلام عليك فقد عرفناه فما الصلاة فأخبرنا كيف نصلي عليك فصمت رسول الله','201','1','0');
INSERT INTO [t5597] VALUES (298,'فقال له بشير بن سعد يا رسول الله كيف الصلاة عليك قال فسكت حتى جاءه الوحي قال فتربد له وجهه فقال تقولون اللهم صل على محمد وعلى آل محمد كما صليت على آل إبراهيم إنك حميد مجيد اللهم بارك على محمد وعلى آل محمد كما','202','1','0');
INSERT INTO [t5597] VALUES (299,'سئل كيف نصلي عليك يا رسول الله قال قولوا اللهم صل على محمد وعلى آل محمد وبارك على محمد وعلى آل محمد كما صليت وباركت على إبراهيم وآل إبراهيم إنك حميد مجيد والسلام قد علمتم','202','1','0');
INSERT INTO [t5597] VALUES (300,'من قال اللهم صل على محمد وعلى آل محمد كما صليت على إبراهيم وعلى آل إبراهيم وبارك على محمد وعلى آل محمد كما باركت على إبراهيم وعلى آل إبراهيم وترحم على محمد وعلى آل محمد كما ترحمت على إبراهيم وعلى آل إبراهيم شهدت له يوم','203','1','0');
INSERT INTO [t5597] VALUES (301,'القول في البيان عن معاني هذه الأخبار','204','1','0');
INSERT INTO [t5597] VALUES (302,'فمحسن وإنما اختلاف الرواة في رواياتهم ما رووا عن رسول الله','205','1','0');
INSERT INTO [t5597] VALUES (303,'في دعائه للميت في الصلاة على الجنازة إذ كان المصلي عليها مخيرا في دعائه له حينئذ أن يتخير ما شاء وأحب من الدعاء بعد أن يدعو للميت بخير وإن كان أحب ذلك إلينا أن يدعو له به أفضله وأبلغه فكذلك الصلاة على النبي','205','1','0');
INSERT INTO [t5597] VALUES (304,'والآثار المنقولة عن الصحابة فإن قال لنا قائل فاذكر لنا بعض الآثار المنقولة عن الصحابة باختلافهم في ذلك لنعلم بذلك حقيقة ما وصفت أن المسلمين غير محصورين في ذلك على دعاء بعينه دون غيره من الدعاء قيل','205','1','0');
INSERT INTO [t5597] VALUES (305,'يقول قولوا اللهم داحي المدحوات وباريء المسموكات','205','1','0');
INSERT INTO [t5597] VALUES (306,'فأحسنوا عليه الصلاة قالوا علمنا قال قولوا اللهم اجعل صلواتك وبركاتك ورحمتك على سيد المسلمين وإمام المتقين وخاتم النبيين محمد عبدك ورسولك إمام الخير وقائد الخير ورسول الرحمة اللهم ابعثه مقاما محمودا يغبطه به','207','1','0');
INSERT INTO [t5597] VALUES (307,'مخير في الصلاة عليه بأي هذه الصلوات التي جاءت بها الآثار وقالتها العلماء من الصحابة وغير ذلك مما شاء المصلي عليه أن يصلي عليه بعد أن يكون ذلك دعاء له بما يثبت فهل الصلاة عليه فرض واجب أم هي نافلة فضل فإن قلت هي فرض واجب','208','1','0');
INSERT INTO [t5597] VALUES (308,'في كتابه بقوله إن الله وملائكته يصلون على النبي يا أيها الذين آمنوا صلوا عليه وسلموا تسليما ندب من الله جل ثناؤه عباده المؤمنين إليها ونافلة فضل من فاعلها إذا فعلها ولا حال من الأحوال هي أولى بالصلاة فيها عليه من غيرها','208','1','0');
INSERT INTO [t5597] VALUES (309,'أكثروا علي الصلاة يوم الجمعة فإنه يوم مشهود تشهده الملائكة وإن أحدا لا يصلي علي إلا عرضت صلاته علي حتى يفرغ منها قال قلت وبعد الموت قال إن الله حرم على الأرض أن تأكل أجساد الأنبياء','209','1','0');
INSERT INTO [t5597] VALUES (310,'قال قال لي جبريل شقي عبد ذكرت عنده فلم يصل عليك فقلت آمين','210','1','0');
INSERT INTO [t5597] VALUES (311,'من ذكرت عنده فلم يصل علي قال أحدهما فقد خطيء طريق الجنة وقال الآخر فقد نسي طريق الجنة','211','1','0');
INSERT INTO [t5597] VALUES (312,'في كتابه بمعنى الندب لإجماع جميع المتقدمين والمتأخرين من علماء الأمة على أن ذلك غير لازم فرضا أحدا حتى يكون تاركه من ذلك في حال أخرى ولو كان ذلك فرضا في حال كسائر الفروض أثم بتركه فيها تاركه ولزمه قضاؤه في حال أخرى كما يلزم','212','1','0');
INSERT INTO [t5597] VALUES (313,'وهو يعلم أنه لم يصل عليه أن صلاته فاسدة عليه استقبالها وأنه إن سلم ناسيا الصلاة عليه فعليه إن كان قريبا أن يعود فيجلس فيصلي على النبي','213','1','0');
INSERT INTO [t5597] VALUES (314,'ظننت أن صلاتي لم تتم قيل له أما الذي ذكرت قوله من المتأخرين فإنه لن يخلو أمره فيما قال من ذلك من أحد أمرين إما أن يكون قال ذلك وهو عالم بالأخبار الواردة عن رسول الله','213','1','0');
INSERT INTO [t5597] VALUES (315,'وما مضى عليه السلف وأجمع عليه الخلف واستفاض به نقل الأمة في ذلك وراثة عن نبيها','214','1','0');
INSERT INTO [t5597] VALUES (316,'إلا تقليدا لغيره من علماء الأمة واتباعا لبعض الأئمة على نحو ما يعمل في ذلك بعض العامة من فزعه فيما نابه من ذلك إلى بعض الخاصة ليعرفه الواجب عليه فيه فيعمل به فإن قال لنا فاذكر بعض ما حضرك من الأخبار الواردة عن رسول الله','214','1','0');
INSERT INTO [t5597] VALUES (317,'السلام على الله السلام على فلان فقال لنا النبي','215','1','0');
INSERT INTO [t5597] VALUES (318,'قلنا السلام على الله قبل عباده السلام على فلان وفلان فأقبل علينا رسول الله','215','1','0');
INSERT INTO [t5597] VALUES (319,'في الصلاة قلنا السلام على الله قبل عباده السلام على جبريل السلام على ميكائل السلام على فلان السلام على فلان يعني الملائكة فانصرف إلينا رسول الله فقال إن الله هو السلام فإذا جلس أحدكم في الصلاة فليقل التحيات لله','216','1','0');
INSERT INTO [t5597] VALUES (320,'علمه التشهد في الصلاة قال كنا نحفظه عن عبد الله بن مسعود كما نحفظ حروف القرآن قال جلس على وركه اليسرى قال التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين','216','1','0');
INSERT INTO [t5597] VALUES (321,'في وسط الصلاة وفي آخرها عبد الرحمن بن الأسود بن يزيد النخعي عن أبيه عن عبد الله بن مسعود قال علمني رسول الله','217','1','0');
INSERT INTO [t5597] VALUES (322,'علم فواتح الخير وجوامعه وخواتمه فقال إذا قعدتم في كل ركعتين فقولوا التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين أشهد ألا إله إلا الله وأشهد أن محمدا','218','1','0');
INSERT INTO [t5597] VALUES (323,'في الصلاة قال فسلمت عليه فلم يجبني وقال إن الله تبارك وتعالى يحدث في أمره ما يشاء وإنه قد أحدث أن لا تكلموا في الصلاة ثم علمنا التشهد التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة الله وبركاته السلام','218','1','0');
INSERT INTO [t5597] VALUES (324,'إذا تشهد أحدكم فليقل اللهم إني أعوذ بك من فتنة المسيح الدجال','219','1','0');
INSERT INTO [t5597] VALUES (325,'إذا فرغ أحدكم من التشهد فليتعوذ من هذه الأربع من عذاب القبر ومن عذاب النار ومن فتنة المحيا والممات ومن فتنة المسيح الدجال','219','1','0');
INSERT INTO [t5597] VALUES (326,'إذا فرغ أحدكم من التشهد فليتعوذ بالله من أربع من عذاب جهنم ومن عذاب القبر ومن فتنة المحيا والممات ومن شر فتنة المسيح الدجال قال ابن البرقي قال','219','1','0');
INSERT INTO [t5597] VALUES (327,'لأعرابي ما تقول إذا جلست في الصلاة قال فذكر التشهد وأقول اللهم إني أسألك الجنة وأعوذ بك من النار ولا أدري ما دندنتك ودندنة معاذ قال عليهما أدندن أنا ومعاذ','220','1','0');
INSERT INTO [t5597] VALUES (328,'لرجل كيف تشهد حين تفرغ من صلاتك فأخبره قال أقول اللهم إني أسألك الجنة وأعوذ بك من النار ولست أحسن دندنتك ولا دندنة معاذ فقال رسول الله حولها ندندن','220','1','0');
INSERT INTO [t5597] VALUES (329,'ذكر الأخبار الواردة في ذلك عن الصحابة وغيرهم','221','1','0');
INSERT INTO [t5597] VALUES (330,'بالتخطئة الزاعم أن صلاة كل من صلى قبل إحداثه القول الذي قاله كانت فاسدة إلا أن يكون صلاها على ما حكينا عنه من قوله فأحد الأقوال الأربعة التي ذكرنا أنه لا خامس لهن قول من قال إذا رفع المصلي رأسه من السجود من آخر ركعة من صلاته','225','1','0');
INSERT INTO [t5597] VALUES (331,'ويسلم سئل البرهان على قوله من أصل أو نظير ويقال له هل بينك وبين آخر اجترأ جرأتكم على خلاف الأمة فزعم أن صلاة المصلي لا تتم إلا أن يصلي بعد التشهد في آخر ركعة منها على نوح أو على إبراهيم وإسحاق ويعقوب أو غيرهم من أنبياء الله','226','1','0');
INSERT INTO [t5597] VALUES (332,'بقوله وسلموا تسليما وقد أجمعوا أن ذلك تسليم على محمد','226','1','0');
INSERT INTO [t5597] VALUES (333,'دون أن يكون أمرا بالتسليم على سائر الأنبياء إذ كان النبي','226','1','0');
INSERT INTO [t5597] VALUES (334,'أنه إذ قيل له قد علمنا كيف السلام عليك فكيف الصلاة عليك قال لهم قولوا اللهم صل على محمد الكلمات التي قد ذكرناها فعلمنا بذلك أنه المعني بقوله يا أيها الذين آمنوا صلوا عليه قيل له فما تنكر أن يكون','226','1','0');
INSERT INTO [t5597] VALUES (335,'وحده بقوله إن الله وملائكته يصلون على النبي فعلم بذلك أنه إنما عنى به نبي الذين خاطبهم بقوله يا أيها الذين آمنوا صلوا عليه قيل له ما تنكر أن يكون إنما خرج ذلك بلفظ الواحد والمراد به جميع الأنبياء كما قيل','227','1','0');
INSERT INTO [t5597] VALUES (336,'ذكر من قال إذا رفع المصلي رأسه من السجدة الآخرة من آخر ركعة من صلاته فقد مضت صلاته وتمت تشهد أو لم يتشهد سلم أو لم يسلم جلس قدر التشهد أو لم يجلس','228','1','0');
INSERT INTO [t5597] VALUES (337,'إذا أحدث الإمام بعدما يرفع رأسه من آخر السجدة واستوى جالسا تمت صلاته وصلاة من خلفه ممن ائتم به ممن أدرك معه أول الصلاة','231','1','0');
INSERT INTO [t5597] VALUES (338,'إذا رفع الإمام رأسه من الركعة الرابعة وأحدث فقد تمت صلاة من خلفه','231','1','0');
INSERT INTO [t5597] VALUES (339,'إذا جلس الإمام في آخر صلاته ثم أحدث فقد تمت صلاته وصلاة من خلفه','232','1','0');
INSERT INTO [t5597] VALUES (340,'قال من رفع رأسه من السجود في آخر صلاته ثم أحدث قبل أن يسلم فقد جازت صلاته وأما علتهم من جهة النظر فالقياس على ما صح به الخبر عن رسول الله','232','1','0');
INSERT INTO [t5597] VALUES (341,'ذكر من قال إذا جلس قدر التشهد تمت صلاته وإن لم يتشهد','233','1','0');
INSERT INTO [t5597] VALUES (342,'ذكر من قال لا تتم صلاة المصلي إلا بتشهد','234','1','0');
INSERT INTO [t5597] VALUES (343,'أمته وأمرهم به في صلاتهم كما أمرهم بقراءة القرآن فمن تركه كان في حكم من ترك القراءة في صلاته في أن صلاته لا تجزئه','237','1','0');
INSERT INTO [t5597] VALUES (344,'قال مفتاح الصلاة الوضوء وتحريمها التكبير وتحليلها التسليم','239','1','0');
INSERT INTO [t5597] VALUES (345,'مفتاح الصلاة الطهور وتحريمها التكبير وتحليلها التسليم وعلى كل ركعتين تسليمة','240','1','0');
INSERT INTO [t5597] VALUES (346,'الوضوء مفتاح الصلاة والتكبير تحريمها والتسليم تحليلها فهذا ما في ذلك عن الصحابة والتابعين والسلف الصالح وأما الخبر الذي روي عن أبي مسعود أنه قال لو صليت صلاة لم أصل فيها على النبي','241','1','0');
INSERT INTO [t5597] VALUES (347,'بعد التشهد في صلاته حتى خرج منها فاسدة عليه الإعادة وذلك أن الذي حكي عن أبي مسعود في الخبر الذي ذكرنا عنه إنما هو أنه قال لو صليت صلاة لم أصل فيها على النبي','241','1','0');
INSERT INTO [t5597] VALUES (348,'إذ كان هو المتبوع لا التابع فهذا هذا لو صح الخبر عن أبي مسعود بما روي عنه في ذلك وكيف وعزيز تصحيحه فإن قال لنا قائل فهل ترى للمصلي أن يصلي على النبي','242','1','0');
INSERT INTO [t5597] VALUES (349,'إذ علم أمته الصلاة علمهم الصلاة عليه فيها وقد علمت أن النبي','242','1','0');
INSERT INTO [t5597] VALUES (350,'بعد التشهد في آخر صلاته أنه خالف بقوله ذلك الأخبار المأثورة عن رسول الله','242','1','0');
INSERT INTO [t5597] VALUES (351,'وفي إذنك للمصلي أن يصلي على النبي','242','1','0');
INSERT INTO [t5597] VALUES (352,'أنه قال إذ علم أمته التشهد في الصلاة فإذا قلتموها فليتخير أحدكم من الدعاء ما أحب والصلاة على النبي','242','1','0');
INSERT INTO [t5597] VALUES (353,'واخترناها على كثير من نوافل الفضل فإنا لا نحب لأحد أن يتقرب إلى الله تعالى ذكره في شيء من أعماله','242','1','0');
INSERT INTO [t5597] VALUES (354,'ولم نجد في كتاب الله ولا في خبر صح عن رسول الله','243','1','0');
INSERT INTO [t5597] VALUES (355,'في شيء من صلاة المصلي ولا ندبا إليها بل الأخبار عن رسول الله','243','1','0');
INSERT INTO [t5597] VALUES (356,'أمته من القول والعمل فيها وأما قول النبي','243','1','0');
INSERT INTO [t5597] VALUES (357,'غير أنا وإن اخترنا ما بينا من الدعاء والقول بعد التشهد فلا نرى صلاة مصل فاسدة بصلاته على النبي','243','1','0');
INSERT INTO [t5597] VALUES (358,'ندب إلى ذلك أمته وقال لهم إذا ركعتم فعظموا الرب وإذا سجدتم فاجتهدوا في الدعاء فإنه قمن أن يستجاب لكم وما أشبه ذلك من الأخبار التي قد مضى ذكرنا لها فأغنى ذلك عن إعادتها فإن قال فهل وجدت أحدا من سلف الأمة ندب إلى الصلاة','243','1','0');
INSERT INTO [t5597] VALUES (359,'ثم يسأل حاجته','244','1','0');
INSERT INTO [t5597] VALUES (360,'القول في البيان عما في هذه الأخبار من الغريب','244','1','0');
INSERT INTO [t5597] VALUES (361,'إذ علم أمته الصلاة عليه قولوا اللهم صل على محمد وقد بينا معنى قوله اللهم فيما مضى من كتابنا هذا وذكرنا اختلاف المختلفين من أهل العربية فيه وبينا الصواب لدينا من القول فيه وأما الصلاة فإنها في كلام العرب','244','1','0');
INSERT INTO [t5597] VALUES (362,'وصل عليهم إن صلاتك سكن لهم يعني بقوله وصل عليهم ادع لهم بخير فإن قال قائل فإن كانت الصلاة دعاء كما قلت فقد وجب أن يكون قولنا اللهم صل على محمد مسألة منا ربنا أن يدعو لمحمد','245','1','0');
INSERT INTO [t5597] VALUES (363,'ويبارك عليه وذلك أن صلاة الله على عبده رحمته إياه وصلاة العباد بعضهم لبعض دعاء بعضهم لبعض فالعباد يرغبون إلى الله جل ثناؤه بقولهم اللهم صل على محمد في أن يرحمه ويبارك عليه والله تعالى ذكره يصلي عليه برحمته','245','1','0');
INSERT INTO [t5597] VALUES (364,'وآل عباس وآل عقيل وقل ما يستعملونه مع المجهول من الأسماء لا يكادون يقولون رأيت آل الرجل وآل المرأة وقد يقال للرجل الذي يطلب النساء ويريدهن ويهواهن هو من آل النساء ومن ذلك قول الشاعر فإنك من آل النساء وإنما يكن','246','1','0');
INSERT INTO [t5597] VALUES (365,'من الإسلام وشرائعه والقرآن وحكمه مثلا للقبس يقتبسه المقتبس فجعل ذلك في نوره وبرهانه وضيائه لمن','248','1','0');
INSERT INTO [t5597] VALUES (366,'ولا أدري ما دندنتك ولا دندنة معاذ وقول النبي','250','1','0');
INSERT INTO [t5597] VALUES (367,'في التشهد قولوا التحيات لله فإن التحيات جمع تحية والتحية في كلام العرب الملك ومنه قول زهير بن جناب الكلبي أبني إني فاعلموا أورثتكم مجدا بنيه وتركتكم سادات أقوام زنادكم وريه من كل ما نال الفتى','250','1','0');
INSERT INTO [t5597] VALUES (368,'ذكر خبر آخر من أخبار موسى بن طلحة عن أبيه عن النبي','251','1','0');
INSERT INTO [t5597] VALUES (369,'فقال مثل مؤخرة الرحل وقال محمد بن عبيد يكون بين أيديكم وقال أبو كريب في حديثه مثل مؤخرة الرحل يكون بين يدي أحدكم ولم يقل شيئا من ذلك إسحاق بن إبراهيم وقالوا جميعا في حديثهم ثم لا يضره ما مر بين يديه','251','1','0');
INSERT INTO [t5597] VALUES (370,'فقال إذا كان بين يدي أحدكم مثل مؤخرة الرحل فلا يضره من مر بين يديه','252','1','0');
INSERT INTO [t5597] VALUES (371,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلل إحداها أنه خبر لا يعرف له عن طلحة عن النبي','252','1','0');
INSERT INTO [t5597] VALUES (372,'ولم يقل فيه عن أبيه والثالثة أن سماك بن حرب عندهم ممن لا يعتمد على نقله','252','1','0');
INSERT INTO [t5597] VALUES (373,'ذكر من روى هذا الحديث فأرسله عن موسى بن طلحة عن النبي','253','1','0');
INSERT INTO [t5597] VALUES (374,'قال يستر الرجل قدر مؤخرة الرحل وقد وافق طلحة بن عبيد الله في رواية معنى هذا الخبر عن رسول الله','253','1','0');
INSERT INTO [t5597] VALUES (375,'ذكر ذلك','253','1','0');
INSERT INTO [t5597] VALUES (376,'سئل في غزوة تبوك عما يستر المصلي فقال مثل مؤخرة الرحل قال سعيد في مثل دقة الخيط','253','1','0');
INSERT INTO [t5597] VALUES (377,'إذا قام أحدكم يصلي فإنما يستره إذا كان بين يديه مثل آخرة الرحل','254','1','0');
INSERT INTO [t5597] VALUES (378,'قال إذا صلى أحدكم فإن سترته بين يديه مثل آخرة الرحل','254','1','0');
INSERT INTO [t5597] VALUES (379,'بالأبطح في قبة حمراء من أدم فأخرج بلال عنزة فركزها ثم خرج رسول الله','254','1','0');
INSERT INTO [t5597] VALUES (380,'خرج في حلة حمراء فركز عنزة يصلي إليها يمر من ورائها الكلب والمرأة والحمار','255','1','0');
INSERT INTO [t5597] VALUES (381,'عنزة يمر وراءها الكلب والمرأة والحمار لا تمنع قال فصلى رسول الله','255','1','0');
INSERT INTO [t5597] VALUES (382,'صلى بهم بالبطحاء وبين يديه عنزة الظهر ركعتين والعصر ركعتين وبين يديه المرأة والحمار','256','1','0');
INSERT INTO [t5597] VALUES (383,'وهو بالأبطح في قبة فلما كان بالهاجرة خرج بلال فنادى بالصلاة ثم دخل بلال فأخرج العنزة فخرج النبي','256','1','0');
INSERT INTO [t5597] VALUES (384,'الظهر ركعتين والعصر ركعتين ويمر بين يديه المرأة والحمار','256','1','0');
INSERT INTO [t5597] VALUES (385,'فصلى بنا الظهر ركعتين والعصر ركعتين إلى عنزة قال القاسم أظنه قال يمر من ورائها المرأة والحمار','256','1','0');
INSERT INTO [t5597] VALUES (386,'بالأبطح فجاء بلال فأذنه بالصلاة قال فدعا بوضوء فتوضأ ثم أخذ بلال العنزة فمشى بها مع رسول الله','257','1','0');
INSERT INTO [t5597] VALUES (387,'إلى عنزة أو شبهها والطريق من ورائها','257','1','0');
INSERT INTO [t5597] VALUES (388,'يصلي بالأبطح قد ركز بين يديه العنزة يمر بين يديه الحمار والمرأة','257','1','0');
INSERT INTO [t5597] VALUES (389,'بالأبطح فركز عنزة بين يديه','258','1','0');
INSERT INTO [t5597] VALUES (390,'العصر بالأبطح ركعتين وكانت معه عنزة يركزها بين يديه يصلي قال قلنا مثل من كنت يومئذ قال كنت أبري وأريش','258','1','0');
INSERT INTO [t5597] VALUES (391,'قدم مكة فنزل الأبطح فلما زالت الشمس خرج فركز عنزة فصلى إليها ركعتين','258','1','0');
INSERT INTO [t5597] VALUES (392,'بالهاجرة إلى البطحاء فتوضأ','258','1','0');
INSERT INTO [t5597] VALUES (393,'كانت تركز له الحربة فيصلي إليها فسئل عبيد الله فقال في العيدين','259','1','0');
INSERT INTO [t5597] VALUES (394,'أنه كان يعرض راحلته فيصلي إليها قال قلت أرأيت إذا هبت الرحال قال يأخذ الرحل فيعدله فيصلي إلى آخرته أو مؤخرته','260','1','0');
INSERT INTO [t5597] VALUES (395,'كان يأمر بالحربة فيخرج يوم العيد فيصلي إليها والناس وراءه وكان يفعل ذلك في السفر فمن ثم اتخذتها الأمراء','260','1','0');
INSERT INTO [t5597] VALUES (396,'كان إذا خرج إلى العيد أمر بالحربة فتوضع له ثم ذكر سائر الحديث نحو حديث ابن المثنى','260','1','0');
INSERT INTO [t5597] VALUES (397,'أنه كان يركز الحربة قدامه يوم الفطر والنحر فيصلي إليها','260','1','0');
INSERT INTO [t5597] VALUES (398,'كان إذا خرج إلى المصلى أخرج بالعنزة حتى تركز بين يديه فيصلي إليها وذاك أن المصلى كان فضاء ليس فيه شيء يستتر به','261','1','0');
INSERT INTO [t5597] VALUES (399,'بعرفات فصلى إليها والحمار من وراء العنزة','261','1','0');
INSERT INTO [t5597] VALUES (400,'والذي فيها من ذلك الإبانة عما يجزئ المصلي بفضاء من الأرض أن يستتر به مما يمر بين يديه وأن ذلك قدر مؤخرة الرحل فإن سألنا سائل عن قدر ذلك وكيف وجه الاستتار به إذا صلى إليه المصلي قيل قد اختلف السلف من أهل العلم في قدر مبلغ','261','1','0');
INSERT INTO [t5597] VALUES (401,'ذكر من قال ذلك','262','1','0');
INSERT INTO [t5597] VALUES (402,'ذكر من قال ذلك','263','1','0');
INSERT INTO [t5597] VALUES (403,'إنما أخبر السائل عن حد ما يستره ولم يحظر عليه الزيادة على قدر ما بين له أنه يجزئه وفي الأخبار الواردة عنه أنه كان تحمل له الحربة والعنزة فتركزان له فيصلي إلى هذه مرة وإلى هذه أخرى أوضح البيان عن أن ما كان أبدا على قدر','263','1','0');
INSERT INTO [t5597] VALUES (404,'ذكر من روي عنه من السلف أنه كان يصلي إلى مثل مؤخرة الرحل أو كان يأمر بذلك','264','1','0');
INSERT INTO [t5597] VALUES (405,'قال إذا صلى أحدكم وبين يديه مثل آخرة الرحل فلا يضره ما مر بين يديه','264','1','0');
INSERT INTO [t5597] VALUES (406,'أنه قال يجزئ المصلي مثل آخرة الرحل','265','1','0');
INSERT INTO [t5597] VALUES (407,'أنه كان يأمر بالعنزة أن تركز له فيصلي إليها','266','1','0');
INSERT INTO [t5597] VALUES (408,'لا تصلوا إلا إلى سترة ولا يدع أحدكم أحدا يمر بين يديه فإن أبى فليقاتله فإن معه القرين','267','1','0');
INSERT INTO [t5597] VALUES (409,'أنه كان يقول إذا كان أحدكم يصلي فلا يدع أحدا يمر بين يديه فإن أبى فليقاتله فإن معه القرين','267','1','0');
INSERT INTO [t5597] VALUES (410,'وغير ما ذكرت لنا أن الأخبار صحت به عن رسول الله','268','1','0');
INSERT INTO [t5597] VALUES (411,'قيل لك فما وجه الخبر الذي','268','1','0');
INSERT INTO [t5597] VALUES (412,'في فضاء ليس بين يديه شيء','268','1','0');
INSERT INTO [t5597] VALUES (413,'يصلي في الناس بعرفة فمررنا على بعض الصف فنزلنا عنها ودخلنا الصف وتركناها ترتع فلم يقل لنا رسول الله','269','1','0');
INSERT INTO [t5597] VALUES (414,'عباسا في بادية لنا ولنا كليبة وحمارة ترعى فصلى النبي','269','1','0');
INSERT INTO [t5597] VALUES (415,'العباس في بادية لنا ولنا كليبة وحمار فصلى النبي','270','1','0');
INSERT INTO [t5597] VALUES (416,'ذكر من قال ذلك','270','1','0');
INSERT INTO [t5597] VALUES (417,'ذكر من قال ذلك','271','1','0');
INSERT INTO [t5597] VALUES (418,'وهو يصلي فما نهانا ولا ردنا','279','1','0');
INSERT INTO [t5597] VALUES (419,'وهو يصلي فلم ينصرف وجاءت جاريتان من بني عبد المطلب فأخذتا بركبتي رسول الله','279','1','0');
INSERT INTO [t5597] VALUES (420,'يصلي بالناس في أرض خلاء فتركنا الحمار بين أيديهم ثم جئنا حتى دخلنا معهم فما بالى ذلك ولقد كان رسول الله','280','1','0');
INSERT INTO [t5597] VALUES (421,'فنزع إحداهما من الأخرى فما بالى ذلك','280','1','0');
INSERT INTO [t5597] VALUES (422,'في الصلاة وتركنا الحمار فلم ينصرف النبي','281','1','0');
INSERT INTO [t5597] VALUES (423,'يصلي فمررنا بين يديه ثم نزلنا فدخلنا معه الصلاة وجاءت جاريتان تسعيان حتى أخذتا بركبتيه وهو يصلي ففرق بينهما ومضى في صلاته','281','1','0');
INSERT INTO [t5597] VALUES (424,'يصلي حتى أخذتا فخذيه فلما أراد أن يركع فرق بينهما ثم ركع','281','1','0');
INSERT INTO [t5597] VALUES (425,'يصلي بهم بعرفة وأنا والفضل على أتان مرتدفين فلم ينكر علينا ذلك','281','1','0');
INSERT INTO [t5597] VALUES (426,'يصلي بعرفة فمررنا ببعض الصف فنزلنا وتركناها ترتع فلم يبال النبي','282','1','0');
INSERT INTO [t5597] VALUES (427,'يصلي بالناس بمنى فنزلنا فوصلنا الصف ومرت الأتان بين يدي الناس فلم تقطع عليهم الصلاة','282','1','0');
INSERT INTO [t5597] VALUES (428,'يصلي بالناس بمنى فمررت بها بين يدي بعض الصف ثم نزلت عنها فتركتها ترعى فأقبلت فصليت مع الناس فلم يعب ذلك علي أحد','282','1','0');
INSERT INTO [t5597] VALUES (429,'يصلي بالناس بمنى فسرت على الأتان بين يدي بعض الصف ثم نزلت فأرسلتها ودخلت في الصف مع الناس فلم ينكر ذلك علي أحد','283','1','0');
INSERT INTO [t5597] VALUES (430,'وهو يصلي في حجة الوداع فما قال لنا شيئا','283','1','0');
INSERT INTO [t5597] VALUES (431,'يصلي وليس بينه وبين الذين يطوفون بالبيت سترة','283','1','0');
INSERT INTO [t5597] VALUES (432,'بالبيت ثم صلى في حاشية المطاف ليس بينه وبين الطواف أحد','284','1','0');
INSERT INTO [t5597] VALUES (433,'يصلي مما يلي باب بني سهم والناس يمرون بين يديه ليس بينه وبين الكعبة سترة','285','1','0');
INSERT INTO [t5597] VALUES (434,'يصلي حذاء الركن الأسود والرجال والنساء يطوفون بين يديه ما بينه وبينهم سترة','285','1','0');
INSERT INTO [t5597] VALUES (435,'إذا فرغ من طوافه السبعة أتى موضع الفسقينة فصلى عندها عند الركن في حاشية المطاف ليس بينه وبين الطواف أحد قال أبو جعفر يعني بالفسقينة البشكينة والبشك هو البعر وقال آخرون إذا صلى مصل وليس بين يديه شيء يستره فإنه','285','1','0');
INSERT INTO [t5597] VALUES (436,'ذكر من قال ذلك','286','1','0');
INSERT INTO [t5597] VALUES (437,'يصلي فمررت بين يديه وكنت على أتان أو حمار فقال قطع صلاتنا قطع الله أثره قال فأقعدت','286','1','0');
INSERT INTO [t5597] VALUES (438,'ذكر من قال ذلك','287','1','0');
INSERT INTO [t5597] VALUES (439,'صلى بأصحابه فمر حمار بينه وبين الصف فأعاد الصلاة فقالوا أميرنا صلى الصبح أربع ركعات فقال اللهم أرحني منهم وأرحهم مني فما جمع الجمعة الثانية حتى مات','289','1','0');
INSERT INTO [t5597] VALUES (440,'لا يقطع الصلاة إلا الكلب أو الحمار أو المرأة','292','1','0');
INSERT INTO [t5597] VALUES (441,'قال يقطع الصلاة الكلب والمرأة والحمار','292','1','0');
INSERT INTO [t5597] VALUES (442,'قال يقطع الصلاة الكلب والحمار والمرأة','292','1','0');
INSERT INTO [t5597] VALUES (443,'قال يقطع الصلاة الكلب والحمار والمرأة فقلنا يا أبا سعيد ما يستر المصلي قال السهم والحجر والرحل','293','1','0');
INSERT INTO [t5597] VALUES (444,'ببعض الوادي نريد أن','293','1','0');
INSERT INTO [t5597] VALUES (445,'فلم يكبر وجرى إليه يعقوب بن زمعة أخو بني أسد حتى رده وقال آخرون يقطعها إذا صلى كذلك الكلب والحمار والمرأة والخنزير والكافر','294','1','0');
INSERT INTO [t5597] VALUES (446,'لا يقطع صلاة المسلم إلا الحمار والكافر والكلب والمرأة فقالت عائشة يا رسول الله لقد قرنا بدواب سوء','294','1','0');
INSERT INTO [t5597] VALUES (447,'قال يقطع صلاة الرجل الكلب والحمار والمرأة الحائض واليهودي والنصراني والمجوسي والخنزير قال ويكفيك ما كانوا منكم قدر رمية بحجر ألا تقطع صلاتك وقال آخرون إنما يقطع الصلاة الكلب والسنور دون غيرهما','295','1','0');
INSERT INTO [t5597] VALUES (448,'ذكر من قال ذلك','296','1','0');
INSERT INTO [t5597] VALUES (449,'قال إذا صلى أحدكم فإن سترته بين يديه مثل مؤخرة الرحل فإن لم يكن بين يديه مثل مؤخرة الرحل فإنه يقطع الصلاة المرأة والحمار والكلب الأسود قال قلت يا أبا ذر ما بال الأسود من الأحمر من الأصفر قال يا ابن أخي','298','1','0');
INSERT INTO [t5597] VALUES (450,'كما سألتني فقال الكلب الأسود شيطان','299','1','0');
INSERT INTO [t5597] VALUES (451,'قال يقطع الصلاة المرأة والحمار والكلب الأسود فقلت يا رسول الله ما بال الأسود من الأحمر والأبيض فقال إن الأسود شيطان','299','1','0');
INSERT INTO [t5597] VALUES (452,'يقطع الصلاة الكلب الأسود والمرأة والحمار قال قلت لأبي ذر ما بال الأسود من الأحمر من الأبيض من الأصفر قال ابن أخي هكذا سألت رسول الله','299','1','0');
INSERT INTO [t5597] VALUES (453,'أنه قال يقطع الصلاة إذا لم يكن بين يدي الرجل مثل آخرة الرحل المرأة والحمار والكلب الأسود فقلت ما بال الأسود من الأحمر فقال سألت رسول الله','300','1','0');
INSERT INTO [t5597] VALUES (454,'فقال إن الكلب الأسود البهيم شيطان','300','1','0');
INSERT INTO [t5597] VALUES (455,'كما سألتني فقال الأسود شيطان وأما الخبر في المرأة الحائض فقد ذكرناه قبل وذلك حديث ابن عباس عن النبي','301','1','0');
INSERT INTO [t5597] VALUES (456,'ذكر من قال ذلك','301','1','0');
INSERT INTO [t5597] VALUES (457,'من أمره المصلي في صلاته أن يستتر بمثل مؤخرة الرحل وصلاته إلى عنزة وفي فضاء من الأرض إلى غير سترة وقوله تقطع المرأة والحمار والكلب الأسود وصلاته وبين يديه الحمار والكلب','301','1','0');
INSERT INTO [t5597] VALUES (458,'المصلي بالاستتار بمثل مؤخرة الرحل فأمر ندب واختيار لا إيجاب وذلك أنه','302','1','0');
INSERT INTO [t5597] VALUES (459,'في فضاء من الأرض ليس بين يديه شيء وصلاته وبين يديه حمارة وكلبة لا تزجران ولا تؤخران فإن ذلك من فعله عليه السلام كان ليعلم أمته أن الذي أمرهم به من الاستتار في الصلاة كان على النحو الذي ذكرت من الاختيار والإرشاد لا على الإيجاب','302','1','0');
INSERT INTO [t5597] VALUES (460,'إذا صلى أحدكم فليجعل تلقاء وجهه شيئا فإن لم يجد فلينصب عصا فإن لم يجد عصا فليخط خطا بين يديه','303','1','0');
INSERT INTO [t5597] VALUES (461,'قال إذا صلى أحدكم فليجعل تلقاء وجهه شيئا فإن لم يجد فليجعل عصاه فإن لم تكن معه عصا فليخط خطا ثم لا يضره ما مر بين يديه','303','1','0');
INSERT INTO [t5597] VALUES (462,'أنه قال إذا صلى أحدكم فليجعل بين يديه شيئا فإن لم يجد فلينصب عودا فإن لم يجد فليخط خطا ثم لا يضيره ما مر أمامه','303','1','0');
INSERT INTO [t5597] VALUES (463,'قال إذا صلى أحدكم فليجعل تلقاء وجهه شيئا فإن لم يجد شيئا فلينصب عصاه فإن لم يكن له عصا فليخط خطا ثم لا يضره من مر من أمامه','304','1','0');
INSERT INTO [t5597] VALUES (464,'إذا صلى أحدكم فليجعل تلقاء وجهه سترة فإن لم يجد فلينصب عودا فإن لم يجد فليخط خطا ثم لا يضره ما مر أمامه','304','1','0');
INSERT INTO [t5597] VALUES (465,'إذا صلى أحدكم فليجعل تلقاء وجهه شيئا فإن لم يكن معه فلينصب عصا فإن لم يجد عصا ولم يكن في يده عصا فليخطط خطا ثم لا يضره ما مر بين يديه','305','1','0');
INSERT INTO [t5597] VALUES (466,'إذا صلى أحدكم فلم يجد ما يستتر به فليخط خطا فإن قال أوليس صحيحا عندك الخبر الذي روي عن رسول الله','305','1','0');
INSERT INTO [t5597] VALUES (467,'في هذه الأشياء الثلاثة أنها تقطع صلاة المصلي نظير قوله','305','1','0');
INSERT INTO [t5597] VALUES (468,'أنه عرض له وهو في صلاته شيطان حتى التبست عليه القراءة فلم يقطع لذلك صلاته','307','1','0');
INSERT INTO [t5597] VALUES (469,'قام فصلى صلاة الصبح وهو خلفه فقرأ فالتبست عليه القراءة ثم انفتح فلما فرغ من صلاته قال لو رأيتموني وإبليس يلوي لي حنكه فأهويت بيدي فتناولته فلم أزل أخنقه حتى وجدت برد لعابه بين إصبعي هاتين الإبهام والتي تليها ولولا دعوة','307','1','0');
INSERT INTO [t5597] VALUES (470,'تقدموا إلى مصلاكم فإن الشيطان حال بيني وبين مصلاي فجعل ينكه في وجهي كنكه القرد فوضعت يدي في وذمته حتى وجدت برد لسانه على يدي فلولا دعوة أخينا سليمان أصبح موثقا يضحك منه من كان يخافه فلم يستقبل النبي','308','1','0');
INSERT INTO [t5597] VALUES (471,'حدودها ولكنه مضى فيها وأتمها وكذلك معنى قطع المرأة والكلب الأسود والحمار الصلاة إنما هو بشغل قلب من مر ذلك به في صلاته بمروره به فيها وإحداثه له فيها من الشك وحديث النفس ما يقطع به صلاته ويفسدها عليه فأما من أقام حدودها','308','1','0');
INSERT INTO [t5597] VALUES (472,'إذا ما خص من هذه الأشياء الثلاثة قيل إنه','309','1','0');
INSERT INTO [t5597] VALUES (473,'أنه يقطع الصلاة إنما قطعه إياها بمعنى السبب الذي بينه','310','1','0');
INSERT INTO [t5597] VALUES (474,'ما خص من هذه الأشياء بأنها تقطع الصلاة وقد يتوجه ذلك إلى معنى آخر وهو أن يكون معنى قوله في الكلب الأسود أنه شيطان أنه مثل الشيطان فيما يحدث بمروره بين يدي المصلي له من الشغل عن صلاته فيكون ذلك كقول العرب عبد الله أسد يراد به','310','1','0');
INSERT INTO [t5597] VALUES (475,'فمن ذلك قول أبي جحيفة ثم دخل بلال فأخرج العنزة والعنزة قضيب في رأسه حديدة كهيئة سنان الرمح وهي التي تسميها العامة العكازة وأما قوله فركزها فإنه يعني به فغرزها في الأرض كما قال الفرزدق وصهب لحاهم راكزون','310','1','0');
INSERT INTO [t5597] VALUES (476,'إن الكلب الأسود البهيم شيطان فإنه يعني بالبهيم الذي لا يخالط لونه شيء غير السواد وكل شيء أسود لم يخالط لونه غير السواد فهو أسود بهيم ومن ذلك قول الشاعر في صفة ليل سواد لا ضوء فيه تطاول ليلك الجون البهيم فما ينجاب عن','312','1','0');
INSERT INTO [t5597] VALUES (477,'فوضعت يدي في وذمته فإن أصل الوذمة الشيء المعلق بغيره كرشا كان أو كبدا أو قطعة لحم وإنما قيل للكرش وذمة لأنها معلقة وكذلك سيور الدلو يقال لها الوذم لأنها معلقة وهي طوال مقدودة وأرى أن رسول الله','313','1','0');
INSERT INTO [t5597] VALUES (478,'ذلك وذمة لأنه معلق به','313','1','0');
INSERT INTO [t5597] VALUES (479,'في نخل المدينة فرأى ناسا في رؤوس النخل يلقحون فقال ما يصنع هؤلاء قالوا يأخذون من الذكر فيجعلونه في الأنثى يلقحون قال ما أظن ذلك يغني شيئا فبلغهم فتركوه فأزلوا عنها فبلغ ذلك رسول الله','313','1','0');
INSERT INTO [t5597] VALUES (480,'القول في علل هذا الخبر','314','1','0');
INSERT INTO [t5597] VALUES (481,'مخرج إلا من هذا الوجه والخبر انفرد به عندهم منفرد وجب التثبت فيه والثانية أنه عن سماك عن موسى ومن رواية إسرائيل عنه وسماك عندهم وإسرائيل ممن لا يثبت بنقلهما في الدين حجة والثالثة أنه غير جائز عند بعضهم','314','1','0');
INSERT INTO [t5597] VALUES (482,'جماعة من أصحابه نذكر ما صح عندنا من ذلك بسنده ثم نتبع جميعه البيان إن شاء الله','314','1','0');
INSERT INTO [t5597] VALUES (483,'سمع أصواتا في النخل فقال ما هذا قالوا يؤبرون النخل قال لو تركوه صلح قال فتركوه فشيص فقال أما ما كان من أمر دنياكم فأنتم أعلم بدنياكم وأما ما كان من أمر دينكم فإلي','315','1','0');
INSERT INTO [t5597] VALUES (484,'والذي فيه من ذلك الدلالة على ألا حرج على المرء في القول في أمر دنياه بما هو عنده حق وإن كان الحق في الذي قال غيره وألا تبعة عليه في الرأي يراه في أسباب المعاش فيشير به على غيره وهو عنده صواب فيعمل به الذي أشار به عليه فيصادف','315','1','0');
INSERT INTO [t5597] VALUES (485,'رأيا رآه في تدبير أمر الدنيا وأسباب المعاش','315','1','0');
INSERT INTO [t5597] VALUES (486,'لا يعمل شيئا ولا يقوله إلا عن وحي من الله به إليه كان عمله ذلك وقوله في أمر الدين أو الدنيا ودلالة على صحة قول من قال قد يجوز أن يفعل رسول الله','316','1','0');
INSERT INTO [t5597] VALUES (487,'قال إنما أنا بشر مثلكم وإنما هو ظن ظننته والظن يخطيء ويصيب وكان قيله ذلك في أمر من أمر المعاش وفيه أيضا الإبانة عن خطأ قول من يقول إن الأنبياء قد كانت علمت كل ما بالخلق إليه الحاجة في أمر الدين والدنيا وذلك أن رسول','316','1','0');
INSERT INTO [t5597] VALUES (488,'أن قوله ذلك كان ظنا ظنه لا يقين علم منه به وأن حكمه فيما لم يكن خبرا منه عن الله تعالى ذكره حكم سائر البشر في أنه لا يعلم من الأمور إلا ما علمه الله تبارك وتعالى','316','1','0');
INSERT INTO [t5597] VALUES (489,'والذي فيه من ذلك قول طلحة فتركوه فأزلوا عنها ويعني بقوله فأزلوا عنها فأزلوا عن تركها من التلقيح فترك ذكر الترك','316','1','0');
INSERT INTO [t5597] VALUES (490,'ذكر خبر آخر من أخبار موسى بن طلحة عن أبيه عن النبي','317','1','0');
INSERT INTO [t5597] VALUES (491,'قال وكانوا لا يجترئون على مسألته فقالوا للأعرابي سل من قضى نحبه من','317','1','0');
INSERT INTO [t5597] VALUES (492,'القول في علل هذا الخبر','318','1','0');
INSERT INTO [t5597] VALUES (493,'إلا من هذا الوجه والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه والثانية أنه من رواية طلحة بن يحيى وطلحة بن يحيى عندهم ممن لا يثبت بنقله في الدين حجة والثالثة أنه خبر قد حدث به عن موسى بن طلحة غير طلحة بن يحيى','318','1','0');
INSERT INTO [t5597] VALUES (494,'فأرسله عن عيسى ولم يرفعه إلى طلحة ولم يذكر فيه موسى بن طلحة','319','1','0');
INSERT INTO [t5597] VALUES (495,'يقول طلحة ممن قضى نحبه وقد حدث هذا الحديث عن إسحاق بن يحيى غير عبد الحميد الحماني فوافق في روايته عنه طلحة بن يحيى فقال فيه عن موسى بن طلحة عن أبيه عن رسول الله','319','1','0');
INSERT INTO [t5597] VALUES (496,'ذكر ذلك','319','1','0');
INSERT INTO [t5597] VALUES (497,'المنبر فخطب الناس وعزاهم وأخبرهم بما لهم فيه من الأجر ثم قرأ رجال صدقوا ما عاهدوا الله عليه الآية قال فقام إليه رجل فقال يا رسول الله من هؤلاء فالتفت إلي وعلي ثوبان أخضران فقال أيها السائل هذا منهم','320','1','0');
INSERT INTO [t5597] VALUES (498,'أحدا ولم يذكر معه موسى بن طلحة','320','1','0');
INSERT INTO [t5597] VALUES (499,'فسأله من الذين قضوا نحبهم فأعرض عنه ثم سأله فأعرض عنه ودخل طلحة من باب المسجد وعليه ثوبان أخضران فقال هذا من الذين قضوا نحبهم','320','1','0');
INSERT INTO [t5597] VALUES (500,'القول فيما في هذا الخبر من الغريب','321','1','0');
INSERT INTO [t5597] VALUES (501,'في طلحة هذا ممن قضى نحبه يعني','321','1','0');
INSERT INTO [t5597] VALUES (502,'لم يكونوا شهدوا قتال المشركين مع رسول الله','321','1','0');
INSERT INTO [t5597] VALUES (503,'من حرب المشركين لئن أشهدنا الله قتالهم يوما لنرين الله ما نصنع فيه فعاهدوا الله أن يبلوا من أنفسهم في جهاد عدوه إن لقوهم يوما فلما كان يوم أحد ولقي المسلمون المشركين أوفى الله بعهده بعضهم ففرغ مما أوجب له على نفسه فقاتل حتى','321','1','0');
INSERT INTO [t5597] VALUES (504,'المشركين لئن أشهدني الله قتالا ليرين ما أصنع فلما كان يوم أحد انكشف المسلمون فقال اللهم إني أبرأ إليك مما جاء به هؤلاء يعني المشركين وأعتذر إليك مما صنع هؤلاء يعني المسلمين فمشى بسيفه فلقيه سعد بن معاذ فقال أي سعد','322','1','0');
INSERT INTO [t5597] VALUES (505,'لئن رأيت قتالا ليرين الله ما أصنع فلما كان يوم أحد وهزم الناس لقي سعد بن معاذ فقال والله إني لأجد ريح الجنة فتقدم فقاتل حتى قتل فنزلت فيه هذه الآية من المؤمنين رجال صدقوا ما عاهدوا الله عليه فمنهم من قضى نحبه','323','1','0');
INSERT INTO [t5597] VALUES (506,'ذكر ما صح عندنا سنده من حديث يحيى بن طلحة بن عبيد الله عن أبيه عن رسول الله','324','1','0');
INSERT INTO [t5597] VALUES (507,'ببعير قد وسم في وجهه فقال لو أن أهل هذا عدلوا النار عن وجه هذه الدابة فقلت لأسمن في أبعد مكان من وجهها فوسمت في عجب الذنب حلقة','325','1','0');
INSERT INTO [t5597] VALUES (508,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين إحداهما أنه خبر لا يعرف له عن طلحة عن النبي','325','1','0');
INSERT INTO [t5597] VALUES (509,'جماعة من أصحابه نذكر ما صح من ذلك عندنا سنده ثم نتبع جميعه البيان إن شاء الله','325','1','0');
INSERT INTO [t5597] VALUES (510,'عن الوسم في الوجه','326','1','0');
INSERT INTO [t5597] VALUES (511,'عن الوسم في الوجه والضرب في الوجه','326','1','0');
INSERT INTO [t5597] VALUES (512,'أن توسم الدابة في وجهها أو تضرب على وجهها','326','1','0');
INSERT INTO [t5597] VALUES (513,'مر بحمار يدخن منخراه قد وسم في وجهه فقال لعن الله من فعل هذا لا يوسمن الوجه ولا يضربن الوجه','327','1','0');
INSERT INTO [t5597] VALUES (514,'لا يسمن أحد في الوجه','327','1','0');
INSERT INTO [t5597] VALUES (515,'حمارا قد وسم وجهه يدخن منخراه فقال لعن الله من فعل هذا ألم أنه ألا يسم أحد الوجه ولا يضرب الوجه','327','1','0');
INSERT INTO [t5597] VALUES (516,'مر بحمار يدخن أنفه قد وسم في أنفه قال فلعن من','327','1','0');
INSERT INTO [t5597] VALUES (517,'حمارا قد وسم في وجهه فقال لعن الله من فعل هذا','328','1','0');
INSERT INTO [t5597] VALUES (518,'أنه رأى حمارا قد وسم في وجهه فلعن من فعل ذلك','328','1','0');
INSERT INTO [t5597] VALUES (519,'أنه رأى حمارا قد وسم في وجهه فلعن من فعل ذلك','328','1','0');
INSERT INTO [t5597] VALUES (520,'حمارا موسوما في وجهه فسب ولعن','328','1','0');
INSERT INTO [t5597] VALUES (521,'أن يضرب على الصور وقال أحدهما ونهى أن يوسم على الصور','329','1','0');
INSERT INTO [t5597] VALUES (522,'حمارا موسوم الوجه فأنكر ذلك قال الرجل والله لا أسمه إلا في أقصى شيء من الوجه فأمر بحمار له فكوي في جاعرتيه فهو أول من كوى الجاعرتين','329','1','0');
INSERT INTO [t5597] VALUES (523,'رأى حمارا موسوم الوجه فأنكر ذلك فقال العباس لا أسمه إلا في أقصى شيء من الوجه فأتي بحمار له فكوى جاعرتيه فهو أول من كوى الجاعرتين','330','1','0');
INSERT INTO [t5597] VALUES (524,'لعن من يسم في الوجه','330','1','0');
INSERT INTO [t5597] VALUES (525,'على بعير قد وسم خديه بحلقتين فقال ما هذا الميسم يا عباس فقال هذا ميسم كنا نسمه في الجاهلية فقال لا تسموا بالخدين فقال العباس لا جرم لا أسم إلا بأقصى عظم منه فوسم بالجاعرتين','330','1','0');
INSERT INTO [t5597] VALUES (526,'عن الوسم في الوجه قال لأسمن في أبعد مكان فيها قال فكان يسم في الجاعرتين','331','1','0');
INSERT INTO [t5597] VALUES (527,'لا تسم في الوجه وأنت عمي فقال العباس لأسمنه في أبعد مكان من الوجه فوسم في الجاعرتين','331','1','0');
INSERT INTO [t5597] VALUES (528,'رأى حمارا قد وسم في وجهه فقال لعن الله من فعل هذا','332','1','0');
INSERT INTO [t5597] VALUES (529,'والذي فيه من ذلك كراهة النبي','332','1','0');
INSERT INTO [t5597] VALUES (530,'بأنه نهى عن الوسم في الوجه وأنه لعن من وسم في الوجه فقد بينت تصريح النبي','333','1','0');
INSERT INTO [t5597] VALUES (531,'عن الوسم في الوجه أن يسم من بهيمة من البهائم وجهها فإن قال لنا قائل فإن كان غير جائز وسمها في وجوهها فأين الموضع الجائز وسمها منها إن كان ذلك جائزا وما البرهان على أن وسمها جائز وقد علمت أن ذلك لها تعذيب إذ كان ذلك إحراقا','333','1','0');
INSERT INTO [t5597] VALUES (532,'وعن أصحابه وغيرهم بأنهم وسموا قيل','333','1','0');
INSERT INTO [t5597] VALUES (533,'يحنكه قال فغدوت فإذا هو في الحائط','333','1','0');
INSERT INTO [t5597] VALUES (534,'يحنكه فإذا النبي','334','1','0');
INSERT INTO [t5597] VALUES (535,'يسم غنما في آذانها','334','1','0');
INSERT INTO [t5597] VALUES (536,'في المربد وفي يده ميسم','334','1','0');
INSERT INTO [t5597] VALUES (537,'فقدمت عليه بإبل كأنها عروق الأرطى فأمر بها أن توسم ميسم إبل الصدقة فتضم إليها','335','1','0');
INSERT INTO [t5597] VALUES (538,'أوضح بإطلاقه وسم البهائم في غير وجوهها أنه حد للوسم من أعضائها قيل نعم وقد ذكرنا الرواية عنه أنه وسم الغنم في آذانها فإن قال فهل من خبر عنه أنه وسم أو أنه أطلق وسم شيء من البهائم في غير الآذان سوى الغنم قيل نعم','336','1','0');
INSERT INTO [t5597] VALUES (539,'بإبل قد وسمتها في آنفها فقال النبي','337','1','0');
INSERT INTO [t5597] VALUES (540,'أخر','337','1','0');
INSERT INTO [t5597] VALUES (541,'سم على بركت الله فوسمتها على أفخاذها وكانت صدقتها حقتين وكانت تسعين قال أبو جعفر هذا غلط لأن الحقة إنما تجب في إحدى وتسعين وبكل الذي قلنا مما أطلقنا الوسم فيه من أعضاء البهائم أو نهينا عن الوسم فيه منها قال','338','1','0');
INSERT INTO [t5597] VALUES (542,'القول في البيان عما في هذه الأخبار من الغريب','339','1','0');
INSERT INTO [t5597] VALUES (543,'بقوله يبلى من ابن آدم كل شيء غير عجب الذنب ومنه يركب الخلق','339','1','0');
INSERT INTO [t5597] VALUES (544,'لنقادة لا تحرق وجوه العجم فإن العجم جمع عجماء والعجماء صفة لكل بهيمة وإنما قيل ذلك لها لأنها لا تنطق فتفصح يقال للذكر أعجم وللأنثى عجماء ومنه قول النبي','340','1','0');
INSERT INTO [t5597] VALUES (545,'لنقادة إذ قال له فأين أسم في موضع الجرير من السالفة فإن الجرير كل حبل مضفور ومنه قول الشاعر يجرره الصبي بكل سهب ويحبسه على الخسف الجرير وأما السالفة فإن السالفة من ابن آدم هو موضع متذبذب القرط من','341','1','0');
INSERT INTO [t5597] VALUES (546,'وفي يده سفرجلة يقلبها فقال لي النبي','341','1','0');
INSERT INTO [t5597] VALUES (547,'وفي يده سفرجلة فألقاها إلي أو قال رمى بها إلي وقال دونكها يا أبا محمد فإنها تجم الفؤاد','342','1','0');
INSERT INTO [t5597] VALUES (548,'القول في علل هذا الخبر','343','1','0');
INSERT INTO [t5597] VALUES (549,'مخرج يصح إلا من رواية ولد طلحة عنه والثانية أنه من رواية طلحة بن يحيى وفي نقل طلحة بن يحيى عندهم نظر وقد روي هذا الخبر عن طلحة بن عبيد الله عن النبي','343','1','0');
INSERT INTO [t5597] VALUES (550,'ذكر ذلك','343','1','0');
INSERT INTO [t5597] VALUES (551,'وهو في نفر من أصحابه وفي يده سفرجلة يقلبها فلما جلست إليه دحا بها نحوي ثم قال لي','343','1','0');
INSERT INTO [t5597] VALUES (552,'ذكر خبر آخر من أخبار يحيى بن طلحة عن أبيه عن النبي','344','1','0');
INSERT INTO [t5597] VALUES (553,'يقول كلمة لا يقولها عبد عند موته إلا فرج الله عنه كربته وأشرق لونه فما منعني أن أسأله عنها إلا القدرة عليها حتى مات فقال عمر إني لأعلمها فقال له طلحة وما هي قال هل تعلم كلمة أعظم من كلمة أمر بها عمه لا إله إلا الله','344','1','0');
INSERT INTO [t5597] VALUES (554,'القول في علل هذا الخبر','345','1','0');
INSERT INTO [t5597] VALUES (555,'يصح إلا من هذا الوجه والثانية أنه خبر قد حدث به عن الشعبي غير مطرف فاختلفوا فيه عليه فقال بعضهم فيه عنه عن يحيى بن طلحة عن أمه سعدى المرية عن طلحة وقال بعضهم فيه عن رجل عن سعدى عن طلحة والثالثة أن بعض من روى','345','1','0');
INSERT INTO [t5597] VALUES (556,'ذكر من حدث هذا الحديث عن الشعبي فقال فيه عنه عن يحيى بن طلحة عن أمه سعدى المرية عن طلحة','346','1','0');
INSERT INTO [t5597] VALUES (557,'فقال مالك مكتئبا أساءك إمرة ابن عمك قال لا ولكن سمعت رسول الله','346','1','0');
INSERT INTO [t5597] VALUES (558,'ذكر من حدث هذا الحديث فقال فيه عن الشعبي عن رجل عن سعدى امرأة طلحة','346','1','0');
INSERT INTO [t5597] VALUES (559,'قال كلمة لم أسأله عنها حتى مات وقبض قال إني لأعلم كلمة لا يقولها رجل عند موته إلا كانت له نورا في صحيفته وإن روحه وجسده ليجدان لها راحة عند موته فقال عمر إني لأعلم ما هي لا إله إلا الله هي الكلمة التي أراد عمه عليها قال لا','347','1','0');
INSERT INTO [t5597] VALUES (560,'يزعم أنها موجبة فلم أسأله عنها فقال أبو بكر قد علمت ما هي قال ما هي قال لا إله إلا الله','347','1','0');
INSERT INTO [t5597] VALUES (561,'يقول إني لأعرف كلمة لا يقولها رجل إلا دخل الجنة فلم أسأله عنها ولم يسأله عنها أحد فيخبرني فقالا نحن نعلمها قال ومن أين ولم تكونا في المجلس حين ذكرها فقالا أجل ولكنا قد علمناها قال وما هي قالا الكلمة التي','348','1','0');
INSERT INTO [t5597] VALUES (562,'وسوس ناس من أصحابه فكنت فيمن وسوس فمر علي عمر فسلم علي فلم أرد عليه فشكاني إلى أبي بكر فجاءنا فقال يسلم عليك أخوك فلم تسلم عليه فقلت ما علمت بتسليمه وإني عن ذلك لفي شغل فقال أبو بكر ولم قال قلت قبض النبي','349','1','0');
INSERT INTO [t5597] VALUES (563,'عن نجاة هذا الأمر فقال من قبل الكلمة التي عرضتها على عمي فردها علي فهي له نجاة','349','1','0');
INSERT INTO [t5597] VALUES (564,'ذكر خبر آخر من أخبار طلحة بن عبيد الله عن النبي','351','1','0');
INSERT INTO [t5597] VALUES (565,'فأسلما فاستشهد أحدهما وأخر الآخر بعده سنة فقال طلحة بن عبيد الله أريت الجنة فرأيت المؤخر منهما أدخل الجنة قبل المستشهد فعجبت لذلك فأصبحت فذكرت ذلك لرسول الله','351','1','0');
INSERT INTO [t5597] VALUES (566,'المؤخر قد صام بعده رمضان وصلى بعده ستة آلاف وكذا وكذا ركعة لصلاة السنة','352','1','0');
INSERT INTO [t5597] VALUES (567,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلل إحداها أنه خبر لا يعرف له عن طلحة بن عبيد الله عن النبي','352','1','0');
INSERT INTO [t5597] VALUES (568,'رجلان من بلي فأضافهما علي فاستشهد أحدهما وبقي الآخر بعده سنة ثم مات فرأيت في المنام كأن المؤخر دخل الجنة قبل الأول','352','1','0');
INSERT INTO [t5597] VALUES (569,'فقال أبا محمد أليس قد صام بعده رمضان وصلى بعده سنة ستة آلاف ركعة وكذا وكذا ركعة صلاة السنة والثانية أنه خبر قد حدث به عن أبي سلمة غير محمد بن عمرو فقال فيه عنه عن طلحة ولم يجعل بين أبي سلمة وطلحة أبا هريرة','353','1','0');
INSERT INTO [t5597] VALUES (570,'وكان إسلامهما جميعا وكان أحدهما أشد اجتهادا من الآخر فغزا المجتهد منهما فاستشهد ثم مكث الآخر بعده سنة ثم توفي قال طلحة بينا أنا عند باب الجنة إذ أتي بهما فخرج خارج فأذن للذي توفي الآخر منهما ثم رجع فأذن للذي استشهد ثم رجع','353','1','0');
INSERT INTO [t5597] VALUES (571,'من أي ذلك تعجبون قالوا يا رسول الله هذا كان أشد الرجلين اجتهادا ثم استشهد في سبيل الله فدخل الآخر الجنة قبله فقال أليس قد مكث هذا بعده سنة قالوا بلى قال وأدرك رمضان وصامه وصلى كذا وكذا سجدة في السنة','354','1','0');
INSERT INTO [t5597] VALUES (572,'ثم ذكر مثله غير أنه قال في حديثه ثم رجع إلي وقال ارجع فإنه لم يأن لك بعد وقد وافق طلحة في رواية معنى هذا الخبر عن رسول الله','355','1','0');
INSERT INTO [t5597] VALUES (573,'ذكر ذلك','355','1','0');
INSERT INTO [t5597] VALUES (574,'قال آخى النبي','355','1','0');
INSERT INTO [t5597] VALUES (575,'فجعلنا ندعو الله ونرغب إليه أن يلحقه بصاحبه فقال النبي','355','1','0');
INSERT INTO [t5597] VALUES (576,'ألا تعدون لهذا فضيلته صلاته وعمله بعد عمله لما بينهما أبعد مما بين السماء والأرض','355','1','0');
INSERT INTO [t5597] VALUES (577,'القول في البيان عما في هذا الخبر من الفقه','356','1','0');
INSERT INTO [t5597] VALUES (578,'لما ذكر له أمر الرجلين اللذين استشهد أحدهما وعاش الآخر بعده سنة قال في الذي عاش بعد صاحبه أليس قد أدرك رمضان وصامه وصلى كذا وكذا سجدة فلما قالوا له بلى قال فلما بينهما أبعد مما بين السماء والأرض','356','1','0');
INSERT INTO [t5597] VALUES (579,'نظير الأخبار الواردة عنه أنه قال إذ قيل له أي الناس خير قال من طال عمره وحسن عمله','357','1','0');
INSERT INTO [t5597] VALUES (580,'ألا أنبئكم بخيركم قالوا بلى يا رسول الله قال خياركم أطولكم أعمارا وأحسنكم أعمالا','357','1','0');
INSERT INTO [t5597] VALUES (581,'قال ألا أخبركم بخياركم من شراركم قال قلنا بلى يا رسول الله قال خياركم أطولكم عمرا وأحسنكم عملا','357','1','0');
INSERT INTO [t5597] VALUES (582,'إن من سعادة المرء أن يطول عمره ويرزقه الله الإنابة','358','1','0');
INSERT INTO [t5597] VALUES (583,'إن من السعادة أن يطول عمر العبد ويرزقه الله الإنابة','359','1','0');
INSERT INTO [t5597] VALUES (584,'فمن ذلك قوله فإنه لم ينأ لك يعني بذلك لم يأن لك فقدم النون قبل الهمزة كما يقال جذب وجبذ وصقع وصعق وأما قوله ويرزقه الله الإنابة فإن الإنابة الرجعة إلى الشيء بعد الإدبار عنه يقال منه أناب فلان من كذا إلى','359','1','0');
INSERT INTO [t5597] VALUES (585,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين','360','1','0');
INSERT INTO [t5597] VALUES (586,'يصح إلا من هذا الوجه والثانية أنه من نقل إسماعيل بن عياش وفي نقل إسماعيل عن غير أهل بلده عندهم نظر وقد حدث نحو هذا الحديث عن رسول الله','361','1','0');
INSERT INTO [t5597] VALUES (587,'ذكر ذلك','361','1','0');
INSERT INTO [t5597] VALUES (588,'إذا كان رأس الخمس والعشرين والمائتين نادى مناد من السماء ألا أيها الناس إن الله قد قطع مدة الجبارين والمنافقين وأتباعهم ووليكم الجابر جبر أمة محمد','361','1','0');
INSERT INTO [t5597] VALUES (589,'هو رجل من ولدي كأنه من رجال بني إسرائيل يخرج عند جهد من أمتي وبلاء عربي اللون ابن أربعين سنة كأن وجهه كوكب دري يملأ الأرض عدلا كما ملئت ظلما وجورا يملك عشرين سنة وهو صاحب مدائن الكفر كلها القسطنطينية ورومية ويخرج','361','1','0');
INSERT INTO [t5597] VALUES (590,'ذكر خبر آخر من أخبار طلحة بن عبيد الله عن النبي','363','1','0');
INSERT INTO [t5597] VALUES (591,'عن ذلك قال فقال علي إن الجريء كل الجريء من قال على رسول الله','364','1','0');
INSERT INTO [t5597] VALUES (592,'رخص له في ذلك قال إن ولد لي ولد فأسميه باسمك وأكنيه بكنيتك قال فرخص له في ذلك','364','1','0');
INSERT INTO [t5597] VALUES (593,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلل إحداها أنه خبر لا يعرف له مخرج يصح عن طلحة عن رسول','364','1','0');
INSERT INTO [t5597] VALUES (594,'إلا من هذا الوجه والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه والثانية أنه قد رواه عن منذر الثوري غير ابنه فقال فيه عنه عن محمد بن الحنفية أن عليا سأل رسول الله','365','1','0');
INSERT INTO [t5597] VALUES (595,'أحدا والثالثة أن محمد بن طلحة بن عبيد الله فيما ذكر كان يكنى أبا القاسم ولو كان الخبر عن طلحة عن رسول الله','365','1','0');
INSERT INTO [t5597] VALUES (596,'من قوله في ذلك أنه قال تسموا باسمي ولا تكنوا بكنيتي فإني إنما أنا قاسم أقسم بينكم فنهى عن التكني بكنيته','366','1','0');
INSERT INTO [t5597] VALUES (597,'أحدا','366','1','0');
INSERT INTO [t5597] VALUES (598,'وكانت تلك رخصة له دون الناس','366','1','0');
INSERT INTO [t5597] VALUES (599,'ذكر من وافق طلحة في رواية هذا الخبر عن رسول الله','368','1','0');
INSERT INTO [t5597] VALUES (600,'لا تجمعوا بين اسمي وكنيتي أنا أبو القاسم الله يعطي وأنا أقسم','368','1','0');
INSERT INTO [t5597] VALUES (601,'أنه نهى أن يكنى الرجل أبا القاسم واسمه محمد','368','1','0');
INSERT INTO [t5597] VALUES (602,'قال لا تجمعوا بين اسمي وبين كنيتي أنا أبو القاسم الله يعطي وأنا أقسم','369','1','0');
INSERT INTO [t5597] VALUES (603,'قال لا تجمعوا اسمي وكنيتي','369','1','0');
INSERT INTO [t5597] VALUES (604,'لا تجمعوا بين اسمي وكنيتي','369','1','0');
INSERT INTO [t5597] VALUES (605,'من تسمى باسمي فلا يكتنين بكنيتي','369','1','0');
INSERT INTO [t5597] VALUES (606,'قال تسموا باسمي ولا تكنوا بكنيتي من تسمى باسمي فلا يكتنين بكنيتي','370','1','0');
INSERT INTO [t5597] VALUES (607,'قال من تسمى باسمي فلا يكتنين بكنيتي ومن اكتنى بكنيتي فلا يتسمين باسمي','371','1','0');
INSERT INTO [t5597] VALUES (608,'قال إذا اكتنيتم بي فلا تسموا بي وإذا تسميتم بي فلا تكتنوا بي','371','1','0');
INSERT INTO [t5597] VALUES (609,'من المعاني','371','1','0');
INSERT INTO [t5597] VALUES (610,'معروفة حالهم غير متروكة روايتهم','371','1','0');
INSERT INTO [t5597] VALUES (611,'كراهته الجمع بين اسمه وكنيته أم برواية علي عن رسول الله','372','1','0');
INSERT INTO [t5597] VALUES (612,'لا تتدافع ولا تتضاد إذا صحت مخارجها وأن ذلك إذا ورد باختلاف الرواة فيه فإنما هو على وجه العموم والخصوص أو المجمل والمفسر أو الناسخ والمنسوخ وأن ذلك إذا كان على وجه الناسخ والمنسوخ كان غير جائز أن يكون الناسخ منهما غير معلوم من','372','1','0');
INSERT INTO [t5597] VALUES (613,'فنذكر ما قالوا وما اعتل به كل قائل منهم لقوله في ذلك ثم نتبع جميعه البيان إن شاء الله فقال بعضهم غير جائز لأحد أن يجمع لابنه في الاسم والكنية اسم النبي','372','1','0');
INSERT INTO [t5597] VALUES (614,'وأخبار أخر سنذكرها بعد إن شاء الله وقالوا خبر علي عن رسول الله','372','1','0');
INSERT INTO [t5597] VALUES (615,'ذكر من لم نذكر قبل ممن قال هذا القول أو رواه عن رسول الله','373','1','0');
INSERT INTO [t5597] VALUES (616,'فقال من تسمى باسمي فلا يتكن بكنيتي قال فكناه النبي','373','1','0');
INSERT INTO [t5597] VALUES (617,'أن يجمعا عليه محمد وأبو القاسم','373','1','0');
INSERT INTO [t5597] VALUES (618,'لا تجمعوا','373','1','0');
INSERT INTO [t5597] VALUES (619,'لعلي أن يسمي ابنه محمدا ويكنيه أبا القاسم إطلاق منه ذلك لجميع أمته إذ لم يخبر أنه خص بذلك عليا دون سائر الناس غيره قالوا وقد سمى ولده باسم النبي','374','1','0');
INSERT INTO [t5597] VALUES (620,'لها ذلك لم يمتنعوا من الجمع لمن جمعوا له بين اسم النبي','375','1','0');
INSERT INTO [t5597] VALUES (621,'ذكر بعض من جمع له اسم النبي','375','1','0');
INSERT INTO [t5597] VALUES (622,'بما','375','1','0');
INSERT INTO [t5597] VALUES (623,'فقالت يا رسول الله إنه ولد لي غلام فسميته محمدا وكنيته بأبي القاسم فبلغني أنك تكره ذلك فقال رسول الله','375','1','0');
INSERT INTO [t5597] VALUES (624,'ما أحل اسمي وحرم كنيتي أو ما حرم اسمي وأحل كنيتي','376','1','0');
INSERT INTO [t5597] VALUES (625,'فقالت يا رسول الله إني ولدت غلاما وسميته محمدا وكنيته أبا','376','1','0');
INSERT INTO [t5597] VALUES (626,'ذكر من قال ذلك','377','1','0');
INSERT INTO [t5597] VALUES (627,'ولم يتسم باسمه أيكره قال نعم','377','1','0');
INSERT INTO [t5597] VALUES (628,'فذكر ذلك له فقال أسم ابنك عبد الرحمن','378','1','0');
INSERT INTO [t5597] VALUES (629,'فذكر له الذي قلنا قال فقال رسول الله','378','1','0');
