INSERT INTO [t5597] VALUES (654,'في التسمي باسمه ونهى عن التكني بكنيته قالوا فغير جائز لأحد صح عنده الخبر عن رسول الله','388','1','0');
INSERT INTO [t5597] VALUES (655,'أو أن يسمي ابنا له باسمه','388','1','0');
INSERT INTO [t5597] VALUES (656,'تسمون محمدا ثم تسبونه','389','1','0');
INSERT INTO [t5597] VALUES (657,'تسمون أولادكم محمدا ثم تلعنونهم والصواب من القول في ذلك عندنا أن يقال كل هذه الأخبار','389','1','0');
INSERT INTO [t5597] VALUES (658,'بما ذكرت صحيحة وليس شيء من ذلك مدافعا غيره ولا ناسخ فيه ولا منسوخ ولو كان في ذلك ناسخ أو منسوخ لقد كانت الأمة نقلت بيان ذلك كما نقلت ما روت مما ذكرنا وإنما كان نهي النبي','390','1','0');
INSERT INTO [t5597] VALUES (659,'والتكني بكنيته لمن فعل ذلك ولا نكروه وقد سمى جماعة منهم ولده محمدا وكناه أبا القاسم فلم ينكر ذلك على من فعله منهم منكر وفي تركهم النكير على من فعل ذلك ورضاهم بما فعل من ذلك الدليل الواضح على أن نهي النبي','390','1','0');
INSERT INTO [t5597] VALUES (660,'وكنيته فإن تكنى بعض من كان اسمه محمدا لم أره تقدم على معصية لله ولا أنه لزمه بفعله ذلك إثم وإن كرهته لما قد بينت قبل','390','1','0');
INSERT INTO [t5597] VALUES (661,'ذكر خبر آخر من أخبار طلحة عن النبي','391','1','0');
INSERT INTO [t5597] VALUES (662,'قال لا تحل الصدقة لغني ولا لذي مرة سوي','391','1','0');
INSERT INTO [t5597] VALUES (663,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلل إحداها أنه خبر لا يعرف له مخرج عن طلحة عن النبي','391','1','0');
INSERT INTO [t5597] VALUES (664,'جماعة نذكر ما صح عندنا من ذلك بسنده ثم نتبع جميعه البيان إن شاء الله','392','1','0');
