INSERT INTO [t5597] VALUES (836,'بنقل العدول أنه أمر بتغيير الشيب وأنه نهى عن تغييره وكان معلوما أن الأمر والنهي غير جائز اجتماعهما في حال واحدة عن شيء واحد وأن أحدهما لو كان ناسخا صاحبه كان ذلك مبينا أوقاتهما ليعلم الذي على الناس من ذلك فيعملوا به','499','688','0');
INSERT INTO [t5597] VALUES (837,'إنما غيروا في الحال التي كان فيها شيبهم وبياض شعورهم كشيب أبي قحافة وشعره أو في حال كان منهم ذلك قريبا من ذلك وأن الذين اختاروا ترك تغيير شيبهم فلم يغيروه كان شيبهم مخالفا شيب أبي قحافة وبياض شعر رأسه ولحيته إما بالإختلاس والإشماط','500','688','0');
INSERT INTO [t5597] VALUES (838,'بتغيير ذلك ندبا لا فرضا وإرشادا لا إيجابا وكذلك لا أرى مغير ذلك وإن كان قليلا ما ابيض منه حرجا بتغييره إذ كان النهي عن ذلك من رسول الله','500','688','0');
INSERT INTO [t5597] VALUES (839,'عن ذلك لو كان على وجه التحريم أو لو كان الأمر فيما أمر به من ذلك كان على وجه الإيجاب لكان تاركوا التغيير قد أنكروا على المغيرين أو كان المغيرون قد أنكروا على تاركي التغيير ولكن الأمر كان في ذلك كالذي وصفت إن شاء الله','500','688','0');
INSERT INTO [t5597] VALUES (840,'والقبيح ما قبحه وقد بينا الحال التي ندب إلى تغيير البياض والحال التي نهي عن تغييره فيها وسننه أولى السنن أن تتبع وأن يستن بها','501','688','0');
INSERT INTO [t5597] VALUES (841,'فمن ذلك قول قيس بن أبي حازم كان أبو بكر يخرج إلينا وكأن لحيته ضرام العرفج من الحناء والكتم يعني بقوله ضرام العرفج لهب نار العرفج في شدة الحمرة من حمرة الخضاب بالحناء والكتم يقال منه اضطرمت النار فهي تضطرم اضطراما إذا','501','688','0');
INSERT INTO [t5597] VALUES (842,'ورأسه ولحيته كأنها ثغامة بيضاء فإن الثغامة فيما قيل شجرة بالبادية معروفة إذا يبست ابيضت تجمع ثغاما وإياه عنى الفرزدق بقوله وقالوا لنا زيدوا عليهم فإنهم لغاء وإن كانوا ثغام اللهازم يعني بالثغام جمع الثغامة','504','688','0');
