INSERT INTO [t5597] VALUES (845,'وهذا خبر عندنا صحيح سنده وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لأنه خبر لا يعرف له مخرج عن الزبير عن','505','688','0');
