INSERT INTO [t5597] VALUES (866,'للفارس سهمين وللراجل سهما','515','688','0');
INSERT INTO [t5597] VALUES (867,'بإسهامه للفارس ثلاثة أسهم وإنكار الرواية عنه بخلاف ذلك وقال آخرون بتصحيح الرواية الواردة عنه بإسهامه للفارس سهمين وللراجل سهما وإنكار الرواية عنه بخلاف ذلك','516','688','0');
INSERT INTO [t5597] VALUES (868,'بإسهامه للفارس ثلاثة أسهم وقال به أو عمل به من السلف','516','688','0');
INSERT INTO [t5597] VALUES (869,'أن للفرس سهمين ولصاحبه سهما','518','688','0');
INSERT INTO [t5597] VALUES (870,'الخيل للفرس سهمين ولصاحبه سهما وأخذ بذلك المسلمون بعد رسول الله','518','688','0');
INSERT INTO [t5597] VALUES (871,'ذكر من صحح الرواية عن رسول الله','519','688','0');
INSERT INTO [t5597] VALUES (872,'في إسهامه الفارس من الغنائم ما ورد بأنه أسهمه ثلاثة أسهم سهمين لفرسه وسهما له فأما في إسهامه الراجل فإنه لا اختلاف في أنه لا زيادة له على سهم واحد بين أهل العلم فيحتاج إلى التشاغل بأمره وأما الرواية عنه أنه أسهم للفارس','520','688','0');
INSERT INTO [t5597] VALUES (873,'من وجهين أحدهما من رواية عبد الله بن عمر العمري عن نافع عن ابن عمر ولا يدفع ذو علم بآثار رسول الله','521','688','0');
INSERT INTO [t5597] VALUES (874,'أنه أسهم للفارس ثلاثة أسهم ومن المحال أن يكون ابن عمر قال لم يزد النبي','521','688','0');
INSERT INTO [t5597] VALUES (875,'للفارس ثلاثة أسهم لأن ذلك إذا قاله قائل لم يخل من أحد وجهين إما أن يكون متعمدا قيله وهو يعلم وجه فساده فيكون كاذبا في قيله والكذب عن ابن عمر رحمه الله منفي غير موهوم منه تعمده أو يكون ساهيا ناسيا أحد قوليه فيكون القولان','521','688','0');
INSERT INTO [t5597] VALUES (876,'على نحو ما بينا وأحسن حالات عبد الله بن عمر العمري فيما روى في ذلك عن نافع عن ابن عمر عن النبي','521','688','0');
INSERT INTO [t5597] VALUES (877,'فهل من أثر عن رسول الله','523','688','0');
INSERT INTO [t5597] VALUES (878,'في ذلك والرواية إذا اختلفت عنه لم يكن أحد فريقيها أولى بالتصديق من الآخر إذا تعادلا في القناعة والرضا والعدالة قيل إن فريقي نقلة سيرة النبي','524','688','0');
INSERT INTO [t5597] VALUES (879,'في ذلك إنما هو من أحد وجهين إما من وجه النقل عن نافع وإما من وجه النقل عن مجمع بن يعقوب الأنصاري على ما ذكرنا من إسنادهما ذلك إلى رسول الله','524','688','0');
INSERT INTO [t5597] VALUES (880,'التي لا مخالف لها ولا مدافع وذلك كخبر هشام بن عروة عن أبيه عن الزبير عن النبي','524','688','0');
INSERT INTO [t5597] VALUES (881,'وسائر الأخبار التي ذكرناها عن رسول الله','524','688','0');
INSERT INTO [t5597] VALUES (882,'الدلالة البينة على أن من أوصى بوصية أو وقف وقفا على ذوي قرابته أن أولاد أخواته وإخوته يستوون فيما يستحقون من تلك الوصية والوقف ذلك أن الزبير ذكر أن رسول الله','525','688','0');
INSERT INTO [t5597] VALUES (883,'فيما ذكر عنه جبير بن مطعم يخص بسهم ذوي القربى قرابته من بني هاشم بن عبد مناف بن قصي وكان يعطي معهم بني المطلب بن عبد مناف بسبب الحلف الذي كان بينهم وبين بني هاشم ولم يكن يعطي منه بني نوفل بن عبد مناف ولا بني عبد شمس بن عبد مناف','525','688','0');
INSERT INTO [t5597] VALUES (884,'أعطاه ذلك بأنه من ولد عمته صفية ابنة عبد المطلب بن هاشم بن عبد مناف ففي ذلك دليل على صحة ما قلت من استواء حق ولد الإخوة والأخوات فيما أوصى به الموصي أو جعله المتصدق الصدقة المؤبدة في صدقته لذوي قرابته وأن ولد إخوانه وولد عماته','525','688','0');
INSERT INTO [t5597] VALUES (885,'فيما قسم له من خمس خمس الغنيمة وهو سهم ذي القربى من بني هاشم لأنه ابن أختهم وهو رجل من ولد أسد بن عبد العزى','526','688','0');
INSERT INTO [t5597] VALUES (886,'بحديث ثم سمعت النبي','526','688','0');
INSERT INTO [t5597] VALUES (887,'القول في علل هذا الخبر','526','688','0');
INSERT INTO [t5597] VALUES (888,'إلا من هذا الوجه والخبر إذا انفرد به','526','688','0');
INSERT INTO [t5597] VALUES (889,'أنه قال إذا حدثكم أهل الكتاب فلا تصدقوهم ولا تكذبوهم','527','688','0');
INSERT INTO [t5597] VALUES (890,'القول في البيان عن معنى هذا الخبر','528','688','0');
INSERT INTO [t5597] VALUES (891,'صحيحا بنهيه أمته عن تصديق أهل الكتاب في حديثهم أو تكذيبهم قلنا بلى فإن قال وكيف حدثه بما سمعه من بعضهم من غير إضافته ذلك إلى من سمعه منه قيل إن النبي','528','688','0');
INSERT INTO [t5597] VALUES (892,'أو عن كتاب الله لكان علينا تصديقه في خبره ذلك وإن كان من الأخبار الممكنة غير الواجبة','528','688','0');
INSERT INTO [t5597] VALUES (893,'أو نبيهم عليه السلام أو كتابنا أو كتابهم مما لا نعلم حقيقته لم يكن لنا تصديقه فذلك المعنى الذي فرق بين حكم خبر الكتابي وخبر غيره من أهل الإسلام فيما أخبرا عن رسول الله','529','688','0');
INSERT INTO [t5597] VALUES (894,'الحديث الذي ذكره الزبير أنه سمع رجلا من اليهود يحدثه به ثم سمع بعد النبي','529','688','0');
INSERT INTO [t5597] VALUES (895,'من علمه مثل ما عند اليهودي منه فحدثه اليهودي فسمعه النبي','529','688','0');
INSERT INTO [t5597] VALUES (896,'على ما كان عنده من علمه من غير أن يضيفه إلى اليهودي الذي حدثه لأنه لم يكن علم ذلك عنده من قبل خبر اليهودي وممكن أيضا أن يكون كان اليهودي حدثه به إذ حدثه به ولا علم عند رسول الله','529','688','0');
INSERT INTO [t5597] VALUES (897,'ذكر خبر آخر من أخبار عروة عن أبيه عن النبي','530','688','0');
INSERT INTO [t5597] VALUES (898,'سيفا في يده يوم أحد فقال من يأخذ هذا السيف بحقه قال فقمت فقلت أنا يا رسول الله قال فأعرض عني ثم قال من يأخذ هذا السيف بحقه قال فقمت فقلت أنا يا رسول الله قال فأعرض عني ثم قال من يأخذ هذا السيف','530','688','0');
