INSERT INTO [t5597] VALUES (908,'فتح خيبر فكنت في أول من دخل مدينة خيبر فقاتلت حتى رئي مكاني وعلي ثوب أحمر فما علمت أني ركبت في الإسلام ذنبا أعظم منه للشهرة','540','688','0');
INSERT INTO [t5597] VALUES (909,'من قاتل لتكون كلمة الله هي العليا فهو في سبيل الله بمعزل لأنه لم يكن قتاله لله وإنما كان ليرى مكانه وطلب الذكر به وفيه أيضا البيان عن أن قتل النساء من مشركي أهل الحرب قد كان جائزا وأن النهي عن قتلهن من رسول الله','541','688','0');
