INSERT INTO [t5597] VALUES (912,'أتي يوم فتح مكة بامرأة مقتولة فقال ما كانت هذه تقاتل ونهى عن قتل النساء والولدان','542','688','0');
INSERT INTO [t5597] VALUES (913,'رأى في بعض أسفاره امرأة مقتولة فنهى عن قتل النساء والصبيان','543','688','0');
