INSERT INTO [t5597] VALUES (915,'في غزوة غزاها كان على مقدمته فيها خالد بن الوليد فمر رياح وأصحاب رسول الله','543','688','0');
INSERT INTO [t5597] VALUES (916,'على ناقة له فانفرجوا عن المرأة فوقف رسول الله','543','688','0');
INSERT INTO [t5597] VALUES (917,'في غزاة فمر بامرأة مقتولة والناس عليها ففرجوا له فقال ها ما كانت هذه لتقاتل أدرك خالدا فقل له لا تقتل ذرية ولا عسيفا','544','688','0');
INSERT INTO [t5597] VALUES (918,'فمررنا على امرأة مقتولة وقد اجتمع عليها الناس ففرجوا له فقال ما كانت هذه','544','688','0');
INSERT INTO [t5597] VALUES (919,'يأمرك ألا تقتل ذرية ولا عسيفا','545','688','0');
INSERT INTO [t5597] VALUES (920,'قال مررنا بامرأة مقتولة لها خلق والناس عليها فوقف بها النبي','545','688','0');
INSERT INTO [t5597] VALUES (921,'ها ما كانت هذه تقاتل ثم قال لرجل إلحق خالد بن الوليد أو قال قل لخالد بن الوليد لا يقتل ذرية ولا عسيفا وقد قتل','545','688','0');
INSERT INTO [t5597] VALUES (922,'يوم فتح مكة بقتل نسوة ثلاث منهن قينتان كانتا لعبد الله بن خطل وسارة مولاة عكرمة بن أبي جهل وكانت القينتان تعنيان بهجاء رسول الله','546','688','0');
