INSERT INTO [t5740] VALUES (1,'(الجزء','1','0','0');
INSERT INTO [t5740] VALUES (2,'(الجزء','1','0','0');
INSERT INTO [t5740] VALUES (3,'(الشيخ الأول شمس الدين، أبو العباس أحمد بن أبي أحمد عبد الواحد بن أحمد بن عبد الرحمن بن إسماعيل بن منصور السعدي المقدسي الحنبلي - رحمه الله - وهو والد المؤلف. (٥٦٤ هـ - ٦٢٣ هـ))','1','2','0');
INSERT INTO [t5740] VALUES (4,'- الغداة، ثم أقبل علينا، فوعظنا موعظة بليغة، ذرفت منها العيون، ووجلت منها القلوب. فقال قائل: يا رسول الله، إن هذه لموعظة مودع فماذا تعهد إلينا؟ قال:','4','2','0');
INSERT INTO [t5740] VALUES (5,'- الصبح ذات يوم، ثم أقبل علينا بوجهه، فوعظنا موعظة بليغة ذرفت منها العيون، ووجلت منها القلوب، فقال قائل: يا رسول الله، كأن هذه موعظة مودع، فماذا تعهد إلينا؟ ،','7','2','0');
INSERT INTO [t5740] VALUES (6,'- صلاة الصبح، ثم أقبل علينا بوجهه، فوعظنا موعظة بليغة ذرفت منها الأعين، ووجلت منها القلوب، فقال قائل: يا رسول الله، كأنها موعظة مودع، فأوصنا، قال: " أوصيكم بتقوى الله والسمع والطاعة، وإن كان عبدا حبشيا، فإنه من يعش منكم فسيرى','13','2','0');
INSERT INTO [t5740] VALUES (7,'- واستخلف أبو بكر رضي الله عنه، وكفر من كفر من العرب، قال عمر بن الخطاب لأبي بكر - رضي الله عنه -: كيف تقاتل الناس، وقد قال رسول الله -','20','2','0');
INSERT INTO [t5740] VALUES (8,'- لقاتلتهم على منعه "، قال عمر - رضي الله عنه -: " فوالله ما هو إلا أن رأيت الله قد شرح صدر أبي بكر للقتال، فعرفت أنه الحق ".','20','2','0');
INSERT INTO [t5740] VALUES (9,'-، واستخلف أبو بكر - رضي الله عنه -، وكفر من كفر من العرب، قال عمر بن الخطاب لأبي بكر - رضي الله عنهما -: كيف تقاتل الناس وقد قال رسول الله -','23','2','0');
INSERT INTO [t5740] VALUES (10,'- لقاتلتهم على منعه ". فقال عمر بن الخطاب - رضي الله عنه -: " فوالله، ما هو إلا أنني رأيت الله قد شرح صدر أبي بكر للقتال، فعرفت أنه الحق ".','23','2','0');
INSERT INTO [t5740] VALUES (11,'-: " لا يدخل الجنة قتات " - يعني نمام -.','28','2','0');
INSERT INTO [t5740] VALUES (12,'- يقول: " لا يدخل الجنة قتات ".','29','2','0');
INSERT INTO [t5740] VALUES (13,'-: " من أقال مسلما عثرته أقاله الله يوم القيامة ".','35','2','0');
INSERT INTO [t5740] VALUES (14,'-: " من أقال عثرة أقاله الله يوم القيامة ".','36','2','0');
INSERT INTO [t5740] VALUES (15,'-: " من أقال أخاه المؤمن عثرته في الدنيا أقاله الله عثرته يوم القيامة ". رواه أبو داود في " البيوع " من " سننه " عن الإمام أبي زكريا','37','2','0');
INSERT INTO [t5740] VALUES (16,'-: " إذا رأيتم المداحين / فاحثوا في وجوههم التراب ".','39','2','0');
INSERT INTO [t5740] VALUES (17,'-: " إذا رأيتم المداحين، فاحثوا في وجوههم التراب ".','41','2','0');
INSERT INTO [t5740] VALUES (18,'-: مثله. رواه مسلم في آخر الكتاب من صحيحه، عن أبي موسى','42','2','0');
INSERT INTO [t5740] VALUES (19,'(الشيخ الثاني محمد بن كامل بن أحمد بن أسد التنوخي المعري، الدمشقي أبو المحاسن (٥٢٥ هـ - ٦٠٣ هـ))','49','2','0');
INSERT INTO [t5740] VALUES (20,'- قال: " الرؤيا الحسنة من الرجل الصالح جزء من ستة وأربعين جزءا من النبوة ".','53','2','0');
INSERT INTO [t5740] VALUES (21,'- قال: " من عرض عليه طيب فلا يرده، فإنه خفيف المحمل، طيب الرائحة ".','57','2','0');
INSERT INTO [t5740] VALUES (22,'- قال: " إذا كان يوم','60','2','0');
INSERT INTO [t5740] VALUES (23,'- قال: " إذا كان يوم الجمعة كان على كل باب من أبواب المسجد ملائكة يكتبون، الأول فالأول، فإذا خرج الإمام طويت الصحف ".','61','2','0');
INSERT INTO [t5740] VALUES (24,'- قال: " المهجر إلى الجمعة كالمهدي بدنة، ثم الذي يليه كالمهدي بقرة، والذي يليه كالمهدي','61','2','0');
INSERT INTO [t5740] VALUES (25,'-، قال: " إذا كان يوم الجمعة قام على كل باب من أبواب المسجد ملائكة يكتبون الناس، الأول فالأول، فالمهجر إلى الجمعة كالمهدي بدنة، ثم الذي يليه','64','2','0');
INSERT INTO [t5740] VALUES (26,'- فقال: " والله، إن الله أقدر عليك منك عليه "، قال: " فما ضربت مملوكا بعد ذلك ".','68','2','0');
INSERT INTO [t5740] VALUES (27,'-، فقال: الله أقدر عليك منك عليه "، قال أبو مسعود: " فما ضربت مملوكا لي بعده ".','72','2','0');
INSERT INTO [t5740] VALUES (28,'-، فقال: " والله، لله أقدر عليك منك على هذا "، فحلفت أن لا أضرب','73','2','0');
INSERT INTO [t5740] VALUES (29,'-، فذكر أحاديث منها، وقال: قال رسول الله -','76','2','0');
INSERT INTO [t5740] VALUES (30,'- يقول: " الكمأة من المن، وماؤها شفاء للعين ".','79','2','0');
INSERT INTO [t5740] VALUES (31,'- يقول: " الكمأة من المن، وماؤها شفاء للعين ". هذا حديث صحيح متفق على صحته، رواه البخاري في " التفسير " من جامعه، عن أبي نعيم الفضل بن دكين. ورواه مسلم في " الأطعمة " من صحيحه، عن أبي عبد الله محمد بن يحيى بن أبي عمر العدني.','80','2','0');
INSERT INTO [t5740] VALUES (32,'-: " الحيات ما سالمناهن منذ حاربناهن، فمن ترك منهن شيئا من خيفتهن فليس منا ".','84','2','0');
INSERT INTO [t5740] VALUES (33,'- يعودني، فقلت: يا رسول الله، تركت مالا، ولم أترك إلا ابنة واحدة فأوصي بثلثي مالي وأترك الثلث؟ قال: لا، قلت: فأوصي بالنصف وأترك لها النصف؟ قال: لا، قلت: فأوصي بالثلث وأترك لها الثلثين؟ ، قال: الثلث، والثلث كثير، ثم قال: اللهم','88','2','0');
INSERT INTO [t5740] VALUES (34,'-: " إنكم ستجندون أجنادا، جندا في الشام، وجندا في العراق، وجندا باليمن، قال: قلت يا رسول الله: خر لي، قال: عليكم بالشام، فمن أبى، فليلحق بيمنه، وليستق من غدره، فإن الله قد تكفل لي بالشام وأهله، قال سعيد: وكان ابن حوالة رجلا','91','2','0');
INSERT INTO [t5740] VALUES (35,'- قال: " إنكم ستجندون أجنادا، فجندا بالشام،','93','2','0');
INSERT INTO [t5740] VALUES (36,'-: " سيصير الأمر إلى أن تكونوا جنودا مجندة، فجند بالشام، وجند باليمن، وجند بالعراق "، قال ابن حوالة: خر لي يا رسول الله، إن أدركت ذلك، قال: " عليك بالشام، فإنها','96','2','0');
INSERT INTO [t5740] VALUES (37,'(الشيخ الثالث مكي بن ريان بن شبة بن صالح الماكسيني المولد، الموصلي الدار، المقرئ النحوي، الضرير. ((٠٠٠ - ٦٠٣ هـ))','105','2','0');
INSERT INTO [t5740] VALUES (38,'-: " يهرم ابن آدم، ويبقى منه اثنتان: الحرص والأمل ".','109','2','0');
INSERT INTO [t5740] VALUES (39,'-: قال: " يهرم ابن آدم، ويبقى منه اثنتان: الحرص والأمل ". رواه مسلم في الزكاة من " صحيحه " عن أبي موسى محمد بن','111','2','0');
INSERT INTO [t5740] VALUES (40,'- وأصحابه ما أصابهم،','114','2','0');
INSERT INTO [t5740] VALUES (41,'-: " يا أيها الناس إن الله طيب لا يقبل إلا طيبا، وإن الله أمر','119','2','0');
INSERT INTO [t5740] VALUES (42,'لو طعنت في فخذها لأجزأ عنك','127','2','0');
INSERT INTO [t5740] VALUES (43,'-: " لو طعنت في فخذها أجزأك ". قال العيشي: " زعم المشايخ قبلنا أن ذلك لا يكون - يعني إلا للمتردية ".','128','2','0');
INSERT INTO [t5740] VALUES (44,'(الشيخ الرابع أبو محمد سعد بن عبد الله بن سعد بن هبة الله بن مفلح (٠٠٠ - ٦٠٣ هـ))','135','2','0');
INSERT INTO [t5740] VALUES (45,'- مر بشاة داجن لبعض أهله قد نفقت، فقال: " ألا','138','2','0');
INSERT INTO [t5740] VALUES (46,'- مر بشاة لمولاة ميمونة، فقال: " ألا أخذوا إهابها فدبغوه، فانتفعوا به؟ ! "، قالوا يا رسول الله إنها ميتة، قال: " إنما حرم أكلها ".','140','2','0');
INSERT INTO [t5740] VALUES (47,'- مر بشاة لمولاة ميمونة ماتت، فقال: " ألا أخذوا إهابها فدبغوه، فانتفعوا به؟ ! ، فقالوا: يا رسول الله إنها ميتة، فقال رسول الله -','141','2','0');
INSERT INTO [t5740] VALUES (48,'(الجزء الثاني من مشيخة الإمام ملحق الأحفاد بالأجداد فخر الدين أبي الحسن علي بن أحمد بن عبد الواحد المشهور بابن البخاري. تخريج الإمام الحافظ عمدة المحدثين جمال الدين أبي العباس أحمد بن محمد الظاهري الحنفي - رحمه الله تعالى - فيه حنبل، وابن المنجى)','149','2','0');
INSERT INTO [t5740] VALUES (49,'(الشيخ الخامس أبو علي، وأبو عبد الله حنبل بن عبد الله بن الفرج بن سعادة الواسطي الأصل، البغدادي المولد والدار، الرصافي المكبر. (٥١٧ هـ - ٦٠٤ هـ))','151','2','0');
INSERT INTO [t5740] VALUES (50,'-، فأمر بقطعه، فقال يا رسول الله: قد تجاوزت عنه، قال: فلولا كان هذا قبل أن تأتيني به، يا أبا وهب، فقطعه رسول الله -','154','2','0');
INSERT INTO [t5740] VALUES (51,'-: " لا يصوم عبد يوما في سبيل الله إلا باعد الله بذلك اليوم النار عن وجهه سبعين خريفا ". رواه النسائي في " الصوم " من " سننه "، عن عبد الله بن الإمام أحمد، فوافقناه بعلو.','158','2','0');
INSERT INTO [t5740] VALUES (52,'-: " ألا أعلمك كلمات إذا قلتهن غفر لك - على أنه مغفور لك -: لا إله إلا الله العلي العظيم، لا إله إلا الله الحليم الكريم، سبحان الله رب العرش العظيم، والحمد لله رب العالمين ". هذا حديث حسن، أخرجه النسائي في " النعوت " و " اليوم','160','2','0');
INSERT INTO [t5740] VALUES (53,'-: " ليس على المسلم في فرسه، ولا مملوكه صدقة ". هذا حديث صحيح متفق على صحته من حديث عراك بن مالك عن أبي هريرة - رضي الله عنه - عن النبي -','164','2','0');
INSERT INTO [t5740] VALUES (54,'- قال: " الغسل يوم الجمعة هو واجب على كل محتلم ". هذا حديث صحيح متفق على صحته، أخرجه الأئمة في كتبهم من طرق أحدها: للبخاري في " الصلاة " من جامعه، عن أبي الحسن علي بن عبد الله بن المديني الحافظ.','167','2','0');
INSERT INTO [t5740] VALUES (55,'-، من طرق منها: للنسائي عن أبي موسى هارون بن عبد الله البزاز','168','2','0');
INSERT INTO [t5740] VALUES (56,'-، فأخبرته بالذي صنعت؟ قال: إنما كان يكفيك التيمم ".','171','2','0');
INSERT INTO [t5740] VALUES (57,'فأخبرته بذلك فقال إنما كان يكفيك من ذلك التيمم','173','2','0');
INSERT INTO [t5740] VALUES (58,'يكفيك من ذلك التيمم ".','173','2','0');
INSERT INTO [t5740] VALUES (59,'-، فذكرت ذلك له، فضحك -','175','2','0');
INSERT INTO [t5740] VALUES (60,'- في حاجة، فأجنبت، فلم أجد الماء فتمرغت في الصعيد كما تمرغ الدابة، ثم أتيت رسول الله -','176','2','0');
INSERT INTO [t5740] VALUES (61,'-: " لو خرجتم إلى ذود لنا فشربتم من ألبانها "، قال حميد: وقال قتادة عن أنس: " وأبوالها " ففعلوا فلما صحوا كفروا وارتدوا بعد إسلامهم، وقتلوا راعي رسول الله -','180','2','0');
INSERT INTO [t5740] VALUES (62,'- وهربوا محاربين، فأرسل رسول الله -','180','2','0');
INSERT INTO [t5740] VALUES (63,'-: " لو خرجتم إلى إبلنا، فأصبتم من ألبانها ". قال حميد: قال قتادة: قال أنس: " وأبوالها ". هذا حديث صحيح رواه مسلم في " الحدود " من صحيحه، عن أبي زكريا يحيى بن يحيى النيسابوري، وأبي بكر عبد الله بن محمد بن أبي شيبة كليهما، عن','181','2','0');
INSERT INTO [t5740] VALUES (64,'- عن الساعة، فقال: " ما أعدتت لها "؟ قال: ما أعددت لها من شيء، ولكني أحب الله ورسوله، قال: المرء مع من أحب ". وقال سفيان - مرة -: " أنت مع من أحببت ".','185','2','0');
INSERT INTO [t5740] VALUES (65,'- عن الساعة، فقال: " ما أعددت لها؟ " فكأنه لم يذكر كبيرا، إلا أنه قال: " إني أحب الله ورسوله "، قال: " فإنك مع من تحب ".','185','2','0');
INSERT INTO [t5740] VALUES (66,'- فقال: يا رسول الله - متى الساعة؟ فقام النبي -','186','2','0');
INSERT INTO [t5740] VALUES (67,'-: " المرء مع من أحب، وأنت مع من أحببت ".','186','2','0');
INSERT INTO [t5740] VALUES (68,'-: متى الساعة؟ قال: " هي آتية، فما أعددت لها؟ " قال: ما أعددت لها من كبير عمل إلا أني أحب الله ورسوله، قال: " المرء مع من أحب ". هذا حديث صحيح ثابت متفق على صحته، أخرجه البخاري ومسلم في كتابيهما من عدة طرق، من حديث سالم بن أبي','188','2','0');
INSERT INTO [t5740] VALUES (69,'- ونهى عن صيام يوم السبت إلا في فريضة، وقال: إن لم يجد أحدكم إلا لحاء شجرة، فليفطر عليه ".','191','2','0');
INSERT INTO [t5740] VALUES (70,'- ونهانا / عن صيام يوم السبت إلا في فريضة، وقال: " إن لم يجد أحدكم إلا لحاء شجرة فليفطر عليها ". هذا حديث حسن، شامي الإسناد، رواه النسائي في الصوم من " سننه " من طرق منها: عن عمران بن بكار عن يزيد بن عبد ربه، عن بقية بن الوليد، عن','193','2','0');
INSERT INTO [t5740] VALUES (71,'-: " إذا قلت لصاحبك يوم الجمعة: أنصت، فقد لغيت "، فقال سفيان: قال أبو الزناد: هو لغة أبي هريرة.','196','2','0');
INSERT INTO [t5740] VALUES (72,'-، قال: " إذا تكلمت يوم الجمعة، فقد لغوت، والغات ". هذا حديث صحيح متفق على صحته من حديث أبي هريرة - رضي الله عنه - رواه عنه جماعة منهم: عبد الرحمن بن هرمز الأعرج','198','2','0');
INSERT INTO [t5740] VALUES (73,'- يقول: " إذا قلت لصاحبك يوم الجمعة: أنصت، والإمام يخطب فقد لغوت ".','202','2','0');
INSERT INTO [t5740] VALUES (74,'- قال: " إذا دخلت العشر، فأراد رجل أن يضحي، فلا يمس من شعره ولا بشره ". هذا حديث صحيح، أخرجه مسلم في " الأضاحي " من صحيحه "، عن أبي عبد الله محمد بن يحيى العدني. وأخرجه النسائي فيه من " سننه " عن عبد الله بن محمد الزهري.','203','2','0');
INSERT INTO [t5740] VALUES (75,'- في واد من أوديتهم يقال له: " المخمص "، صلاة العصر، فقال: " إن هذه الصلاة عرضت على الذين','206','2','0');
INSERT INTO [t5740] VALUES (76,'- نهى عنه، فتركناه ". هذا حديث صحيح، رواه مسلم في " البيوع " من صحيحه، عن','209','2','0');
INSERT INTO [t5740] VALUES (77,'- قال: " إذا كان لإحداكن مكاتب عنده ما يؤدي، فلتحتجب عنه ".','211','2','0');
INSERT INTO [t5740] VALUES (78,'- في مجلس، فقال: بايعوني على أن لا تشركوا بالله شيئا ولا تسرقوا، ولا تزنوا، ولا تقتلوا أولادكم، قرأ الآية التي أخذت على النساء {إذا جاءك المؤمنات} ، فمن وفى منكم فأجره على الله تبارك وتعالى،','215','2','0');
INSERT INTO [t5740] VALUES (79,'- غداة جمع والفضل بن عباس ردفه، أن فريضة الله في الحج على','218','2','0');
INSERT INTO [t5740] VALUES (80,'- قال: الشؤم في ثلاثة: الفرس، والمرأة، والدار ". قال','220','2','0');
INSERT INTO [t5740] VALUES (81,'- قال: " إن الله عز وجل يحب الرفق في الأمر كله ". هذا حديث صحيح اتفق الأئمة على إخراجه في كتبهم، فرواه البخاري في " استتابة المرتدين "، عن أبي نعيم الفضل بن دكين.','222','2','0');
INSERT INTO [t5740] VALUES (82,'-، ليس بيني وبينه إلا آخرة الرحل، فقال: يا معاذ، قلت: لبيك - رسول الله - وسعديك، قال: ثم سار ساعة، ثم قال:','224','2','0');
INSERT INTO [t5740] VALUES (83,'-: نحوه أو مثله ".','225','2','0');
INSERT INTO [t5740] VALUES (84,'- قال: " من صلى البردين دخل الجنة ". رواهما البخاري ومسلم في كتابيهما، عن أبي خالد هدبة بن','226','2','0');
INSERT INTO [t5740] VALUES (85,'- إلى اليمن، فلقيت فيها رجلين، ذا كلاع وذا عمرو، قال: وأخبرتهما شيئا من خبر رسول الله -','228','2','0');
INSERT INTO [t5740] VALUES (86,'-، واستخلف أبو بكر - رضي الله عنه - والناس صالحون، قال: فقال لي: أخبر','228','2','0');
INSERT INTO [t5740] VALUES (87,'- غير اسم عاصية، قال: أنت جميلة ".','230','2','0');
INSERT INTO [t5740] VALUES (88,'-: " أخنع اسم عند الله يوم القيامة رجل تسمى بملك الأملاك ". قال أبي: سألت أبا عمرو الشيباني عن أخنع، قال: أوضع اسم عند الله. رواها مسلم في " الاستئذان " من " صحيحه "،','230','2','0');
INSERT INTO [t5740] VALUES (89,'- عامل أهل خيبر بشطر ما يخرج من','231','2','0');
INSERT INTO [t5740] VALUES (90,'- من منى إلى عرفات، منا الملبي، ومنا المكبر ". رواه مسلم وأبو داود في " الحج " من " كتابيهما "، عن الإمام أحمد، فوقع لنا موافقة عالية لهما.','233','2','0');
INSERT INTO [t5740] VALUES (91,'- قال: " إذا / أقيمت الصلاة، فلا صلاة إلا المكتوبة ". رواه مسلم وأبو داود في " الصلاة " من كتابيهما، عن الإمام','234','2','0');
INSERT INTO [t5740] VALUES (92,'- ست عشرة غزوة ". هذا حديث صحيح متفق على صحته، رواه البخاري في " المغازي " من صحيحه، عن أبي الحسن أحمد بن الحسن بن جنيدب الترمذي الحافظ، عن الإمام أحمد. ورواه مسلم " فيه " من " صحيحه "، عن الإمام أحمد فوقع لنا بدلا عاليا للبخاري،','236','2','0');
INSERT INTO [t5740] VALUES (93,'- قال: " إذا وقعت رميتك في الماء، فغرق، فلا تأكل ". رواه أبو داود في " الصيد " من سننه، عن أبي عبد الله محمد بن يحيى بن فارس الذهلي، عن الإمام أحمد، فوقع لنا بدلا عاليا له.','237','2','0');
INSERT INTO [t5740] VALUES (94,'- قال: " إن القبر أول منازل الآخرة، فإن ينج منه فما بعده أيسر منه، وإن لم ينج منه، فما بعده أشد منه "، قال: وقال رسول الله -','239','2','0');
INSERT INTO [t5740] VALUES (95,'- قال: " المؤمنون تكافأ دماؤهم، وهم يد على من سواهم، يسعى بذمتهم أدناهم، ألا، لا يقتل مؤمن بكافر، ولا ذو عهد في عهده ". رواه النسائي في " القود " من " سننه " عن أبي بكر أحمد بن','242','2','0');
INSERT INTO [t5740] VALUES (96,'- سورة " النجم " فسجد، وسجد من عنده، فرفعت رأسي، وأبيت أن أسجد، - ولم يكن أسلم يومئذ المطلب، فكان بعد لا يسمع أحدا يقرأ بها إلا سجد معه ". رواه النسائي في " الصلاة " من " سننه " عن أبي الحسن','244','2','0');
INSERT INTO [t5740] VALUES (97,'-: " من ذرعه القيء فليس عليه قضاء، ومن استقاء فليقض ". رواه ابن ماجه في " الصيام " من سننه "، عن أبي زرعة عبيد','246','2','0');
INSERT INTO [t5740] VALUES (98,'- قال في البحر: " هو الطهور ماؤه، الحل ميتته ". رواه ابن ماجه في " الطهارة " من " سننه "، عن محمد بن يحيى','248','2','0');
INSERT INTO [t5740] VALUES (99,'-، قال: " كمنزلتها الساعة ". سئل شيخنا أبو علي حنبل عن مولده، فقال: في سنة سبع عشرة','250','2','0');
INSERT INTO [t5740] VALUES (100,'(الشيخ السادس القاضي الإمام أبو المعالي محمد، ويسمى أيضا " أسعد " ابن أبي المنجى بن أبي البركات وقيل ابن البركات ابن المؤمل التنوخي المعري ثم الدمشقي الحنبلي. (٥١٩ هـ - ٦٠٦ هـ))','253','2','0');
INSERT INTO [t5740] VALUES (101,'-، ونحن نغسل ابنته، فقال: اغسلنها ثلاثا أو خمسا أو أكثر من ذلك إن رأيتن ذلك بماء وسدر، وليكن في آخره كافور أو شيء من كافور، فإذا فرغتن فآذنني، فلما فرغنا آذناه، فألقى إلينا حقوه، وقال أشعرنها إياه ".','256','2','0');
INSERT INTO [t5740] VALUES (102,'-، فأمرنا أن نغسلها ثلاثا أو خمسا أو اكثر من ذلك إن رأيتن أن تجعلن في الغسلة الأخيرة شيئا من سدر وكافور ". هذا حديث صحيح متفق على صحته، أخرجه الأئمة في كتبهم من طرق منها، ما رواه البخاري في " الجنائز " من صحيحه، عن أبي','257','2','0');
INSERT INTO [t5740] VALUES (103,'- خيبر، فخرجوا إلينا، ومعهم المساحي، فلما رأونا قالوا: محمد والخميس، ورجعوا إلى الحصن يسعون، قال: فرفع رسول الله -','260','2','0');
INSERT INTO [t5740] VALUES (104,'-، فقال: إن الله ورسوله ينهيانكم عن لحوم الحمر، فإنها رجس ". هذا حديث صحيح رواه البخاري منفردا به في " الجهاد " من " صحيحه " عن أبي جعفر عبد الله بن محمد المسندي، وفي " علامات','260','2','0');
INSERT INTO [t5740] VALUES (105,'-: " انظر إليها، فإن في أعين الأنصار شيئا ". هذا حديث صحيح انفرد بإخراجه مسلم، فرواه في " النكاح " من " سننه "، عن أبي عبد الله محمد بن يحيى العدني. ورواه النسائي فيه من " سننه "، عن محمد بن عبد الله بن يزيد','262','2','0');
INSERT INTO [t5740] VALUES (106,'- أن يعتكف، فلم يعتكف حتى كان بعد " حنين "، وكان أعطاه من السبي جارية، قال: وهو في المسجد معتكف، وكان السبي أرسلوا، وقد أسلموا، فجاءوا يكبرون، فقال عمر: ما هذا؟ فقالوا: قد أعتقهم النبي -','263','2','0');
INSERT INTO [t5740] VALUES (107,'- أغيلمة بني عبد المطلب على','264','2','0');
INSERT INTO [t5740] VALUES (108,'- إذا دخلت العشر الأواخر أيقظ أهله وأحيى الليل، وشد المئزر، فقالت عائشة: ومن كل الليل قد أوتر رسول الله -','267','2','0');
INSERT INTO [t5740] VALUES (109,'- يقول: " الحج عرفات - ثلاثا -، فمن أدرك عرفة قبل أن يطلع الفجر، فقد أدرك الحج، وأيام منى ثلاث، فمن تعجل في يومين، فلا إثم عليه ومن تأخر فلا إثم عليه ". رواه الترمذي في " الحج "، من " جامعه "، عن محمد بن يحيى العدني. ورواه','269','2','0');
INSERT INTO [t5740] VALUES (110,'- لصلى العيد قبل الخطبة ثم خطب، فرأى أنه لم يسمع النساء، فأتاهن، فوعظهن، وذكرهن، وأمرهن بالصدقة،، وبلال قائل بثوبه هكذا، قال وكانت المرأة تلقي الخرص، وتلقي الحاتم والشيء ". هذا حديث صحيح متفق على صحته، أخرجه الأئمة في كتبهم من','270','2','0');
INSERT INTO [t5740] VALUES (111,'-: " ليس لنا مثل السوء، العائد في هبته كالكلب يعود في قيئه ". رواه البخاري في " ترك الحيل " من " جامعه " عن أبي نعيم','272','2','0');
INSERT INTO [t5740] VALUES (112,'- بين المهاجرين والأنصار في دارنا، فقال بعض الناس: ليس هو حلفا إنما آخى بينهم ". رواه أبو داود [في " الفرائض " (من سننه) ، عن مسدد بن مسرهد،','275','2','0');
INSERT INTO [t5740] VALUES (113,'- فدى رجلا من المسلمين برجلين من المشركين، أعطاهم مشركا وأخذ','277','2','0');
INSERT INTO [t5740] VALUES (114,'- قال: " إن في الجمعة ساعة لا يوافقها رجل يصلي، فيدعو الله بخير إلا أعطاه الله إياه ". رواه ابن ماجه في " الصلاة " من " سننه " عن محمد بن الصباح،','279','2','0');
INSERT INTO [t5740] VALUES (115,'(الجزء الثالث من مشيخة الشيخ الإمام العالم، ملحق الأحفاد بالأجداد فخر الدين بقية المسندين أبي الحسن علي بن أحمد بن عبد الواحد المقدسي المعروف بابن البخاري. تخريج الإمام الحافظ معين الطلبة وعمدتهم، جمال الدين أبي العباس أحمد بن محمد بن عبد الله','283','2','0');
INSERT INTO [t5740] VALUES (116,'(الشيخ السابع الإمام، الزاهد، أبو عمر محمد بن الشيخ أبي العباس أحمد بن محمد بن قدامة بن مقدام بن نصر المقدسي. (٥٢٨ هـ - ٦٠٧ هـ))','285','2','0');
INSERT INTO [t5740] VALUES (117,'- في الشعب بعدما دفع من عرفات، فبال، ثم توضأ، فقلت يا رسول الله: ألا تصلي؟ فقال لي:','289','2','0');
INSERT INTO [t5740] VALUES (118,'- من عرفات، فلما انتهى إلى الشعب قام بال - ولم يقل أسامة أهراق الماء -، قال: فدعا بماء، فتوضأ وضوءا ليس بالبالغ، قلت: يا رسول الله: الصلاة، قال: " الصلاة أمامك " هذا حديث صحيح، متفق على صحته من حديث أسامة بن زيد، عن النبي -','293','2','0');
INSERT INTO [t5740] VALUES (119,'- قال: " لا نذر في المعصية، وكفارته، كفارة يمين ".','297','2','0');
INSERT INTO [t5740] VALUES (120,'-: " لا نذر في','298','2','0');
INSERT INTO [t5740] VALUES (121,'- أكون مساويا - في الرواية الثانية - لأبي داود، والترمذي، والنسائي، ومن سمعه مني، فكأنما سمعه منهم، ذلك فضل الله يؤتيه من يشاء، وساويت في إسناده','301','2','0');
INSERT INTO [t5740] VALUES (122,'- كان يتعوذ من عذاب القبر ". هذا حديث صحيح رواه البخاري في " صلاة الخوف " من " صحيحه "، عن أبي أحمد محمود بن غيلان المروزي، عن أبي أحمد الزبيري، عن سفيان الثوري. ورواه مسلم فيه من " صحيحه " عن أبي عبد الله محمد بن يحيى العدني، عن','309','2','0');
INSERT INTO [t5740] VALUES (123,'-، من أكرم الناس؟ قال: أتقاهم، قال يا رسول الله: ليس عن هذا نسألك، قال: أفعن معادن العرب تسألوني؟ ! قالوا: نعم قال: فخير الناس في الجاهلية خيرهم في الإسلام إذا فقهوا ". رواه البخاري في " صحيحه " من طرق أحدها في " أحاديث الأنبياء "','311','2','0');
INSERT INTO [t5740] VALUES (124,'- مثله - يعني -: " ائذنوا بالليل لنسائكم إلى المساجد ".','313','2','0');
INSERT INTO [t5740] VALUES (125,'- قال: " ائذنوا للنساء أن يصلين بالليل في المسجد ". أخرجه البخاري في " الصلاة " من " صحيحه " عن عبد الله بن','313','2','0');
INSERT INTO [t5740] VALUES (126,'(الشيخ الثامن أبو المعالي محمد بن أبي القاسم وهب بن سلمان بن أحمد بن علي السلمي، الدمشقي المعروف بابن الزنف (٥٣٣ هـ - ٦٠٦ هـ))','317','2','0');
INSERT INTO [t5740] VALUES (127,'- عن كسب الإماء ". رواه البخاري في " الطلاق " من " صحيحه "، عن أبي الحسن علي بن الجعد بن عبيد الجوهري هذا، فوافقناه بعلو.','321','2','0');
INSERT INTO [t5740] VALUES (128,'- قرأ هذه الآية: {يوم يقوم الناس لرب العالمين} قال: يقومون حتى يبلغ الرشح أطراف آذانهم ". رواه في " صفة النار " من " صحيحه "، عن أبي نصر عبد الملك بن عبد العزيز التمار، نحو ما رويناه، فوقع لنا موافقة عالية له.','323','2','0');
INSERT INTO [t5740] VALUES (129,'- قال للعباس - رضي الله عنه -: " يا عماه! ألا أعطيك، ألا أمنحك، ألا أجيزك عشر خصال، إذا أنت فعلت ذلك غفر الله لك ذنبك أوله وآخره، صغيره وكبيره، سره وعلانيته، خطأه وعمده، تصلي أربع ركعات تقرأ في كل ركعة بفاتحة الكتاب وسورة، فإذا فرغت من','325','2','0');
INSERT INTO [t5740] VALUES (130,'- في بعض أسفاره حتى إذا كنا بالبيداء أو بذات الجيش، انقطع عقدي، فأقام رسول الله -','329','2','0');
INSERT INTO [t5740] VALUES (131,'واضع رأسه على فخذي قد','329','2','0');
INSERT INTO [t5740] VALUES (132,'- والناس وليسوا على ماء، وليس معهم ماء، قالت عائشة: فعاتبني أبو بكر، وقال ما شاء الله أن يقول، وجعل يطعن بيده في خاصرتي، فلا يمنعني من التحرك إلا مكان رسول الله -','330','2','0');
INSERT INTO [t5740] VALUES (133,'- حتى أصبح على غير ماء، فأنزل الله " آية التيمم "، فقال أسيد بن حضير - وهو أحد النقباء -: ما هذا بأول بركتكم يا آل أبي بكر. فبعثنا البعير الذي كنت عليه، فوجدنا العقد تحته ". قال البغوي: هذا معنى لفظ الحديث.','330','2','0');
INSERT INTO [t5740] VALUES (134,'-: " من نذر أن يطيع الله فليطعه، ومن نذر أن يعصي الله فلا يعصه ". قال خلف، قال مالك: نعم.','333','2','0');
INSERT INTO [t5740] VALUES (135,'- قال: " لأن يتصدق الرجل في حياته بدرهم خير من أن يتصدق بمائة دينار عند موته ".','336','2','0');
INSERT INTO [t5740] VALUES (136,'- قال: " فمن الوفد أو قال القوم؟ قالوا: ربيعة، قال: مرحبا بالوفد أو القوم، غير الخزايا ولا الندامى، قالوا: يا رسول الله: أتيناك من شقة بعيدة، وبيننا وبينك هذا الحي من كفار مضر، ولسنا نستطيع أن نأتيك إلا في شهر حرام، فأخبرنا بأمر','340','2','0');
INSERT INTO [t5740] VALUES (137,'- فقال: من القوم أو من الوفد؟ قالوا: ربيعة، قال: مرحبا بالقوم، أو بالوفد غير خزايا ولا ندامى، قالوا: يا رسول الله: إنا لا نستطيع أن نأتيك إلا في الشهر الحرام، وبيننا وبينك هذا الحي من كفار مضر، فمرنا بأمر فصل نخبر به من وراءنا،','342','2','0');
INSERT INTO [t5740] VALUES (138,'- ضرب وغرب، وأن أبا بكر ضرب وغرب وأن عمر - رضي الله عنهما - ضرب وغرب ".','345','2','0');
INSERT INTO [t5740] VALUES (139,'-: جلد وغرب وأن أبا بكر جلد وغرب، وجلد عمر وغرب ". رواه الترمذي في " الحدود " من " جامعه "، والنسائي في " الرجم " من " سننه " كلاهما عن أبي كريب محمد بن العلاء الهمداني، فوافقناهما بعلو. وقال الترمذي حسن غريب.','346','2','0');
INSERT INTO [t5740] VALUES (140,'- خطب فقال: " ما من الناس أحد أمن علينا في صحبته، وذات يده من ابن أبي قحافة، ولو كنت متخذا من الناس خليلا لاتخذت ابن أبي قحافة خليلا، ولكن ود وإخاء إيمان - مرتين أو ثلاثا - وإن صاحبكم خليل الله ". رواه الترمذي في " المناقب " من "','349','2','0');
INSERT INTO [t5740] VALUES (141,'- قال: " إذا مات الإنسان انقطع عمله إلا من ثلاث: من صدقة جارية، أو علم ينتفع به أو ولد صالح يدعو له ".','352','2','0');
INSERT INTO [t5740] VALUES (142,'- قال: " إذا مات الإنسان انقطع عمله إلا من صدقة جارية أو علم ينتفع به، أو ولد صالح يدعو له ".','353','2','0');
INSERT INTO [t5740] VALUES (143,'- قال: " إذا مات الإنسان انقطع عمله إلا من ثلاثة أشياء: من صدقة جارية، أو علم ينتفع به، أو ولد صالح يدعو له ". رواه أبو داود في " الوصايا " من " سننه " عن الربيع بن سليمان، كما رويناه، فوافقنا بعلو. ورواه الترمذي في " الأحكام "','355','2','0');
INSERT INTO [t5740] VALUES (144,'- يقول: " اكفلوا لي بست أكفل لكم بالجنة: إذا حدث أحدكم فلا يكذب، وإذا اؤتمن فلا يخن، وإذا وعد فلا يخلف، غضوا أبصاركم، وكفوا أيديكم، واحفظوا فروجكم ".','358','2','0');
INSERT INTO [t5740] VALUES (145,'(بلغنا السماء مجدنا وجدودنا ... وإنا لنرجوا فوق ذلك مظهرا) فقال: أين المظهر - يا أبا ليلى؟ قلت: الجنة، قال أجل - إن شاء الله تعالى - ثم قلت: (ولا خير في حلم إذا لم تكن له ... بوادر تحمي صفوه أن يكدرا) ولا خير في جهل إذا','360','2','0');
INSERT INTO [t5740] VALUES (146,'-: " إلى أين يا أبا ليلى؟ قال: إلى الجنة، فقال (أجل) إن شاء الله. (ولا خير في حلم إذا لم تكن له ... بوادر تحمي صفوه أن يكدرا) (ولا خير في جهل إذا لم يكن له ... حليم إذا ما اورد الأمر أصدارا) فقال النبي -','362','2','0');
INSERT INTO [t5740] VALUES (147,'(الشيخ التاسع أبو حفص عمر بن أبي بكر محمد بن معمر بن أحمد بن يحيى ابن حسان المؤدب البغدادي الدارقزي المعروف بابن طبرزد (٥١٦ هـ - ٦٠٧ هـ))','367','2','0');
INSERT INTO [t5740] VALUES (148,'- قالت: كان رسول الله -','370','2','0');
INSERT INTO [t5740] VALUES (149,'وسئلت عن الرجل يصبح جنبا وهو يريد الصوم فقالت: " كان رسول الله -','371','2','0');
INSERT INTO [t5740] VALUES (150,'- قالت: " كان رسول الله -','371','2','0');
INSERT INTO [t5740] VALUES (151,'- يمس أهله من الليل، فيصبح جنبا من غير احتلام، فيغتسل، ويصوم ".','372','2','0');
INSERT INTO [t5740] VALUES (152,'- يصبح جنبا من غير احتلام أو غير حلم، ثم يغتسل، فيصوم ". فانطلقا أبو بكر وأبوه إلى مروان، فأخبراه، فقال: " عزمت عليكما لما انطلقتما إلى أبي هريرة فحدثتماه، فانطلقا إلى أبي هريرة - رضي الله عنه فحدثاه، فقال: هما أعلم مني ". هذا','373','2','0');
INSERT INTO [t5740] VALUES (153,'- رواه عنها جماعة منهم أبو بكر بن عبد الرحمن المخزومي، وأبو أيوب سليمان بن يسار الفقيه مولى ميمونة زوج النبي -','374','2','0');
INSERT INTO [t5740] VALUES (154,'-: " الحج جهاد كل ضعيف ". رواه ابن ماجه " الجهاد " من " سننه "، عن أبي بكر عبد الله بن أبي شيبة، عن أبي سفيان وكيع بن الجراح، عن القاسم بن الفضل، نحو ما أخرجناه، فكأن شيوخ شيخي سمعوه من صاحبه. ورواه النسائي من حديث أبي هريرة، عن','379','2','0');
INSERT INTO [t5740] VALUES (155,'- كأن أبا عبد الرحمن النسائي الإمام سمعه من شيخي، وساويته فيه، ووقع لنا عاليا عاليا، ولله الحمد والمنة.','381','2','0');
INSERT INTO [t5740] VALUES (156,'- يقول: " إن بني هشام بن المغيرة استأذنوني في أن ينكحوا ابنتهم علي بن أبي طالب، فلا آذن، ثم لا آذن إلا أن يريد ابن أبي طالب أن يطلق ابنتي، وينكح ابنتهم، فإنما هي بضعة مني، يريبني ما أرابها، ويؤذيني ما آذاها ". هذا حديث صحيح متفق على','382','2','0');
INSERT INTO [t5740] VALUES (157,'- قال: " ليؤمكم أقرؤكم لكتاب الله وأقدمكم قراءة للقرآن، فإن كانت قراءتكم سواء فأقدمكم هجرة، فإن كانت هجرتكم سواء فأقدمكم سنا، ولا يؤم رجل رجلا في سلطانه، ولا','385','2','0');
INSERT INTO [t5740] VALUES (158,'-: " يؤم القوم أقرؤهم لكتاب الله، فإن كانت قراءتهم سواء فليؤمهم أقدمهم هجرة، فإن كانت هجرتهم سواء فليؤمهم أكبرهم سنا، ولا يؤم الرجل في سلطانه، ولا في أهله، ولا يجلس على تكرمته إلا بإذنه أو يأذن لك ". هذا حديث صحيح انفرد به مسلم،','386','2','0');
INSERT INTO [t5740] VALUES (159,'- قالت: " طيبت رسول الله -','389','2','0');
INSERT INTO [t5740] VALUES (160,'- بيدي، ثم أشعرها، وقلدها، ثم بعث بها إلى البيت، فأقام بالمدينة، فما حرم عليه شيء كان له حلا ".','392','2','0');
INSERT INTO [t5740] VALUES (161,'- ذكر كلمة، بعدها [أشعر] بدنته وقلدها، ثم بعث بها إلى البيت، فأقام بالمدينة، فما حرم عليه','393','2','0');
INSERT INTO [t5740] VALUES (162,'-: " اقرأ - أبا عتيك -، فإذا مثل المصابيح مدلاة بين السماء والأرض، ورسول الله -','400','2','0');
INSERT INTO [t5740] VALUES (163,'-: " تلك الملائكة تنزلت لقراءة " سورة البقرة " أما إنك لو مضيت لرأيت العجائب ".','400','2','0');
INSERT INTO [t5740] VALUES (164,'- قلنا: السلام على الله دون عباده، السلام على جبريل وميكائيل، السلام على فلان وفلان، فالتفت إلينا النبي -','403','2','0');
INSERT INTO [t5740] VALUES (165,'-، رواه عنه جماعة منهم: عبد الله بن عباس، وعبد الله بن حنين وغيرهما. أما حديث عبد الله بن عباس، فرواه مسلم والنسائي في','407','2','0');
INSERT INTO [t5740] VALUES (166,'- أنها قالت: قال رسول الله -','411','2','0');
INSERT INTO [t5740] VALUES (167,'أن النبي','414','2','0');
INSERT INTO [t5740] VALUES (168,'-: " العائد في هبته كالعائد في قيئه ".','416','2','0');
INSERT INTO [t5740] VALUES (169,'- قال: " العائد في هبته كالعائد في قيئه ". هذا حديث صحيح متفق على صحته، أخرجه الأئمة في كتبهم منها: البخاري في " الهبة "، عن أبي عمرو مسلم بن إبراهيم، عن هشام الدستوائي، وشعبة. ومنها لمسلم، عن أبي موسى محمد بن المثنى، وأبي بكر','416','2','0');
INSERT INTO [t5740] VALUES (170,'- أعلى من هذا بدرجة، أخبرناه الأشياخ القاضي أبو الفضائل محمود بن أحمد بن عبد الواحد العبدكوي القزويني الحنفي، والإمام أبو الفتوح أسعد بن أبي الفضائل محمود بن خلف بن أحمد بن محمد العجلي الأصبهاني الشافعي المفتي والإمام','418','2','0');
INSERT INTO [t5740] VALUES (171,'-: " العائد في هبته كالعائد في قيئه ".','421','2','0');
INSERT INTO [t5740] VALUES (172,'(الجزء الرابع من مشيخة الإمام العالم، ملحق الأحفاد بالأجداد، بقية المسندين، فخر الدين، أبي الحسن، علي بن أحمد ابن عبد الواحد المقدسي، الحنبلي، عرف بابن البخاري تخريج: الإمام الحافظ عمدة المحدثين، ومفيدهم، جمال الدين أبي العباس أحمد بن محمد','423','2','0');
INSERT INTO [t5740] VALUES (173,'(بقية ترجمة ابن طبرزد عمر بن محمد بن معمر بن يحيى بن أحمد بن حسان)','425','2','0');
INSERT INTO [t5740] VALUES (174,'-، قال: " جاورت','427','2','0');
INSERT INTO [t5740] VALUES (175,'- في بيع العرايا بخرصها ". هذا حديث صحيح، متفق على صحته، رواه البخاري في " البيوع " من " صحيحه " عن عبد الله بن مسلمة القعنبي.','433','2','0');
INSERT INTO [t5740] VALUES (176,'- يلبسها، ويتوضأ فيها، وأما الخلوق فإنه كان أحب الطيب إلى رسول الله -','437','2','0');
INSERT INTO [t5740] VALUES (177,'- يستلم إلا هذين الركنين ". [هذا] حديث متفق على صحته، أخرجه البخاري في " الطهارة " من " صحيحه " عن أبي عبد الله بن يوسف التنيسي، وفي " اللباس "، عن عبد الله بن مسلمة القعنبي. وأخرجه مسلم في " المناسك " من " صحيحه " عن يحيى بن يحيى','437','2','0');
INSERT INTO [t5740] VALUES (178,'- وأنا مع فاطمة، فقال: ألا تقومان (فتصليان) ، فقلت: إن أنفسنا بيد الله - عز وجل -، فإذا شاء أنبهنا، فضرب برجله الأرض وقال: {وكان الإنسان أكثر شيء جدلا} . هذا حديث صحيح، متفق على صحته، رواه البخاري في " صحيحه " من طرق أحدها','440','2','0');
INSERT INTO [t5740] VALUES (179,'- وعنده نسوة من قريش يسألنه، ويستكثرنه، عالية أصواتهن على صوته، فلما أذن له','443','2','0');
INSERT INTO [t5740] VALUES (180,'- تبادرن الحجاب، فدخل ورسول الله يضحك، فقال: - بأبي أنت وأمي -، يا رسول الله ما يضحكك، فقال رسول الله -','444','2','0');
INSERT INTO [t5740] VALUES (181,'-، قلن: نعم أنت أفظ وأغلظ من رسول الله -','444','2','0');
INSERT INTO [t5740] VALUES (182,'-: " أيها يا ابن الخطاب فوالذي نفس محمد بيده ما لقيك الشيطان سالكا فجا إلا سلك فجا غير فجك ".','444','2','0');
INSERT INTO [t5740] VALUES (183,'- وعنده / نسوة من قريش، عالية أصواتهن على صوته، فلما أذن له، تبادرن الحجاب، فدخل ورسول الله -','445','2','0');
INSERT INTO [t5740] VALUES (184,'-[قلن: نعم، أنت أفظ وأغلظ من رسول الله -','445','2','0');
INSERT INTO [t5740] VALUES (185,'-: يا ابن الخطاب - والذي نفس محمد بيده - ما لقيك الشيطان سالكا فجا إلا سلك فجا غير فجك ". هذا حديث صحيح، متفق على صحته، رواه البخاري في " صفة إبليس من " جامعه "، عن علي بن المديني أبي الحسن الحافظ.','445','2','0');
INSERT INTO [t5740] VALUES (186,'-: " الأيم أولى بنفسها من وليها، والبكر تستأمر في نفسها "، فقيل له - يا رسول الله - ": إن البكر تستحي أن تتكلم، قال: " إذنها صماتها ".','451','2','0');
INSERT INTO [t5740] VALUES (187,'- قال: " الأيم أحق بنفسها من وليها، والبكر تستأمر في نفسها وإذنها إنصاتها ".','451','2','0');
INSERT INTO [t5740] VALUES (188,'- قال: " من كانت لأخيه عنده مظلمة في عرضه أو ماله، فليتحلله اليوم قبل أن يؤخذ منه يوم لا دينار ولا درهم، فإن كان له عمل صالح أخذ منه بقدر مظلمته، وإن لم يكن له عمل أخذ من سيئاته، فجعلت عليه ". هذا حديث صحيح، رواه البخاري في " المظالم "','454','2','0');
INSERT INTO [t5740] VALUES (189,'- عام خيبر فلم نغنم ذهبا ولا ورقا إلا الثياب والمتاع والأموال، قال: فوجه','456','2','0');
INSERT INTO [t5740] VALUES (190,'إذ جاءه سهم عائر فقتله فقال الناس هنيئا له الجنة فقال رسول الله','457','2','0');
INSERT INTO [t5740] VALUES (191,'- نحو وادي القرى، وقد أهدي لرسول الله -','457','2','0');
INSERT INTO [t5740] VALUES (192,'فقال رسول الله','457','2','0');
INSERT INTO [t5740] VALUES (193,'-: " لا تبع ما ليس عندك " [هكذا] قاله يوسف بن مهران. هذا حديث حسن، رواه الأئمة في كتبهم من طرق منها الترمذي في " البيوع "، عن الحسن بن علي الخلال، وعبدة بن عبد الله وغير واحد، عن عبد الصمد بن عبد الوارث، عن يزيد بن','460','2','0');
INSERT INTO [t5740] VALUES (194,'-: " قال ربكم - عز وجل -: أعددت لعبادي الصالحين ما لا عين رأت، ولا أذن سمعت، ولا خطر على قلب بشر، اقرءوا إن شئتم {فلا تعلم نفس ما أخفي لهم من قرة أعين جزاء بما كانوا يعملون} ، ولموضع سوط في الجنة خير من الدنيا','463','2','0');
INSERT INTO [t5740] VALUES (195,'-: " ما قال عبد عند مريض: أسأل الله العظيم رب العرش العظيم أن يشفيك - سبع مرات - إلا عوفي ". رواه النسائي في " اليوم والليلة " من " سننه " من طرق أحدها،','468','2','0');
INSERT INTO [t5740] VALUES (196,'- كان يغتسل من جنابة، فيأخذ حفنة لشق رأسه الأيمن، ثم يأخذ حفنة لشق رأسه الأيسر ". هذا حديث متفق على صحته، رواه البخاري ومسلم وأبو داود والنسائي في " الطهارة " من " كتبهم "، عن أبي موسى','470','2','0');
INSERT INTO [t5740] VALUES (197,'- العشاء، ثم صلى ثمان ركعات قائما وركعتين جالسا، وركعتين بين النداء ولم يدعهما ".','471','2','0');
INSERT INTO [t5740] VALUES (198,'-، فقال: إني أعزل عن امرأتي، فقال: ولم؟ ، فقال:','473','2','0');
INSERT INTO [t5740] VALUES (199,'- يقول: " إنما الأعمال بالنيات، وإنما لامرئ ما نوى، فمن كانت هجرته إلى الله وإلى رسوله، فهجرته إلى الله وإلى رسوله، ومن كانت هجرته إلى دنيا يصيبها أو امرأة يتزوجها، فهجرته إلى ما هاجر إليه ". هذا حديث صحيح ثابت، متفق على صحته، أخرجه','475','2','0');
INSERT INTO [t5740] VALUES (200,'- يقول: " إنما الأعمال بالنية وإنما لامرئ ما نوى، فمن كانت هجرته إلى / الله ورسوله، فهجرته إلى الله ورسوله ومن كانت هجرته لدنيا يصيبها أو امرأة يتزوجها، فهجرته إلى ما هاجر إليه ".','477','2','0');
INSERT INTO [t5740] VALUES (201,'-: يا عائشة، فكنت لك كأبي زرع لأم زرع ". رواه مسلم في " الفضائل " من " صحيحه "، عن الحسن بن','478','2','0');
INSERT INTO [t5740] VALUES (202,'- وهو يصلي، قال: فنهاني أو قالت كره ذلك، قال: فجعلته وسادتين ". رواه مسلم في " اللباس "، من " صحيحه "، عن أبي يعقوب','480','2','0');
INSERT INTO [t5740] VALUES (203,'- قال: " إن أصحاب هذه الصور يعذبون يوم القيامة، ويقال لهم: أحيوا ما خلقتم ".','481','2','0');
INSERT INTO [t5740] VALUES (204,'- أن يسافر بالقرآن مخافة أن يناله العدو ".','481','2','0');
INSERT INTO [t5740] VALUES (205,'- قال: " من عمل عملا ليس عليه أمرنا فهو رد " رواه مسلم في " كتاب ما قضى به النبي -','484','2','0');
INSERT INTO [t5740] VALUES (206,'- يضع ركبتيه قبل يديه، ويرفع يديه قبل ركبتيه ". أخرجه أبو داود في " الصلاة " من " سننه "، عن الحسن بن علي بن محمد الخلال، وأبي علي الحسين بن عيسى بن حمران القومسي البسطامي. وأخرجه الترمذي فيه من " جامعه "، عن أبي عبد الرحمن سلمة بن','486','2','0');
INSERT INTO [t5740] VALUES (207,'-: " انصر أخاك ظالما أو مظلوما، قال: قلت - يا رسول الله -: أنصره مظلوما، فكيف أنصره ظالما؟ ! قال: تمنعه من الظلم، فذلك نصرك إياه ". رواه الترمذي في " الفتن " من " جامعه "، عن أبي عبد الله محمد','489','2','0');
INSERT INTO [t5740] VALUES (208,'- " من كذب علي متعمدا فليتبوأ مقعده من النار ". رواه النسائي في " العلم " من " سننه "، عن أبي الحسن علي بن','490','2','0');
INSERT INTO [t5740] VALUES (209,'- المدينة، أخذت أم سليم بيدي، فقالت - يا رسول الله: هذا أنس غلام لبيب كاتب يخدمك، قال: فقبلني رسول الله -','491','2','0');
INSERT INTO [t5740] VALUES (210,'- في طريق، ومعه أناس من أصحابه، فعرضت له امرأة، فقالت: يا رسول الله لي إليك حاجة، فقال: يا أم فلان اجلسي في أدنى نواحي السكك حتى أجلس','492','2','0');
INSERT INTO [t5740] VALUES (211,'- فقدمت عليه المدينة، فوجدته جالسا بين المهاجرين والأنصار، فأتيته بإبل كأنها عروق الأرطاة، فقال: من الرجل؟ فقلت: عكراش بن ذؤيب، قال:','495','2','0');
INSERT INTO [t5740] VALUES (212,'-، ثم قال: هذه إبل قومي، هذه صدقات قومي، فأمر رسول الله أن توسم بميسم إبل الصدقة، وتضم إليها، ثم أخذ بيدي، فانطلق بي إلى منزل أم سلمة - رضي الله عنها، زوج النبي -','496','2','0');
INSERT INTO [t5740] VALUES (213,'- مما بين يديه، وجعلت أخبط في نواحيها، فقبض رسول الله -','496','2','0');
INSERT INTO [t5740] VALUES (214,'- في الطبق، ثم قال: يا عكراش " كل من','496','2','0');
INSERT INTO [t5740] VALUES (215,'- يديه، ثم مسح ببلل كفيه وجهه وذراعيه ورأسه، ثم قال: - يا عكراش " هكذا الوضوء مما غيرت النار ". رواه الترمذي بطوله في " الأطعمة " من " جامعه ". ورواه ابن ماجه بعضه فيه من " سننه " كلاهما عن أبي بكر محمد بن بشار بندار، عن العلاء بن','497','2','0');
INSERT INTO [t5740] VALUES (216,'(الشيخ العاشر الخضر بن كامل بن سالم بن سبيع السروجي أبو العباس (٥٢٣ هـ - ٦٠٨ هـ))','509','2','0');
INSERT INTO [t5740] VALUES (217,'- قال: " من أعتق رقبة أعتق الله بكل عضو منها عضوا منه من النار، حتى فرجه بفرجه ".','512','2','0');
INSERT INTO [t5740] VALUES (218,'- قال: " من أعتق رقبة، أعتق الله بكل إرب منها إربا من النار حتى باليد اليد وبالرجل الرجل، وبالفرج الفرج ". فقال له علي بن الحسين: يا سعيد: سمعت هذا من أبي هريرة؟ قال: نعم، قال لغلام له أقرب غلمانه: ادع لي قبطي، فلما قام بين يديه','513','2','0');
INSERT INTO [t5740] VALUES (219,'- يفعل ". رواه البخاري في " الحج " من " صحيحه " عن أبي الحسن عثمان بن محمد بن أبي شيبة العبسي الكوفي هذا، نحو ما رويناه، فوقع لنا موافقة عالية له ".','516','2','0');
INSERT INTO [t5740] VALUES (220,'- وهو ابن ثلاث وستين ". قال: وأخبرني ابن المسيب: بمثل ذلك ". رواه مسلم في " صحيحه "، عن','517','2','0');
INSERT INTO [t5740] VALUES (221,'- رمى جمرة العقبة يوم النحر ضحى، وأما بعد فإذا زالت الشمس ".','519','2','0');
INSERT INTO [t5740] VALUES (222,'- طاف بالبيت على راحلته، وهو يستلم الحجر بمحجنه ". رواهما مسلم في " المناسك " من " صحيحه "، عن أبي بكر بن','519','2','0');
INSERT INTO [t5740] VALUES (223,'-: " من ابتاع طعاما فلا يبيعه حتى يكتاله ".','521','2','0');
INSERT INTO [t5740] VALUES (224,'- فسألته عن المسجد الذي أسس على التقوى، فقبض قبضة من الحصى، ثم ضرب بها الأرض ثم قال: " هذا - يعني مسجد المدينة ". روى مسلم هذه الأحاديث الثلاثة في " صحيحه " عن أبي','523','2','0');
INSERT INTO [t5740] VALUES (225,'- قال: " لهن في غسيل ابنته ابدأن بميامنها، ومواضع الوضوء منها ".','525','2','0');
INSERT INTO [t5740] VALUES (226,'-: " يعذب المصورون يوم القيامة، يقال لهم: أحيوا ما خلقتم ".','526','2','0');
INSERT INTO [t5740] VALUES (227,'قال','527','2','0');
INSERT INTO [t5740] VALUES (228,'رواه الترمذي في " الحج " من " جامعه "، عن أحمد بن منيع،','530','2','0');
INSERT INTO [t5740] VALUES (229,'-، فأنا من أهل النار، فذكر ذلك لرسول الله -','532','2','0');
INSERT INTO [t5740] VALUES (230,'- يقول: إن طول صلاة الرجل مئنة من فقهه فأطيلوا الصلاة واقصروا الخطبة، فإن من البيان سحرا ". أخرحه مسلم في " صلاة الجمعة " من " صحيحه " عن أبي الحارث سريج بن يونس بن إبراهيم بن الحارث البغدادي هذا، فوافقناه بعلوه.','535','2','0');
INSERT INTO [t5740] VALUES (231,'- قال: " إن لكل شيء شرفا، وإن أشرف المجالس ما استقبل به القبلة، وإنما تجالسون بالأمانة، ولا تصلوا خلف النائم، ولا المتحدث، واقتلوا الحية، والعقرب وإن كنتم في صلاتكم، ولا تستروا الجدر بالثياب، ومن نظر في كتاب أخيه بغير إذنه، فكأنما','537','2','0');
INSERT INTO [t5740] VALUES (232,'(الشيخ الحادي عشر أبو الحسين غالب بن عبد الخالق بن أسد الطرابلسي الأصل، الدمشقي المولد والدار، الحنفي البزاز)','543','2','0');
INSERT INTO [t5740] VALUES (233,'- صلى صلاة فقرأ فيها فلبس عليه، فلما انصرف، قال لأبي - رضي','548','2','0');
INSERT INTO [t5740] VALUES (234,'- صلى صلاة فلبس عليه فلما انصرف قال لأبي بن كعب: " أصليت معنا؟ " قال [نعم، قال] : " فما منعك أن تفتح علي؟ ". رواه أبو داود في " الصلاة " من " سننه "، عن أبي القاسم بن','549','2','0');
INSERT INTO [t5740] VALUES (235,'- فلم نره طلاقا ". هذا حديث صحيح، رواه البخاري في " الطلاق " من " صحيحه "، عن عمر بن حفص بن غياث عن أبيه.','551','2','0');
INSERT INTO [t5740] VALUES (236,'- قال: " من حلف على يمين يقتطع بها مال امرئ مسلم، لقي الله يوم القيامة، وهو عليه غضبان، قيل: يا رسول الله: وإن كان شيئا يسيرا، قال: " وإن كان سواكا من أراك ".','553','2','0');
INSERT INTO [t5740] VALUES (237,'- يقول: " من حلف على مال امرئ مسلم بغير حقه لقي الله وهو عليه غضبان "، قال عبد الله: " ثم قرأ علينا مصداقه من كتاب الله {إن الذين يشترون بعهد الله وأيمانهم ثمنا قليلا ... .} إلى آخر الآية.','555','2','0');
INSERT INTO [t5740] VALUES (238,'- قال: " من حلف على يمين يقتطع بها مال امرئ مسلم لقى الله وهو عليه غضبان "، وقرأ علينا رسول الله -','556','2','0');
INSERT INTO [t5740] VALUES (239,'يقول','558','2','0');
INSERT INTO [t5740] VALUES (240,'- يقول: " إنما أنا خازن، وإنما يعطي الله عز وجل، فمن أعطيته عطاء بطيب نفس فإنه يبارك له فيه، ومن أعطيته عطاء بشره نفس، وشدة مسألة، فهو كالذي يأكل ولا يشبع ". / رواه مسلم في " الزكاة " من " صحيحه " عن أبي بكر بن أبي','559','2','0');
INSERT INTO [t5740] VALUES (241,'- مثل حديث الزهري: " من باع عبدا وله مال، فماله للبائع، إلا أن يشترط المبتاع ومن باع نخلا فيها ثمر قد أبرت، فثمرتها للبائع، إلا أن يشترط المبتاع ".','561','2','0');
INSERT INTO [t5740] VALUES (242,'-: " الثمرة للذي أبرها، إلا أن يشترط المبتاع ". رواه النسائي في " الشروط " و " العتق " عن محمد بن رافع، وزاد في " العتق "، وإسحاق بن إبراهيم، كلاهما، عن عبد الرزاق، فوقع لنا بدلا له في الرواية الأولى، وقال: مطر بن طهمان ضعيف، وقد','562','2','0');
INSERT INTO [t5740] VALUES (243,'- قال: " من اشترى نخلا قد أبرت، فثمرها للبائع إلا أن يشترط المبتاع ". هذا حديث صحيح متفق على صحته، رواه البخاري في " البيوع والشروط " من " صحيحه "، عن عبد الله بن يوسف. ورواه مسلم في " البيوع " من " صحيحه " عن يحيى بن يحيى.','563','2','0');
INSERT INTO [t5740] VALUES (244,'(الجزء الخامس من مشيخة الشيخ الإمام العالم ملحق الأحفاد بالأجداد بغية الطالبين فخر الدين أبي الحسن علي بن أحمد ابن عبد الواحد المقدسي الحنبلي، عرف بابن البخاري تخريج الإمام الحافظ عمدة الطالبين ومعينهم جمال الدين أبي العباس أحمد بن محمد بن عبد الله','565','2','0');
INSERT INTO [t5740] VALUES (245,'(الشيخ الثاني عشر عبد الجليل بن أبي غالب بن أبي المعالي بن محمد بن الحسن ابن مندويه السريجاني أو السرنجاني الأصبهاني المقريء الشروطي، أبو بكر وأبو مسعود (٢١ أو ٥٢٢ هـ - ٦١٠ هـ))','567','2','0');
INSERT INTO [t5740] VALUES (246,'-: " أوقد على النار ألف سنة حتى احمرت، ثم أوقد عليها ألف سنة حتى ابيضت، ثم أوقد عليها ألف سنة حتى اسودت، فهي سوداء كالليل المظلم ". رواه الترمذي في " صفة جهنم " من " جامعه "، وابن ماجه في " الزهد " من " سننه " كلاهما','571','2','0');
INSERT INTO [t5740] VALUES (247,'- ألست ألست؟ فقال سلمان - رضي الله عنه -: واحدة من اثنتين ما أبكاني','572','2','0');
INSERT INTO [t5740] VALUES (248,'-، عهد إلينا عهدا فما أراني إلا قد تعديت، قال: وما عهد إليك؟ ، قال: عهد إلينا أنه يكفي أحدكم مثل زاد الراكب فما أراني إلا قد تعديت وأما أنت، يا سعد فاتق الله عند حكمك إذا حكمت وعند قسمك إذا قسمت، وعند همك إذا هممت. قال ثابت ":','573','2','0');
INSERT INTO [t5740] VALUES (249,'- يقول: " والله ما الدنيا في الآخرة إلا مثل ما يضع أحدكم إصبعه في اليم فلينظر بماذا ترجع إليه ".','575','2','0');
INSERT INTO [t5740] VALUES (250,'- يقول: " والله ما الدنيا في الآخرة إلا كما يضع أحدكم إصبعه في اليم، فلينظر بم ترجع. هذا حديث صحيح، رواه مسلم في " صحيحه " من طرق، أحدها، عن أبي بكر عبد الله بن أبي شيبة، عن عبد الله بن إدريس، عن إسماعيل بن أبي خالد، نحو ما رويناه،','577','2','0');
INSERT INTO [t5740] VALUES (251,'- فقال: إن أمي ماتت وعليها نذر، فقال: اقضه عنها ".','580','2','0');
INSERT INTO [t5740] VALUES (252,'-: " اقضه عنها ". هذا حديث صحيح متفق على صحته، رواه (خ) في " الوصايا " من " صحيحه "، عن عبد الله بن يوسف الدمشقي، ورواه " م " في النذور " في " صحيحه "، عن يحيى بن يحيى. ورواه " د " في " الأيمان " في " سننه " عن القعنبي ثلاثتهم عن','581','2','0');
INSERT INTO [t5740] VALUES (253,'- لم يجتمع له غذاء ولا عشاء من خبز ولحم إلا على الضفف ". رواه الترمذي في " الشمائل "، عن أبي محمد عبد الله بن','584','2','0');
INSERT INTO [t5740] VALUES (254,'(الشيخ الثالث عشر أبو الفتوح محمد بن علي بن المبارك بن محمد ابن الجلاجلي البغدادي التاجر (٥٤١ هـ - ٦١٢ هـ))','591','2','0');
INSERT INTO [t5740] VALUES (255,'-: " يا بني ادن وكل بيمينك، وكل مما يليك ".','594','2','0');
INSERT INTO [t5740] VALUES (256,'- يقول: " دعاني النبي -','595','2','0');
INSERT INTO [t5740] VALUES (257,'-: نحوه.','595','2','0');
INSERT INTO [t5740] VALUES (258,'-: " ادن - يا بني - فسم الله، وكل مما يليك ".','596','2','0');
INSERT INTO [t5740] VALUES (259,'-، وكانت يدي تدور ههنا وههنا، فقال النبي -','596','2','0');
INSERT INTO [t5740] VALUES (260,'-، رواه البخاري ومسلم من حديث أبي نعيم وهب بن كيسان، عنه ورواه أبو داود في " الأطعمة " من " سننه "، عن أبي جعفر محمد بن سليمان بن حبيب لوين كما رويناه، فوافقناه بعلو. ورواه النسائي من طرق " أحدها، عن أبي عمر هلال بن العلاء بن هلال','597','2','0');
INSERT INTO [t5740] VALUES (261,'-، فقال: " لتتب هذه المرأة إلى الله عز وجل وإلى رسوله','599','2','0');
INSERT INTO [t5740] VALUES (262,'- وترد على الناس متاعهم، قم يا بلال فاقطع يدها ". رواه النسائي في " القطع " من " سننه "، عن عثمان بن عبد الله بن محمد بن خرزاذ الأنطاكي الحافظ، عن الحسن بن حماد سجادة، كما رويناه، فوقع لنا بدلا عاليا له.','600','2','0');
INSERT INTO [t5740] VALUES (263,'- عمن اعتمر معه من نسائه في حجة الوداع بقرة بينهن ". رواه أبو داود والنسائي في " الحج " من " سننهما "، عن أبي حفص عمرو بن عثمان بن سعيد الحمصي هذا، على الموافقة العالية.','601','2','0');
INSERT INTO [t5740] VALUES (264,'- قال: " أعمار أمتي ما بين الستين إلى السبعين، وأقلهم من يجوز ذلك ".','602','2','0');
INSERT INTO [t5740] VALUES (265,'-: " إنما أعمار أمتي ما بين الستين إلى السبعين، وأقلهم من يجاوز ذلك ". رواه الترمذي وابن ماجه في " الزهد " من كتابيهما، عن أبي','603','2','0');
INSERT INTO [t5740] VALUES (266,'-: متى وجبت لك النبوة؟ قال: " بين خلق آدم ونفخ الروح فيه ". رواه الترمذي في " المناقب " من " جامعه " عن أبي همام الوليد بن','605','2','0');
INSERT INTO [t5740] VALUES (267,'- لإحرامه وطيبته لإحلاله بطيب لا يشبه طيبكم هذا ". قال ابن يونس في حديثه: " يعني ليس له بقاء " قال أبو بكر بن أبي داود: " لم يروه عن الأوزاعي إلا ضمرة ". رواه النسائي في " الحج " من " سننه " عن أبي','607','2','0');
INSERT INTO [t5740] VALUES (268,'- يقول: " علي مني وأنا من علي لا يؤدي عني إلا أنا أو هو ". رواه ابن ماجه في " الزهد " من " سننه " عن سويد ابن سعيد هذا،','608','2','0');
INSERT INTO [t5740] VALUES (269,'- قال: " ما من أيام أحب إلى الله أن يتعبد له فيها من أيام العشر، وإن اليوم من صيامها يعدل بصيام سنة، وليلة منها بليلة القدر ".','610','2','0');
INSERT INTO [t5740] VALUES (270,'- على السمع والطاعة في العسر واليسر، والمنشط والمكره وأن لا ننازع الأمر','615','2','0');
INSERT INTO [t5740] VALUES (271,'- ليلة العقبة على السمع في عسرنا ويسرنا ومكرهنا،','616','2','0');
INSERT INTO [t5740] VALUES (272,'وأنا قائم على رأس رسول الله','620','2','0');
INSERT INTO [t5740] VALUES (273,'- يبايع الناس، وأنا رافع غصنا من أغصانها عن رأسه، لم نبايعه على الموت ولكن بايعناه على أن لا نفر ".','621','2','0');
INSERT INTO [t5740] VALUES (274,'- يوم الحديبية وهو رافع غصنا من أغصان الشجرة بيده عن رأس رسول الله -','622','2','0');
INSERT INTO [t5740] VALUES (275,'الجزء ٢','625','0','0');
INSERT INTO [t5740] VALUES (276,'- يقول: " إذا حضرتم الميت أو المريض، فقولوا خيرا، فإن الملائكة يؤمنون على ما تقولون ".','626','275','0');
INSERT INTO [t5740] VALUES (277,'-: " إذا شهدتم المريض فقولوا خيرا، فإن الملائكة يؤمنون على ما تقولون ".','627','275','0');
INSERT INTO [t5740] VALUES (278,'-: " إذا حضرتم الميت أو المريض، فقولوا خيرا، فإن الملائكة يؤمنون على ما تقولون، قالت: فلما مات أبو سلمة أتيت النبي -','628','275','0');
INSERT INTO [t5740] VALUES (279,'هذا حديث صحيح، رواه مسلم في " الجنائز " من " صحيحه "، عن أبي بكر بن أبي شيبة، وأبي كريب محمد بن العلاء، ورواه الترمذي فيه من " جامعه "، عن هناد بن السري. ورواه ابن ماجه فيه من " سننه "، عن أبي بكر بن أبي شيبة،','628','275','0');
INSERT INTO [t5740] VALUES (280,'-: كان يبعث من يخرص كرومهم وثمارهم ". رواه ابن ماجه في " الزكاة "','632','275','0');
INSERT INTO [t5740] VALUES (281,'- مر به وهو بالأبواء أبو بودان فأهديت له لحم حمار وحش، قال فرده النبي -','634','275','0');
INSERT INTO [t5740] VALUES (282,'- بالأبواء أو بودان، فأهديت له لحم حمار وحش، فرد علي، فلما رأى في وجهي الكراهية، قال: " ليس بنا رد عليك، ولكنا حرم "، وسألته عن أهل الدار من المشركين يبيتون، فيصاب من نسائهم وذراريهم، قال: " هم منهم "، وسمعته يقول: " لا حمى إلا لله','636','275','0');
INSERT INTO [t5740] VALUES (283,'- وأنا بالأبواء أو بودان، فأهديت له لحم حمار وحش وهو محرم، فرده علي، فلما رأى في وجهي الكراهية قال: " ليس بنا رد عليك ولكنا حرم ". قال:','636','275','0');
INSERT INTO [t5740] VALUES (284,'- حمارا وحشيا وهو بالأبواء أو بودان فرده رسول الله فلما رأى رسول الله -','637','275','0');
INSERT INTO [t5740] VALUES (285,'- عن أهل الدار من المشركين يبيتون ويصاب من نسائهم وذراريهم قال: " هم منهم ". الحديث الأول: رواه البخاري في " الحج " من " صحيحه "، عن أبي محمد عبد الله بن يوسف الدمشقي، وفي " الهبة " عن إسماعيل بن أبي أويس، كلاهما عن مالك. ورواه','638','275','0');
INSERT INTO [t5740] VALUES (286,'يقرأ في المغرب بالمرسلات','642','275','0');
INSERT INTO [t5740] VALUES (287,'يقرأ في المغرب بالمرسلات عرفا','643','275','0');
INSERT INTO [t5740] VALUES (288,'يقرأ في المغرب بالمرسلات','643','275','0');
INSERT INTO [t5740] VALUES (289,'- يقرأ في صلاة المغرب: والمرسلات عرفا ".','644','275','0');
INSERT INTO [t5740] VALUES (290,'- يقرؤها في صلاة المغرب ".','645','275','0');
INSERT INTO [t5740] VALUES (291,'- يقرأ في المغرب ". هذا حديث صحيح متفق على صحته، رواه مسلم في " الصلاة " من " صحيحه "، عن أبي بكر بن أبي شيبة، وعمر بن محمد الناقد. ورواه النسائي فيه من " سننه " عن قتيبة بن سعيد، ورواه ابن ماجه فيه من " سننه "، عن أبي بكر بن أبي','646','275','0');
INSERT INTO [t5740] VALUES (292,'قال من صام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه ومن قام ليلة القدر إيمانا واحتسابا غفر له ما تقدم من ذنبه','649','275','0');
INSERT INTO [t5740] VALUES (293,': " من صام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه، ومن قام ليلة القدر إيمانا واحتسابا غفر له ما تقدم من ذنبه ".','649','275','0');
INSERT INTO [t5740] VALUES (294,'- قال: " من صام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه، ومن قام ليلة القدر إيمانا واحتسابا غفر له ما تقدم من ذنبه ".','650','275','0');
INSERT INTO [t5740] VALUES (295,'- قال: " من صام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه، ومن قام ليلة القدر إيمانا واحتسابا غفر له وما تقدم من ذنبه ". قال عبد الله، قال أبي: سمعته من سفيان أربع مرار قال: من صام رمضان، وقال مرة: من قام رمضان ".','651','275','0');
INSERT INTO [t5740] VALUES (296,'- قال: من صام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه ".','652','275','0');
INSERT INTO [t5740] VALUES (297,'-، كان يرغب في قيام شهر رمضان من غير أن يأمر فيه بعزيمة، فيقول: " من قام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه ". قال ابن شهاب: " فتوفي رسول الله -','653','275','0');
INSERT INTO [t5740] VALUES (298,'- قال: " من قام رمضان ... فذكره ". وقال: " لا أعلم أحدا تابع ابن أبي هلال " فباعتبار العدد إلى الزهري كأن','657','275','0');
INSERT INTO [t5740] VALUES (299,'كما أسمع','659','275','0');
INSERT INTO [t5740] VALUES (300,'- يقول: " من كذب علي فليتبوأ مقعده من النار " والله ما قال " متعمدا "، وأنتم تقولون متعمدا ".','660','275','0');
INSERT INTO [t5740] VALUES (301,'-، كما يحدث ابن مسعود وفلان','660','275','0');
INSERT INTO [t5740] VALUES (302,'- نهانا أن ندعوا بالموت لدعوت به، ثم ذكر من مضى من أصحابه أنهم مضوا ولم يأكلوا من أجورهم شيئا وإنا بقينا بعدهم حتى نلنا من الدنيا ما لا يدري أحدنا ما يصنع به إلا أن ينفقه في التراب، وإن المسلم ليؤجر في كل شيء إلا ما أنفق في التراب ".','662','275','0');
INSERT INTO [t5740] VALUES (303,'- نهانا أن ندعوا بالموت لدعوت به ".','663','275','0');
INSERT INTO [t5740] VALUES (304,'- نهى أن ندعوا بالموت لدعوت به ".','663','275','0');
INSERT INTO [t5740] VALUES (305,'- نهى أن ندعوا بالموت لدعوت به ". رواه البخاري في " الدعوات " من صحيحه عن مسدد بن مسرهد، فوفقناه بعلو في هذه الرواية، ورواه في (الرقاق) عن يحيى بن موسى، عن وكيع [نحو ما رويناه في الرواية الثانية، وفي','664','275','0');
INSERT INTO [t5740] VALUES (306,'-: " مراء في القرآن كفر ".','666','275','0');
INSERT INTO [t5740] VALUES (307,'- قال: " مراء في القرآن كفر ". رواه أبو داود في " السنة " من " سننه "، عن الإمام أحمد بن محمد بن حنبل، كما رويناه فوافقناه بعلو في هذه الرواية ".','667','275','0');
INSERT INTO [t5740] VALUES (308,'-: " أسرعوا بالجنازة، فإن تك صالحة خير تقدمونها إليه ". هذا حديث صحيح متفق على [صحته] ، رواه البخاري في " الجنائز " من " صحيحه " عن علي بن المديني. ورواه مسلم فيه من " صحيحه "، عن أبي بكر بن أبي شيبة، وأبي خيثمة زهير بن حرب.','669','275','0');
INSERT INTO [t5740] VALUES (309,'الشيخ الخامس عشر','675','275','0');
INSERT INTO [t5740] VALUES (310,'الفقيه أبو محمد عبد الواحد بن إسماعيل بن ظافر الأزدي الدمياطي الشافعي المتكلم (٥٥٦ هـ - ٦١٣ هـ)','675','275','0');
INSERT INTO [t5740] VALUES (311,'- قال: " إذا دخل شهر رمضان فتحت أبواب الجنة وغلقت أبواب النار، وصفدت الشياطين ".','679','275','0');
INSERT INTO [t5740] VALUES (312,'- قال: " إذا جاء رمضان فتحت أبواب الجنة وغلقت أبواب النار، وصفدت الشياطين ". هذا حديث صحيح رواه البخاري ومسلم في " الصوم " من " كتابيهما "، عن أبي رجاء قتيبة بن سعيد البلخي، ورواه مسلم أيضا عن أبي زكريا يحيى بن أيوب العابد. وأخرجه','680','275','0');
INSERT INTO [t5740] VALUES (313,'-: " إن الله إذا تكلم بالوحي سمع أهل السماء صلصلة كجر السلسلة على الصفا فيصعقون، فلا يزالون كذلك حتى يأتيهم جبريل فإذا جاءهم جبريل فزع عن قلوبهم، فيقولون: يا جبريل ماذا قال ربك؟ ، فيقول الحق، فينادون: الحق، الحق.','684','275','0');
INSERT INTO [t5740] VALUES (314,'-: " إن الله عز وجل إذا تكلم بالوحي سمع أهل السموات السبع له صلصلة كصلصلة السلسلة على الصفا، فيفزعون حتى يأتيهم جبريل، فإذا فزع عن قلوبهم يقولون: جبريل، ماذا قال ربك؟ ، فيقول: الحق، قالوا: الحق، الحق ". رواه أبو داود في " السنة "','685','275','0');
INSERT INTO [t5740] VALUES (315,'-، وهو جالس في أصحابه، فدرت من خلفه، فعرف الذي أريد فألقى الرداء عن ظهره، فرأيت موضع الخاتم على نغض','687','275','0');
INSERT INTO [t5740] VALUES (316,'-، قال: نعم، ولكم، ثم تلا الآية: { ... واستغفر لذنبك وللمؤمنين والمؤمنات} ". رواه الترمذي في " الشمائل " عن أبي الأشعث أحمد بن','688','275','0');
INSERT INTO [t5740] VALUES (317,'- يقول: " الجاهر بالقرآن كالجاهر بالصدقة والمسر بالقرآن كالمسر بالصدقة ".','691','275','0');
INSERT INTO [t5740] VALUES (318,'-: " بينما أنا نائم رأيت الناس يعرضون علي وعليهم قمص منه ما يبلغ الثدي، ومنها ما يبلغ دون ذلك، ومر علي عمر بن الخطاب - رضي الله عنه - وعليه قميص يجره، قالوا: ماذا أولت ذلك يا رسول الله؟ قال: الدين ". رواه النسائي في " الأيمان والنذور "','694','275','0');
INSERT INTO [t5740] VALUES (319,'-: " البيعان بالخيار ما لم يتفرقا ".','696','275','0');
INSERT INTO [t5740] VALUES (320,'- يخطب يوم الجمعة، فقال: " أصليت يا فلان؟ ، قال: لا، قم فاركع ". هذا حديث صحيح متفق على صحته، أخرجه البخاري في " الصلاة " من " صحيحه "، عن أبي النعمان محمد بن الفضل السدوسي. وأخرجه مسلم فيه من " صحيحه " عن أبي الربيع سليمان بن','698','275','0');
INSERT INTO [t5740] VALUES (321,'- قال: " خمس من الدواب لا جناح في قتلهن في الحل والحرم: الغراب والفأرة والحدأة والعقرب والكلب العقور ".','700','275','0');
INSERT INTO [t5740] VALUES (322,'- عما يقتل المحرم من الدواب فقال: " خمس لا جناح في قتلهن على من قتلهن، في الحل والحرم: العقرب والفأرة والغراب والحدأة والكلب والعقور ".','700','275','0');
INSERT INTO [t5740] VALUES (323,'-: " خمس من الدواب لا جناح في قتلهن على من قتلهن في الحرم والإحرام: الفأرة، والحدأة، والغراب، والكلب، والعقور، والعقرب ". هذا حديث صحيح، رواه مسلم في " المناسك " من " صحيحه "، عن أبي خيثمة زهير بن حرب، وأبي عبد الله محمد بن يحيى','701','275','0');
INSERT INTO [t5740] VALUES (324,'- قال: " ما من حسنة يعملها ابن آدم إلا كتبت له','702','275','0');
INSERT INTO [t5740] VALUES (325,'-: قال الله عز وجل: " الصوم لي وأنا أجزي به، يدع شهوته وأكله وشربه من أجلي، والصوم جنة، وللصائم فرحتان، فرحة حين يفطر، وفرحة حين يلقى الله عز وجل، ولخلوف فم الصائم أطيب عند الله من ريح المسك ". هذا حديث صحيح أخرجه الشيخان في "','703','275','0');
INSERT INTO [t5740] VALUES (326,'الجزء السادس','709','275','0');
INSERT INTO [t5740] VALUES (327,'من مشيخة الإمام العالم بقية المسندين، ملحق الأصاغر بالأكابر، فخر الدين أبي الحسن علي بن أحمد بن عبد الواحد المقدسي، الحنبلي، المعروف بابن البخاري.','709','275','0');
INSERT INTO [t5740] VALUES (328,'تخريج','709','275','0');
INSERT INTO [t5740] VALUES (329,'الإمام الحافظ عمدة المحدثين، ومفيدهم جمال الدين أبي العباس أحمد بن محمد بن عبد الله الظاهري الحنفي الحلبي، له تغمدهم الله برحمته وإيانا آمين','709','275','0');
INSERT INTO [t5740] VALUES (330,'وفيه: الكندي وابن عبد الغني وابن قصة','709','275','0');
INSERT INTO [t5740] VALUES (331,'الشيخ السادس عشر','711','275','0');
INSERT INTO [t5740] VALUES (332,'الشيخ الإمام العلامة أبو اليمن زيد بن الحسن بن زيد ابن الحسن بن زيد بن سعيد بن عصمة بن حمير ابن الحارث ذي رعين الأصغر الكندي البغدادي النحوي اللغوي. (٥٢٠ هـ - ٦١٣ هـ)','711','275','0');
INSERT INTO [t5740] VALUES (333,'-[ووالله لا أسمع أحدا بعده يقول سمعت رسول الله -','714','275','0');
INSERT INTO [t5740] VALUES (334,'- دخل على أم سليم فرأى أبا عمير حزينا، فقال: " يا أم سليم وما بال أبي عمير حزينا؟ فقالت: يا رسول الله مات نغيره، فقال رسول الله -','715','275','0');
INSERT INTO [t5740] VALUES (335,'- يمازحه إذا دخل على أم سليم، فدخل يوما فوجده حزينا، فقال: " ما لأبي عمير حزينا؟ قالوا: يا رسول الله مات نغيره الذي كان يلعب به، فجعل يقول: يا أبا عمير ما فعل النغير ".','716','275','0');
INSERT INTO [t5740] VALUES (336,'- فلم يزل يلبي حتى رمى الجمرة التي عند العقبة يوم النحر ". هذا حديث صحيح متفق على صحته، رواه البخاري في " الحج "','719','275','0');
INSERT INTO [t5740] VALUES (337,'في','722','275','0');
INSERT INTO [t5740] VALUES (338,'- عن بيع الولاء، وعن هبته ".','726','275','0');
INSERT INTO [t5740] VALUES (339,'نهى عن بيع الولاء وعن هبته','727','275','0');
INSERT INTO [t5740] VALUES (340,'عن بيع الولاء، وعن هبته ".','727','275','0');
INSERT INTO [t5740] VALUES (341,'- نهى عن بيع الولاء، وعن هبته ".','728','275','0');
INSERT INTO [t5740] VALUES (342,'- نهى عن بيع الولاء وعن هبته ".','729','275','0');
INSERT INTO [t5740] VALUES (343,'عن بيع الولاء وعن هبته ".','729','275','0');
INSERT INTO [t5740] VALUES (344,'- عن بيع الولاء','730','275','0');
INSERT INTO [t5740] VALUES (345,'- فأمره أن يراجعها ثم تستقبل عدتها، قلت له: إذا طلق الرجل امرأته وهي حائض أيعتد بتلك التطليقة؟ قال:','733','275','0');
INSERT INTO [t5740] VALUES (346,'- فذكر ذلك له، فقال النبي -','734','275','0');
INSERT INTO [t5740] VALUES (347,'- من فضة، فصه منه ".','737','275','0');
INSERT INTO [t5740] VALUES (348,'- من فضة. هذا حديث صحيح أخرجه الأئمة في كتبهم من طرق منها للنسائي في " الزينة " من " سننه "، عن أبي الحسين محمد بن خالد بن','737','275','0');
INSERT INTO [t5740] VALUES (349,'- عام الحديبية البدنة عن سبعة، والبقرة عن سبعة ". هذا حديث صحيح، رواه مسلم والترمذي والنسائي في " الحج " من كتبهم، عن أبي رجاء قتيبة بن سعيد الثقفي، ورواه مسلم أيضا عن يحيى بن يحيى، ورواه أبو داود في " الأضاحي " من " سننه " عن عبد','740','275','0');
INSERT INTO [t5740] VALUES (350,'- تقول: سمعت رسول الله -','742','275','0');
INSERT INTO [t5740] VALUES (351,'- أنه قال: " ليخسفن بقوم ببيداء من الأرض ".','743','275','0');
INSERT INTO [t5740] VALUES (352,'- الجيش الذي يخسف بهم، فقالت أم سلمة: لعل فيهم المكره، قال: إنهم يبعثون على نياتهم ".','744','275','0');
INSERT INTO [t5740] VALUES (353,'- الجيش لذي يخسف بهم، فقالت أم سلمة لعل فيهم','744','275','0');
INSERT INTO [t5740] VALUES (354,'- قال: " الغادر ينصب له','747','275','0');
INSERT INTO [t5740] VALUES (355,'- قال: " إن الغادر ينصب له لواء يوم القيامة، فيقال: هذه غدرة فلان ".','751','275','0');
INSERT INTO [t5740] VALUES (356,'-: " من أدرك ركعة من الصلاة فقد أدرك الصلاة ".','752','275','0');
INSERT INTO [t5740] VALUES (357,'-: " من أدرك ركعة من الصلاة فقد أدرك الصلاة ".','752','275','0');
INSERT INTO [t5740] VALUES (358,'-: " من أدرك ركعة من الصلاة فقد أدرك الصلاة ".','753','275','0');
INSERT INTO [t5740] VALUES (359,'- قال: " من أدرك من صلاة ركعة، فقد أدرك ". هذا حديث صحيح متفق على صحته، رواه البخاري في " الصلاة " من " جامعه " عن أبي محمد عبد الله بن يوسف الدمشقي.','753','275','0');
INSERT INTO [t5740] VALUES (360,'- فأمرهم بالقصاص فجاء أخوها أنس بن النضر، فقال: - يا رسول الله -: أتكسر سن الربيع؟ والذي بعثك بالحق لا تكسر سنها، قال: يا أنس، كتاب الله القصاص، فعفا القوم، فقال رسول الله -','756','275','0');
INSERT INTO [t5740] VALUES (361,'- رجلان فسمت أو فشمت أحدهما ولم يسمت الآخر، [فقيل يا رسول الله: عطس عندك رجلان فسمت أحدهما ولم تسمت الآخر] أو فشمته ولم تشمت الآخر قال: " إن هذا حمد الله، فشمته وإن هذا لم يحمد الله فلم أشمته ". هذا حديث صحيح متفق على صحته، رواه','758','275','0');
INSERT INTO [t5740] VALUES (362,'رويدك','759','275','0');
INSERT INTO [t5740] VALUES (363,'-: " لا هجرة بين المسلمين فوق ثلاث أيام، أو قال: ثلاث ليال ".','761','275','0');
INSERT INTO [t5740] VALUES (364,'-: " الصائم بالخيار ما بينه وبين نصف النهار ".','762','275','0');
INSERT INTO [t5740] VALUES (365,'- فرسا لنا يقال له " مندوب "، فقال رسول الله -','763','275','0');
INSERT INTO [t5740] VALUES (366,'- ناسا من الأنصار، فقال: هل فيكم أحد من غيركم؟ قالوا: لا، إلا ابن أخت لنا، قال: ابن أخت القوم منهم،','763','275','0');
INSERT INTO [t5740] VALUES (367,'- إلى بيوتكم، قالوا: بلى، قال: لو سلك الناس واديا أو شعبا لسلكت وادي الأنصار أو شعبهم ".','764','275','0');
INSERT INTO [t5740] VALUES (368,'- قال: " ما من أحد يدخل الجنة، يحب أن يرجع إلى الدنيا وله ما على الأرض من شيء إلا الشهيد فإنه يتمنى أن يرجع إلى الدنيا، فيقتل عشر مرار لما يرى من الكرامة ".','764','275','0');
INSERT INTO [t5740] VALUES (369,'- أو سمعتهن منه - أينقنني وأعجبنني، لا تسافر امرأة مسيرة يومين ولا ليلتين - وفي رواية ابن طبرزد: أو ليلتين - إلا ومعها ذو رحم - وقال ابن طبرزد: محرم أو زوجها -، ولا صوم يومين، يوم النحر ويوم الفطر، ولا صلاة بعد الصبح حتى تطلع الشمس،','768','275','0');
INSERT INTO [t5740] VALUES (370,'- قال: " يخرج من النار قوم بعدما يصيبهم منها سفع يسميهم أهل الجنة الجهنميين ". رواه البخاري في " صفة الجنة " من " صحيحه "، عن هدبة بن خالد، فوقع لنا موافقة عالية له.','770','275','0');
INSERT INTO [t5740] VALUES (371,'-: فقال: " ليست لك عليهم نفقة، وعليك العدة وانتقلي إلى أم شريك، ثم قال: إن أم شريك يدخل عليها إخوتها من المهاجرين','771','275','0');
INSERT INTO [t5740] VALUES (372,'-: " إن الله عز وجل لا يقبض العلم انتزاعا ينتزعه من الناس - ولم يقل ابن المكتفي: من الناس - ثم اتفقوا: ولكن يقبض العلم بقبض العلماء - وفي حديث ابن طبرزد: ولكن يقبض العلماء فقط - ثم اتفقوا: حتى إذا يبق عالما اتخذ الناس رءوسا جهالا','775','275','0');
INSERT INTO [t5740] VALUES (373,'-: " إن الله لا يقبض العلم انتزاعا ينتزعه من الناس، ولكن يقبض العلم بقبض العلماء، فإذا لم يبق عالما اتخذ الناس رءوسا جهالا فسئلوا فأفتوا بغير علم، فضلوا وأضلوا ".','776','275','0');
INSERT INTO [t5740] VALUES (374,'-: " إن الله لا يقبض العلم انتزاعا من أهله، ولكن بالذهاب العلماء، فإذا لم يبق عالم اتخذ الناس رءوسا جهالا، فأفتوا بغير علم فضوا وأضلوا ". هذا حديث صحيح متفق على صحته وأخرجه الأئمة في كتبهم من طرق، أحدها لمسلم عن أبي خيثمة زهير بن','777','275','0');
INSERT INTO [t5740] VALUES (375,'- توسلنات إليك بنبينا، اللهم إنا نتوسل إليك بعم نبيك -','779','275','0');
INSERT INTO [t5740] VALUES (376,'- احتجم وهو صائم محرم ". رواه الترمذي في " الصوم " من " جامعه " و " النسائي " فيه من','780','275','0');
INSERT INTO [t5740] VALUES (377,'- تزوج ميمونة - رضي الله عنه - وهو محل ".','781','275','0');
INSERT INTO [t5740] VALUES (378,'-: " ثلاث دعوات مستجابات، دعوة الصائم، ودعوة المسافر، ودعوة المظلوم ". رواه الترمذي في " جامعه "، عن أبي بكر محمد بن بشار بندار،','783','275','0');
INSERT INTO [t5740] VALUES (379,'- يقول: " ما من غازية تغزو في سبيل الله فيصيبون غنيمة إلا تعجلوا ثلثي أجرهم في الآخرة، وبقي لهم الثلث (فإن) - وفي حديث ابن طبرزد - (وإن) لم يصيبوا غنيمة تم لهم أجرهم ".','787','275','0');
INSERT INTO [t5740] VALUES (380,'- يصبح وهو جنب فيتم صومه ". رواه النسائي في " الصوم " من " سننه " عن أبي بكر أحمد بن علي بن سعيد المروزي الدمشقي القاضي، عن داود بن رشيد، فوقع لنا بدلا عاليا له.','789','275','0');
INSERT INTO [t5740] VALUES (381,'- كان يقول: " اللهم بك أصبحنا، وبك أمسينا، وبك نحيا، وبك نموت، وإليك النشور، وإذا أمسى قال: اللهم بك أمسينا وبك أصبحنا، وبك نحيا، وبك نموت وإليك المصير ". رواه النسائي في " اليوم والليلة " من " سننه " عن زكريا بن يحيى،','791','275','0');
INSERT INTO [t5740] VALUES (382,'الشيخ السابع عشر','799','275','0');
INSERT INTO [t5740] VALUES (383,'الإمام الحافظ أبو الفتح محمد بن الحافظ أبي محمد عبد الغني بن عبد الواحد بن علي بن سرور بن رافع ابن حسن بن جعفر المقدسي (٥٦٦ هـ - ٦١٣ هـ)','799','275','0');
INSERT INTO [t5740] VALUES (384,'- أمر رجلا إذا أخذ مضجعه من الليل أن يقول: " اللهم أسلمت ظهري إليك - رغبة ورهبة إليك، لا ملجأ ولا','802','275','0');
INSERT INTO [t5740] VALUES (385,'- قال: " إذا ادهن لم ير، وإذا لم يدهن','804','275','0');
INSERT INTO [t5740] VALUES (386,'-، وكان قد شهد بيعة الرضوان قال: " كنا يومئذ ألف وثلاثمائة، وكان','805','275','0');
INSERT INTO [t5740] VALUES (387,'- فاستحييت منه - قال سليمان في حديثه وليس في حديث شعبة: " أن رسول الله -','807','275','0');
INSERT INTO [t5740] VALUES (388,'رأى امرأة','808','275','0');
INSERT INTO [t5740] VALUES (389,'رواه مسلم في " التوبة " من " صحيحه "، عن محمد بن المثنى،','810','275','0');
INSERT INTO [t5740] VALUES (390,'-، وخلف أبي بكر، وخلف عمر، وخلف عثمان - رضي الله عنهم - فكانوا يستفتحون بالحمد لله رب العالمين ". رواه مسلم في " الصلاة " من " صحيحه " عن محمد بن المثنى عن','811','275','0');
INSERT INTO [t5740] VALUES (391,'- أربعة، أبي بن كعب، ومعاذ، وزيد بن ثابت، وأبو زيد، قال: قلت لأنس: من أبو زيد؟ قال: " أحد عمومتي ". رواه مسلم في " الفضائل " من " صحيحه "، عن محمد بن','813','275','0');
INSERT INTO [t5740] VALUES (392,'- قال: " رؤيا المؤمن جزء من ستة وأربعين جزءا من النبوة ". هذا حديث صحيح رواه مسلم في " الرؤيا " من " صحيحه "، عن أبي موسى بن محمد بن المثنى، وأبي بكر محمد بن بشار بندار كلاهما عن محمد بن جعفر غندر، وأبي داود الطيالسي كلاهما عن شعبة فوقع','815','275','0');
INSERT INTO [t5740] VALUES (393,'- الذراع ذراع الشاة، وقد كان سم فيها، وكان يرى أن اليهود سموه ". رواه أبو داود في " الأطعمة " من " سننه " والترمذي في " الشمائل " كلاهما عن أبي بكر محمد بن بشار بندار، عن أبي داود،','817','275','0');
INSERT INTO [t5740] VALUES (394,'- كان يصوم ثلاثة أيام من غرة كل شهر ". رواه أبو داود في " الصيام " من " سننه "، عن أبي كامل الفضيل بن الحسين الجحدري، ورواه النسائي فيه من " سننه "، عن عمرو بن علي الفلاس كلاهما عن أبي داود، فوقع لنا بدلا عاليا لهما.','819','275','0');
INSERT INTO [t5740] VALUES (395,'- قال: " إن للوضوء شيطانا يقال له الولهان فاحذروه، أو قال: فاتقوه ".','820','275','0');
INSERT INTO [t5740] VALUES (396,'-: " شغلونا عن صلاة الوسطى، صلاة العصر، ملأ الله بيوتهم وقبورهم نارا ". رواه الترمذي في " الصلاة " و " التفسير " من " جامعه "، عن محمود بن غيلان، عن أبي داود، فوقع لنا عاليا له ".','822','275','0');
INSERT INTO [t5740] VALUES (397,'- كان في سفر ومعه بلال - رضي الله عنه - فأراد أن يقيم، فقال رسول الله -','823','275','0');
INSERT INTO [t5740] VALUES (398,'- قال: إن شدة الحر من فيح جهنم، فأبردوا عن الصلاة ". رواه الترمذي في " الصلاة " من " جامعه " عن محمود بن غيلان، عن أبي داود، فوقع لنا بدلا عاليا له.','824','275','0');
INSERT INTO [t5740] VALUES (399,'-: " ثلاثة لا ينظر الله إليهم يوم القيامة ولا يكلمهم، ولهم عذاب أليم، قلت يا رسول فمن هؤلاء، فقد خابوا وخسروا؟ ، قال: المنان، والمسبل إزاره، والمنفق سلعته بالحلف الكاذب ".','825','275','0');
INSERT INTO [t5740] VALUES (400,'- يقول: " إن أول ما خلق الله القلم، فقال: اكتب؟ فقال رب ما أكتب؟ قال اكتب القدر ما هو كائن إلى الأبد ". رواه الترمذي في " القدر " و " التفسير " من " جامعه "، عن أبي زكريا يحيى بن موسى البلخي خت، عن أبي داود، فوقع لنا بدلا عاليا','827','275','0');
INSERT INTO [t5740] VALUES (401,'-: كل مسكر حرام ". رواه النسائي في " الأشربة " و " الوليمة " من " سننه "، عن أحمد بن عبد الله بن سويد بن منجوف، وعبد الله بن','828','275','0');
INSERT INTO [t5740] VALUES (402,'- مفطرا يوم الجمعة ". رواه ابن ماجه في " الصوم " من " سننه " عن إسحاق بن منصور، عن أبي داود، فوقع لنا بدلا عاليا له.','830','275','0');
INSERT INTO [t5740] VALUES (403,'الشيخ الثامن عشر','833','275','0');
INSERT INTO [t5740] VALUES (404,'شجاع بن مفرج بن قصة المقدسي (٠٠٠ - ٦١٣ هـ)','833','275','0');
INSERT INTO [t5740] VALUES (405,'- قال: " لا يدخل الجنة من في قلبه مثقال حبة من خردل من كبر، فقال رجل: يا رسول الله إني رجل أحب الجمال حتى في شراك نعلي وعلاقة سوطي، فقال: ليس ذلك من الكبر، ولكن الكبر من سفه الحق وغمص الناس ".','837','275','0');
INSERT INTO [t5740] VALUES (406,'- " لا يدخل الجنة من كان في قلبه مثقال حبة من خردل من كبر ". هذا حديث صحيح رواه الإمام أحمد في " مسنده "، عن يزيد بن هارون، فوافقناه بعلو في هذه الرواية، ورواه مسلم في " الإيمان " من','838','275','0');
INSERT INTO [t5740] VALUES (407,'- قال: " إن أبر البر صلة رحم دانية بعد أن','841','275','0');
INSERT INTO [t5740] VALUES (408,'-: " الراكب شيطان، والراكبان شيطانان، والثلاثة ركب ".','844','275','0');
INSERT INTO [t5740] VALUES (409,'(الجزء السابع من مشيخة الإمام العالم ... ملحق الأحفاد بالأجداد فخر الدين أبي الحسن علي بن أحمد بن عبد الواحد المقدسي عرف بابن البخاري.)','849','275','0');
INSERT INTO [t5740] VALUES (410,'(تخريج الإمام الحافظ مفيد الطالبين جمال الدين أبي العباس أحمد بن محمد بن عبد الله الظاهري، الحنفي الحلبي له. تغمدهم الله برحمته، وإيانا آمين.)','849','275','0');
INSERT INTO [t5740] VALUES (411,'(الشيخ التاسع عشر هبة الله بن أبي العباس أحمد بن عبد الواحد ابن عبد الوهاب السلمي الكهفي (٥٥٣ هـ تقريبا - ٦١٤ هـ))','851','275','0');
INSERT INTO [t5740] VALUES (412,'-، وأبي بكر، وعمر وعثمان - رضي الله عنهم - فما سمعت أحدا منهم يجهر ببسم الله الرحمن الرحيم ".','853','275','0');
INSERT INTO [t5740] VALUES (413,'- وقال ابن كرم في روايته: خلف رسول الله -','854','275','0');
INSERT INTO [t5740] VALUES (414,'- ركع ركعتين في حجرة حفصة، وبلال - رضي الله عنهما - يقيم الصلاة ". رواه النسائي في " الصلاة " من " سننه "، عن عبيد الله بن فضالة، عن محمد بن المبارك الصوري، عن معاوية بن سلام، عن','856','275','0');
INSERT INTO [t5740] VALUES (415,'- رجل فقال يا رسول الله: أي الذنب أكبر؟ قال: أن تجعل لله ندا وهو خلقك، قال: ثم أي، قال: أن تقتل ولدك خشية أن يطعم معك، قال: ثم أي؟ قال: أن تزاني حليلة جارك. فأنزل الله عز وجل تصديق ذلك: {والذين لا يدعون مع الله إلها آخر}','857','275','0');
INSERT INTO [t5740] VALUES (416,'- فقال: أي الذنب أكبر؟ ! قال: أن تجعل لله ندا وهو خلقك، قال: ثم أي؟ ، قال: ثم أن تقتل ولدك من أجل أن يطعم معك، قال: ثم أي؟ قال: ثم أن تزاني بحليلة جارك، قال: فأنزل الله عز وجل - تصديق ذلك في كتابه: {والذين لا يدعون مع الله','858','275','0');
INSERT INTO [t5740] VALUES (417,'- أي الذنب أكبر؟ ، قال: أن تجعل لله ندا وهو خلقك، قال: ثم أي؟ قال: أن تقتل ولدك أن يطعم معك، قال: ثم أي؟ قال: أن تزاني حليلة جارك، قال عبد الله: فأنزل الله - عز وجل - تصديق ذلك في كتابه {والذين لا يدعون مع الله إلها آخر} ".','859','275','0');
INSERT INTO [t5740] VALUES (418,'- فقال: يا رسول الله أي الذنب أعظم؟ ، قال: أن تجعل لله ندا وهو خلقك، قال: ثم أي؟ قال: أن تقتل ولدك من أجل أن يطعم معك، قال: ثم أي؟ قال: أن تزاني حليلة جارك، قال: فأنزل الله تصديق ذلك في كتابه: {والذين لا يدعون مع الله إلها آخر','859','275','0');
INSERT INTO [t5740] VALUES (419,'- نهى عن القزع ". رواه ابن ماجه في " اللباس " من " سننه " عن أبي بكر بن أبي شيبة، عن شبابة بن سوار، عن شعبة، عن عبد الله بن دينار.','862','275','0');
INSERT INTO [t5740] VALUES (420,'- عن متعة النساء يوم خيبر ". ولد الكهفي - تقريبا - في سنة ثلاث وخمسين وخمسمائة، وتوفي بجبل قاسيون بالكهف، في يوم الجمعة سادس جمادى الأولى، من سنة أربع عشرة وستمائة، ودفن من يومه تحت الكهف - رحمه الله وإيانا -.','864','275','0');
INSERT INTO [t5740] VALUES (421,'(الشيخ العشرون أبو الحجاج يوسف بن الشيخ الزاهد أبي الحسين بن عبد الله بن أحمد المقدسي. (٥٣١ هـ تقريبا - ٦١٤ هـ))','865','275','0');
INSERT INTO [t5740] VALUES (422,'- فقال: يا رسول الله إنا نركب البحر، ونحمل معنا القليل من الماء، فإن توضأنا به عطشنا، أفنتوضأ من ماء البحر؟ فقال رسول الله -','868','275','0');
INSERT INTO [t5740] VALUES (423,'- يقول: " قام رسول الله -','871','275','0');
INSERT INTO [t5740] VALUES (424,'- بسنة قال: " قام رسول الله -','872','275','0');
INSERT INTO [t5740] VALUES (425,'-، فقال: يا رسول الله (أرأيت) إن قتلت في سبيل الله صابرا محتسبا مقبلا غير مدبر أيكفر الله - عز وجل - عني من خطاياي؟ فقال رسول الله -','874','275','0');
INSERT INTO [t5740] VALUES (426,'- أو أمر به فنودي له، فقال رسول الله -','874','275','0');
INSERT INTO [t5740] VALUES (427,'(الشيخ الحادي والعشرون الإمام، الزاهد، أبو إسماعيل، وأبو إسحاق إبراهيم بن عبد الواحد ابن علي بن سرور بن رافع بن حسن بن جعفر المقدسي (٥٤٣ هـ - ٦١٤ هـ))','879','275','0');
INSERT INTO [t5740] VALUES (428,'- قال: " من قال لا إله إلا الله وحده لا شريك له، له الملك، وله الحمد، بيده الخير، وهو على كل شيء قدير، عشر مرات، كن له كعدل عشر رقاب أو رقبة ".','882','275','0');
INSERT INTO [t5740] VALUES (429,'- قال: " من قال لا إله إلا الله وحده، لا شريك له، له الملك، وله الحمد، وهو على كل شيء قدير عشر مرات، كن له كعدل عشر رقاب أو رقبة ".','883','275','0');
INSERT INTO [t5740] VALUES (430,'- قال: " من قال لا إله إلا الله وحده، لا شريك له، له الملك، وله الحمد، بيده الخير، وهو على كل شيء قدير، عشر مرات، كن له كعدل عشر رقاب، أو رقبة ".','883','275','0');
INSERT INTO [t5740] VALUES (431,'من قال','884','275','0');
INSERT INTO [t5740] VALUES (432,'هذا حديث صحيح متفق على صحته، أخرجه البخاري في " الدعوات " من " صحيحه " عن أبي جعفر عبد الله بن محمد المسندي، وأخرجه مسلم فيه من " صحيحه " عن أبي أيوب سليمان بن عبيد الله الغيلاني، كلاهما عن أبي عامر عبد الملك بن عمرو العقدي، عن عمر','885','275','0');
INSERT INTO [t5740] VALUES (433,'- قال: " لا يرث المسلم الكافر ".','889','275','0');
INSERT INTO [t5740] VALUES (434,'-: " لا يرث المؤمن الكافر ". اختلف على مالك في هذا الحديث، فأكثر أصحابه، نقلوا عنه تسمية ابن عثمان (عمر) ، كما رويناه، وقيل: إن مالكا روجع فيه،','889','275','0');
INSERT INTO [t5740] VALUES (435,'قال: " لا يرث المسلم الكافر، ولا الكافر المسلم ".','892','275','0');
INSERT INTO [t5740] VALUES (436,'- قال: " لا يرث الكافر المسلم، ولا المسلم الكافر ". هذا حديث صحيح متفق على صحته، أخرجه الأئمة في كتبهم من طرق منها: ما رواه البخاري في " الفرائض " عن أبي عاصم الضحاك بن مخلد، عن ابن جريج، عن الزهري. ورواه مسلم فيه من " صحيحه " عن','893','275','0');
INSERT INTO [t5740] VALUES (437,'- قال: " لولا أن أشق على أمتي لأحببت أن لا أتخلف خلف سرية تخرج / في سبيل الله، ولكن لا أجد ما أحملهم عليه ولا يجدون ما يتحملون عليه، ويشق عليهم أن يتخلفوا بعدي، فوددت أني أقاتل في سبيل الله، فأقتل ثم أحيا، فأقتل ثم أحيا - قال الهاشمي في','896','275','0');
INSERT INTO [t5740] VALUES (438,'- رضي الله عنه - قال: " كان رسول الله -','898','275','0');
INSERT INTO [t5740] VALUES (439,'- قال: " كان رسول الله -','899','275','0');
INSERT INTO [t5740] VALUES (440,'أن رسول الله','900','275','0');
INSERT INTO [t5740] VALUES (441,'- قال: " كان رسول الله -','900','275','0');
INSERT INTO [t5740] VALUES (442,'- فيهم رجل ضخم يقال له: أصرم قد ابتاع حبشيا، فقال، يا رسول الله، سمه، وادع له بالبركة، قال: ما اسمك؟ قال: أصرم، قال: بل أنت زرعة، فقال: ما تريد به؟ قال: أريده راعيا، فقال النبي -','902','275','0');
INSERT INTO [t5740] VALUES (443,'- كان يقطع في ربع دينار فصاعدا ".','903','275','0');
INSERT INTO [t5740] VALUES (444,'- كان يقطع في ربع دينار فصاعدا ".','904','275','0');
INSERT INTO [t5740] VALUES (445,'- كان يقطع في ربع دينار فصاعدا ". هذا حديث صحيح، رواه مسلم في " الحدود " من " صحيحه "، عن يحيى بن يحيى، وإسحاق بن راهويه، ومحمد بن يحيى العدني،','905','275','0');
INSERT INTO [t5740] VALUES (446,'(الشيخ الثاني والعشرون الشيخ الإمام أبو القاسم عبد الصمد بن محمد بن أبي الفضل ابن علي بن عبد الواحد الأنصاري الخزرجي العبادي السعدي الدمشقي المعروف بابن الحرستاني (٥٢٠ هـ - ٦١٤ هـ))','911','275','0');
INSERT INTO [t5740] VALUES (447,'- أي الأعمال أفضل؟ قال: " إيمان بالله، وجهاد في سبيل','913','275','0');
INSERT INTO [t5740] VALUES (448,'- عن الصوم في السفر وكان يسرد الصوم، فقال رسول الله -','918','275','0');
INSERT INTO [t5740] VALUES (449,'- يقول: " من أوى إلى فراشه، فذكر الله عز وجل حتى يدركه النعاس، لم يتقلب ساعة من الليل يسأل الله فيها شيئا من خير الدنيا والآخرة إلا أعطاه إياه ". أخرجه الترمذي في " الدعوات من " جامعه " عن أبي علي الحسن بن عرفة هذا، فوافقناه بعلو. وقال:','921','275','0');
INSERT INTO [t5740] VALUES (450,'-، فرواه عن هلال بن العلاء، عن أبيه، عن عبيد الله، عن زيد بن أبي أنيسة، عن عاصم، عن شمر بن عطية، عن','922','275','0');
INSERT INTO [t5740] VALUES (451,'- يقول: " لا يحل لأحد أن يحمل بمكة سلاحا ". رواه مسلم في " المناسك " من " صحيحه "، عن سلمة بن شبيب، فوافقناه بعلو.','925','275','0');
INSERT INTO [t5740] VALUES (452,'- وعني، قلنا: بلى، قالت: لما كانت ليلتي انقلب، فوضع نعليه عند رجليه، ووضع رداءه، وبسط طرف إزاره على فراشه، فاضطجع، ولم يلبث إلا ريثما ظن أني قد رقدت، فانتظر ثم انتعل رويدا، وأخذ رداءه رويدا، ثم فتح الباب رويدا، فخرج وأجافه رويدا،','926','275','0');
INSERT INTO [t5740] VALUES (453,'-: " مروا أولادكم بالصلاة أبناء سبع سنين، واضربوهم عليها وهم أبناء عشر سنين، وفرقوا بينهم في المضاجع ". رواه أبو داود في " الصلاة " من " سننه "، عن المؤمل بن هشام كما رويناه، فوقع لنا موافقة عالية له.','929','275','0');
INSERT INTO [t5740] VALUES (454,'-، وذكر حديث الجساسة بطوله، هكذا في كتابه الأصل. رواه الترمذي في " الفتن " من " جامعه "، عن أبي بكر محمد بن بشار بندار هذا، فوافقناه بعلو.','931','275','0');
INSERT INTO [t5740] VALUES (455,'- صعد على المنبر، فقال: " أيها الناس حدثني تميم الداري أن ناسا من قومه كانوا في البحر في سفينة فخرجوا إلى جزيرة في البحر، فإذا هم بامرأة شعثة سوداء لها شعر منكر، فقالوا: ما أنت؟ قالت: أنا الجساسة أتعجبون مني؟ قالوا: نعم، قالت: فادخلوا','932','275','0');
INSERT INTO [t5740] VALUES (456,'-: وهذه طيبة ". رواه مسلم في " الفتن " من " صحيحه " عن أبي بكر محمد بن','933','275','0');
INSERT INTO [t5740] VALUES (457,'- يصليها لسقوط القمر لثالثة ". أخرجه النسائي في " الصلاة " من " سننه " عن أبي عبد الله محمد بن قدامة بن أعين المصيصي الجوهري هذا، كما أخرجناه، فوقع لنا موافقة عالية له.','935','275','0');
INSERT INTO [t5740] VALUES (458,'-: " من كانت له صلاة صلاها نصف الليل فنام عنها كان ذلك صدقة تصدق الله عليه، وكتب له أجر صلاته ". رواه النسائي في " الصلاة " من " سننه " عن أبي داود سليمان بن','936','275','0');
INSERT INTO [t5740] VALUES (459,'-: " تابعوا بين الحج والعمرة، فإنهما ينفيان الفقر والذنوب كما ينفي الكير خبث الحديد ". أخرجه النسائي في الحج " من " سننه " عن أبي داود سليمان بن','938','275','0');
INSERT INTO [t5740] VALUES (460,'-: " يا علي ألا أعلمك كلمات إذا قلتهن وعليك عدد الذر من الخطايا، غفر لك - على أنه مغفور لك - تقول: لا إله إلا الله العلي العظيم، لا إله إلا الله الحليم الكريم، سبحان الله، وتبارك الله رب العرش العظيم، الحمد لله رب العالمين ". رواه','940','275','0');
INSERT INTO [t5740] VALUES (461,'- إذا أخذ مضجعه وضع كفه اليمنى تحت خده اليمنى، قالت: وكانت يمينه لطعامه وطهوره، وصلاته وشرابه، وكانت شماله لما سوى ذلك ". أخرج النسائي بعضه في " اليوم والليلة " عن علي بن حرب','942','275','0');
INSERT INTO [t5740] VALUES (462,'-: " من صلى ست ركعات بين المغرب والعشاء لا يتكلم بينهن بسوء عدلن له عبادة ثلاثين سنة ".','944','275','0');
INSERT INTO [t5740] VALUES (463,'-: " من صلى ست ركعات بعد المغرب لا يتكلم بينهن بسوء عدلن له بعبادة اثنتي عشرة سنة ". رواه ابن ماجه في " الصلاة " من " سننه " عن حفص بن عمرو الربالي، فوافقناه بعلو.','946','275','0');
INSERT INTO [t5740] VALUES (464,'- في حجة الوداع وهو يقول: " أمك وأباك، وأختك وأخاك، ثم أدناك أدناك "، ثم أتاه قومه وقالوا: يا رسول الله هؤلاء قتلة فلان، قال رسول الله -','948','275','0');
INSERT INTO [t5740] VALUES (465,'(الشيخ الثالث والعشرون أبو محمد عبد الوهاب بن المنجى بن المؤمل التنوخي المعري ( ... - ٦١٥ هـ))','953','275','0');
INSERT INTO [t5740] VALUES (466,'قال ينادي مناد إن لكم أن تحيوا فلا تموتوا أبدا وإن لكم أن تصحوا فلا تسقموا أبدا وإن لكم أن تشبوا فلا تهرموا أبدا وإن لكم أن تنعموا فلا تبؤسوا أبدا فذلك قول الله عز وجل ونودوا أن تلكم الجنة أورثتموها بما كنتم تعملون','956','275','0');
INSERT INTO [t5740] VALUES (467,'- قال: ينادي مناد: إن لكم أن تحيوا فلا تموتوا','956','275','0');
INSERT INTO [t5740] VALUES (468,'- قال: " ينادي مناد: يا أهل الجنة إن لكم أن تحيوا فلا تموتوا أبدا، وإن لكم أن تصحوا فلا تسقموا أبدا، وإن تشبوا فلا تهرموا أبدا، وإن لكم أن تنعموا فلا تبؤسوا أبدا، فذلك قول الله عز وجل: {وتلك الجنة التي أورثتموها بما كنتم تعملون','958','275','0');
INSERT INTO [t5740] VALUES (469,'-: " يقال لأهل الجنة: إن لكم أن تصحوا فلا تسقموا أبدا، وإن لكم أن تعيشوا فلا تموتوا أبدا، وإن لكم أن تنعموا فلا تبؤسوا أبدا، وإن لكم أن تشبوا فلا تهرموا أبدا ". قال الطبراني: " لم يروه عن الثوري إلا عبد الرزاق، قال: ووهم أبو إسحاق','959','275','0');
INSERT INTO [t5740] VALUES (470,'-: كان يشير في الصلاة ".','962','275','0');
INSERT INTO [t5740] VALUES (471,'كان يشير في الصلاة ". رواه أبو داود في " الصلاة " من " سننه "، عن أحمد [بن','962','275','0');
INSERT INTO [t5740] VALUES (472,'- عن نبيذ الجر ". قال علي: وأخبرني إسحاق قال: حدثتني هنيدة عن عائشة: بمثله ". رواه مسلم في " الأشربة " من " صحيحه "، عن يعقوب بن','964','275','0');
INSERT INTO [t5740] VALUES (473,'-: " تخرج رايات سود من قبل المشرق فلا يردها شيء حتى تنصب بإيلياء ".','966','275','0');
INSERT INTO [t5740] VALUES (474,'- يقول: " بينا أنا نائم رأيتني على قليب عليها دلو، فنزعت، منها ما شاء الله أن أنزع، ثم أخذها ابن أبي قحافة فنزع منها ذنوبا أو ذنوبين، وفي نزعه ضعف، وليغفر الله له ثم استحالت غربا فأخذها ابن الخطاب فلم أر عبقريا من الناس ينزع نزع ابن','968','275','0');
INSERT INTO [t5740] VALUES (475,'-: " أنا زعيم ببيت في ربض الجنة لمن ترك المراء وإن كان محقا، وببيت في وسط الجنة لمن ترك الكذب وإن كان مازحا، وببيت في أعلى الجنة لمن حسب خلقه ".','970','275','0');
INSERT INTO [t5740] VALUES (476,'- قال: " أنا زعيم ببيت في ربض الجنة لمن ترك المراء، وإن كان محقا، وببيت في وسط الجنة لمن ترك الكذب وإن كان مازحا، وببيت في أعلى الجنة لمن حسن خلقه ". أخرجه أبو داود في " الأدب " / من " سننه "، عن أبي الجماهير','971','275','0');
INSERT INTO [t5740] VALUES (477,'(الشيخ الرابع والعشرون أبو الفتوح محمد بن أبي سعد محمد بن محمد بن عمروك ابن سعيد بن عبد الله بن الحسن بن القاسم بن علقمة بن النضر ابن معاذ بن عبد الرحمن بن القاسم بن محمد ابن أبي بكر الصديق النيسابوري الصوفي (٥١٨ هـ - ٦١٥ هـ))','973','275','0');
INSERT INTO [t5740] VALUES (478,'- يقول: " إذا أخذ أحدكم مضجعه - ولم يقل الأصم: أحدكم - يقول: اللهم إليك أسلمت نفسي، وإليك وجهت وجهي، وإليك فوضت أمري، وإليك ألجأت ظهري رغبة ورهبة، لا ملجأ ولا منجى منك إلا إليك، آمنت بكتابك الذي أنزلت، وبرسولك - وقال الأصم: أو نبيك','979','275','0');
INSERT INTO [t5740] VALUES (479,'- أنه أوصى رجلا قال: " إذا أخذت مضجعك فقل: اللهم أسلمت نفسي إليك، ووجهت وجهي إليك، وفوضت أمري إليك، وألجأت ظهري إليك رغبة ورهبة','980','275','0');
INSERT INTO [t5740] VALUES (480,'-: " من نسي صلاة فليصلها إذا ذكرها ". رواه مسلم والترمذي والنسائي في " الصلاة " من كتبهم، عن أبي رجاء قتيبة بن سعيد نحو ما رويناه، فوقع لنا موافقة عالية لثلاثتهم.','984','275','0');
INSERT INTO [t5740] VALUES (481,'- إغفاءة فرفع رأسه متبسما، فإما قال لهم، وإما قالوا له: يا رسول الله: مم ضحكت؟ قال: " إنه أنزلت علي آنفا سورة "، فقرأ: {بسم الله الرحمن الرحيم إنا أعطيناك الكوثر (١) } حتى ختمها، فلما قرأها قال: " هل تدرون ما الكوثر؟ "','986','275','0');
INSERT INTO [t5740] VALUES (482,'- قال: " إن الذي تفوته صلاة العصر كأنما وتر أهله وماله ".','987','275','0');
INSERT INTO [t5740] VALUES (483,'- قال: " إن الذي تفوته صلاة العصر فكأنما وتر أهله وماله ". رواه الترمذي في " الصلاة " من " جامعه " عن قتيبة بن سعيد،','988','275','0');
INSERT INTO [t5740] VALUES (484,'- يقول: " من يقل علي ما لم أقل فليتبوأ مقعده من النار ".','991','275','0');
INSERT INTO [t5740] VALUES (485,'-: " من كذب علي متعمدا فليتبوأ مقعده من النار ".','991','275','0');
INSERT INTO [t5740] VALUES (486,'- يقول: " من يقل علي غير الحق فليتبوأ مقعده من النار ".','991','275','0');
INSERT INTO [t5740] VALUES (487,'-: " من قال علي ما لم أقل فليتبوأ مقعده من النار ". رواه البخاري في " العلم " من " جامعه "، عن مكي بن','992','275','0');
INSERT INTO [t5740] VALUES (488,'- يقول للقوم: " من وحد الله، وكفر بما يعبد دونه حرم ماله ودمه، وحسابه على الله - عز وجل - ".','994','275','0');
INSERT INTO [t5740] VALUES (489,': " من وحد الله، وكفر بما يعبد دونه حرم ماله ودمه، وحسابه على الله - عز وجل - ".','994','275','0');
INSERT INTO [t5740] VALUES (490,'- يقول: " من وحد الله، وكفر بما يعبد من دونه، حرم الله ماله ودمه، وحسابه على الله - عز وجل - ".','995','275','0');
INSERT INTO [t5740] VALUES (491,'- يقول: " من وحد الله وكفر بما يعبد من دونه، فقد حرم ماله ودمه، وحسابه على الله - عز وجل - ".','995','275','0');
INSERT INTO [t5740] VALUES (492,'- يقول للقوم: " من وحد الله وكفر بما يعبد دونه، حرم ماله ودمه، وحسابه على الله - تعالى - ".','996','275','0');
INSERT INTO [t5740] VALUES (493,'- يقول: " من وحد الله، وكفر بما يعبد من دونه حرم ماله ودمه، وحسابه على الله - عز وجل - ". رواه مسلم في " الإيمان "، من " صحيحه "، عن أبي خيثمة','996','275','0');
INSERT INTO [t5740] VALUES (494,'- على النصح لكل مسلم، فأنا لكم ناصح ".','997','275','0');
INSERT INTO [t5740] VALUES (495,'- على النصح لكل مسلم " قال مسعر، عن زياد: " فإني لكم ناصح ". أخرجه البخاري في " الشروط " من " جامعه "، عن أبي نعيم الفضل بن دكين. ورواه مسلم في " الإيمان " من " صحيحه "، عن أبي بكر بن أبي شيبة، وأبي خيثمة وزهير بن حرب، ومحمد بن','998','275','0');
INSERT INTO [t5740] VALUES (496,'- عن وقت الصلاة - وفي رواية عن وقت صلاة الغداة - فلما أصبح من الغد أمر حين انشق الفجر، فأقام الصلاة، وصلى فلما كان من الغد أخر حتى أسفر، ثم أمر، فأقيمت الصلاة، فصلى لنا، فقال: أين السائل عن وقت الصلاة؟ [ما بين هذين وقت] ".','1000','275','0');
INSERT INTO [t5740] VALUES (497,'- سأله رجل عن وقت صلاة الصبح، وأمر بلالا، فأذن حين طلع الفجر، ثم أقام فصلى، فلما كان من الغد أسفر، ثم أمره أن يقيم فصلى، ثم / دعا الرجل فقال: ما بين هذا وهذا وقت ".','1000','275','0');
INSERT INTO [t5740] VALUES (498,'عن وقت الصلاة، صلى حين طلع الفجر، ثم صلى بعد ذلك حين أسفر، فقال: ما بين هذين وقت " رواه النسائي في " الصلاة " من " سننه "، عن إسحاق بن راهويه،','1001','275','0');
INSERT INTO [t5740] VALUES (499,'-، ومعه مدرى يحك رأسه، فقال: لو أعلم أنك تنظرني','1003','275','0');
INSERT INTO [t5740] VALUES (500,'- ومعه مدرى يحك به رأسه، فقال: " لو أعلمك تنظر لطعنت بها في عينك، إنما جعل الاستئذان من أجل النظر ". أخرجه البخاري في " الاستئذان " من " جامعه "، عن علي بن عبد الله بن المديني الحافظ. وأخرجه مسلم [فيه] من " صحيحه "، عن أبي بكر','1004','275','0');
INSERT INTO [t5740] VALUES (501,'- قال: " إذا أقيمت الصلاة وحضر العشاء، فابدءوا بالعشاء ".','1006','275','0');
INSERT INTO [t5740] VALUES (502,'- قال: " إذا حضر العشاء وأقيمت الصلاة فابدءوا بالعشاء ".','1006','275','0');
INSERT INTO [t5740] VALUES (503,'- قال: " إذا حضر العشاء، وأقيمت الصلاة فابدءوا بالعشاء ". رواه مسلم في " صحيحه " عن عمرو بن محمد الناقد، وأبي خيثمة زهير بن حرب، وأبي بكر بن أبي شيبة. ورواه الترمذي في " الصلاة " من جامعه، عن قتيبة بن سعيد. / ورواه النسائي فيه من "','1007','275','0');
INSERT INTO [t5740] VALUES (504,'(الجزء الثامن من مشيخة الإمام العالم فخر الدين، بقية المسندين رحلة الزمان حسنه، الإمام ملحق الأحفاد بالأجداد أبي الحسن علي بن أحمد بن عبد الواحد المقدسي عرف بابن البخاري)','1009','275','0');
INSERT INTO [t5740] VALUES (505,'تخريج الإمام الحافظ جمال الدين معين الطالبين أبي العباس أحمد بن محمد بن عبد الله الظاهري','1009','275','0');
INSERT INTO [t5740] VALUES (506,'(الشيخ الخامس والعشرون أبو القاسم أحمد بن عبد الله بن عبد الصمد بن عبد الرزاق السلمي، البغدادي، العطار (٥٤٦ هـ - ٦١٥ هـ))','1011','275','0');
INSERT INTO [t5740] VALUES (507,'- قال: " لا تزال جهنم تقول: هل من مزيد؟ حتى يضع فيها رب العزة تبارك وتعالى قدمه فتقول: قط قط، وعزتك، ويزوى بعضها إلى بعض ". رواه مسلم في " صفة النار "، من " صحيحه "، والترمذي في','1018','275','0');
INSERT INTO [t5740] VALUES (508,'- قال: " لا يحل لمسلم أن يهجر أخاه فوق ثلاثة أيام، يلتقيان فيصد هذا ويصد هذا، وخيرهما الذي يبدأ بالسلام ". رواه الإمام أحمد في " مسنده "، عن عبد الرزاق بن همام، فوقع لنا في هذه الرواية، موافقة عالية له. ورواه مسلم في " الاستئذان "','1020','275','0');
INSERT INTO [t5740] VALUES (509,'- سبع غزوات، وغزوت مع ابن حارثة استعمله علينا ".','1022','275','0');
INSERT INTO [t5740] VALUES (510,'- سبع غزوات، ومع زيد بن حارثة سبع غزوات، كان يؤمره علينا ". هذا حديث صحيح، رواه البخاري في " المغازي " من " صحيحه "، عن أبي عاصم الضحاك بن مخلد النبيل، فوافقناه بعلو في هذه الرواية، وهو أحد ثلاثياته.','1023','275','0');
INSERT INTO [t5740] VALUES (511,'- بعرفات، يوم الجمعة ".','1024','275','0');
INSERT INTO [t5740] VALUES (512,'- بعرفات يوم جمعة ". رواه الإمام أحمد في " مسنده "، عن جعفر بن عون، فوافقناه بعلو. ورواه البخاري في " الإيمان " من " جامعه "، عن الحسن بن','1025','275','0');
INSERT INTO [t5740] VALUES (513,'- في حجة الوداع، فمرضت مرضا أشفى علي منه الموت، فعادني رسول الله -','1027','275','0');
INSERT INTO [t5740] VALUES (514,'-، وكان مات بمكة ".','1028','275','0');
INSERT INTO [t5740] VALUES (515,'- قال: " يا سعد إنك لن تنفق نفقة تبتغي بها وجه الله إلا أجرت عليها، حتى اللقمة ترفعها إلى في امرأتك ". هذا حديث صحيح، رواه الإمام أحمد في " مسنده "، بطوله، عن عبد الرزاق وكذلك رواه مسلم في " الوصايا " من / " صحيحه " عن إسحاق بن راهويه،','1029','275','0');
INSERT INTO [t5740] VALUES (516,'-، وعدلت إلى ظل شجرة، فلما خف الناس، قال: يا ابن الأكوع: ألا تبايع؟ قال: قلت: قد بايعت يا رسول الله، قال: وأيضا، فبايعته الثانية، فقلت: يا أبا مسلم: على أي شيء كنتم تبايعون - يومئذ -؟ قال: على الموت ".','1031','275','0');
INSERT INTO [t5740] VALUES (517,'- يوم الحديبية، ثم تنحيت، فقال: يا سلمة ألا تبايع؟ قلت: قد بايعت، قال: أقبل فبايع، فدنوت فبايعته، قلت:','1031','275','0');
INSERT INTO [t5740] VALUES (518,'- وهو يخطب، فقال: لولا أن الكلاب أمة من الأمم، لأمرت بقتلها، ولكن اقتلوا منها كل أسود بهيم، وأيما أهل بيت يربطون كلبا، إلا نقص من أجورهم، كل يوم قيراط، إلا كلب صيد، أو كلب حرث أو كلب غنم ". رواه الترمذي في " الصلاة " من " جامعه "،','1034','275','0');
INSERT INTO [t5740] VALUES (519,'- قال: " في ثلاثين من البقر تبيع أو تبيعة، وفي أربعين مسنة ".','1036','275','0');
INSERT INTO [t5740] VALUES (520,'- يقول في دعائه: " اللهم أحيني مسكينا وأمتني مسكينا، واحشرني في زمرة المساكين ". رواه ابن ماجه في " الزهد " من " سننه "، عن أبي سعيد الأشج،','1038','275','0');
INSERT INTO [t5740] VALUES (521,'(الشيخ السادس والعشرون أبو البركات داود بن أبي منصور أحمد بن محمد بن من منصور ابن ملاعب بن الحارث بن ملاعب البغدادي الوكيل (٥٤٢ هـ - ٦١٦ هـ))','1043','275','0');
INSERT INTO [t5740] VALUES (522,'-: " من صلى على جنازة فله قيراط، ومن تبعها حتى تدفن فله قيراطان،','1045','275','0');
INSERT INTO [t5740] VALUES (523,'- قال: " من صلى على جنازة فله قيراط، فإن تبعها حتى توضع في القبر فقيراطان ". قال: قلت: يا أبا هريرة: ما القيراط؟ قال: مثل أحد.','1046','275','0');
INSERT INTO [t5740] VALUES (524,'-، رواه عنه جماعة منهم أبو حازم سلمان الأشجعي ومحمد بن سيرين ومن حديثهما أخرجناه وخباب المدني صاحب المقصورة.','1047','275','0');
INSERT INTO [t5740] VALUES (525,'- قال: " من صلى على جنازة فله قيراط، فإن انتظر حتى يفرغ منها فله قيراطان ".','1052','275','0');
INSERT INTO [t5740] VALUES (526,'- فقال: هلكت، قال: وما أهلكك؟ قال: وقعت على أهلي في شهر رمضان، قال: عندك ما تعتق رقبة، قال: لا، قال: هل تستطيع أن تصوم شهرين متتابعين؟ قال: لا، قال: فهل تستطيع أن تطعم ستين مسكينا؟ قال: لا، قال: اجلس، فأتى النبي -','1053','275','0');
INSERT INTO [t5740] VALUES (527,'- حتى بدت نواجذه، ثم قال: خذه واذهب فأطعمه عيالك ".','1054','275','0');
INSERT INTO [t5740] VALUES (528,'- فقال: هلكت، قال: وما أهلكك؟ قال: وقعت على امرأتي في رمضان، فقال: أتجد رقبة؟ قال: لا، قال: تستطيع تصوم شهرين متتابعين؟ قال: لا، قال: تستطيع تطعم ستين مسكينا؟ قال: لا، قال: اجلس، قال: فأتي النبي -','1054','275','0');
INSERT INTO [t5740] VALUES (529,'- وقال: " أطعمه أهلك ". فقال مرة: " فتبسم حتى بدت أنيابه، وقال: أطعمه عيالك ".','1054','275','0');
INSERT INTO [t5740] VALUES (530,'-، فقال: هلكت، قال: وما شأنك؟ قال: وقعت على امرأتي في رمضان، قال: هل تجد ما تعتق رقبة؟ قال: لا، قال: أتستطيع أن تصوم شهرين متتابعين؟ قال: لا، قال: أتستطيع أن تطعم ستين مسكينا؟ قال: لا، قال: اجلس فجلس، قال: فأتى بعرق فيه','1055','275','0');
INSERT INTO [t5740] VALUES (531,'- فسئل عنها، فقال: ألقوها وما حولها وكلوه ".','1058','275','0');
INSERT INTO [t5740] VALUES (532,'- فقال: خذوها وما حولها وكلوه ".','1059','275','0');
INSERT INTO [t5740] VALUES (533,'-: " ألقوها وما حولها، وكلوه ".','1059','275','0');
INSERT INTO [t5740] VALUES (534,'- فقال: ألقوها وما حولها وكلوه ".','1060','275','0');
INSERT INTO [t5740] VALUES (535,'- سئل عن فأرة [وقعت في سمن فماتت فيه] ، قال: " ألقوها وما حولها وكلوه ". هذا حديث صحيح، رواه البخاري في " الذبائح " من','1060','275','0');
INSERT INTO [t5740] VALUES (536,'-: " أحبوا الله لما يغذوكم به من نعمه، وأحبوني لحب الله، وأحبوا أهل بيتي لحبي ".','1064','275','0');
INSERT INTO [t5740] VALUES (537,'-: " أحبوا الله لما يغذوكم به من نعمه وأحبوني لحب الله، وأحبوا أهل بيتي لحبي ". رواه الترمذي في " المناقب " من " جامعه " عن أبي داود','1065','275','0');
INSERT INTO [t5740] VALUES (538,'- لم يكن يصافح امرأة قط ". رواه النسائي، عن معاوية بن صالح الأزدي، عن يحيى بن','1067','275','0');
INSERT INTO [t5740] VALUES (539,'- ذات يوم من جنازة بالبقيع، وأنا أجد صداعا في رأسي','1069','275','0');
INSERT INTO [t5740] VALUES (540,'- " إلى هنا انتهى حديث ابن أخي ميمي - وزاد الكتاني وحده: " ثم بدئ به في وجعه الذي مات فيه ".','1070','275','0');
INSERT INTO [t5740] VALUES (541,'- ذات يوم من جنازة بالبقيع وأنا أجد صداعا في رأسي، وأنا أقول: وارأساه، فقال: بل','1070','275','0');
INSERT INTO [t5740] VALUES (542,'- ثم بدئ به في وجعه الذي مات فيه ". رواه ابن ماجه في " الجنائز " من " سننه " عن أبي عبد الله محمد بن','1071','275','0');
INSERT INTO [t5740] VALUES (543,'- لما جاء إلى','1072','275','0');
INSERT INTO [t5740] VALUES (544,'-: " إن الله عز وجل قال: من عادى لي وليا، فقد آذنني بالحرب، وما تقرب إلي عبدي بشيء أحب إلي مما افترضت عليه، وما يزال عبدي يتقرب إلي بالنوافل حتى أحبه، فإذا أحببته كنت سمعه الذي يسمع به، وبصره الذي يبصر به، ويده التي يبطش بها، ورجله','1077','275','0');
INSERT INTO [t5740] VALUES (545,'-، فقالت - يا رسول الله -: إن ابن أختي وجع، فمس رأسي، ودعا لي رسول الله -','1080','275','0');
INSERT INTO [t5740] VALUES (546,'رواه البخاري في " الطهارة " من " صحيحه "، عن أبي مسلم عبد الرحمن بن يونس البغدادي وغيره. ورواه مسلم في " صفة النبي -','1080','275','0');
INSERT INTO [t5740] VALUES (547,'- وقال الكتاني -: مع رسول الله -','1082','275','0');
INSERT INTO [t5740] VALUES (548,'- والآخر: رافع ثوبه - وقال الكتاني: - بثوبه يستره من الحر - حتى رمى جمرة العقبة ". أخرجه مسلم وأبو داود في " الحج " من " كتابيهما "، عن الإمام','1082','275','0');
INSERT INTO [t5740] VALUES (549,'(الشيخ السابع والعشرون أحمد بن أبي عبد الله محمد بن سيدهم بن هبة الله بن سرايا الأنصاري الدمشقي الوكيل المعروف أبوه بابن الهراس (٥٣٢ هـ - ٦١٦ هـ))','1085','275','0');
INSERT INTO [t5740] VALUES (550,'- المشركين، لئن أشهدني الله قتال المشركين ليرين الله كيف أصنع؟ فلما كان يوم أحد انكشف المسلمون، فقال: اللهم إني أعتذر إليك مما صنع هؤلاء - يعني أصحابه - وأبرأ إليك مما جاء به هؤلاء - يعني المشركين -، ثم تقدم فلقيه سعد دون أحد، فقال:','1088','275','0');
INSERT INTO [t5740] VALUES (551,'- المشركين، لئن الله أشهدني قتالا، ليرين الله كيف أصنع، قال: فلما كان يوم أحد انكشف المسلمون، فقال: اللهم إني أبرأ إليك مما جاء به هؤلاء - يعني المشركين - وأعتذر إليك مما صنع هؤلاء - يعني أصحابه -، قال: ثم تقدم فلقيه سعد بآخرها دون أحد،','1089','275','0');
INSERT INTO [t5740] VALUES (552,'- المشركين، لئن أشهدني الله قتالا ليرين الله ما أصنع، فلما كان يوم أحد انكشف الناس، فقال: اللهم إني أبرأ إليك مما جاء به هؤلاء - يعني المشركين - وأعتذر إليك مما صنع هؤلاء - يعني المسلمين - ثم مشى بسيفه فلقيه سعد بن معاذ - رضي الله عنه -','1090','275','0');
INSERT INTO [t5740] VALUES (553,'قلت سمى النبي','1093','275','0');
INSERT INTO [t5740] VALUES (554,'- فقال: تلك السكينة نزلت للقرآن، أو تنزلت على القرآن ". هذا حديث صحيح متفق على صحته، رواه البخاري في " علامات النبوة "، ومسلم في (" الصلاة " من) " صحيحه " كلاهما عن محمد بن بشار بندار ومحمد بن المثنى كليهما عن محمد بن جعفر غندر.','1094','275','0');
INSERT INTO [t5740] VALUES (555,'- كان يصلي وهو حامل أمامة بنت زينب ابنة رسول الله -','1097','275','0');
INSERT INTO [t5740] VALUES (556,'-، كان يصلي وهو حامل أمامة بنت زينب بنت رسول الله -','1099','275','0');
INSERT INTO [t5740] VALUES (557,'- كان يصلي وهو حامل أمامة بنت أبي العاص، بنت زينب، فإذا ركع وضعها، وإذا قام رفعها ". هذا حديث صحيح متفق على صحته، رواه البخاري (في " الصلاة ") من " صحيحه " عن عبد الله بن يوسف الدمشقي. ورواه مسلم فيه من " صحيحه "، عن قتيبة بن سعيد،','1100','275','0');
INSERT INTO [t5740] VALUES (558,'-: " العجماء جرحها جبار، والنار','1102','275','0');
INSERT INTO [t5740] VALUES (559,'- في جنازة فلما انصرف، قال: ههنا أحد من آل فلان؟ ، فلم يقم أحد - قالها ثلاث مرات -، فقام رجل، فقال: أنا يا رسول الله، فقال له النبي -','1106','275','0');
INSERT INTO [t5740] VALUES (560,'- نظر إلى أبي بكر وعمر - رضي الله عنهما - فقال: " هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين والمرسلين - ولا تخبرهما يا علي - ".','1108','275','0');
INSERT INTO [t5740] VALUES (561,'- نظر إلى أبي بكر وعمر - رضي الله عنهما - فقال: " هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين والمرسلين، ولا تخبرهما يا علي ".','1109','275','0');
INSERT INTO [t5740] VALUES (562,'- فقال: " هذان سيدا كهول أهل الجنة من الأولين والآخرين، إلا النبيين والمرسلين لا تخبرهما "، قال: فما ذكرت ذلك لهما حتى ماتا ". رواه النسائي في " المناقب " من " سننه " عن يعقوب بن إبراهيم الدورقي، عن سفيان (قال) ذكره داود عن الشعبي.','1110','275','0');
INSERT INTO [t5740] VALUES (563,'(الشيخ الثامن والعشرون أبو بكر عبد الله بن عمر بن علي بن الخضر بن عبد الله بن علي ... القرشي، الدمشقي ثم البغدادي. (٥٥٨ هـ - ٦١٦ هـ))','1113','275','0');
INSERT INTO [t5740] VALUES (564,'- قال: " لا يزال هذا الأمر في قريش ما بقي في الناس','1116','275','0');
INSERT INTO [t5740] VALUES (565,'-: " لا يزال هذا الأمر في قريش ما بقي من الناس اثنان ". هذا حديث صحيح متفق على صحته، رواه البخاري في " مناقب قريش " من " صحيحه " عن أبي الوليد كما رويناه في الرواية الأولى.','1117','275','0');
INSERT INTO [t5740] VALUES (566,'- خط خطوطا، وخط فيها خطا بعيدا، وقال: " أتدرون ما هذا؟ هذا مثل ابن آدم، ومثل التمني وذلك / الخط الأمل فبينا هو يتأمل إذ جاءه الموت ". رواه البخاري في " الرقاق " من " صحيحه " عن مسلم بن إبراهيم.','1118','275','0');
INSERT INTO [t5740] VALUES (567,'-: " الأيدي ثلاثة، فيد الله العليا، ويد المعطي التي تليها، ويد السائل السفلى فأعط الفضل ولا تعجز عن نفسك ". رواه أبو داود في " الزكاة " من " سننه " عن الإمام أحمد، فوافقناه بعلو في هذه الرواية.','1121','275','0');
INSERT INTO [t5740] VALUES (568,'- أريد الإسلام فأمرني','1122','275','0');
INSERT INTO [t5740] VALUES (569,'فأمره النبي','1123','275','0');
INSERT INTO [t5740] VALUES (570,'(الشيخ التاسع والعشرون الشيخ الرحال أبو عبد الله محمد بن عمر بن عبد الغالب ابن نصر بن عبد الله بن محمد ... القرشي العثماني الدمشقي البتلهي (٥٦٩ هـ - ٦١٨ هـ))','1127','275','0');
INSERT INTO [t5740] VALUES (571,'- " يقول الله - عز وجل - من جاء','1129','275','0');
INSERT INTO [t5740] VALUES (572,'-: " يقول الله عز وجل: من جاء بالحسنة فله عشر أمثالها أو أزيد، ومن جاء بالسيئة فجزاء سيئة مثلها أو أغفر، ومن تقرب مني شبرا تقربت منه ذراعا، ومن تقرب مني ذراعا تقربت منه باعا ". هذا حديث صحيح رواه مسلم في " الدعوات " من " صحيحه "،','1131','275','0');
INSERT INTO [t5740] VALUES (573,'- في العشر الأوسط من المحرم من سنة ثمان عشرة وستمائة، ودفن بها. آخر الجزء الثامن من تخريج المخرج وتجزئته - رحمه الله - والحمد لله وحده، وصلى الله على سيدنا محمد وآله وصحبه وتابعيهم - وسلم تسليما كثيرا، غفر الله لكاتبها ومالكها وختم لهما','1136','275','0');
INSERT INTO [t5740] VALUES (574,'(الجزء التاسع من مشيخة الإمام العالم فخر الدين، عمدة المسندين ملحق الأحفاد بالأجداد أبي الحسن علي بن أحمد بن عبد الواحد المقدسي الشهير بابن البخاري رحمه الله تعالى)','1137','275','0');
INSERT INTO [t5740] VALUES (575,'تخريج الإمام الحافظ جمال الدين مفيد الطالبين أبي العباس أحمد بن محمد بن عبد الله الظاهري الحلبي الحنفي تغمدهم الله بالرحمة والرضوان آمين','1137','275','0');
INSERT INTO [t5740] VALUES (576,'(الشيخ الثلاثون الفقيه الإمام أبو عبد الله محمد بن خلف بن راجح ابن بلال بن عيسى بن موسى بن الفتح بن زريق المقدسي)','1139','275','0');
INSERT INTO [t5740] VALUES (577,'- يدخل على أم حرام بنت ملحان فتطعمه - وكانت أم حرام تحت عبادة بن','1142','275','0');
INSERT INTO [t5740] VALUES (578,'- عليها يوما، فأطعمته ثم جلست تفلي رأسه، فنام رسول الله -','1143','275','0');
INSERT INTO [t5740] VALUES (579,'وهو يضحك، قالت: فقلت: ما يضحكك يا رسول','1143','275','0');
INSERT INTO [t5740] VALUES (580,'- يعتكف العشر الوسط من شهر رمضان، فاعتكف عاما حتى إذا كانت ليلة إحدى وعشرين وهي الليلة التي يخرج من صبيحتها من اعتكافه فقال: من كان اعتكف معي فليعتكف العشر الأواخر وقد رأيت هذه الليلة، ثم أنسيتها، وقد رأيتني أسجد من صبيحتها في ماء وطين،','1146','275','0');
INSERT INTO [t5740] VALUES (581,'- فانصرف علينا وعلى جبهته وأنفه أثر الماء والطين من صبيحة إحدى وعشرين ". هذا حديث صحيح، رواه البخاري في " الاعتكاف " من " صحيحه " عن إسماعيل بن أبي أويس. ورواه أبو داود في " الصلاة " من " سننة " عن القعنبي. ورواه النسائي في "','1147','275','0');
INSERT INTO [t5740] VALUES (582,'- فعظم ذلك علي / قلت: يا رسول الله، أعتقها؟ قال: ائتني بها، فأتيته بها، قال: من أنا؟ قالت: أنت رسول الله، قال: أعتقها فإنها','1150','275','0');
INSERT INTO [t5740] VALUES (583,'- قال: " يا عبد الله بن قيس: ألا أعلمك كلمة من كنوز الجنة، لا حول ولا قوة إلا بالله ".','1152','275','0');
INSERT INTO [t5740] VALUES (584,'- في سفر، فترقينا عقبة أو ثنية، قال: فكان الرجل منا إذا ما علاها قال: لا إله إلا الله، والله أكبر، فقال رسول الله -','1153','275','0');
INSERT INTO [t5740] VALUES (585,'- في سفر فترقينا عقبة أو ثنية، قال: فكان الرجل منا إذا ما علاها قال: لا إله إلا الله، والله أكبر، قال: فقال رسول الله -','1153','275','0');
INSERT INTO [t5740] VALUES (586,'- في سفر فرفع الناس أصواتهم بالدعاء والتهليل والتكبير، فقال النبي -','1154','275','0');
INSERT INTO [t5740] VALUES (587,'- فقال: ألا أدلك على كنز من كنوز الجنة، لا حول ولا قوة إلا بالله ".','1154','275','0');
INSERT INTO [t5740] VALUES (588,'- وأصحابه يصعدون في ثنية أو قال: عقبة، ورسول الله -','1155','275','0');
INSERT INTO [t5740] VALUES (589,'- فقال: " والله لا يدخل قلب رجل الإيمان حتى يحبكم لله عز وجل ولقرابتكم مني ". رواه ابن ماجه في " السنة " من " سننه " عن محمد بن','1161','275','0');
INSERT INTO [t5740] VALUES (590,'(الشيخ الحادي والثلاثون أبو محمد هبة الله بن أبي طالب الخضر بن أبي محمد هبة الله ابن أبي البركات أحمد بن عبد الله بن علي بن طاووس بن موسى ابن العباس بن طاووس البغدادي الأصل الدمشقي المولد والدار (٥٣٧ هـ - ٦١٨ هـ))','1167','275','0');
INSERT INTO [t5740] VALUES (591,'- قال: " ما يصيب المسلم من نصب، ولا وصب ولا خوف ولا أذى ولا غم، حتى الشوكة يشاكها إلا كفر الله بها من خطاياه ".','1170','275','0');
INSERT INTO [t5740] VALUES (592,'- قال: " ما يصيب المرء المسلم من نصب ولا وصب ولا هم ولا حزن ولا غم ولا أذى حتى الشوكة يشاكها إلا كفر الله عنه بها من خطاياه ". أخرجه البخاري عن عبد الله بن محمد، عن أبي عامر كما','1171','275','0');
INSERT INTO [t5740] VALUES (593,'-: " الحسد يأكل الحسنات، كما تأكل النار الحطب، والصدقة تطفئ الخطيئة كما يطفئ الماء النار، والصلاة نور المؤمن، والصيام جنة من النار ".','1174','275','0');
INSERT INTO [t5740] VALUES (594,'-: " الحسد يأكل الحسنات كما تأكل النار الحطب، والصدقة تطفئ الخطيئة كما يطفئ الماء النار، والصلاة نور المؤمن، والصيام جنة من النار ". رواه ابن ماجه في " الزهد " من " سننه " عن هارون بن عبد الله','1175','275','0');
INSERT INTO [t5740] VALUES (595,'-: " إذا لعن آخر هذه الأمة أولها، فمن كان عنده علم فليظهره، فإن كاتم العلم يومئذ ككاتم ما أنزل على محمد','1177','275','0');
INSERT INTO [t5740] VALUES (596,'-: " سيلعن آخر هذه الأمة أولها، فمن كان عنده علم فليظهره، فإن كاتم العلم - يومئذ - ككاتم ما أنزل الله على محمد رسول الله -','1177','275','0');
INSERT INTO [t5740] VALUES (597,'(الشيخ الثاني والثلاثون أبو الحسن علي بن نابت بن طالب البغدادي الأزجي الفقيه الحنبلي (- ٦١٨ هـ))','1181','275','0');
INSERT INTO [t5740] VALUES (598,'- قال: " إذا سجد العبد سجد على سبعة آراب، وجهه وكفيه وركبتيه وقدميه ". كذا وقع في أصل سماعنا: الحارث بن سعد وهو خطأ والصواب عامر بن سعد بن أبي وقاص.','1185','275','0');
INSERT INTO [t5740] VALUES (599,'- يقول: " إذا سجد العبد سجد معه سبعة آراب: وجهه وكفاه وركبتاه وقدماه ".','1185','275','0');
INSERT INTO [t5740] VALUES (600,'- ذات يوم العصر، فقام في الركعتين، ولم يجلس حتى قضى صلاته ثم سجد سجدتين وهو جالس ".','1188','275','0');
INSERT INTO [t5740] VALUES (601,'- قام في صلاة الظهر وعليه جلوس، فلما أتم صلاته سجد سجدتين يكبر في كل سجدة وهو جالس قبل أن يسلم وسجدهما الناس معه مكان ما نسي من الجلوس ".','1189','275','0');
INSERT INTO [t5740] VALUES (602,'- ركعتين من بعض الصلوات ثم قام فلم يجلس فقام الناس معه، فلما قضى صلاته، ونظرنا تسليمه، كبر وسجد سجدتين وهو جالس قبل التسليم ".','1189','275','0');
INSERT INTO [t5740] VALUES (603,'- فصلى ركعتين، وبين يديه عنزة تمر المرأة والحمار من ورائها ".','1192','275','0');
INSERT INTO [t5740] VALUES (604,'- " أنه صلى وبين يديه عنزة تمر المرأة والحمار من ورائها ".','1193','275','0');
INSERT INTO [t5740] VALUES (605,'- بالأبطح قال: فجاءه بلال فآذنه بالصلاة قال: فدعا بماء فتوضأ فجعل الناس يأتون وصلى ركعتين، والظعن يمرون بين يديه والمرأة والحمار والبعير ". هذا حديث صحيح، رواه البخاري في " الصلاة "، من (جامعه)','1194','275','0');
INSERT INTO [t5740] VALUES (606,'- " نهى عن الحبوة يوم الجمعة والإمام يخطب ".','1197','275','0');
INSERT INTO [t5740] VALUES (607,'-: " من أحسن في الإسلام لم يؤاخذ بما عمل في الجاهلية والإسلام، ومن أساء في الإسلام أخذ بما عمل في الجاهلية والإسلام ".','1201','275','0');
INSERT INTO [t5740] VALUES (608,'-: " يبعث كل عبد على ما مات عليه ". هذا حديث صحيح رواه مسلم في " صحيحه " عن قتيبة بن سعيد وعثمان بن أبي شيبة كلاهما عن جرير، وعن أبي بكر بن نافع، عن عبد الرحمن بن مهدي، عن سفيان الثوري كلاهما عن الأعمش.','1204','275','0');
INSERT INTO [t5740] VALUES (609,'- عن النذر، وقال إنه لا يرد شيئا، ولكن يستخرج به من','1204','275','0');
INSERT INTO [t5740] VALUES (610,'- " نهى عن النذر وقال إنه لا يأتي بخير إنما يستخرج [به] من البخيل ". رواه البخاري في " الإيمان والنذور " من " جامعه " عن خلاد بن يحيى، على الموافقة النازلة. ورواه مسلم في " النذور " من " صحيحه " عن أبي بكر بن أبي','1205','275','0');
INSERT INTO [t5740] VALUES (611,'(الشيخ الثالث والثلاثون أبو عبد الله محمد بن إبراهيم بن سعد المقدسي ( ... - ٦١٨ هـ))','1209','275','0');
INSERT INTO [t5740] VALUES (612,'- أحدهما، وعن يساره أحدهما يقاتلان أشد القتال، ثم ثلثهما ثالث من خلفه، ثم ربعهما رابع أما [مه] ".','1213','275','0');
INSERT INTO [t5740] VALUES (613,'(الشيخ الرابع والثلاثون أبو عبد الله الحسين يسمى محمدا أيضا - ابن أبي الفخر يحيى بن الحسين بن عبد الرحمن بن إسماعيل بن داود ابن أبي الرداد البصري الأصل المولد والدار الشافعي الكاتب. (٥٤٠ هـ - ٦٢٠ هـ))','1215','275','0');
INSERT INTO [t5740] VALUES (614,'- أنا والزبير والمقداد، قال: انطلقوا حتى تأتوا روضة خاخ، فإن بها ظعينة معها كتاب فخذوه منها، فانطلقنا تعادي بنا خيلنا حتى انتهينا إلى الروضة / فإذا نحن بالظعينة، قلنا: أخرجي الكتاب، قالت: ما معي كتاب، قلنا: لتخرجن الكتاب أو لنقلعن','1218','275','0');
INSERT INTO [t5740] VALUES (615,'فقال النبي','1219','275','0');
INSERT INTO [t5740] VALUES (616,'-: " إنه قد صدقكم "، قال عمر - رضي الله عنه -: يا رسول الله دعني أضرب عنق هذا المنافق، قال: إنه قد شهد بدرا، وما يدريك لعل الله اطلع على أهل بدر، فقال: اعملوا ما شئتم فقد غفرت لكم ". هذا حديث صحيح متفق على صحته، رواه البخاري في "','1219','275','0');
INSERT INTO [t5740] VALUES (617,'- يقول: " إنما أنا بشر، وإنكم تختصمون إلي، ولعل بعضكم أن يكون ألحن بحجته من بعض، فأقضي له على ماأسمع منه، فمن قضيت له من حق أخيه شيئا فلا يأخذه، فإنما أقطع له','1222','275','0');
INSERT INTO [t5740] VALUES (618,'- يقول: " إنما أنا بشر، وإنكم تختصمون إلي ولعل بعضكم يكون هو ألحن بحجته من بعض، فأقضي له على نحو ما أسمع منه فمن قضيت له بشيء من حق أخيه، فلا يأخذن منه شيئا، فإنما أقطع له قطعة من النار ".','1223','275','0');
INSERT INTO [t5740] VALUES (619,'-، من " صحيحه " من طرق','1224','275','0');
INSERT INTO [t5740] VALUES (620,'- قال: " إذا رأيتم الرجل يعتاد المسجد فاشهدوا','1226','275','0');
INSERT INTO [t5740] VALUES (621,'عليكم','1229','275','0');
INSERT INTO [t5740] VALUES (622,'- قال: " إن الله عز وجل أوحى إلي أن تواضعوا ولا','1231','275','0');
INSERT INTO [t5740] VALUES (623,'(الشيخ الخامس والثلاثون الإمام العلامة شيخ الإسلام أبو محمد عبد الله بن أبي العباس أحمد بن محمد بن قدامة بن مقدام بن نصر المقدسي. (٥٤١ هـ - ٦٢٠ هـ))','1237','275','0');
INSERT INTO [t5740] VALUES (624,'-: " نهى عن متعة النساء يوم خيبر، وعن أكل لحوم الحمر الإنسية ".','1240','275','0');
INSERT INTO [t5740] VALUES (625,'-: نهى عن نكاح المتعة، وعن لحوم الحمر الأهلية زمن خيبر ".','1241','275','0');
INSERT INTO [t5740] VALUES (626,'نهى عن','1241','275','0');
INSERT INTO [t5740] VALUES (627,'- نهى عن نكاح المتعة وعن','1242','275','0');
INSERT INTO [t5740] VALUES (628,'- نهى يوم خيبر عن لحوم الحمر الأهلية ".','1246','275','0');
INSERT INTO [t5740] VALUES (629,'- مر بقدور تغلي من لحوم الحمر الأهلية، فقال: " اكفئوها " يوم خيبر ".','1247','275','0');
INSERT INTO [t5740] VALUES (630,'- لحوم الخيل ونهانا عن لحوم الحمر ".','1249','275','0');
INSERT INTO [t5740] VALUES (631,'- يوم خيبر، فأصابتنا مجاعة، وأصابوا حمرا أهلية، فذبحوها، فغلت القدور ببعضها، فنادى مناد النبي -','1251','275','0');
INSERT INTO [t5740] VALUES (632,'- عن أكل لحوم الحمر الأهلية ".','1251','275','0');
INSERT INTO [t5740] VALUES (633,'- كأنني سمعته من أبي إسحاق الهروي شيخ شيخ أبي عبد الرحمن النسائي، ومن يسمعه مني يكون مساويا له، ووقع لنا عاليا جدا بحمد الله تعالى.','1252','275','0');
INSERT INTO [t5740] VALUES (634,'- كان يقبل وهو صائم وكان أملككم لإربه ".','1253','275','0');
INSERT INTO [t5740] VALUES (635,'- يقبلني وهو صائم وأيكم أملك لإربه من رسول الله ".','1253','275','0');
INSERT INTO [t5740] VALUES (636,'- كان يقبل أو يقبلني وهو صائم وأيكم كان أملك لإربه من رسول الله ".','1254','275','0');
INSERT INTO [t5740] VALUES (637,'- يقبل وهو صائم ".','1255','275','0');
INSERT INTO [t5740] VALUES (638,'- كان يقبلها وهو صائم. فسكت عني هنيهة ثم قال: نعم ".','1255','275','0');
INSERT INTO [t5740] VALUES (639,'- يقبلني وهو صائم ".','1256','275','0');
INSERT INTO [t5740] VALUES (640,'- يقبل وهو صائم ".','1256','275','0');
INSERT INTO [t5740] VALUES (641,'- ليقبل بعض أزواجه وهو صائم " ثم تضحك. وقال عروة: لم أر القبلة تدعو إلى خير.','1257','275','0');
INSERT INTO [t5740] VALUES (642,'- يقبل وهو صائم ثم ضحكت ".','1257','275','0');
INSERT INTO [t5740] VALUES (643,'-[كان يقبل وهو صائم] ".','1258','275','0');
INSERT INTO [t5740] VALUES (644,'- يقبلني وأنا صائمة ". كذا وقع في هذه الرواية. هذا حديث صحيح من أحاديث أم المؤمنين عائشة بنت أبي بكر الصديق - رضي الله عنها - عن النبي -','1259','275','0');
INSERT INTO [t5740] VALUES (645,'- قال لعائشة - رضي الله عنه -: " طوافك بالبيت وبين الصفا والمروة يكفيك لحجك وعمرتك ".','1263','275','0');
INSERT INTO [t5740] VALUES (646,'بمثله','1263','275','0');
INSERT INTO [t5740] VALUES (647,'- قال لعائشة - رضي الله عنها -. رواه أبو داود في " الحج " من " سننه " عن أبي محمد الربيع بن','1264','275','0');
INSERT INTO [t5740] VALUES (648,'-: " قضى باليمين مع الشاهد ". قال عبد العزيز: فذكرت ذلك لسهيل، فقال: أخبرني ربيعة - وهو عندي ثقة - أني حدثته إياه ولا أحفظه. قال عبد العزيز: قد كان أصاب سهيلا','1265','275','0');
INSERT INTO [t5740] VALUES (649,'- إذا أراد سفرا أقرع بين نسائه فأيتهن خرج سهمها خرج بها ".','1267','275','0');
INSERT INTO [t5740] VALUES (650,'- كان إذا أراد سفرا أقرع بين أزواجه فأيتهن خرج سهمها خرج بها معه ". رواه النسائي في " عشرة النساء " من " سننه " عن الربيع بن','1268','275','0');
INSERT INTO [t5740] VALUES (651,'- بايعناه على السمع والطاعة، يقول لنا: فيما استطعت ". هذا حديث صحيح، رواه البخاري في " الأحكام " من " جامعه " عن عبد الله بن يوسف، عن مالك - رحمه الله -، فوقع لنا بدلا له. ورواه النسائي في " البيعة " من " سننه " عن الحسن بن محمد','1270','275','0');
INSERT INTO [t5740] VALUES (652,'- مر على رجل وهو يعظ أخاه في الحياء، فقال رسول الله -','1271','275','0');
INSERT INTO [t5740] VALUES (653,'- لطعام صنعه، قال أنس: فذهبت مع رسول الله -','1272','275','0');
INSERT INTO [t5740] VALUES (654,'- يتبع الدباء من حروف الصحفة، قال: فلم أزل أحب الدباء من ذلك اليوم ".','1272','275','0');
INSERT INTO [t5740] VALUES (655,'-: " الكوثر نهر في الجنة حافتاه الذهب، مجراه على الدر والياقوت، تربته أطيب من المسك و [ماؤه] أشد بياضا من الثلج ". رواه ابن ماجه في " الزهد " من " سننه " عن أبي سعيد الأشج، فوافقناه بعلو.','1275','275','0');
INSERT INTO [t5740] VALUES (656,'يتوضأ فقال عبد الله بن زيد: نعم، فدعا بوضوء فأفرغ على يديه، فغسل يديه مرتين وتمضمض واستنشق ثلاثا، ثم غسل وجهه ثلاثا، ثم غسل يديه مرتين، مرتين إلى المرافق، ثم مسح رأسه بيديه فأقبل بهما وأدبر، بدأ بمقدم رأسه، ثم ذهب بهما','1276','275','0');
INSERT INTO [t5740] VALUES (657,'(الجزء العاشر من مشيخة الإمام الرحلة فخر الدين، بقية المسندين أبي الحسن علي بن أحمد بن عبد الواحد المقدسي المعروف بابن البخاري - رحمه الله تعالى -)','1281','275','0');
INSERT INTO [t5740] VALUES (658,'(تخريج الإمام الحافظ جمال الدين أوحد المخرجين أبي العباس أحمد بن محمد بن عبد الله الظاهري، الحنفي الحلبي لابن البخاري)','1281','275','0');
INSERT INTO [t5740] VALUES (659,'(الشيخ السادس والثلاثون أبو البركات عبد القوي بن أبي المعالي عبد العزيز بن الحسين بن عبد الله بن الجباب التميمي السعدي الأغلبي المصري المالكي العدل (٥٣٦ هـ - ٦٢١ هـ))','1283','275','0');
INSERT INTO [t5740] VALUES (660,'- أول ما افترضت ركعتين، ركعتين كل صلاة، ثم إن الله أتمها في الحضر أربعا، وأقرها في السفر على فرضها الأول ركعتين ".','1286','275','0');
INSERT INTO [t5740] VALUES (661,'- قال: " إذا فتحتم مصر فاستوصوا بأهلها خيرا، فإن / لهم ذمة ورحما ". فقلت لمحمد بن مسلم: الرحم التي ذكر رسول الله -','1289','275','0');
INSERT INTO [t5740] VALUES (662,'- قال: " إذا افتتحتم مصر فاستوصوا بالقبط فإنهم لهم الذمة، وإن لهم رحما ". فسألت ابن شهاب الزهري عن رحمهم، فقال: " أم إسماعيل بن إبراهيم خليل الرحمن كانت منهم ".','1291','275','0');
INSERT INTO [t5740] VALUES (663,'(الشيخ السابع والثلاثون أبو المجد محمد بن أبي عبد الله الحسين بن أبي المكارم أحمد بن أبي عبد الله الحسين بن بهرام القزويني الشافعي الصوفي (٥٥٤ هـ - ٦٢٢ هـ))','1293','275','0');
INSERT INTO [t5740] VALUES (664,'- بين التكبير والقراءة إسكاتة، قال: حسبته قال: هنيهة، قلت: - بأبي وأمي - يا رسول','1296','275','0');
INSERT INTO [t5740] VALUES (665,'- إذا كبر في الصلاة سكت بين التكبير والقراءة، فقلت - بأبي أنت وأمي - أرأيت سكتاتك بين التكبير والقراءة، أخبرني ما هن؟ ، فقال: أقول: اللهم باعد بيني وبين خطاياي / كما باعدت بين المشرق والمغرب، اللهم أنقني من خطاياي كالثوب الأبيض من الدنس، -','1297','275','0');
INSERT INTO [t5740] VALUES (666,'(الشيخ الثامن والثلاثون أبو حفص عمر بن بدر بن سعيد الموصلي ( ... - ٦٢٢ هـ))','1301','275','0');
INSERT INTO [t5740] VALUES (667,'- قال: " ينزل الله عز وجل إلى','1303','275','0');
INSERT INTO [t5740] VALUES (668,'- قال: " ينزل الله إلى السماء الدنيا حين يمضي ثلث الليل الأول، فيقول: أنا الملك، أنا الملك، من ذا الذي يدعوني فأستجيب له، من ذا الذي يسألني فأعطيه، من ذا الذي يستغفرني فأغفر له ". رواه مسلم والترمذي في " الصلاة " من كتابيهما، عن','1308','275','0');
INSERT INTO [t5740] VALUES (669,'(الشيخ التاسع والثلاثون الإمام أبو محمد عبد الرحمن بن إبراهيم بن أحمد بن عبد الرحمن بن إسماعيل بن منصور السعدي المقدسي الفقيه الحنبلي (٥٥٥ هـ - ٦٢٤ هـ))','1311','275','0');
INSERT INTO [t5740] VALUES (670,'- قال: " من توضأ فليستنثر ومن استجمر فليوتر ".','1313','275','0');
INSERT INTO [t5740] VALUES (671,'- قال: " من توضأ فليستنثر ومن استجمر فليوتر ".','1313','275','0');
INSERT INTO [t5740] VALUES (672,'- قال: " من توضأ فليستنثر، ومن استجمر فليوتر ".','1314','275','0');
INSERT INTO [t5740] VALUES (673,'- قال: " إذا توضأت فاستنثر، وإذا استجمرت فأوتر ". قال أبو القاسم - يعني البغوي -: هكذا نا كامل بهذا الحديث عن أبي ثعلبة، وغلط فيه وإنما هو عن أبي هريرة. هذا حديث صحيح، رواه البخاري في " الطهارة " من " صحيحه " عن عبدان، عن عبد','1315','275','0');
INSERT INTO [t5740] VALUES (674,'- على المنبر، فذكر ذلك، وتلا القرآن، فلما نزل أمر برجلين وامرأة فضربوا الحد ". رواه أبو داود في " الحدود " والنسائي في " الرجم " من " سننهما " كلاهما عن قتيبة بن سعيد - وزاد أبو داود ومالك بن عبد الواحد المسمعي -. ورواه الترمذي في "','1318','275','0');
INSERT INTO [t5740] VALUES (675,'- إذ طلع أبو بكر وعمر - رضي الله عنهما -، فلما نظر إليهما، قال: " هذان السمع والبصر ". رواه الترمذي في " المناقب " من " جامعه "، عن قتيبة بن سعيد،','1321','275','0');
INSERT INTO [t5740] VALUES (676,'- يقول: " المسلم أخو المسلم، فلا يحل لمسلم باع من أخيه بيعا، فعلم فيه عيبا إلا بينه له ". رواه ابن ماجه في " التجارات " من " سننه "، عن محمد بن بشار بندار، عن وهب بن جرير، كما أخرجناه فوقع لنا بدلا له.','1323','275','0');
INSERT INTO [t5740] VALUES (677,'(الشيخ الأربعون الشيخ أبو القاسم الحسين بن أبي الغنائم هبة الله ابن محفوظ بن الحسن بن محمد بن أحمد بن الحسين ابن صصرى التغلبي الدمشقي (٥٤٠ هـ - ٦٢٦ هـ))','1327','275','0');
INSERT INTO [t5740] VALUES (678,'- فقال: " صدقة تصدق الله بها عليكم ".','1330','275','0');
INSERT INTO [t5740] VALUES (679,'- فقال: " صدقة تصدق الله بها عليكم فاقبلوا صدقته ".','1331','275','0');
INSERT INTO [t5740] VALUES (680,'-، قال: " صدقة تصدق الله بها عليكم فاقبلوا صدقته ". هذا حديث صحيح أخرجه مسلم في " الصلاة " من " صحيحه " عن أبي بكر عبد الله بن محمد بن أبي شيبة، وأبي كريب محمد بن العلاء، وأبي خيثمة زهير بن حرب وإسحاق بن إبراهيم. وأخرجه النسائي فيه','1332','275','0');
INSERT INTO [t5740] VALUES (681,'- نهل بالحج، قدم لأربع من ذي الحجة، فصلى رسول الله -','1335','275','0');
INSERT INTO [t5740] VALUES (682,'- أنه قال: " الحج المبرور ليس له جزاء إلا الجنة، والعمرة إلى العمرة','1336','275','0');
INSERT INTO [t5740] VALUES (683,'- عن صلاة الليل، فقال: " مثنى مثنى، فإذا خشيت الصبح فأوتر بركعة ". رواه مسلم في " الصلاة " من " صحيحه "، عن محمد بن','1338','275','0');
INSERT INTO [t5740] VALUES (684,'- ونحن معه بمنى حتى ذهبت فرقة من خلف','1339','275','0');
INSERT INTO [t5740] VALUES (685,'-: " اشهدوا ". هذا حديث صحيح، رواه البخاري في " انشقاق القمر " من " جامعه "، عن عمر بن حفص بن غياث عن أبيه. رواه مسلم في " التوبة " من " صحيحه " عن أبي بكر بن أبي شيبة، وأبي كريب محمد بن العلاء، إسحاق بن راهويه، ثلاثتهم عن أبي','1340','275','0');
INSERT INTO [t5740] VALUES (686,'- يحكي نبيا ضربه قومه وهو يمسح الدم عن وجهه وهو يقول: " رب اغفر لقومي، فإنهم لا يعلمون ". رواه البخاري في " ذكر بني إسرائيل " و " استتابة المرتدين " من " جامعه "، عن عمر بن حفص بن غياث، عن أبيه. ورواه مسلم في " المغازي "، عن محمد','1341','275','0');
INSERT INTO [t5740] VALUES (687,'-: " يا معشر الشباب من استطاع منكم الباءة [فليتزوج] فإنه أغض للبصر وأحصن للفرج، ومن لم','1342','275','0');
INSERT INTO [t5740] VALUES (688,'-: " إن بالمدينة نفرا من الجن قد أسلموا، فإذا رأيتم من','1344','275','0');
INSERT INTO [t5740] VALUES (689,'- قال: " لا ينبغي للقاضي أن يقضي بين اثنين وهو غضبان ". رواه ابن ماجه " الأحكام " من " سننه "، عن هشام بن عمار، عن سفيان، فوقع لنا بدلا له.','1346','275','0');
INSERT INTO [t5740] VALUES (690,'(الشيخ الحادي والأربعون أبو الفضل عبد السلام بن أبي محمد عبد الله بن أحمد ابن بكران الداهري، البغدادي الخفاف (٥٤٦ هـ تقريبا - ٦٢٧ هـ))','1349','275','0');
INSERT INTO [t5740] VALUES (691,'- أعينهم، لأنهم سملوا أعين الرعاء ". هذا حديث صحيح، رواه مسلم في " الحدود " من " صحيحه " والترمذي في " الطهارة " من " جامعه "، والنسائي في','1351','275','0');
INSERT INTO [t5740] VALUES (692,'-: " من طاف بالبيت خمسين مرة، خرج من ذنوبه كيوم ولدته أمه ". رواه الترمذي في " الحج " من " جامعه "، عن سفيان بن وكيع هذا، فوافقناه بعلو.','1353','275','0');
INSERT INTO [t5740] VALUES (693,'- يقول: " من طلب العلم ليجاري به العلماء أو يماري به السفهاء، أو يصرف به وجوه الناس إليه أدخله الله النار ".','1354','275','0');
INSERT INTO [t5740] VALUES (694,'- قال: " من أحرم بالحج والعمرة أجزأه طواف واحد، ولا يحل لواحد منهما حتى يحل منهما جميعا ". رواه الترمذي في " الحج " من " جامعه " عن خلاد بن','1356','275','0');
INSERT INTO [t5740] VALUES (695,'-: " الوقت الأول من الصلاة رضوان الله، والوقت الآخرعفو الله ".','1357','275','0');
INSERT INTO [t5740] VALUES (696,'-، أين صلى الظهر يوم التروية؟ قال منى، قلت فأين صلى العصر يوم النفر؟ قال: بالأبطح، ثم قال: افعل كما يفعل أمراؤكم ". رواه الترمذي في " الحج " من " جامعه "، عن أحمد بن منيع، ومحمد بن الوزير الواسطي، كما رويناه، فوافقنا بعلو.','1359','275','0');
INSERT INTO [t5740] VALUES (697,'- فأخبره، فقال له النبي -','1360','275','0');
INSERT INTO [t5740] VALUES (698,'- وهو واقف بالمزدلفة، فقال: " من صلى معنا ههنا، ثم أفاض معنا، ووقف قبل ذلك بعرفة ليلا أو نهارا، فقد تم حجه ".','1362','275','0');
INSERT INTO [t5740] VALUES (699,'- وهو بجمع، فقلت، يا رسول الله إني أقبلت من جبلي طيء، لم أدع حبلا إلا وقفت عليه فهل لي من حج؟ ، فقال رسول الله -','1363','275','0');
INSERT INTO [t5740] VALUES (700,'-: " لا تنتهي البعوث عن غزو بيت الله، أو يخسف بجيش منهم ". رواه النسائي في " الحج " من " سننه "، عن أبي حاتم محمد بن','1366','275','0');
INSERT INTO [t5740] VALUES (701,'-: " لا تقولوا هكذا، فإن الله هو السلام، ولكن قولوا: التحيات لله والصلوات والطيبات، السلام عليك أيها النبي ورحمة الله وبركاته، السلام علينا وعلى عباد','1367','275','0');
INSERT INTO [t5740] VALUES (702,'-، فندب الناس فانتدبوا','1368','275','0');
INSERT INTO [t5740] VALUES (703,'- وأصحابه موعدكم موسم بدر حيث قتلتم أصحابنا، فأما الجبان فرجع، وأما الشجاع فأخذ هبة القتال والتجارة، فأتوه فلم يجدوا به أحدا فتسربوا فأنزل الله عز وجل: {فانقلبوا بنعمة من الله وفضل لم يمسسهم سوء} وبه قال يحيى بن صاعد: " ولا أعلم','1369','275','0');
INSERT INTO [t5740] VALUES (704,'-: " العجماء جرحها جبار والبئر جبار والمعدن جبار، وفي الركاز الخمس ". قال سفيان: فلما خرجنا من عند الزهري مررت بإسماعيل بن أمية وإسماعيل بن مسلم وأناس يقولون: ثنا عن سعيد، وآخرون يقولون عن أبي سلمة، فلما رأوني قالوا: " سلوا الصغير','1370','275','0');
INSERT INTO [t5740] VALUES (705,'الجزء ٣','1373','0','0');
INSERT INTO [t5740] VALUES (706,'- صلى بين العمودين تلقاء وجهه في جوف الكعبة ". رواه مسلم في " الصلاة " من " صحيحه " عن أبي الربيع','1373','705','0');
INSERT INTO [t5740] VALUES (707,'كان يخرج على أصحابه من المهاجرين والأنصار، وفيهم أبو بكر وعمر - رضي الله عنهم - فلا يرفع أحد منهم بصره إليه إلا أبو بكر وعمر، فإنهما كانا ينظران إليه وينظر إليهما ويتبسمان إليه ويتبسم إليهما ". رواه الترمذي في " المناقب " من " جامعه " عن','1375','705','0');
INSERT INTO [t5740] VALUES (708,'- قال: " إن الله ليملي للظالم فإذا أخذه لم يفلته، ثم تلا {وكذلك أخذ ربك إذا أخذ القرى وهي ظالمة} .','1376','705','0');
INSERT INTO [t5740] VALUES (709,'-: " إن الله ليملي للظالم، فإذا أخذه / لم يفلته، ثم قرأ: {وكذلك أخذ ربك إذا أخذ القرى وهي ظالمة إن أخذه أليم شديد}','1377','705','0');
INSERT INTO [t5740] VALUES (710,'- قال: " إن الله ليملي للظالم فإذا أخذه لم يفلته، ثم تلا {وكذلك أخذ ربك إذا أخذ القرى وهي ظالمة} قال ابن صاعد: " كان هذا الحديث عند إبراهيم، عن أبي أسامة، وعند غيره عن أبي معاوية: لم يروه عن أبي أسامة غير إبراهيم بن سعيد ".','1378','705','0');
INSERT INTO [t5740] VALUES (711,'- إلى الخلاء وكان إذا أراد حاجة أبعد ". رواه النسائي في " الطهارة " من " سننه "، عن عمرو بن علي','1380','705','0');
INSERT INTO [t5740] VALUES (712,'-، فقلت: أرسل كلبي فأجد مع كلبي كلبا لا أدري أيهما أخذه، قال: لا تأكله، فإنما سميت على كلبك ولم تسم على غيره ".','1381','705','0');
INSERT INTO [t5740] VALUES (713,'- عن المعراض، فقال: إذا أصاب بحده فقتل فكل، وإذا أصاب بعرضه فقيل فهو وقيذ فلا تأكل. قلت: أرسل / كلبي، قال إذا أرسلت كلبك على الصيد وسميت فأخذ وكل، وإذا أكل منه فلا تأكل، فإنما أمسك على نفسه، قلت: أرسل كلبي فأجد مع كلبي كلبا آخر لا','1382','705','0');
INSERT INTO [t5740] VALUES (714,'- قال: " اللهم اغفر للأنصار ولأبنائهم ولأبناء أبنائهم ".','1383','705','0');
INSERT INTO [t5740] VALUES (715,'- وفد عبد القيس حين قدموا عليه: عن الدباء، وعن النقير، وعن المزفت، والمزادة','1384','705','0');
INSERT INTO [t5740] VALUES (716,'-: " مثل المؤمن الذي يقرأ القرآن كمثل الأترنجة طعمها طيب وريحها طيب، ومثل المؤمن الذي لا يقرأ القرآن كمثل التمرة طعمها طيب ولا ريح لها، ومثل الفاجر الذي يقرأ القرآن كمثل الريحانة ريحها طيب ولا طعم لها،','1386','705','0');
INSERT INTO [t5740] VALUES (717,'-: " خيرت بين أن يدخل نصف أمتي الجنة وبين الشفاعة، فاخترت الشفاعة، لأنها أعم وأكفى، أترونها للمتقين المنقين؟ ، لا ولكنها للمذنبين الخطائين المتلوثين ".','1389','705','0');
INSERT INTO [t5740] VALUES (718,'" خيرت بين الشفاعة وبين أن يدخل نصف أمتي الجنة، فاخترت الشفاعة لأنها أعم وأكفى، أترونها للمتقين المؤمنين؟ ، لا ولكنها للمذنبين الخطائين الملوثين ". رواه ابن ماجه في " الزهد " من " سننه " عن إسماعيل بن أبي الحارث أسد هذا، فوافقناه','1390','705','0');
INSERT INTO [t5740] VALUES (719,'- في المنام فعرضتها عليه فما عرف منها إلا اليسير خمسة أو ستة أحاديث، فتركت الحديث عنه " [روى مسلم هذه الحكاية في " مقدمة كتابه " عن سويد بن','1392','705','0');
INSERT INTO [t5740] VALUES (720,'- إلا بخير فإنك لا تدري ما سبق لهم من الفضل ".','1398','705','0');
INSERT INTO [t5740] VALUES (721,'-: " ما يزال الرجل يسأل حتى يأتي يوم القيامة ليس في وجهه','1406','705','0');
INSERT INTO [t5740] VALUES (722,'- وعليهم بين الخلق، فيمشي حتى يأخذ بحلقة الجنة - فيومئذ يبعثه الله مقاما محمودا، يحمده أهل الجمع كلهم ".','1407','705','0');
INSERT INTO [t5740] VALUES (723,'-: " ما يزال الرجل يسأل الناس حتى يأتي يوم القيامة ليس في وجهه مزعة لحم. فقال: إن الشمس تدنو حتى يبلغ العرق نصف الأذن فبينما هم كذلك استغاثوا بآدم، فيقول: ليست صاحب ذلك، ثم موسى، فيقول كذلك، ثم بمحمد -','1408','705','0');
INSERT INTO [t5740] VALUES (724,'- وهو متوسد بردة له في ظل الكعبة، فقلنا: ألا تستنصر الله لنا، ألا تدعو الله لنا، فقال: " قد كان من كان قبلكم يؤخذ الرجل فيحفر له في الأرض، فيجعل فيها ويجاء بالمنشار فيوضع على رأسه،','1410','705','0');
INSERT INTO [t5740] VALUES (725,'- وهو مضطجع في برد له في ظل الكعبة فقلنا: ألا تدعو الله لنا، ألا تستنصر الله لنا، فجلس محمر وجهه، ثم قال: والله إن من كان قبلكم ليؤخذ الرجل فيشق باثنتين ما يصرفه عن دينه شيء / أو يمشط بأمشاط الحديد ما بين عصب ولحم، ما يصرفه عن دينه شيء،','1412','705','0');
INSERT INTO [t5740] VALUES (726,'-: " لو كنت متخذا خليلا من أمتي لاتخذت أبا بكر (خليلا) ولكنه أخي وصاحبي ".','1414','705','0');
INSERT INTO [t5740] VALUES (727,'- قال: " لو كنت متخذا خليلا لاتخذت أبا بكر خليلا ". ورواه البخاري في " فضل أبي بكر - رضي الله عنه - من','1414','705','0');
INSERT INTO [t5740] VALUES (728,'-: اللهم إني أعوذ بك من زوال نعمتك، ومن تحول عافيتك، ومن فجاءة نقمتك، ومن جميع سخطك أو غضبك ".','1415','705','0');
INSERT INTO [t5740] VALUES (729,'- كان يدعو: " اللهم إني أعوذ بك من زوال نعمتك، وتحول عافيتك، وفجاءة نقمتك وجميع سخطك ". هذا حديث صحيح انفرد بإخراجه مسلم فرواه في " الدعوات " من " صحيحه " عن أبي زرعة عبيد الله بن عبد الكريم الرازي عن يحيى بن، بكير، فوقع لنا عاليا في','1416','705','0');
INSERT INTO [t5740] VALUES (730,'- فقام رجل فقال: " أنشدك بالله - إلا قضيت بيننا بكتاب الله، فقام خصمه - وكان أفقه منه _، فقال صدق اقض بيننا','1423','705','0');
INSERT INTO [t5740] VALUES (731,'-: والذي نفسي بيده لأقضين بينكما بكتاب الله المائة شاة والخادم مردود عليك، وعلى ابنك جلد مائة وتغريب عام، واغد يا أنيس على امرأة هذا، فإن اعترفت فارجمها، فغدا عليها فاعترفت فرجمها ".','1424','705','0');
INSERT INTO [t5740] VALUES (732,'- فقام رجل فقال: أنشدك بالله إلا قضيت بيننا بكتاب الله، فقام خصمه - وكان أفقه -، فقال: صدق اقض بيننا بكتاب الله وائذن لي فأتكلم، قال: قل، قال: إن ابني كان عسيفا على هذا، وإنه زنى بامرأته فافتديت منه بمائة شاة وخادم. ثم سألت رجالا','1425','705','0');
INSERT INTO [t5740] VALUES (733,'- فقام رجل فقال: نشدتك بالله إلا قضيت بيننا بكتاب الله، فقام خصمه - وكان أفقه منه - فقال: صدق اقض بيننا بكتاب الله، وائذن لي فأتكلم، فقال: إن ابني كان عسيفا على هذا وإنه زنى بامرأته، فأخبروني أن على ابني الرجم فافتديته بمائة شاة وخادم،','1426','705','0');
INSERT INTO [t5740] VALUES (734,'- فقام إليه رجل، فقال: نشدتك الله إلا قضيت بيننا بكتاب الله، وائذن لي، قال: فقال: إن ابني كان عسيفا على هذا، فأخبرت أنه رنى بامرأته، وأخبرت أن عليه الرجم فافتديت بمائة شاة وخادم، ثم سألت رجلا من أهل العلم فأخبرني أن على ابني جلد','1427','705','0');
INSERT INTO [t5740] VALUES (735,'، ورواه المتقدمون من أصحابه فلم يذكروا (شبلا) . والحديث صحيح أخرجه الأئمة في كتبهم من عدة طرق منهم','1428','705','0');
INSERT INTO [t5740] VALUES (736,'- قال: " إن لكل نبي دعوة، فأريد أن أختبئ دعوتي - إن شاء الله - شفاعة لأمتي يوم القيامة ". رواه مسلم في " الإيمان " من " صحيحه "، والنسائي فيما " حمعه','1430','705','0');
INSERT INTO [t5740] VALUES (737,'- قال: " والله إني لأستغفر الله وأتوب إليه في اليوم أكثر من سبعين مرة ". رواه النسائي في " اليوم والليلة " من " سننه " عن يونس بن','1431','705','0');
INSERT INTO [t5740] VALUES (738,'ثماني سنين أن النبي','1432','705','0');
INSERT INTO [t5740] VALUES (739,'- يقول عند انقضاء الطعام: " الحمد لله حمدا كثيرا طيبا مباركا في غير مكفي ولا مودع ولا مستغنى عنه " رواهما النسائي في " الوليمة " من " سننه "، عن يونس بن','1434','705','0');
INSERT INTO [t5740] VALUES (740,'- قال: " نحن أحق بالشك من إبراهيم {رب أرني كيف تحي الموتى قال أولم تؤمن قال بلى ولكن ليطمئن قلبي}','1435','705','0');
INSERT INTO [t5740] VALUES (741,'- قال: " أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فمن قال لا إله إلا الله عصم مني ماله ونفسه إلا بحقها وحسابه على الله عز وجل ". رواه النسائي في " الجهاد " من " سننه "، عن يونس بن عبد الأعلى','1437','705','0');
INSERT INTO [t5740] VALUES (742,'- قال: " أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوا: لا إله إلا الله فقد عصموا مني دماءهم وأموالهم إلا بحقها، وحسابهم على الله عز وجل ". رواه النسائي في " جمعه حديث مالك "، عن يونس بن عبد الأعلى','1438','705','0');
INSERT INTO [t5740] VALUES (743,'- عن صيامهن وأمر بفطرهن فأمره فأفطر، وأحدهما يزيد على صاحبه ". رواه النسائي في (حديث مالك من جمعه) عن الربيع بن سليمان هذا، فوافقناه بعلو.','1440','705','0');
INSERT INTO [t5740] VALUES (744,'-، قال: " لا يزداد الأمر إلا شدة، ولا الدنيا إلا إدبارا، ولا الناس إلا شحا، ولا تقوم الساعة إلا على شرار الناس، ولا مهدي إلا عيسى بن مريم عليه السلام ".','1441','705','0');
INSERT INTO [t5740] VALUES (745,'-: " لا يزداد الأمر إلا شدة ولا الدنيا إلا إدبارا، ولا الناس إلا شحا، ولا تقوم الساعة إلى على شرار الناس، ولا مهدي إلا عيسى بن مريم ". قال ابن عبدان: قال لنا أبو بكر هذا الحديث غريب. اه. رواه ابن ماجه في " الفتن " من " سننه " عن يونس','1442','705','0');
INSERT INTO [t5740] VALUES (746,'-: " وقت لأهل المدينة ذا الحليفة ولأهل الشام الجحفة، ولأهل نجد قرن المنازل، ولأهل اليمن يلملم، وقال:','1450','705','0');
INSERT INTO [t5740] VALUES (747,'-: " فأتى الخلاء ثم إنه رجع فأتي بطعام، فقيل - يا رسول الله -: ألا تتوضأ؟ قال: لم أصل فأتوضأ ".','1452','705','0');
INSERT INTO [t5740] VALUES (748,'- فأتى الغائط، ثم خرج، فدعا بالطعام - وقال مرة: " فأتى بالطعام -، فقيل: يا رسول الله: ألا تتوضأ، قال: لم أصل فأتوضأ ".','1452','705','0');
INSERT INTO [t5740] VALUES (749,'- خرج من الخلاء فأتي بطعام، فذكر له الوضوء، فقال: أريد أن أصلي فأتوضأ ". هذا حديث صحيح رواه مسلم في " الطهارة " من " صحيحه "، عن أبي بكر بن أبي شيبة، ورواه الترمذي في " الشمائل "، عن سعيد بن عبد الرحمن المخزومي، كلاهما عن ابن عيينة.','1454','705','0');
INSERT INTO [t5740] VALUES (750,'-: المرء مع [من] أحب ".','1460','705','0');
INSERT INTO [t5740] VALUES (751,'-: المرء مع من أحب ".','1461','705','0');
INSERT INTO [t5740] VALUES (752,'-: المرء مع من أحب ".','1461','705','0');
INSERT INTO [t5740] VALUES (753,'- رجل، فقال - يا رسول الله: " أرأيت رجلا أحب قوما ولما يلحق بهم، فقال النبي -','1462','705','0');
INSERT INTO [t5740] VALUES (754,'-: " إن مما أدرك الناس من كلام النبوة الأولى: إذا لم تستحي فاعمل ما شئت ".','1465','705','0');
INSERT INTO [t5740] VALUES (755,'-: " إن مما أدرك الناس من كلام النبوة الأولى: إذا لم تستحي فاصنع ما شئت ". هذا حديث صحيح، أخرجه البخاري في " ذكر بني إسرائيل " من " صحيحه "، عن أبي الحسن آدم بن أبي إياس العسقلاني. ورواه أبو داود في " الأدب " من " سننه " عن عبد الله','1466','705','0');
INSERT INTO [t5740] VALUES (756,'-: " السفر قطعة من العذاب، يمنع أحدكم طعامه وشرابه ومنامه، فإذا قضى أحدكم نهمته من','1467','705','0');
INSERT INTO [t5740] VALUES (757,'- قال: " السفر قطعة من العذاب، يمنع أحدكم (من) طعامه وسرابه، فإذا قضى أحدكم سفره فليرجع إلى أهله ".','1468','705','0');
INSERT INTO [t5740] VALUES (758,'-: " السفر قطعة من العذاب يمنع أحدكم نومه وطعامه وشرابه، فإذا قضى أحدكم نهمته من وجهه، فليعجل الرجوع إلى أهله ".','1469','705','0');
INSERT INTO [t5740] VALUES (759,'- قال: " السفر قطعة من العذاب يمنع أحدكم نومه وطعامه وشرابه، فإذا قضى أحدكم نهمته من سفره، فيلعجل إلى أهله ". هذا حديث صحيح متفق على صحته، رواه البخاري في " الحج ". من " جامعه "، عن القعنبي، وفي " الجهاد " عن عبد الله بن يوسف، وفي "','1470','705','0');
INSERT INTO [t5740] VALUES (760,'-: " لا يدخر شيئا لغد ".','1472','705','0');
INSERT INTO [t5740] VALUES (761,'-: كان لا يدخر شيئا لغد ". رواه الترمذي في " الزهد " من " جامعه "، عن قتيبة بن سعيد فوافقناه بعلو. مولد مرتضى بن العفيف المقدسي - تخمينا - في سنة ثمان','1473','705','0');
INSERT INTO [t5740] VALUES (762,'-: أنه أدرك عمر بن الخطاب - رضي الله عنه - في ركب، وعمر يحلف بأبويه فناداهم رسول الله -','1477','705','0');
INSERT INTO [t5740] VALUES (763,'- فآذاه القمل في رأسه، فقال رسول الله -','1479','705','0');
INSERT INTO [t5740] VALUES (764,'- قال: " لعلك آذاك هوام رأسك، فقلت: نعم يا رسول الله، فقال رسول الله -','1480','705','0');
INSERT INTO [t5740] VALUES (765,'- يقول: " أيما مملوك كان بين شركاء فأعتق أحدهم نصيبه فإنه يقوم في مال الذي أعتق قيمة عدل، فيعتق إن بلغ ذلك ماله ".','1482','705','0');
INSERT INTO [t5740] VALUES (766,'- قال: " من أعتق شركا له في عبد، وكان له مال يبلغ ثمن العبد، قوم عليه قيمة العدل، فأعطى شركاءه حصصهم، وعتق عليه العبد، وإلا فقد عتق منه ما عتق ".','1483','705','0');
INSERT INTO [t5740] VALUES (767,'-: " من أعتق من عبد شركا، فعليه أن يعتق ما بقي " هذا حديث صحيح أخرجه الأئمة في كتبهم من طرق، أما حديث الليث فانفرد مسلم بإخراجه، فرواه في " العتق والنذور " من " صحيحه ". ورواه النسائي في " العتق " من " سننه "، كلاهما عن قتيبة بن','1483','705','0');
INSERT INTO [t5740] VALUES (768,'- أنه قام فقال: " لا يحلبن أحدكم ماشية أحد بغير إذنه، أيحب أحدكم أن تؤتى مشربته فيكسر باب خزانته فينتقل طعامه وإنما تخزن لهم ضروع مواشيهم أطعماتهم، فلا يحلبن أحد ماشية امريء بغير إذنه ".','1486','705','0');
INSERT INTO [t5740] VALUES (769,'- يقول: " لا يحلبن أحدكم ماشية أخيه بغير إذنه، أيحب أحدكم أن تؤتى مشربته فتكسر خزانته وينتقل طعامه، إنما تخزن لهم ضروع مواشيهم ". هذا حديث صحيح متفق على صحته، أخرجه البخاري في " اللقطة " من " صحيحه "، عن عبد الله بن يوسف الدمشقي.','1487','705','0');
INSERT INTO [t5740] VALUES (770,'- من " صحيحه "، عن يحيى بن يحيى. وأخرجه أبو داود في " الجهاد " من " سننه " عن عبد الله بن مسلمة القعنبي. وأخرجه النسائي " فيما جمعه من حديث مالك بن أنس الإمام " عن أبي القاسم عبد الرحمن بن عبد الله بن عبد الحيكم المصري أخي محمد، عن','1488','705','0');
INSERT INTO [t5740] VALUES (771,'- في الواشمة لبعد الرحمن بن عابس، فقال: " قد سمعته عن أم يعقوب، عن عبد الواحد، ولا أجيء به كما أريد ".','1489','705','0');
INSERT INTO [t5740] VALUES (772,'- وهو في كتاب الله، بلى قد نهى عنه رسول الله -','1490','705','0');
INSERT INTO [t5740] VALUES (773,'- لعن الواشمات والموتشمات والمتنمصات للحسن المغيرات خلق الله - عز وجل ". قال البغوي: واللفظ لجدي.','1491','705','0');
INSERT INTO [t5740] VALUES (774,'- وهو في كتاب الله، قالت: لقد قرأت ما بين لوحي المصحف، فما وجدته، قال: لئن كنت قرأتيه لقد وجدتيه، أما وجدت {وما ءاتكم الرسول فخذوه / (وما نهاكم عنه فانتهوا} ، قالت: بلى، وإني لأظن بعض أهلك يفعلون ذلك، قال: فادخلي فانظري،','1493','705','0');
INSERT INTO [t5740] VALUES (775,'- اعتكف، واعتكف معه بعض نسائه، وهي مستحاضة ترى الدم، وربما وضعت الطست تحتها من الدم، وزعم أن عائشة - رضي الله عنها - رأت مثل ماء العصفر فقالت: كل هذا شيء كانت فلانة تجده " ورواه البخاري في " الطهارة " عن أبي بشر إسحاق بن شاهين','1496','705','0');
INSERT INTO [t5740] VALUES (776,'-: " لن يبرح الناس يسألون حتى يقولوا هذا الله خلق كل شيء، وذكر كلمة ". رواه البخاري في " الاعتصام " من " جامعه " عن أبي علي الحسن بن الصباح بن محمد الواسطي البزار، فوافقناه بعلو.','1498','705','0');
INSERT INTO [t5740] VALUES (777,'- في غزاة أو في غار - وقال يحيى بن آدم: في غار - فأنزل عليه {والمرسلات عرفا} فإنا لنتلقاها من فيه إذ خرجت علينا حية فابتدرناها فسبقتنا فدخلت جحرها، فقال رسول الله -','1500','705','0');
INSERT INTO [t5740] VALUES (778,'-: " جنتان من ذهب، آنيتهما وما فيهما، وجنتان من فضة آنيتهما وما فيهما، وما بين القوم وبين أن ينظروا إلى ربهم إلا رداء الكبرياء على وجهه في جنة عدن ". رواه مسلم في " الإيمان " من " صحيحه " عن أبي عمرو نصر بن علي الجهضمي.','1502','705','0');
INSERT INTO [t5740] VALUES (779,'- ثوبين معصفرين، فقال: " أمك أمرتك بهذا؟ قلت: أغسلهما؟ قال: أحرقهما ".','1504','705','0');
INSERT INTO [t5740] VALUES (780,'- علي ثوبين معصفرين، فقال: " أمك أمرتك بهذا؟ قلت: أغسلهما؟ ، قال: لا بل أحرقهما ". رواه مسلم في " اللباس " من " صحيحه " منفردا به، عن أبي الفضل داود بن رشيد هذا، فوافقناه بعلو.','1505','705','0');
INSERT INTO [t5740] VALUES (781,'- يتبرز لحاجته فآتيه بماء فيغتسل به ". هذا حديث صحيح، رواه مسلم في / " الطهارة " من " صحيحه "','1506','705','0');
INSERT INTO [t5740] VALUES (782,'-: " إن أعف الناس قتلة أهل الإيمان ".','1508','705','0');
INSERT INTO [t5740] VALUES (783,'-: " إن أعف الناس قتلة أهل الإيمان ". رواه أبو داود في " الجهاد " من " سننه " عن زياد بن أيوب نحو ما رويناه في الرواية الأولى. ورواه ابن ماجه في " الديات " من " سننه "، عن يعقوب بن إبراهيم','1508','705','0');
INSERT INTO [t5740] VALUES (784,'-: " يؤتى بالعبد يوم القيامة فيقال ألم أجعل لك سمعا وبصرا ومالا وولدا وسخرت لك الأنعام والحرث، وتركتك ترأس وتربع، أكنت تظن أنك ملاقي يومك هذا؟ ، فيقول: لا، فيقول: اليوم أنساك كما نسيتني ". قال أبو بكر بن أبي داود: " لم يروه عن','1510','705','0');
INSERT INTO [t5740] VALUES (785,'- " ما في الجنة شجرة إلا وساقها من ذهب ". رواه الترمذي في " صفة الجنة " من " جامعة "، عن عبد الله ابن','1512','705','0');
INSERT INTO [t5740] VALUES (786,'- قال: " لا تؤذي امرأة زوجها في الدنيا إلا قالت زوجته من الحور العين: " لا تؤذيه - قاتلك الله - فإنما هو عندك دخيل يوشك أن يفارقك إلينا ". رواه الترمذي في " النكاح " من " جامعه "، عن الحسن بن عرفة','1513','705','0');
INSERT INTO [t5740] VALUES (787,'- ركعتين، ولوددت أن حظي من أربع ركعتين متقبلتين ". رواه النسائي في " الصلاة " من " سننه " عن محمد بن علي بن','1515','705','0');
INSERT INTO [t5740] VALUES (788,'- في امرأة منا، يقال لها: بروع بنت واشق، تزوجت رجلا فدخل بئرا فسن، فمات قبل أن يدخل بها، فقضى رسول الله -','1517','705','0');
INSERT INTO [t5740] VALUES (789,'صلاة - لا ندري زاد أو نقص - فسأل فحدثناه بصنيعه فثنى رجله واستقبل القبلة، وسجد سجدتين، ثم سلم، ثم أقبل علينا بوجهه فقال: إنه لو حدث في','1518','705','0');
INSERT INTO [t5740] VALUES (790,'- يوما ببعض جسدي، فقال، يا عبد الله بن عمر: " كن في الدنيا كأنك غريب، أو كأنك عابر سبيل، وعد نفسك من أهل القبور ". قال مجاهد: ثم قال لي ابن عمر: " إذا أصبحت فلا تحدث نفسك بالمساء، وإذا أمسيت فلا تحدث نفسك بالصباح، وخذ من حياتك','1524','705','0');
INSERT INTO [t5740] VALUES (791,'- بمنكبي، فقال: كن في الدنيا كأنك غريب أو عابر سبيل ". قال: وقال لي ابن عمر: " إذا أصبحت فلا تنتظر المساء، وإذا أمسيت فلا تنتظر الصباح، وخذ من صحتك لمرضك، ومن حياتك لموتك ".','1526','705','0');
INSERT INTO [t5740] VALUES (792,'- ببعض جسدي، فقال: " كن في الدنيا كغريب أو كعابر سبيل، وعد نفسك من أهل القبور ".','1526','705','0');
INSERT INTO [t5740] VALUES (793,'- قال: " الإيمان معرفة بالقلب وإقرار باللسان، وعمل بالأركان ".','1529','705','0');
INSERT INTO [t5740] VALUES (794,'- رجلا غنما بين جبلين، قال: فرجع إلى أصحابه فقال: اذهبوا فأسلموا، فإن محمدا يعطي عطاء رجل ما يخشى الفاقة ".','1534','705','0');
INSERT INTO [t5740] VALUES (795,'-: " لا يزال العبد في صلاة ما دام ينتظر الصلاة، فتقول الملائكة " اللهم اغفر له، اللهم ارحمه ".','1535','705','0');
INSERT INTO [t5740] VALUES (796,'- كان يقول: " ما يزال الله - عز وجل في حاجة العبد ما دام العبد في حاجة أخيه ".','1536','705','0');
INSERT INTO [t5740] VALUES (797,'- في الحضر أربعا، وفي السفر ركعتين، وفي الخوف ركعة ".','1542','705','0');
INSERT INTO [t5740] VALUES (798,'- فاستفتيته، فقلت يا رسول الله: إني كنت أعلم ناسا من أهل الصفة الكتاب والقرآن، فأهدى إلي رجل منهم قوسا، فقلت: أرمي عليها، وليست','1550','705','0');
INSERT INTO [t5740] VALUES (799,'- على الرماة يوم أحد - وكانوا خمسين رجلا - عبد الله بن جبير الخزاعي، وقال: " إن رأيتمونا تخطفنا','1552','705','0');
INSERT INTO [t5740] VALUES (800,'- قالوا: والله لنأتين الناس ولنصيبن من الغنيمة، فلما أتوهم صرفت وجوههم فأقبلوا منهزمين، وقد كان الرسول يدعوهم في أخراهم فلم يبق مع رسول الله -','1553','705','0');
INSERT INTO [t5740] VALUES (801,'- على رماة الناس يوم أحد عبد الله بن جبير، وكانوا خمسين رجلا، وقال لهم: كونوا مكانكم لا تبرحوا، وإن رأيتم الطير تخطفنا، قال البراء: وأنا - والله - رأيت النساء باديات خلاخيلهن قد استرخت ثيابهن، يصعدن الجبل، فلما كان من الأمر ما كان،','1554','705','0');
INSERT INTO [t5740] VALUES (802,': لا تجيبوه، فقال: أفيكم محمد؟ .، فلم يجيبوه، ثم قال أفيكم محمد، الثالثة فلم يجيبوه، فقال: أفيكم ابن أبي قحافة؟ فلم يجيبوه، قالها ثلاثا، ثم قال: أفيكم ابن الخطاب؟ قالها ثلاثا فلم يجيبوه، قال: أما هؤلاء فقد كفيتموهم، فلم','1554','705','0');
INSERT INTO [t5740] VALUES (803,'-، وأبو بكر، وأنا أحياء، ولك منا يوم سوء، فقال: يوم بيوم بدر، والحرب سجال، وقال: أعل هبل، فقال رسول الله -','1555','705','0');
INSERT INTO [t5740] VALUES (804,'-: أجيبوه قالوا: يا رسول الله ما نقول؟ ، قال: قولوا: الله مولانا ولا مولى لكم، ثم قال أبو سفيان: إنكم سترون في القوم مثلة لم آمر بها، ثم قال: ولم تسؤني ". رواه أبو داود في " الجهاد " من " سننه " عن عبد الله بن محمد النفيلي، ورواه','1555','705','0');
INSERT INTO [t5740] VALUES (805,'- قال: " من صام رمضان وأتبعه ستا من شوال كان كصيام الدهر ".','1561','705','0');
INSERT INTO [t5740] VALUES (806,'- قال: " من صام رمضان ثم أتبعه بست من شوال، كان كصيام الدهر ".','1562','705','0');
INSERT INTO [t5740] VALUES (807,'-: " من صام رمضان وأتبعه بست من شوال فقد صام الدهر ".','1563','705','0');
INSERT INTO [t5740] VALUES (808,'- يقول: " من صام رمضان وأتبعه ستة من شوال كان ذلك كصيام الدهر ". هذا حديث صحيح انفرد بإخراجه مسلم دون البخاري، فرواه في " الصوم " من " صحيحه "، عن علي بن حجر بن إياس السعدي، وأبي بكر بن أبي شيبة كما رويناه من طريقهما. ورواه ابن','1564','705','0');
INSERT INTO [t5740] VALUES (809,'-: " يصاح برجل من أمتي على رءوس الخلائق يوم القيامة، فينشر له تسعة وتسعين سجلا، كل سجل منها مد البصر، ثم يقول الله - تبارك وتعالى - له أتنكر من هذا شيئا؟ فيقول: لا يا رب، فيقول عز وجل: ألك عذر أو حسنة، فيهاب الرجل، فيقول: لا يا رب،','1572','705','0');
INSERT INTO [t5740] VALUES (810,'-: " سيصاح يوم القيامة برجل من أمتي على رءوس الخلائق، وينشر عليه تسعة وتسعون سجلا كل سجل مد البصر، ثم يقال له: هل','1573','705','0');
INSERT INTO [t5740] VALUES (811,'سيصيح يوم القيامة','1574','705','0');
INSERT INTO [t5740] VALUES (812,'-: " يؤتى برجل يوم القيامة ثم يؤتى بالميزان، ثم يؤتى بتسعة وتسعين سجلا كل سجل منها مد البصر، فيها ذنوبه وخطاياه، فتوضع في كفة الميزان، ثم يؤتى بقرطاس مثل هذا - وأشار بيده وأمسك بإبهامه -، فيها أشهد أن لا إله إلا الله وأن محمدا رسول','1576','705','0');
INSERT INTO [t5740] VALUES (813,'-، عن جبريل - عليه السلام -، عن الله - تبارك وتعالى - أنه قال: " يا عبادي إني حرمت الظلم على نفسي وجعلته بينكم حراما، فلا تظلموا، يا عبادي إنكم الذين تخطئون بالليل والنهار، وأنا الذي أغفر الذنوب ولا أبالي فاستغفروني، أغفر لكم، يا','1583','705','0');
INSERT INTO [t5740] VALUES (814,'- قال الله - تعالى -: " يا عبادي إني حرمت الظلم على نفسي وجعلته عليكم محرما فلا تظالموا، ويا عبادي تخطئون بالليل والنهار وأنا أغفر الذنوب جميعا ولا أبالي، فاستغفروني أغفر لكم، ويا عبادي كلكم جائع إلا من أطعمته فاستطعموني أطعمكم، ويا عبادي','1584','705','0');
INSERT INTO [t5740] VALUES (815,'-، قال الله - عز وجل: " يا عبادي كلكم جائع إلا من أطعمت فاستطعموني أطعمكم، يا عبادي كلكم عار إلا من كسوت فاستكسوني أكسكم، ويا عبادي لو أن أولكم وآخركم وجنكم وإنسكم اجتمعوا في صعيد واحد، فسألوني جميعا فأعطيت كل إنسان منهم مسألته لم ينقص','1585','705','0');
INSERT INTO [t5740] VALUES (816,'-: " من قال حين يسمع النداء اللهم / بحق هذه الدعوة المحمود الذي وعدته، حلت له الشفاعة يوم القيامة ".','1587','705','0');
INSERT INTO [t5740] VALUES (817,'-: " من قال حين يسمع النداء: " اللهم رب هذه الدعوة التامة والصلاة القائمة آت محمدا الوسيلة والفضيلة وابعثه مقاما محمودا الذي وعدته إلا حلت له شفاعتي يوم القيامة ". هذا حديث صحيح أخرجه الإمام أحمد في " مسنده " والبخاري في " الصلاة " من "','1588','705','0');
INSERT INTO [t5740] VALUES (818,'- بل أرجو أن يخرج الله من أصلابهم من يعبد الله وحده لا يشرك به شيئا ". / هذا حديث صحيح، رواه البخاري في " بدء الخلق " و " التوحيد " من " جامعه " عن عبد الله بن يوسف الدمشقي. ورواه مسلم في " المغازي " من " صحيحه " عن أبي الطاهر أحمد','1592','705','0');
INSERT INTO [t5740] VALUES (819,'- إذا رفع يديه من الطعام، قال: الحمد لله كثيرا طيبا مباركا فيه غير مكفي ولا مودع ولا مستغنى عنه، ربنا ".','1594','705','0');
INSERT INTO [t5740] VALUES (820,'- إذا رفع مائدته قال: " الحمد لله كثيرا طيبا مباركا فيه غير مكفي ولا مودع ".','1595','705','0');
INSERT INTO [t5740] VALUES (821,'- كان إذا رفع العشاء من بين يديه قال: " الحمد لله كثيرا طيبا مباركا فيه غير مكفي ولا مودع، ولا مستغنى عنه ربنا ". رواه [البخاري في " الأطعمة " من " جامعه "، عن أبي نعيم الفضل بن دكين، وأبي عاصم الضحاك بن مخلد، فوافقناه بعلو في','1595','705','0');
INSERT INTO [t5740] VALUES (822,'- قال: " لو يعلم الناس ما في الصف الأول لكانت قرعة ".','1597','705','0');
INSERT INTO [t5740] VALUES (823,'- قال: " لو يعلم الناس ما في الصف الأول / لكانت قرعة ". رواه مسلم في " الصلاة " من " صحيحه " عن إبراهيم بن دينار،','1598','705','0');
INSERT INTO [t5740] VALUES (824,'- يسوي صفوفنا، فخرج يوما فرأى رجلا خارجا صدره عن القوم، فقال: " لتسون صفوفكم أو ليخالفن الله بين وجوهكم ". رواه مسلم والترمذي في " الصلاة " من " كتابيهما "، عن','1600','705','0');
INSERT INTO [t5740] VALUES (825,'- قال: " فسطاط المسلمين يوم الملحمة بالغوطة إلى جانب مدينة يقال لها: دمشق من خير مدائن الشام ".','1602','705','0');
INSERT INTO [t5740] VALUES (826,'- قال: " فسطاط المسلمين يوم الملحمة إلى جانب مدينة يقال لها: دمشق من خير مدائن الشام ". رواه أبو داود في " الملاحم " من " سننه "، عن هشام بن عمار، عن','1603','705','0');
INSERT INTO [t5740] VALUES (827,'- دخل مكة على رأسه المغفر، فقيل: هذا ابن خطل متعلق بأستار الكعبة فقال: اقتلوه ".','1612','705','0');
INSERT INTO [t5740] VALUES (828,'- دخل مكة - قال ابن معروف في روايته: يوم الفتح - وقال ابن الصلت -: عام الفتح - ثم اتفقا وعلى رأسه المغفر فلما نزعه جاءه رجل فقال يا رسول الله: ابن خطل متعلق بأستار الكعبة، فقال رسول الله -','1614','705','0');
INSERT INTO [t5740] VALUES (829,'- مكة وعلى رأسه مغفر فلما نزعه، قيل: هذا ابن خطل متعلق بأستار الكعبة قال: " اقتلوه ".','1615','705','0');
INSERT INTO [t5740] VALUES (830,'- مكة وعلى رأسه المغفر، فلما نزعه، قيل له: هذا ابن خطل متعلق بأستار الكعبة، قال: اقتلوه ".','1616','705','0');
INSERT INTO [t5740] VALUES (831,'-: دخل مكة وعلى رأسه مغفر، فقيل: هذا ابن خطل متعلق بأستار الكعبة، فقال: اقتلوه ".','1616','705','0');
INSERT INTO [t5740] VALUES (832,'- مكة، وعلى رأسه المغفر، فلما نزعه، قيل له: هذا ابن خطل متعلق بأستار الكعبة، قال: اقتلوه ".','1617','705','0');
INSERT INTO [t5740] VALUES (833,'-: دخل يوم الفتح مكة وعلى رأسه المغفر ". هذا حديث صحيح متفق على صحته، أخرجه البخاري في','1617','705','0');
INSERT INTO [t5740] VALUES (834,'" أيعجز أحدكم أن يكسب في اليوم ألف حسنة، قالوا: وكيف يكسب أحدنا في اليوم ألف حسنة؟ قال: يسبح الله في اليوم مائة تسبيحة فيكتب له بها ألف حسنة ويحط عنه بها ألف خطيئة ". هذا حديث صحيح، رواه مسلم في " الدعوات " من " صحيحه "، عن','1624','705','0');
INSERT INTO [t5740] VALUES (835,'- ما لنا طعام إلا ورق الحبلة وهذا السمر حتى أن أحدنا لبضع كما تضع الشاة ما له خلط، ثم أصبحت بنو أسد تعزرني لقد خبت إذن وضل','1626','705','0');
INSERT INTO [t5740] VALUES (836,'-: " لا حسد إلا في اثنتين: رجل آتاه الله مالا فسلطه على هلكته بالحق، ورجل آتاه الله حكمة فهو يقضي بها','1628','705','0');
INSERT INTO [t5740] VALUES (837,'- فقال: علمني كلاما أقوله، قال: قال: قل لا إله إلا الله وحده لا شريك له، الله أكبر كبيرا، والحمد لله كثيرا، والحمد لله رب العالمين، ولا حول ولا قوة إلا بالله العزيز الحكيم. قال: هؤلاء لربي، فما لي؟ ، قال: تقول: اللهم اغفر لي','1630','705','0');
INSERT INTO [t5740] VALUES (838,'- فقال: يا رسول الله: إني لأتأخر عن صلاة الغداة من / أجل فلان مما يطيل بنا، قال: فما رأيت رسول الله -','1631','705','0');
INSERT INTO [t5740] VALUES (839,'- إلى قباء ليصلي، فجاءت الأنصار ليسلموا عليه وهو يصلي فقال لبلال: كيف رأيت رسول الله -','1632','705','0');
INSERT INTO [t5740] VALUES (840,'- قال: يأتي أحدكم الشيطان فيلبس عليه وهو في الصلاة حتى لا يدري كم صلى، فإذا وجد ذلك شيئا فليسجد سجدتين وهو جالس ".','1643','705','0');
INSERT INTO [t5740] VALUES (841,'-: " يأتي الشيطان أحدكم في صلاته فيلبس عليه صلاته، حتى لا يدري كم صلى فإذا وجد ذلك أحدكم فليسجد سجدتين وهو جالس ".','1644','705','0');
INSERT INTO [t5740] VALUES (842,'- قال: إن أحدكم إذا قام يصلي جاءه الشيطان فيلبس عليه حتى يدري كم صلى فإذا وجد ذلك أحدكم فليسجد سجدتين وهو جالس ". هذا حديث صحيح أخرجه الأئمة في " كتبهم " من طرق منها لمسلم والترمذي في " الصلاة " من " كتابيهما " عن قتيبة بن سعيد عن الليث','1644','705','0');
INSERT INTO [t5740] VALUES (843,'-: " ما حلف عند منبري هذا من عبد وأمة أحد على يمين آثمة ولو على سواك رطب إلا وجبت له النار ". رواه ابن ماجه في " الأحكام " من " سننه " عن محمد بن يحيى','1646','705','0');
INSERT INTO [t5740] VALUES (844,'- بالمدينة سنة ما يمنعني من الهجرة إلا المسألة كان أحدنا إذا هاجر لم يسأل رسول الله -','1652','705','0');
INSERT INTO [t5740] VALUES (845,'-: " البر حسن الخلق، والإثم ما حاك في نفسك، وكرهت أن يطلع عليه الناس ".','1652','705','0');
INSERT INTO [t5740] VALUES (846,'- عن قوله تعالى {لهم البشرى في الحيواة الدنيا وفي الآخرة} قال: " ما سألني [عنها] أحد قبلك هي الرؤيا الصالحة يراها المؤمن أو ترى له ". رواه الترمذي في " التفسير " من " جامعه " عن أحمد بن عبدة الضبي، عن حماد بن زيد، عن عاصم بن','1655','705','0');
INSERT INTO [t5740] VALUES (847,'- وقال أبو القاسم: " خلق الله الأرض يوم السبت والجبال يوم الأحد، والشجر يوم الاثنين والمكروه يوم الثلاثاء والنور يوم الأربعاء، والدواب يوم الخميس، وآدم -','1661','705','0');
INSERT INTO [t5740] VALUES (848,'- بيدي فقال: خلق الله التربة يوم السبت وخلق فيها الجبال يوم الأحد خلق الشجر يوم الاثنين، وخلق المكروه يوم الثلاثاء، وخلق النور يوم الأربعاء، وبث','1662','705','0');
INSERT INTO [t5740] VALUES (849,'-: " من مشى إلى سلطان الله في الأرض','1668','705','0');
INSERT INTO [t5740] VALUES (850,'-: " مثل المؤمن الذي يقرأ القرآن مثل الأترجة ريحها طيب وطعمها طيب، ومثل المؤمن الذي لا يقرأ القرآن مثل التمرة لا ريح لها وطعمها حلو، ومثل المنافق','1681','705','0');
INSERT INTO [t5740] VALUES (851,'- قال: " مثل المؤمن الذي يقرأ القرآن مثل الأترجة ... فذكر الحديث.','1682','705','0');
INSERT INTO [t5740] VALUES (852,'- قال: " مثل المؤمن الذي يقرأ القرآن كمثل الأترجة ... . الحديث','1682','705','0');
INSERT INTO [t5740] VALUES (853,'- قالت: " مثل المؤمن الذي يقرأ القرآن ويعمل به كمثل الأترجة طيبة الرائحة، ومثل المنافق الذي يقرأ القرآن كمثل الريحانة طيبة الريح وطعمها مر، ومثل المنافق الذي لا يقرأ القرآن كمثل الحنظلة مرة الطعم، لا ريح لها ".','1683','705','0');
INSERT INTO [t5740] VALUES (854,'- قال: مثل المؤمن الذي يقرأ القرآن ويعمل به كمثل الأترجة طيبة الطعم طيبة الريح، ومثل المؤمن الذي يقرأ القرآن ويعمل به كمثل التمرة طيبة الطعم ولا وريح لها، ومثل المنافق الذي يقرأ القرآن كمثل الريحانة طيبة الريح وطعمها مر، ومثل المنافق الذي لا','1684','705','0');
INSERT INTO [t5740] VALUES (855,'- رجلا يثني على رجل فيطريه، فقال: لقد أهلكتم أو قطعتم ظهر الرجل في المدحة ".','1686','705','0');
INSERT INTO [t5740] VALUES (856,'- قال: آية المنافق ثلاث: إذا حدث كذب، وإذا وعد أخلف، وإذا اؤتمن خان ". رواه البخاري في " الشهادات " من " جامعه " ومسلم في " الإيمان " من " صحيحه " "، والنسائي في " التفسير " من " سننه ".','1688','705','0');
INSERT INTO [t5740] VALUES (857,'-: " أي الإسلام أفضل؟ قال: من سلم المسلمون من لسانه ويده ". أخرجه البخاري ومسلم والنسائي في كتبهم، عن سعيد بن يحيى الأموي هذا، فوافقناهم بعلو.','1690','705','0');
INSERT INTO [t5740] VALUES (858,'-: " أعطيت مفاتيح الكلم ونصرت بالرعب، وبينا أنا نائم البارحة إذ أتيت بمفاتيح خزائن الأرض، حتى وضعت في يدي، قال أبو هريرة','1691','705','0');
INSERT INTO [t5740] VALUES (859,'- وأنتم تنتشلونها وربما قال تنتقلونها ". رواه البخاري في " التفسير " من " صحيحيه " عن أبي الأشعث هذا، فوافقناه بعلو.','1692','705','0');
INSERT INTO [t5740] VALUES (860,'- عن أشياء كرهها، فلما أكثر عليه غضب، ثم قال للناس: سلوني عم شئتم؟ فقال له رجل من أبي؟ قال: أبوك سالم مولى بني شيبة، فلما رأى عمر بن الخطاب - رضي الله عنه - ذلك قال: يا رسول الله: إنا نتوب إلى الله عز وجل ". هذا حديث صحيح متفق','1694','705','0');
INSERT INTO [t5740] VALUES (861,'-: " أربع من كن فيه كان منافقا، ومن كانت فيه خلة منهن كانت فيه خلة من النفاق حتى يدعها: إذا حدث كذب، وإذا وعد أخلف، وإذا عاهد غدر، وإذا خاصم فجر ". رواه مسلم في " الإيمان " من " صحيحه " وأبو داود في " السنة "','1695','705','0');
INSERT INTO [t5740] VALUES (862,'- قال: " بادروا بالأعمال فتنا كقطع الليل المظلم يصبح الرجل مؤمنا ويمسي كافرا، ويمسي مؤمنا ويصبح كافرا / يبيع دينه بعرض من الدنيا ".','1696','705','0');
INSERT INTO [t5740] VALUES (863,'- قال: بادروا بالأعمال - فذكر مثله. رواه مسلم والترمذي في " الفتن " من كتابيهما عن قتيبة بن','1696','705','0');
INSERT INTO [t5740] VALUES (864,'- بشأنهم، فقال: إن هذه النار إنما هي عدو لكم، فإذا نمتم فأطفئوها ".','1698','705','0');
INSERT INTO [t5740] VALUES (865,'- بشأنهم فقال: إنما هذه النار عدو لكم، فإذا نمتم فأطفئوها عنكم ".','1698','705','0');
INSERT INTO [t5740] VALUES (866,'- إذا بعث أحدا من أصحابه / في بعض أمره قال: بشروا ولا تنفروا يسروا ولا تعسروا ". قال ابن كرامة: ". . في أمره قال: " يسروا ولا تعسروا، وبشروا ولا تنفروا ".','1700','705','0');
INSERT INTO [t5740] VALUES (867,'إذا','1700','705','0');
INSERT INTO [t5740] VALUES (868,'- وقال ابن كرامة: فذهبت به إلى النبي -','1702','705','0');
INSERT INTO [t5740] VALUES (869,'- فسماه إبراهيم وحنكه بتمرة ". هذا حديث صحيح، رواه البخاري في " العقيقة " من " جامعه " عن إسحاق بن نصر وفي " الأدب "، عن أبي كريب. ورواه مسلم في " الاستئذان " من " صحيحه " عن عبد الله بن براد، وأبي بكر بن أبي شيبة، وأبي كريب،','1703','705','0');
INSERT INTO [t5740] VALUES (870,'-: " آية المنافق ثلاث، إذا حدث كذب، وإذا وعد أخلف، وإذا اؤتمن خان ". رواه الترمذي في " الإيمان " من " جامعه " عن عمرو بن علي،','1705','705','0');
INSERT INTO [t5740] VALUES (871,'- قال: " يكون بين يدي الساعة فتن كقطع الليل المظلم، يصبح الرجل فيها مؤمنا ويمسي كافرا، ويمسي مؤمنا ويصبح كافرا، يبيع أقوام دينهم بعرض من الدنيا ". رواه الترمذي في " الفتن " من " جامعه "، عن قتيبة بن سعيد، فوقع لنا موافقة عالية له.','1706','705','0');
INSERT INTO [t5740] VALUES (872,'-: " المؤمن يأكل في معى واحد، والكافر يأكل في سبعة أمعاء ". رواه الترمذي في " العلل " من " جامعه " عن أبي هشام محمد بن','1707','705','0');
INSERT INTO [t5740] VALUES (873,'- قال: " إن مثل ما بعثني الله به من الهدى والعلم كمثل غيث أصاب الأرض - وقال يوسف: أصاب','1708','705','0');
INSERT INTO [t5740] VALUES (874,'- قال: " إن مثل منا بعثني الله به من الهدى والعلم كمثل غيث أصاب','1709','705','0');
INSERT INTO [t5740] VALUES (875,'- " من " صحيحه "، عن أبي بكر بن أبي شيبة، كلاهما عن أبي أسامة، فوقع لنا بدلا','1710','705','0');
INSERT INTO [t5740] VALUES (876,'- ": لا يبولن أحدكم في الماء الدائم، ثم يغتسل منه ". رواه النسائي في " الطهارة " من " سننه " عن يعقوب بن إبراهيم','1711','705','0');
INSERT INTO [t5740] VALUES (877,'- في جنازة فقعد حيال القبلة ". رواه ابن ماجه في " الجنائز " من " سننه " عن محمد بن زياد بن عبيد الله الزيادي هذا، فوافقنا بعلو.','1713','705','0');
INSERT INTO [t5740] VALUES (878,'-: " من حمل علينا السلاح فليس منا ". رواه ابن ماجه في " الحدود " من " سننه "، عن يوسف بن موسى','1714','705','0');
INSERT INTO [t5740] VALUES (879,'- نسميها (المانعة) ، وإنها في كتاب الله سورة، من قرأها، كل ليلة فقد أكثر وأطاب ".','1716','705','0');
INSERT INTO [t5740] VALUES (880,'-: " خير الصحابة أربعة وخير السرايا أربعمائة، وخير الجيوش أربعة آلاف، ولن تغلب اثنا عشر ألفا من قلة ". رواه أبو داود في " الجهاد " من " سننه "، عن أبي خيثمة زهير بن حرب. ورواه الترمذي في " الفتن "، عن محمد بن يحيى الأزدي.','1718','705','0');
INSERT INTO [t5740] VALUES (881,'- بيد عبد الرحمن بن عوف - رضي الله عنه - فانطلق إلى النخيل وفيه إبراهيم - رضي الله عنه - فأخذه فوضعه في حجره وقال: يا بني لا أملك لك من الله شيئا وتغرغرت عيناه، فقال له عبد الرحمن: يا رسول الله: أتبكي وتنهى عن البكاء؟ قال: إنما هي رحمة،','1720','705','0');
INSERT INTO [t5740] VALUES (882,'- صلاتي العشي، أركد في الأوليين، وأحذف في الأخريين، فقال عمر: ذلك الظن بك أبا إسحاق، وبعث رجالا يسألون عنه في مساجد الكوفة، قال: فلا يأتون مسجدا من مساجد الكوفة إلا أثنوا عليه خيرا، وقالوا معروفا','1728','705','0');
INSERT INTO [t5740] VALUES (883,'- لا أخرم عنها (صلاتي) العشي أركد في الأوليين وأحذف في الأخريين، فقال عمر: ذاك الظن بك يا أبا إسحاق ". هذا حديث صحيح متفق على صحته، أخرجه البخاري ومسلم في " الصلاة " من " كتابيهما " من عدة طرق منها للبخاري عن','1730','705','0');
INSERT INTO [t5740] VALUES (884,'-: " الحياء خير كله ". وقال: فقال رجل عند عمران بن حصين - رضي الله عنه -. أن الحياء ضعفا، أو قال: عجزا، فقال: أحدثك عن رسول الله -','1732','705','0');
INSERT INTO [t5740] VALUES (885,'-: الحياء خير كله ". هذا حديث صحيح متفق على صحته، رواه البخاري في " الأدب " من " صحيحه "، عن آدم بن أبي إياس. ورواه مسلم في " الإيمان " من / " صحيحه "، عن أبي موسى','1733','705','0');
INSERT INTO [t5740] VALUES (886,'- ثم يأتي فيؤم قومه، فصلى مع النبي -','1735','705','0');
INSERT INTO [t5740] VALUES (887,'- فلأخبرنه، فأتى رسول الله -','1735','705','0');
INSERT INTO [t5740] VALUES (888,'- على معاذ فقال: يا معاذ أفتان أنت؟ اقرأ بكذا ". قال سفيان: فقلت لعمرو: إن أبا الزبير: حدثنا عن جابر - رضي الله عنه - أنه قال: اقرأ {والشمس وضحاها،} و {واليل إذا يغشى،} و {سبح اسم ربك الأعلى،} فقال عمرو: نحوها ".','1736','705','0');
INSERT INTO [t5740] VALUES (889,'-، ثم يأتي مسجد قومه فيصلي لهم ". رواه مسلم في " الصلاة " من " صحيحه "، عن محمد بن عباد','1737','705','0');
INSERT INTO [t5740] VALUES (890,'- كان يفرغ على رأسه ثلاثا ". قال شعبة: أظنه من الغسل من الجنابة -، فقال رجل من بني هاشم: إن شعري كثير، قال جابر: " كان رسول الله -','1738','705','0');
INSERT INTO [t5740] VALUES (891,'- يأخذ ثلاث أكف فيفيضها على رأسه، ثم يفيض على جلده، فقال الحسن بن محمد: إني رجل كثير الشعر، فقال: كان رسول الله -','1739','705','0');
INSERT INTO [t5740] VALUES (892,'- بشماله، فقال: " كل بيمينك، قال: لا أستطيع، قال: لا استطعت ما منعه إلا الكبر قال: فما ذرفعها إلى فيه ".','1742','705','0');
INSERT INTO [t5740] VALUES (893,'- أبصر بسر بن راعي العير يأكل بشماله، فقال: كل بيمينك، فقال: لا أستطيع، فقال: لا استطعت، فما نالت يمينه إلى فيه بعد ". رواه مسلم في " الأشربة " من " صحيحه "، عن أبي بكر بن أبي شيبة، عن زيد بن الحباب نحو ما رويناه.','1743','705','0');
INSERT INTO [t5740] VALUES (894,'- صلاة العصر فسلم من ركعتين، فقام ذو اليدين، فقال: أقصرت الصلاة، أم نسيت يا رسول الله؟ ! فقال رسول الله -','1744','705','0');
INSERT INTO [t5740] VALUES (895,'- فقال: أصدق ذو اليدين؟ ، فقالوا: نعم يا رسول الله، فأتم رسول الله -','1745','705','0');
INSERT INTO [t5740] VALUES (896,'- صلاة العصر، فسلم من ركعتين، فقام ذو اليدين، فقال: أقصرت الصلاة أن نسيت؟ {. . فأقبل رسول الله -','1745','705','0');
INSERT INTO [t5740] VALUES (897,'- ما بقي من الصلاة، ثم سجد سجدتين وهو جالس بعد التسليم ". رواه مسلم والنسائي في " الصلاة " من كتابيهما، عن قتيبة بن','1745','705','0');
INSERT INTO [t5740] VALUES (898,'- قرأ في الصلاة فتعايا في آية، فقال رجل: يا رسول الله، إنك تركت آية، قال: فهلا ذكرتنيها، قال: ظننت أنها نسخت، قال: إنها لم تنسخ ".','1747','705','0');
INSERT INTO [t5740] VALUES (899,'- وترك آية، فقل له رجل - يا رسول الله -: تركت آية كذا، وكذا، قال: فهلا ذكرتنيها ". رواه أبو داود في " الصلاة " من " سننه "، عن أبي كريب محمد بن العلاء، وسليمان بن عبد الرحمن الدمشقي، كلاهما عن مروان [بن معاوية] فوقع لنا بدلا له','1748','705','0');
INSERT INTO [t5740] VALUES (900,'-، قال: " من صام رمضان، إيمانا واحتسابا غفر له ما تقدم من ذنبه، ومن قام ليلة القدر / إيمانا، واحتسابا غفر له ما تقدم من ذنبه ".','1755','705','0');
INSERT INTO [t5740] VALUES (901,'-: " لا يزال الناس بخير ما عجلوا الإفطار ".','1758','705','0');
INSERT INTO [t5740] VALUES (902,'- قال: " لا يزال الناس بخير ما عجلوا الفطر ".','1758','705','0');
INSERT INTO [t5740] VALUES (903,'- قال: " لا','1759','705','0');
INSERT INTO [t5740] VALUES (904,'- قال: " تسحروا فإن في السحور بركة ".','1762','705','0');
INSERT INTO [t5740] VALUES (905,'- ": تسحروا فإن في السحور بركة ".','1765','705','0');
INSERT INTO [t5740] VALUES (906,'- قال: " تسحروا فإن في السحور بركة ". أخرجه البخاري عن آدم، عن شعبة، وأخرجه مسلم في " الصوم "، عن يحيى بن يحيى، عن هشيم، عن عبد العزيز، وعن قتيبة عن أبي عوانة، عن قتادة وعبد العزيز، عن أنس. وأخرجه الترمذي والنسائي فيه، عن','1765','705','0');
INSERT INTO [t5740] VALUES (907,'- إني أنا محمد، وأنا أحمد، وأنا الماحي الذي يمحى بي الكفر، وأنا الحاشر الذي أحشر الناس، وأنا العاقب الذي ليس بعده نبي ".','1770','705','0');
INSERT INTO [t5740] VALUES (908,'- قال: إن لي أسماء، أنا محمد، وأنا أحمد، وأنا الحاشر الذي يحشر الناس على قدمي، وأنا الماحي الذي يمحى بي الكفر، وأنا العاقب،','1770','705','0');
INSERT INTO [t5740] VALUES (909,'- "، من " صحيحه "، عن إبراهيم بن المنذر، عن معن بن عيسى القزاز عن مالك بن أنس. ومنها لمسلم في " فضائل النبي -','1771','705','0');
INSERT INTO [t5740] VALUES (910,'-، فجاء رجل فقال يا رسول الله / متى قيام الساعة؟ وحضرت الصلاة، فقام رسول الله -','1777','705','0');
INSERT INTO [t5740] VALUES (911,'- إلى الصلاة فلما قضى صلاته، قال: أين السائل عن الساعة؟ ، فقال: أنا - يا رسول الله - قال: ما أعددت لها؟ قال: والله ما أعددت لها كثير عمل ولا صلاة ولا صوم، إلا أني أحب الله ورسوله، قال: المرء مع من أحب','1777','705','0');
INSERT INTO [t5740] VALUES (912,'- انفكت قدمه فقعد في مشربة له، درجها من جذوع، فقالوا: آلى من نسائه شهرا فأتاه أصحابه، فصلى بهم جالسا، وهم قيام، فلما حضرت الصلاة الأخرى قال لهم النبي -','1778','705','0');
INSERT INTO [t5740] VALUES (913,'- سقط عن فرسه فجحش شقه أو فخذه، وآلى من نسائه شهرا، فجلس في مشربة له، درجها من جذوع، فأتاه أصحابه يعودونه، قال: فصلى بهم جالسا وهم قيام فلما سلم، قال: إنما جعل الإمام ليؤتم به، فإذا كبر فكبروا، وإذا ركع فاركعوا، وإذا سجد فاسجدوا،','1779','705','0');
INSERT INTO [t5740] VALUES (914,'- شج في وجهه وكسرت رباعيته، ورمي رمية على كتفه فجعل يسيل الدم عن وجهه ويقول: كيف يفلح قوم فعلوا هذا بنبيهم فأنزل الله - عز','1781','705','0');
INSERT INTO [t5740] VALUES (915,'- قال: مثل البيت الذي يذكر الله فيه، والبيت الذي لا يذكر الله فيه مثل الحي والميت ".','1787','705','0');
INSERT INTO [t5740] VALUES (916,'-: مثل بيت يذكر الله فيه، وبيت لا يذكر الله فيه مثل الحي والميت ".','1788','705','0');
INSERT INTO [t5740] VALUES (917,'- قال: مثل البيت الذي يذكر الله فيه، والبيت الذي لا يذكر الله فيه مثل الحي والميت ". رواه البخاري في " الدعوات "، ومسلم في " الصلاة " من كتابيهما، عن أبي كريب محمد بن العلاء، كلاهما عن أبي أسامة، فوقع','1788','705','0');
INSERT INTO [t5740] VALUES (918,'- قال: " سووا صفوفكم فإن تسوية الصف من تمام الصلاة ". قال: لم يمنعني أن أسأل قتادة: سمعته من أنس إلا أن يفسده علي.','1796','705','0');
INSERT INTO [t5740] VALUES (919,'- قال: " أقيموا صفوفكم، فإن تسوية الصف من تمام الصلاة ". هذا حديث صحيح متفق على صحته، أخرجه البخاري في " الصلاة " من " جامعه "، عن أبي الوليد. وأخرجه مسلم فيه من " صحيحه "، عن بندار محمد بن بشار،','1796','705','0');
INSERT INTO [t5740] VALUES (920,'- بجنازة، فأثنوا عليها شرا، فقال: وجبت، ومروا عليه بأخرى فأثنوا عليها خيرا، فقال: وجبت. فقال عمر - رضي الله عنه -: يا رسول الله: ما وجبت؟ قال: " أثنوا شرا، فوجبت النار، وأثنوا خيرا، فوجبت الجنة، وأنتم شهداء الله في الأرض ".','1798','705','0');
INSERT INTO [t5740] VALUES (921,'- فأثنوا خيرا، فقال: وجبت،، ثم مروا بأخرى، فأثنوا شرا، فقال: وجبت، فقال عمر - رضي الله عنه -: ما قولك يا رسول الله: وجبت؟ ، فقال: " أثنيتم على هذا خيرا، فوجبت له الجنة، وأثنيتم على هذا شرا فوجبت له النار وأنتم شهداء الله في','1799','705','0');
INSERT INTO [t5740] VALUES (922,'- من النبوة - حين أراد الله كرامته ورحمة العباد به أن لا يرى رؤيا إلا جاءت كفلق الصبح، فمكث على ذلك ما شاء الله أن يمكث وحبب إليه الخلوة، فلم يكن شيء أحب إليه من أن يخلو وحده ". هذا حديث صحيح ثابت متفق على صحته من حديث أبي بكر محمد بن','1804','705','0');
INSERT INTO [t5740] VALUES (923,'- يقول: " لي خمسة أسماء أنا محمد وأحمد والماحي الذي يمحو الله بي الكفر، وأنا العاقب، وأنا الحاشر الذي يحشر الناس على قدميه ".','1809','705','0');
INSERT INTO [t5740] VALUES (924,'- قال: " لي خمسة أسماء، أنا محمد وأنا أحمد، وأنا الماحس الذي يمحو الله به الكفر، وأنا الحاشر الذي أحشر الناس على قدمي، وأنا العاقب ".','1810','705','0');
INSERT INTO [t5740] VALUES (925,'- يقول: " إن لي أسماء، أنا محمد، وأنا أحمد، وأنا الماحي الذي يمحو الله بي الكفر وأنا الحاشر الذي يحشر الناس على قدمي، وأنا العاقب ". هذا حديث صحيح متفق على صحته من حديث محمد بن جبير بن مطعم، عن أبيه عن النبي -','1811','705','0');
INSERT INTO [t5740] VALUES (926,'- عن إبراهيم بن المنذر الحزامي، عن معن بن عيسى عن مالك، ومنها ما رواه مسلم في " فضائل النبي -','1811','705','0');
INSERT INTO [t5740] VALUES (927,'- " عن أبي خيثمة زهير بن حرب، ومحمد بن يحيى ابن أبي عمر، وإسحاق بن راهويه ثلاثتهم عن سفيان بن عيينة عن الزهري','1812','705','0');
INSERT INTO [t5740] VALUES (928,'- قال: " إن لي أسماء، أنا محمد، وأنا أحمد، وأنا الحاشر الذي يحشر الناس على قدمي، وأنا الماحي الذي يمحى بي الكفر، وأنا العاقب، والعاقب الذي ليس بعده نبي ". فباعتبار العدد إلى الزهري كأن شيخ شيخي سمعه من مسلم في روايته عن عبد الملك','1812','705','0');
INSERT INTO [t5740] VALUES (929,'- قال: " أنا محمد، وأنا أحمد، وأنا الماحي الذي يمحى بي الكفر، وأنا الحاشر الذي يحشر الناس على عقبي، وأنا العاقب، والعاقب الذي ليس بعده نبي ".','1813','705','0');
INSERT INTO [t5740] VALUES (930,'- أخبرناه أبو محمد عبد المجيب بن عبد الله بن زهير الحربي، أنا عبد الله بن أحمد اليوسفي، أنا أبو الحسين بن النقور، أنا أبو طاهر المخلص، أنا رضوان بن أحمد الصيدلاني، أنا أحمد بن عبد الجبار العطاردي، أنا يونس بن بكير، عن عبد الرحمن بن عبد','1813','705','0');
INSERT INTO [t5740] VALUES (931,'- من " صحيحه " - عن إسحاق بن راهويه، عن جرير _ وهو ابن عبد الحميد -، عن الأعمش، عن عمرو بن مرة به، فوقع لنا عاليا.','1814','705','0');
INSERT INTO [t5740] VALUES (932,'- وهو على دين قومه - يقف على بعير له بعرفات من بين قومه، حتى يدفع معهم توفيقا من الله عز وجل له ".','1815','705','0');
INSERT INTO [t5740] VALUES (933,'- واقفا مع الناس بعرفة فقلت: إن هذا من الحمس فما باله ههنا؟ ".','1815','705','0');
INSERT INTO [t5740] VALUES (934,'- وثابت من رواية سفيان بن عيينة عنه، رواه البخاري في " الحج " من صحيحه عن علي بن المديني ومسدد بن مسرهد. رواه مسلم فيه من " صحيحه " عن أبي بكر بن أبي شيبة، وعمرو ابن محمد الناقد. ورواه النسائي في من " صحيحه "، عن قتيبة بن سعيد.','1816','705','0');
INSERT INTO [t5740] VALUES (935,'- قال لجبريل عليه السلام -: " ما يمنعك تزورنا أكثر مما تزورنا؟ فأنزل الله عز وجل {وما نتنزل إلا بأمر ربك. .} الآية ". هذا حديث صحيح من حديث ذر بن عبد الله المرهبي عن سعيد ابن جبير، عن ابن عباس، وثابت من رواية عمر بن ذر، عن','1817','705','0');
INSERT INTO [t5740] VALUES (936,'- بين حاقنتي وذاقنتي ".','1824','705','0');
INSERT INTO [t5740] VALUES (937,'- وإنه لبين حاقنتي وذاقنتي، فلا أكره شدة الموت لأحد أبدا بعد النبي -','1824','705','0');
INSERT INTO [t5740] VALUES (938,'- وهو بين سحري ونحري ". هذا حديث صحيح من حديث عبد الله بن عبيد الله بن أبي مليكة، عن عائشة رضي الله عنها - انفرد به البخاري أيضا، فرواه في " الخمس " من " صحيحه "، عن سعيد بن أبي مريم، عن نافع بن عمر الجمحي نحو ما رويناه، ورواه أيضا في "','1826','705','0');
INSERT INTO [t5740] VALUES (939,'- في بيتي وفي يومي، وبين سحري " قالت عائشة: " ودخل عبد الرحمن بن أبي بكر بسواك فضعف عنه النبي -','1827','705','0');
INSERT INTO [t5740] VALUES (940,'- قالت: " لما اجتمعوا لغسل رسول الله -','1828','705','0');
INSERT INTO [t5740] VALUES (941,'- كما نجرد أمواتنا، أم نغسله وعليه ثيابه؟ . قالت: فأرسل الله تعالى عليهم سنة النوم حتى إن منهم رجل إلا وذقنه في صدره، قالت: إذ نادى مناد من ناحية البيت ما يدرون من هو: اغسلوه في ثيابه، قالت: فوثبوا إليه رجل واحد فغسلوه في قميصه،','1828','705','0');
INSERT INTO [t5740] VALUES (942,'- اختلف القوم فيه، فقال بعضهم: أنجرد رسول الله -','1829','705','0');
INSERT INTO [t5740] VALUES (943,'- وعليه ثيابه، فغسلوه وعليه ثيابه يصبون الماء عليه، ويدلكون من فوقه "، قالت عائشة: - رضي الله عنها -: " وأيم الله لو استقبلت من أمري ما استدبرت ما غسل رسول الله -','1829','705','0');
INSERT INTO [t5740] VALUES (944,'- " ألا أخبركم بأفضل من درجة الصيام والصلاة والصدقة، قالوا: بلى، قال: إصلاح ذات البين، وفساد ذات البين الحالقة ". هذا حديث حسن رواه أبو داود في " سننه " هكذا.','1833','705','0');
INSERT INTO [t5740] VALUES (945,'- " ألا أحدثكم بأفضل من درجة الصيام والصلاة والصدقة؟ ، قلنا: بلى يا رسول الله -','1835','705','0');
INSERT INTO [t5740] VALUES (946,'- قال له: ما اسمك؟ قال: حزن، قال: أنت سهل، قال: لا، السهل يوطأ، ويمتهن ".','1836','705','0');
INSERT INTO [t5740] VALUES (947,'-: " أنت سهل، فأبى، فقال: اسم سمانيه أهلي، قال سعيد: " فعرفت أنا سنلقى بعده حزونة ". شيخنا أبو محمد بن البن هذا، شيخ جليل ثقة، سمع الكثير من جده، ولم يعرف له سماع من غيره. ذكر ما يدل على أن مولده في سنة سبع وثلاثين وخمسمائة،','1838','705','0');
