INSERT INTO [t5776] VALUES (1,'الجزء','1','0','0');
INSERT INTO [t5776] VALUES (2,'-: " إنها لحابستنا؟ " قالت عائشة: إنها قد أفاضت. قال: فلتنفر إذا ".','2','1','0');
INSERT INTO [t5776] VALUES (3,'- قال: " ليس على المرء المسلم في فرسه ولا مملوكه صدقة ".','4','1','0');
INSERT INTO [t5776] VALUES (4,'-، قالوا: قال رسول الله -','6','1','0');
INSERT INTO [t5776] VALUES (5,'- " لبس خاتما نقشه: محمد','8','1','0');
INSERT INTO [t5776] VALUES (6,'-، فكان إذا دخل الخلاء وضعه ". قال: وقال طاوس: إنما لبسه يوما واحدا.','9','1','0');
INSERT INTO [t5776] VALUES (7,'أن يعجل قبل رمضان بصوم يوم أو يومين','16','1','0');
INSERT INTO [t5776] VALUES (8,'- " نهى أن يتنفس في الإناء، وأن يمس الرجل ذكره بيمينه، وأن يستطيب بيمينه ".','18','1','0');
INSERT INTO [t5776] VALUES (9,'- والناس يسألونه، فقال رجل: يا رسول الله {ذبحت قبل أن أحلق، قال: " لا حرج "، وكان يقول: " لا حرج، لا حرج ". فقال رجل: يا رسول الله} ما خير ما أعطي الناس؟ قال: " خلق حسن ". قالوا: نتداوى؟ ! قال: نعم؛ إن الله لم ينزل داء إلا أنزل','19','1','0');
INSERT INTO [t5776] VALUES (10,'ابردوا بالظهر في الحر','23','1','0');
INSERT INTO [t5776] VALUES (11,'- " نهى عن الدباء، والنقير، والمزفت ".','28','1','0');
INSERT INTO [t5776] VALUES (12,'يتبدى','33','1','0');
INSERT INTO [t5776] VALUES (13,'- لم يجعل لها سكنى ولا نفقة. فأخذ الأسود حصى فحصبه ثم قال: تحدث بمثل هذا؟ قال عمر: لا نترك كتاب ربنا وسنة نبينا لقول امرأة حفظت أم نسيت. لها السكنى والنفقة؛ قال الله تبارك وتعالى: {لا تخرجوهن من بيوتهن ولا يخرجن إلا أن يأتين بفاحشة','34','1','0');
INSERT INTO [t5776] VALUES (14,'- صدقة رمضان، صاع من شعير، أو صاع من تمر. قال ابن عمر: فعدل المسلمون ذلك بمدين من قمح.','39','1','0');
INSERT INTO [t5776] VALUES (15,'أن: لا تنتفعوا من الميتة بإهاب ولا عصب','42','1','0');
INSERT INTO [t5776] VALUES (16,'-: " اللحد لنا، والشق لغيرنا.','45','1','0');
INSERT INTO [t5776] VALUES (17,'- خرج على أرض بهم فقال: " لمن هذه؟ " فقالوا: اكتراها فلان. قال: " أما إنه لو منحها إياه كان خيرا له من أن يأخذ عليها خرجا معلوما ".','47','1','0');
INSERT INTO [t5776] VALUES (18,'- قال: " إن أخاكم النجاشي قد مات، فصلوا عليه ". فقام رسول الله -','49','1','0');
INSERT INTO [t5776] VALUES (19,'-: " لا نذر في معصية الله عز وجل، وكفارته كفارة يمين ".','51','1','0');
INSERT INTO [t5776] VALUES (20,'-: " إذا صلى أحدكم فلم يدر كم صلى أثلاثا أم أربعا، فليصل ركعة ثم يسجد سجدتين وهو جالس، فإن كانت الركعة خامسة كانت السجدتان ترغيما للشيطان ".','55','1','0');
INSERT INTO [t5776] VALUES (21,'وهو يبول، فلم يرد عليه السلام حتى توضأ','58','1','0');
INSERT INTO [t5776] VALUES (22,'يقرأ يوم الجمعة في الفجر ب " تنزيل السجدة وهل أتى ة على الإنسان ".','60','1','0');
INSERT INTO [t5776] VALUES (23,'نحوه','63','1','0');
INSERT INTO [t5776] VALUES (24,'- فقال: يا رسول الله!','64','1','0');
INSERT INTO [t5776] VALUES (25,'-: " تم صومك؛ فإن الله أطعمك وسقاك ".','65','1','0');
INSERT INTO [t5776] VALUES (26,'- قال: " رؤيا المؤمن جزء من سبعين جزءا من النبوة ".','67','1','0');
INSERT INTO [t5776] VALUES (27,'- قال: " أيما نخل بيع أصولها، فثمرتها للذي أبرها، إلا أن يشترط المبتاع ".','71','1','0');
INSERT INTO [t5776] VALUES (28,'لم','76','1','0');
INSERT INTO [t5776] VALUES (29,'- سرية، ثم عرسنا، فلم نستيقظ إلا بحر الشمس. فاستيقظ مناستة، فأنسيت أسماءهم، ثم استيقظ أبو بكر، فجعل يمنعهم أن يوقظوه؛ يقول: لعل الله أن يكون احتبسه لحاجته. فجعل أبو بكر يكبر، حتى استيقظ النبي -','76','1','0');
INSERT INTO [t5776] VALUES (30,'- ثم قال لنا: " إن الله قد أتم صلاتكم "، فقالوا: يا رسول الله {إن فلانا لم يصل معنا. قال: فقال: " ما منعك أن تصلي؟ " قال: يا رسول الله} أصابتني جنابة. قال: " فتيمم الصعيد فصله، فإذا قدرت على الماء فاغتسل ". قال: وبعث رسول الله -','77','1','0');
INSERT INTO [t5776] VALUES (31,'- ابتدرناه بالماء. قال: فانطلق علي حتى ارتفع علينا النهار ولم نجد ماء، فإذا شخص، فقال علي: مكانكم حتى ننظر ما هذا. قال: فإذا امرأة بين مزداتين ماء، فقيل لها: يا أمة الله {أين الماء؟ فقالت: لا ماء والله لكم؛ استقيت أمس فسرت نهاري','77','1','0');
INSERT INTO [t5776] VALUES (32,'قالت: أمجنون قريش؟ قالوا: إنه ليس بمجنون، ولكنه رسول الله -','77','1','0');
INSERT INTO [t5776] VALUES (33,'فأمر بالبعير فأنيخ، وحل المزادة من أعلاها، ثم دعا بإناء عظيم فملأ من الماء، ثم دفعه إلى الجنب، فقال: " اذهب فاغتسل "، فقال: " وأيم الله ما ترك لنا من مزادة ولا قربة ولا إداوة ولا إناء إلا ملأه من الماء وهي تنظر. ثم قال: " شدوا','77','1','0');
INSERT INTO [t5776] VALUES (34,'- يغير على من حولهم وهم آمنون، فقالت المرأة لقومها: أي قوم! والله ما أرى هذا الرجل إلا قد شكر لكم ما أخذ من مائكم؛ ألا ترون أنه يغار على ما حولكم وأنتم آمنون لا يغار عليكم؟ هل لكم في خير؟ قالوا: وما هو؟ قالت: نأتي رسول الله -','78','1','0');
INSERT INTO [t5776] VALUES (35,'فأسلموا','78','1','0');
INSERT INTO [t5776] VALUES (36,'- احتجم وأعطى الحجام أجره، ولو كان خبيثا لم يعطه.','80','1','0');
INSERT INTO [t5776] VALUES (37,'-، فأتاه عين المشركين، فجلس يتغدى مع رسول الله -','82','1','0');
INSERT INTO [t5776] VALUES (38,'- وانطلق هاربا. فقال: " من أدركه فله سلبه ". قال سلمة: وكنت رجلا حسن العدو، فاتبعته حتى أخذت بخطام الناقة فقلت: إخ إخ. فبركت به، وقد كرهت أن أعقرها، فضربته ثم احتززت رأسه، فجئت به رسول الله -','82','1','0');
INSERT INTO [t5776] VALUES (39,'- كان يفتح القراءة " بالحمد لله رب العالمين ".','84','1','0');
INSERT INTO [t5776] VALUES (40,'-: " يرحم الله عبدا سمع مقالتي فحملها؛ فرب حامل فقه ليس بفقيه، ورب حامل فقه إلى من هو أفقه منه. ثلاث لا يغل عليهن قلب مؤمن: إخلاص العمل لله، ومناصحة ولاة المسلمين، ولزوم جماعة المسلمين ". ما روى عن النعمان عن أبيه إلا محمد بن كثير','92','1','0');
INSERT INTO [t5776] VALUES (41,'-، لا أحتاج فيهما إلى أحد من الناس: صلاة الإمام خلف رجل من رعيته؛ وقد رأيت رسول الله -','95','1','0');
INSERT INTO [t5776] VALUES (42,'يفعله','95','1','0');
INSERT INTO [t5776] VALUES (43,'قنت في المغرب والفجر','101','1','0');
INSERT INTO [t5776] VALUES (44,'- قال: " لا نذر في معصية الله عز وجل ".','103','1','0');
INSERT INTO [t5776] VALUES (45,'- إليهم وهم رافعو أيديهم في الصلاة؛ فقال: " مالكم رافعو أيديكم كأنها أذناب خيل شمس {} اسكنوا في الصلاة ".','105','1','0');
INSERT INTO [t5776] VALUES (46,'- قال: " لينتهين أقوام يرفعون أبصارهم إلى السماء أو لا ترجع إليهم ".','106','1','0');
INSERT INTO [t5776] VALUES (47,'-: " ألا تصفون كما تصف الملائكة عند ربهم؟ "، قال: " يتمون الصفوف، ويتراصون في الصف ".','108','1','0');
INSERT INTO [t5776] VALUES (48,'على قبر منبوذ فصلى عليه وصليت معه','109','1','0');
INSERT INTO [t5776] VALUES (49,'- صلى؛ فقام في الركعتين، فسبحوا، فلما فرغ من صلاته سجد سجدتين السهو، ثم سلم.','114','1','0');
INSERT INTO [t5776] VALUES (50,'- قال: " لا سمر بعد العشاء إلا لمصل أو مسافر ".','116','1','0');
INSERT INTO [t5776] VALUES (51,'يضحي بالمدينة بالجزور وبالكبش إذا لم يكن جزور','119','1','0');
INSERT INTO [t5776] VALUES (52,'فما نؤمر بشيء إلا فعلناه','120','1','0');
INSERT INTO [t5776] VALUES (53,'- قال: " إذا حضرت الصلاة وحضر العشاء فابدءوا بالعشاء ". وكان ابن عمر يقرب إليه عشاؤه ثم يقام للصلاة، فلا يعجل عنه.','120','1','0');
INSERT INTO [t5776] VALUES (54,'- قال: " صلاة الليل مثنى مثنى، والوتر ركعة، وصلاة المغرب وتر النهار ".','122','1','0');
INSERT INTO [t5776] VALUES (55,'-: " يقطع الصلاة الكلب الأسود، والحمار، والمرأة ".','134','1','0');
INSERT INTO [t5776] VALUES (56,'- استسقى، فاستقبل القبلة وحول رداءه الأيمن الأيسر، وجعل الأيسر الأيمن.','135','1','0');
INSERT INTO [t5776] VALUES (57,'- وقف على الجمرة القصوى، فسئل عن رجل زار البيت قبل أن يرمي. قال: يرمي ولا حرج ". وسئل عن رجل حلق قبل أن يذبح، فقال: يذبح ولا حرج ". فما سئل يومئذ عن شيء قدم ولا أخر إلا قال: " لا حرج ".','138','1','0');
INSERT INTO [t5776] VALUES (58,'-: " لا طلاق إلا بعد ملك، ولا عتاق إلا من بعد ملك ".','140','1','0');
INSERT INTO [t5776] VALUES (59,'فأمر رسول الله','141','1','0');
INSERT INTO [t5776] VALUES (60,'عام الفتح من الثنية العليا التي أعلى مكة','144','1','0');
INSERT INTO [t5776] VALUES (61,'- قال: " اعتدلوا في السجود، ولا يبسط أحدكم ذراعيه بسط الكلب ".','145','1','0');
INSERT INTO [t5776] VALUES (62,'-، فيكون لكم المهنى وعلي الإثم؟ أشهد لسمعت رسول الله -','147','1','0');
INSERT INTO [t5776] VALUES (63,'- " " ليس في حب ولا تمر صدقة [حتى يبلغ خمسة أوسق، ولا فيما دون خمس ذود صدقة، ولا فيما دون خمس أواق صدقة] ".','149','1','0');
INSERT INTO [t5776] VALUES (64,'ومن الجزء','153','1','0');
INSERT INTO [t5776] VALUES (65,'الجزء','153','0','0');
INSERT INTO [t5776] VALUES (66,'كتب إلى الضحاك بن سفيان أن يورث امرأة أشيم الضبابي من ديته','153','65','0');
INSERT INTO [t5776] VALUES (67,'من إناء واحد','154','65','0');
INSERT INTO [t5776] VALUES (68,'-: " إزرة المؤمن إلى أنصاف الساق، وأسفل من ذلك إلى فوق الكعبين، فما كان أسفل من الكعبين ففي النار ".','157','65','0');
INSERT INTO [t5776] VALUES (69,'- قال له: يا رسول الله {ما الإيمان؟ قال: " تشهد أن لا إله إلا الله وأن محمدا رسول الله، وتقيم الصلاة، وتؤتي الزكاة، وتحج البيت، وتحب للمسلم ما تحب لنفسك وأهل بيتك، وتكره للمسلمين ما تكره بنفسك ". فولى الأعرابي، فرجعت به دابته في','162','65','0');
INSERT INTO [t5776] VALUES (70,'-: " لا حلب ولا جنب في الرهان، ولا شعار. ومن انتهب فليس مني ".','163','65','0');
INSERT INTO [t5776] VALUES (71,'كان يسمع الصبي وهو في الصلاة فيخفف','164','65','0');
INSERT INTO [t5776] VALUES (72,'- قال: " إن أدنى أهل النار عذابا من يجعل له نعلان من نار، يغلي منهما دماغه ".','166','65','0');
INSERT INTO [t5776] VALUES (73,'- قال: " من كان يؤمن بالله واليوم الآخر فليكرم ضيفه، من كان يؤمن بالله واليوم الآخر فليكرم جاره، من كان يؤمن بالله واليوم الآخر فليقل خيرا أو يسكت ".','167','65','0');
INSERT INTO [t5776] VALUES (74,'-: " ذروني ما تركتكم؛ فإنما أهلك من كان قبلكم بسؤالهم واختلافهم، فما نهيتكم عنه، فانتهوا، وما أمرتكم به فخذوا ما استطعتم ".','168','65','0');
INSERT INTO [t5776] VALUES (75,'- قال: " إذا تصدقت المرأة من بيت زوجها كتب لها أجر وللزوج مثل ذلك ".','170','65','0');
INSERT INTO [t5776] VALUES (76,'-؛ أنه ركع دون الصف، فقال له النبي -','172','65','0');
INSERT INTO [t5776] VALUES (77,'- يقول: " إذا كان بأحدكم رز فليتوضأ ".','173','65','0');
INSERT INTO [t5776] VALUES (78,'-: " إن بلالا يؤذن بليل، فكلوا واشربوا حتى يؤذن ابن أم مكتوم ".','177','65','0');
INSERT INTO [t5776] VALUES (79,'- يشكو أباه، فقال: " أنت ومالك لأبيك ... ".','180','65','0');
INSERT INTO [t5776] VALUES (80,'نهى عن المثلة','181','65','0');
INSERT INTO [t5776] VALUES (81,'- كان يفتتح القراءة " بالحمد لله رب العالمين ".','183','65','0');
INSERT INTO [t5776] VALUES (82,'- في عمره رمضان، فأفطر النبي -','183','65','0');
INSERT INTO [t5776] VALUES (83,'-[وأتممت] ، فلما قدمنا قلت: يا رسول الله','183','65','0');
INSERT INTO [t5776] VALUES (84,'أحسنت يا عائشة','183','65','0');
INSERT INTO [t5776] VALUES (85,'-: " إن الذي يتخطى رقاب الناس يوم الجمعة ويفرق بين اثنين والإمام يخطب، كالجار قصبه في النار ".','186','65','0');
INSERT INTO [t5776] VALUES (86,'عن بيع الحيوان نسيئة','187','65','0');
INSERT INTO [t5776] VALUES (87,'- قال: " إذا استجمر أحدكم فليستجمر ثلاثا ".','188','65','0');
INSERT INTO [t5776] VALUES (88,'- يقول: " من أوى إلى فراشه طاهرا يذكر الله عز وجل حتى يدركه النعاس، لم ينقلب ساعة من الليل يسأل الله عز وجل فيها شيئا من خير الدنيا والآخرة، إلا أعطاه إياه ".','190','65','0');
INSERT INTO [t5776] VALUES (89,'الألد الخصم','191','65','0');
INSERT INTO [t5776] VALUES (90,'-: " بادروا بالأعمال ستا قبل طلوع الشمس من مغربها، والدجال، والدخان، ودابة الأرض، وخويصة أحدكم، وأمر العامة ".','192','65','0');
INSERT INTO [t5776] VALUES (91,'- قال: " من يعمل سوءا يجز به " في آخر الأمر "، فجدع ابن الزبير، فقلت: إن يك ذا.','193','65','0');
INSERT INTO [t5776] VALUES (92,'ربه في الدنيا','194','65','0');
INSERT INTO [t5776] VALUES (93,'ربه بقلبه','196','65','0');
INSERT INTO [t5776] VALUES (94,'- قال: " لا نذر في معصية الله ".','199','65','0');
INSERT INTO [t5776] VALUES (95,'-: " لا حلب ولا جنب في الرهان، ولا شعار. ومن انتهب فليس مني ".','202','65','0');
INSERT INTO [t5776] VALUES (96,'- كان يقول: " انطلقوا إلى الشهيدة نزورها " وأمر أن يؤذن لها، وأن تؤم أهل دارها في الفرائض ... . ".','207','65','0');
INSERT INTO [t5776] VALUES (97,'كل مسكر حرام','208','65','0');
INSERT INTO [t5776] VALUES (98,'-: " إذا صليت فلا تبزقن بين يديك ولا عن يمينك، ولكن تلقاء شمالك إن كان فارغا أو تحت نعلك ".','211','65','0');
INSERT INTO [t5776] VALUES (99,'-: " ثلاث من فعلهن ثقة بالله واحتسابا، كان حقا على الله أن يعينه وأن يبارك له: من سعى في فكاك رقبة ثقة بالله واحتسابا كان حقا على الله أن يعينه وأن يبارك له، ومن تزوج ثقة بالله واحتسابا كان حقا على الله أن يعينه وأن يبارك له، ومن','213','65','0');
INSERT INTO [t5776] VALUES (100,'أنا لك كأبي زرع لأم زرع','214','65','0');
INSERT INTO [t5776] VALUES (101,'نهى عن المثلة','216','65','0');
INSERT INTO [t5776] VALUES (102,'أمر بقطع الجرس','216','65','0');
INSERT INTO [t5776] VALUES (103,'- في عمرة رمضان، فأفطر النبي -','218','65','0');
INSERT INTO [t5776] VALUES (104,'- وأتممت، فلما قدمنا قلت: يا رسول الله -','218','65','0');
INSERT INTO [t5776] VALUES (105,'-: " أحسنت يا عائشة ". ابن الحامض، والحمد لله، وصلى الله على محمد وآله وسلم تسليما إلى يوم الدين نقلت هذا المنتقى من نسخة ابن عمي ... ... ... ... بن أبي محمد عبد الرحمن بن إبراهيم، وفيه سماعنا على عبد الرزاق بن نصر بن المسلم','218','65','0');
