INSERT INTO [t5848] VALUES (1,'مقدمة المؤلف','1','0','0');
INSERT INTO [t5848] VALUES (2,'وقواما بما عطل من شرائع الإسلام وضيع من أحكام الحلال والحرام وجنبني وبني أحكام الظالمين وعج بي وبهم عن مذاهب المبتدعين واصرف قلوبنا عن اعتقاد المبطلين ونزه ألسنتنا وأسماعنا عن لغو الجاهلين إنك ذو الفضل العظيم والطول الجسيم','1','0','0');
INSERT INTO [t5848] VALUES (3,'باب من لم يكثر من الرواية مخافة الزلل','2','0','0');
INSERT INTO [t5848] VALUES (4,'قال إني أخشى أن يزل لساني بشيء لم يقله رسول الله إني سمعته يقول من كذب علي متعمدا فليتبوأ مقعده من النار وعن أنس ما يمنعني أن أحدثكم حديثا كثيرا إلا أني سمعت رسول الله','2','0','0');
INSERT INTO [t5848] VALUES (5,'ألا أكون أوعى أصحابه عنه ولكني أشهد لسمعته يقول من قال علي ما لم أقل فليتبوأ مقعده من النار وعن عمرو بن دينار عن بعض ولد صهيب قال قال له بنوه يا أبانا مالك لا تحدثنا كما يحدث أصحاب رسول الله','2','0','0');
INSERT INTO [t5848] VALUES (6,'من كذب علي كلف يوم القيامة أن يعقد بين شعيرتين فذاك الذي يمنعني من الحديث وعمرو بن دينار هذا هو قهرمان آل الزبير لا المكي ولم يحدثه عن صهيب غير جعفر بن سليمان عن عمرو وعن مسروق كان عبد الله بن مسعود يأتي عليه الحول','2','0','0');
INSERT INTO [t5848] VALUES (7,'وأنتم تتحدثون لا محالة فإن كنتم متحدثون فتحدثوا بما كان يتحدث به في عهد عمر بن الخطاب فإنه كان يخيف الناس في الله','2','0','0');
INSERT INTO [t5848] VALUES (8,'باب وزر الكاذب على رسول الله','3','0','0');
INSERT INTO [t5848] VALUES (9,'خرج من طريق هشام بن عمار نا محمد بن عيسى بن سميع نا محمد بن أبي الزعيزعة قال سمعت نافعا يقول قال ابن عمر قال رسول الله','3','0','0');
INSERT INTO [t5848] VALUES (10,'تحدثوا عني ولا حرج فإنما أنتم في ذلك كما قلت لكم في بني إسرائيل تحدثوا عنهم ولا حرج فإنكم لن تبلغوا ما كانوا فيه من خير ولا شر ألا ومن قال علي كذبا ليضل به الناس بغير علم فإنه بين عيني جهنم يوم القيامة وما قال من حسنة فالله ورسوله','3','0','0');
INSERT INTO [t5848] VALUES (11,'باب أعظم الكذب هو الكذب على رسول الله','3','0','0');
INSERT INTO [t5848] VALUES (12,'ومن طريق سعيد بن زيد بن عمرو بن نفيل سمعت رسول الله','3','0','0');
INSERT INTO [t5848] VALUES (13,'إن من أفرى الفرى أن','3','0','0');
INSERT INTO [t5848] VALUES (14,'من كذب على نبيه أو على عينيه أو على والديه فإنه لا يريح رائحة الجنة وعن مقاتل بن سليمان عن ابن سيرين عن أبي هريرة قال قال رسول الله','4','0','0');
INSERT INTO [t5848] VALUES (15,'باب اتقاء حديث النبي','4','0','0');
INSERT INTO [t5848] VALUES (16,'عن ابن عباس قال قال رسول الله','4','0','0');
INSERT INTO [t5848] VALUES (17,'فقال مالك إن صاحبكم عاقل أو هالك إن النبي','4','0','0');
INSERT INTO [t5848] VALUES (18,'قال إذا حدثتم عني حديثا تعرفونه ولا تنكرونه','4','0','0');
INSERT INTO [t5848] VALUES (19,'باب تحريم الكذب على رسول الله','5','0','0');
INSERT INTO [t5848] VALUES (20,'خرج من رواية عزة بنت أبي قرصافة عن أبيها قال قال النبي','5','0','0');
INSERT INTO [t5848] VALUES (21,'من روى عني حديثا هو يرى أنه كذب فهو أحد الكاذبين','5','0','0');
INSERT INTO [t5848] VALUES (22,'قال ابن أبي ليلى كنا إذا أتينا زيد بن أرقم فقلنا له حدثنا عن رسول الله يقول إنا قد كبرنا ونسينا والحديث عن رسول الله شديد وقال السائب بن يزيد صحبت عبد الرحمن بن عوف وطلحة بن عبيد الله وسعد بن أبي وقاص والمقداد بن الأسود','5','0','0');
INSERT INTO [t5848] VALUES (23,'روى حماد بن زيد عن ابن عون عن محمد قال كان أنس قليل الحديث عن رسول الله','6','0','0');
INSERT INTO [t5848] VALUES (24,'وقال عمرو بن ميمون الأودي كنت آتي ابن مسعود كل خميس فإذا قال سمعت رسول الله','6','0','0');
INSERT INTO [t5848] VALUES (25,'فلا بأس أن يؤتى به على المعنى وما كان عن رسول الله فيؤتى اللفظ كما قال وقال أبو هريرة لولا آية من كتاب الله ما حدثتكم بشيء ثم تلا إن الذين يكتمون ما أنزلنا من البينات البقرة إلى آخر الآية وقال تحفظت','6','0','0');
INSERT INTO [t5848] VALUES (26,'لشبع بطني قال فلقيت رجلا فقلت له بأي سورة قرأ رسول الله البارحة في العتمة قال لا أدري قال فقلت ألم تشهدها قال بلى قال فقلت ولكني أدري قرأ رسول الله','6','0','0');
INSERT INTO [t5848] VALUES (27,'لم يكن يسرد الحديث كسردكم','6','0','0');
INSERT INTO [t5848] VALUES (28,'يقول من كذب علي متعمدا فليتبوأ مقعده من النار فدعوا أبو هريرة يتبوأ مقعده من النار إن كان هو كذب على رسول الله','7','0','0');
INSERT INTO [t5848] VALUES (29,'خرج من طريق سفيان بن عيينة عن عبد الرحمن بن زيد بن أسلم عن أبيه عن عطاء بن يسار عن أبي سعيد قال استأذنا النبي','7','0','0');
INSERT INTO [t5848] VALUES (30,'وقال هشام بن حسان ما كتبت حديثا قط إلا حديثا واحدا أملاه علي ابن سيرين فقال إذا حفظته فامحه وقال مجاهد عن عبد الله بن عمرو كان عند رسول الله','7','0','0');
INSERT INTO [t5848] VALUES (31,'من كذب علي متعمدا فليتبوأ مقعده من النار فلما خرج القوم قلت لهم كيف تحدثون عن رسول الله وقد سمعتم ما قال وأنتم تنهمكون في الحديث عن رسول الله','7','0','0');
INSERT INTO [t5848] VALUES (32,'استعن بيمينك وكان أنس بن مالك إذا حدث فأكثر الناس في الحديث جاء بمجال له فألقاها إليهم ثم قال هذه أحاديث سمعتها وكتبتها من رسول الله','8','0','0');
INSERT INTO [t5848] VALUES (33,'باب من اختار قلة الحديث وذم من أكثر منه','8','0','0');
INSERT INTO [t5848] VALUES (34,'باب الكاذب يكتب عند الله كذابا أو يهديه إلى كذبه الفجور وما نهي عن الكذب في الجد والهزل وإن الكذاب مخلاف لموعده','9','0','0');
INSERT INTO [t5848] VALUES (35,'قال إن الرجل ليصدق ويتحرى الصدق حتى يكتب عند الله صديقا وإن الرجل ليكذب ويتحرى الكذب حتى يكتب عند الله كذابا','9','0','0');
INSERT INTO [t5848] VALUES (36,'عليكم بالصدق فإنه يهدي إلى البر والبر يهدي إلى الجنة ولا يزال الرجل يصدق حتى يكتب عند الله صديقا وإياكم والكذب فإن الكذب يهدي إلى الفجور والفجور يهدي إلى النار ولا يزال الرجل يكذب حتى يكتب عند الله كذابا قال ابن عدي','10','0','0');
INSERT INTO [t5848] VALUES (37,'باب الكذاب يكون مجانبا للإيمان وأن الكذب يكون من مهانة النفس وأن الظريف لا يكذب','10','0','0');
INSERT INTO [t5848] VALUES (38,'قال إن الكذب باب من أبواب النفاق وإن آية النفاق أن يكون الرجل جدلا خصما وقال إسماعيل عن قيس عن أبي بكر','10','0','0');
INSERT INTO [t5848] VALUES (39,'قال يطبع ابن آدم على كل شيء إلا الخيانة والكذب هذا غريب عن الأعمش لا أعلمه رواه عنه غير علي بن هاشم ولا عن علي غير داود بن رشيد وقال بقية حدثني طلحة القرشي عن جعفر بن الزبير عن القاسم عن أبي أمامة قال قال رسول','11','0','0');
INSERT INTO [t5848] VALUES (40,'إن في المعاريض لمندوحة عن الكذب وقال محمد بن كعب القرظي لا يكذب الكاذب إلا من مهانة نفسه عليه وقال محمد بن سيرين الكلام أوسع من أن يكذب ظريف','11','0','0');
INSERT INTO [t5848] VALUES (41,'قال ابن المبارك وشعبة وحماد بن زيد التدليس كذب وقال أبو أسامة خرب الله بيوت المدلسين ما هم عندي إلا كذابين وقال الشافعي قال شعبة التدليس أخو الكذب وقال شعبة والله لأن أزني أحب إلي من أن أدلس','11','0','0');
INSERT INTO [t5848] VALUES (42,'المتشبع بما لم يعط كلابس ثوبي زور وقال أبو الأسود الديلي إذا سرك أن يكذب صاحبك فلقنه وقال قتادة إذا أردت أن يكذبك صاحبك فلقنه وقال ابن سيرين إذا أردت أن أكذب لك فلقني وقال ابن أبي مليكة إذا سرك أن يكذب','12','0','0');
INSERT INTO [t5848] VALUES (43,'روى شهر بن حوشب عن أسماء بنت يزيد قالت سمعت رسول الله','12','0','0');
INSERT INTO [t5848] VALUES (44,'إن أعظم الخطيئة عند الله','12','0','0');
INSERT INTO [t5848] VALUES (45,'كبرت خيانة أن تحدث أخاك حديثا هو لك مصدق وأنت له كاذب','13','0','0');
INSERT INTO [t5848] VALUES (46,'قال أبو يوسف من طلب الدين بالكلام تزندق ومن طلب غريب الحديث كذب ومن طلب المال بالكمياء أفلس وقال أحمد لا تكتبوا هذه الأحاديث الغرائب فإنها مناكير وعامتها عن الضعفاء وقال أبو نعيم إذا كان الكتاب مشجوجا كان من علامة','13','0','0');
INSERT INTO [t5848] VALUES (47,'يقول آفة الحديث الكذب وآفة العلم النسيان في حديث ذكره ورواه عن عثمان بن سعيد الزيات أبو كريب وقال ابن أبي ليلى إذا كنت كذابا فكن ذاكرا','14','0','0');
INSERT INTO [t5848] VALUES (48,'وما يتوقع من ظهور الشياطين للناس فيحدثون ويفتنون','14','0','0');
INSERT INTO [t5848] VALUES (49,'يكون في آخر الزمان دجالون كذابون يحدثونكم من لأحاديث بما لم تسمعوا أنتم ولا آباؤكم فإياكم وإياهم لا يضلوكم ولا يفتنوكم وقال أبو العالية لا تقوم الساعة حتى يمشي إبليس في الطرق والأسواق فيقول حدثني فلان عن فلان عن نبي','14','0','0');
INSERT INTO [t5848] VALUES (50,'والناس يكتبون وقال شعبة إذا حدث المحدث ولم تر وجهه فلا تصدقه لعله شيطان قد تصور في صورته يقول نا وأنا','15','0','0');
INSERT INTO [t5848] VALUES (51,'قال أبو مجلز قرأ أبي من الذين استحق عليهم الأوليان المائدة فقال له عمر كذبت فقال له أنت أكذب فقيل له تكذب أمير المؤمنين فقال أنا أشد تعظيما لأمير المؤمنين منك فقال إني كرهت أن أصدق في تكذيب كتاب الله','15','0','0');
INSERT INTO [t5848] VALUES (52,'قال عبد الله بن الحارث اعتمرت مع علي في زمن عمر أو في زمن عثمان','15','0','0');
INSERT INTO [t5848] VALUES (53,'قالوا أجل عن ذلك جئناك نسألك قال كذب أحدث الناس عهدا برسول الله قثم بن العباس','16','0','0');
INSERT INTO [t5848] VALUES (54,'قال سعيد بن جبير قلت لابن عباس إن نوفا البكالي يزعم أن موسى صاحب بني إسرائيل ليس صاحب الخضر فقال كذب عدو الله حدثني أبي بن كعب أن رسول الله','16','0','0');
INSERT INTO [t5848] VALUES (55,'إذ لم يكذب عليه فأما إذا ركب الناس الصعب والذلول تركنا الحديث عنه','16','0','0');
INSERT INTO [t5848] VALUES (56,'قال أبو هريرة أتيت الطور فوجدت بها كعب الأحبار فذكره بطوله فلقيت عبد الله بن سلام فذكرت له أني قلت لكعب قال رسول الله','16','0','0');
INSERT INTO [t5848] VALUES (57,'وعبادة بن الصامت','17','0','0');
INSERT INTO [t5848] VALUES (58,'يقول خمس صلوات كتبهن الله على العباد من أتى بهن لم يضيع منهن شيئا استخفافا بحقهن كان له عند الله عهد أن يدخله الجنة ومن لم يأت بهن فليس له عند الله عهد إن شاء عذبه وإن شاء رحمه','17','0','0');
INSERT INTO [t5848] VALUES (59,'قال البخاري نا مسدد نا عبد الواحد نا عاصم قال سألت أنس بن مالك عن القنوت فقال قد كان القنوت قلت قبل الركوع أو بعده قال قبله قال فإن فلانا أخبرني عنك أنك قلت بعد الركوع فقال كذب إنما قنت رسول الله','17','0','0');
INSERT INTO [t5848] VALUES (60,'وعائشة أم المؤمنين رضي الله عنها','17','0','0');
INSERT INTO [t5848] VALUES (61,'يصبح فيوتر','17','0','0');
