INSERT INTO [t5848] VALUES (68,'قال إياكم ومعبد الجهني فإنه ضال مضل','19','0','0');
INSERT INTO [t5848] VALUES (69,'ومحمد بن سيرين','20','0','0');
INSERT INTO [t5848] VALUES (70,'وأنس بن سيرين','20','0','0');
INSERT INTO [t5848] VALUES (71,'وأبو العالية فيروز بن مهران الرياحي','20','0','0');
INSERT INTO [t5848] VALUES (72,'ومالك بن دينار','20','0','0');
INSERT INTO [t5848] VALUES (73,'والشعبي','21','0','0');
INSERT INTO [t5848] VALUES (74,'إبراهيم أو مسروق','21','0','0');
