INSERT INTO [t5848] VALUES (77,'سباب المسلم فسوق وقتاله كفر قال لا أتهم هؤلاء ولكني أتهم أبا وائل','21','0','0');
INSERT INTO [t5848] VALUES (78,'وسعد بن إبراهيم الزهري','22','0','0');
INSERT INTO [t5848] VALUES (79,'والزهري محمد بن مسلم','22','0','0');
INSERT INTO [t5848] VALUES (80,'محله من العلم','22','0','0');
INSERT INTO [t5848] VALUES (81,'فأبو بكر وعمر وعثمان وأكثرهم فقها وأعلمهم بما مضى من أمر الناس فابن المسيب وأما أغزرهم حديثا فعروة ولا تشاء أن تفجر من عبيد الله بحرا إلا فجرته قال عراك أما أعلمهم عندي جميعا فابن شهاب لأنه قد جمع علمهم جميعا إلى علمه وقال','23','0','0');
INSERT INTO [t5848] VALUES (82,'قال رجاء بن جميل الأيلي سألت ربيعة عن حديث فقال ما علمت أني','23','0','0');
INSERT INTO [t5848] VALUES (83,'وأيوب بن أبي تميمة السختياني','24','0','0');
INSERT INTO [t5848] VALUES (84,'ومن فضائله','24','0','0');
INSERT INTO [t5848] VALUES (85,'وحديثه بكى حتى نرحمه ونقول ما رأينا أحدا أرق منه وقال حماد بن زيد الحمد لله الذي أكرمني بمجالسة أيوب وقال الحسين بن واقد ما رأيت أفقه أو قال أثبت من أيوب وقال أيوب إذا ذكر الصالحون كنت منهم بمعزل وقال إنه','25','0','0');
INSERT INTO [t5848] VALUES (86,'قال أبو بكر بن عياش كنا نسمي الأعمش سيد المسلمين وكنا نمر به إذا انصرفنا من عند المشيخة فكان يقول لنا عند من كنتم اليوم فنقول عند فلان فيقول جيد ويعقد ثلاثين ثم يقول عند من كنتم اليوم فنقول عند فلان فيقول','25','0','0');
INSERT INTO [t5848] VALUES (87,'ومن فضائله','26','0','0');
INSERT INTO [t5848] VALUES (88,'وأبو حصين عثمان بن عاصم الأسدي','26','0','0');
INSERT INTO [t5848] VALUES (89,'جلالته ومحله وحفظه','27','0','0');
INSERT INTO [t5848] VALUES (90,'ومن تابعي التابعين','27','0','0');
