INSERT INTO [t5848] VALUES (92,'شدة حرصه وحسده في العلم','28','0','0');
INSERT INTO [t5848] VALUES (93,'نهى عن بيع الولاء وعن هبته فقال شعبة لوددت أن عبد الله ابن دينار أذن لي حتى كنت أقوم إليه فأقبل رأسه ومر أبو عوانة على شعبة وهو عند عمرو بن مرة فقال من هذا الشيخ فقال شعبة شيخ يروي أبيات للحطيئة فلما مات عمرو قال شعبة لأبي','28','0','0');
INSERT INTO [t5848] VALUES (94,'مسامحته في الرجال','29','0','0');
INSERT INTO [t5848] VALUES (95,'من سلم له من الأئمة كلامه في الرجال لمعرفته بهم','29','0','0');
INSERT INTO [t5848] VALUES (96,'تعبه في الحديث وزهده وأدبه وغير ذلك','30','0','0');
INSERT INTO [t5848] VALUES (97,'وسفيان بن سعيد الثوري','31','0','0');
INSERT INTO [t5848] VALUES (98,'حرصه على العلم وحسده فيه','33','0','0');
INSERT INTO [t5848] VALUES (99,'من سلم للثوري من الأئمة كلامه في الرجال','33','0','0');
INSERT INTO [t5848] VALUES (100,'الأوزاعي عبد الرحمن بن عمرو','33','0','0');
INSERT INTO [t5848] VALUES (101,'ومالك بن أنس أبو عبد الله','34','0','0');
INSERT INTO [t5848] VALUES (102,'وأنا قائم وقال أحمد ويحيى بن معين لا تبالي عن رجل حدث عنه مالك إلا أن يحيى قال إلا رجلا أو رجلين وقال ابن المديني كل مديني لم يحدث عنه مالك ففي حديثه شيء لا أعلم مالكا ترك إنسانا إلا إنسانا في حديثه شيء وقال بشر بن','35','0','0');
INSERT INTO [t5848] VALUES (103,'ولا من شيخ له عبادة وفضل إذا كان لا يعرف ما يحدث وقال مطرف بن عبد الله اليساري سمعت مالكا يقول أدركت بهذا البلد مشيخة لهم فضل وعبادة يحدثوا ما سمعت من واحد منهم حديثا قط قيل له ولم يا أبا عبد الله قال لم يكونوا يعرفون ما','35','0','0');
INSERT INTO [t5848] VALUES (104,'وهشيم بن بشير','36','0','0');
INSERT INTO [t5848] VALUES (105,'وسفيان بن عيينة','37','0','0');
INSERT INTO [t5848] VALUES (106,'ويحيى بن سعيد القطان','37','0','0');
INSERT INTO [t5848] VALUES (107,'يقول بلغك عني حديث وقع في وهمك أنه عني غير صحيح فلم تنكره وقال يحيى إذا كان الشيخ يثبت على شيء واحد خطأ كان أو صوابا فلا بأس به وإذا كان الشيخ كل شيء يقال له فليس يقول بشيء وقال الفلاس قال لي يحيى بن سعيد لا تكتب عن','38','0','0');
INSERT INTO [t5848] VALUES (108,'قال أبو إسحاق الفزاري ابن المبارك عندنا إمام المسلمين وقال ابن عيينة ما رأيت أحدا ممن قدم علينا مثل عبد الله بن المبارك ويحيى بن أبي زائدة وقال ابن مهدي ما رأيت مثل ابن المبارك وقال عبد الله بن محمد الضعيف سمعت ابن','38','0','0');
INSERT INTO [t5848] VALUES (109,'وجرير بن عبد الحميد','40','0','0');
INSERT INTO [t5848] VALUES (110,'والفضل بن موسى السيناني','40','0','0');
INSERT INTO [t5848] VALUES (111,'يلاحظ في الصلاة يمينا وشمالا لا يلوي عنقه خلف ظهره فقال أبو خيثمة إن هذا حديث يرويه وكيع مرسل فقال له يحيى تدري عمن يحدثك عن أمير المؤمنين الفضل بن موسى وقال أبو نعيم الفضل بن موسى أثبت من ابن المبارك','40','0','0');
INSERT INTO [t5848] VALUES (112,'قال ابن معين من فضل عبد الرحمن بن مهدي على وكيع فعليه لعنة الله والملائكة والناس أجمعين وقال ما رأيت أحفظ من وكيع وقال عبد الرزاق رأيت الثوري وابن عيينة ومعمرا ومالكا ورأيت ورأيت فما رأت عيناي قط مثل وكيع وقال','40','0','0');
INSERT INTO [t5848] VALUES (113,'وعبد الرحمن بن مهدي','41','0','0');
INSERT INTO [t5848] VALUES (114,'وسفيان الرأس','42','0','0');
INSERT INTO [t5848] VALUES (115,'والمظفر بن مدرك أبو كامل','42','0','0');
INSERT INTO [t5848] VALUES (116,'والشافعي محمد بن إدريس','42','0','0');
INSERT INTO [t5848] VALUES (117,'حدثوا عن بني إسرائيل ولا حرج وحدثوا عني ولا تكذبوا علي قال معناه أن الحديث إذا حدثت به فأديته على ما سمعت حقا كان أو غير حق لم يكن عليك حرج والحديث عن الرسول لا ينبغي أن يحدث به ثقة إلا عن ثقة وقد قيل من حدث حديثا وهو','43','0','0');
INSERT INTO [t5848] VALUES (118,'وأبو مسهر عبد الأعلى بن مسهر الغساني','44','0','0');
INSERT INTO [t5848] VALUES (119,'وسعيد بن منصور أبو عثمان الخراساني','44','0','0');
INSERT INTO [t5848] VALUES (120,'وطبقة بعدهم منهم أحمد بن حنبل','44','0','0');
INSERT INTO [t5848] VALUES (121,'ولولاه لهلك الناس وقال أحمد ثلاثة كتب ليس لها أصول المغازي والملاحم والتفسير وقال والله لوددت أني أنجو من هذا الأمر كفافا لا علي ولا لي والله لقد أعطيت المجهود من نفسي','45','0','0');
INSERT INTO [t5848] VALUES (122,'قال سفيان بن عيينة تلومني على حب علي والله لما أتعلم منه أكثر مما يتعلم مني وقال أبو عبيد القاسم بن سلام انتهى الحديث إلى أربعة إلى أبي بكر بن أبي شيبة وأحمد بن حنبل ويحيى بن معين وعلي بن المديني وأبو بكر أسردهم له','45','0','0');
INSERT INTO [t5848] VALUES (123,'قال هارون بن معروف قدم علينا بعض الشيوخ من الشام وكنت أول من بكر فدخلت عليه فسألته أن يملي علي شيئا فأخذ الكتاب يملي علي فإذا إنسان يدق الباب فقال الشيخ من هذا قال أحمد بن حنبل فأذن له والشيخ على حالته والكتاب في يده لا','45','0','0');
INSERT INTO [t5848] VALUES (124,'أنه مسح على الجبائر باطل ما حدث به معمر قط وقال ما رأيت الكذب أنفق منه ببغداد وقال كتبت بيدي ستمائة ألف حديث واجتمع أحمد ويحيى وعلي عند عفان فأتى بصك فشهدوا فيه فقال عفان أما أنت يا أحمد فضعيف في إبراهيم بن سعد','46','0','0');
INSERT INTO [t5848] VALUES (125,'قال الحسن بن علي بن بحر قدم دحيم بغداد سنة فرأيت أبي ويحيى وأحمد وخلف بن سالم بين يديه كالصبيان','46','0','0');
