INSERT INTO [t5848] VALUES (131,'وأبو بكر بن أبي شيبة','48','0','0');
INSERT INTO [t5848] VALUES (132,'وعمرو بن علي الفلاس','48','0','0');
INSERT INTO [t5848] VALUES (133,'وطبقة أخرى تليهم منهم البخاري محمد بن إسماعيل','48','0','0');
INSERT INTO [t5848] VALUES (134,'وأبو زرعة عبيد الله بن عبد الكريم الرازي','49','0','0');
INSERT INTO [t5848] VALUES (135,'وجدت له أصلا ثابتا ما خلا أربعة أحاديث وقال أبو بكر بن أبي شيبة ما رأيت أحدا أحفظ من أبي زرعة الرازي وقال ابن عدي سمعت أبي يقول كنت بالري فحلف رجل بطلاق امرأته أن','49','0','0');
INSERT INTO [t5848] VALUES (136,'وأبو حاتم محمد بن إدريس الرازي','50','0','0');
INSERT INTO [t5848] VALUES (137,'ومحمد بن مسلم بن وارة الرازي','50','0','0');
INSERT INTO [t5848] VALUES (138,'محمد بن عوف الحمصي','50','0','0');
INSERT INTO [t5848] VALUES (139,'ويزيد بن عبد الصمد وعبد الرحمن بن عمرو أبو زرعة الدمشقيان','51','0','0');
INSERT INTO [t5848] VALUES (140,'ومحمد بن يحيى بن كثير يلقب يؤيؤ','51','0','0');
INSERT INTO [t5848] VALUES (141,'وبعد هؤلاء طبقة قال ابن عدي أدركت أيامهم منهم إبراهيم بن أورمة أبو إسحاق الأصبهاني','51','0','0');
INSERT INTO [t5848] VALUES (142,'وعبيد العجل الحسين بن محمد بن حاتم أبو محمد','52','0','0');
INSERT INTO [t5848] VALUES (143,'وصالح بن محمد أبو علي البغدادي يلقب جزرة','52','0','0');
INSERT INTO [t5848] VALUES (144,'وعبد الله بن أحمد بن حنبل أبو عبد الله','52','0','0');
INSERT INTO [t5848] VALUES (145,'موسى بن هارون الحمال','53','0','0');
INSERT INTO [t5848] VALUES (146,'قال ابن عدي وفي هذه الطبقة ممن أدركتهم وكتبت عنهم أو يقاربونهم في الإسناد والمعرفة ومحلهم محل من ذكرت في طبقتهم وكلهم يجوز لهم الكلام في الرجال عبدان الأهوازي','53','0','0');
INSERT INTO [t5848] VALUES (147,'والنسائي أبو عبد الرحمن أحمد بن شعيب','53','0','0');
INSERT INTO [t5848] VALUES (148,'وعبد الله بن محمد بن سيار الفرهاذاني','54','0','0');
INSERT INTO [t5848] VALUES (149,'والحسين بن محمد بن مودود أبو عروبة الحراني','54','0','0');
INSERT INTO [t5848] VALUES (150,'وعلي بن سعيد بن بشير عليك الرازي','54','0','0');
INSERT INTO [t5848] VALUES (151,'من مدح الحجاز والعراق ورواتهما وذم البصرة والكوفة وبغداد ورواتهم','54','0','0');
INSERT INTO [t5848] VALUES (152,'ما يخاف على هذه الأمة من الهلكة إذا رووا عن غير الثقات','55','0','0');
INSERT INTO [t5848] VALUES (153,'هلاك أمتي في العصبية والقدرية والرواية عن غير ثبت ورواه بقية عن عبد الله بن سمعان عن عمرو بن دينار عن ابن عباس ورواه أيضا عن أبي العلاء عن مجاهد قال ابن عدي رواة هذا الحديث شوشوا هذا الإسناد وبلاء هذه الأحاديث من','55','0','0');
INSERT INTO [t5848] VALUES (154,'فقال العنوا أصحاب العصب قلنا عليهم لعنة الله والملائكة والناس أجمعين فمن هم يا رسول الله قال أصحاب العصبية والقدرية والرواية عن غير ثبت من مات تحت راية عصبية أو غدا إلى عصبية يحشر مع أعراب الجاهلية قال ابن عدي','56','0','0');
INSERT INTO [t5848] VALUES (155,'قال يحيى بن سعيد القطان ما رأيت الصالحين في شيء أشد فتنة منهم في الحديث وقال ما رأيت الكذب في أحد أكثر منه فيمن ينسب إلى الخير وقال مرة ما رأيت الصالحين أكذب منهم في الحديث وقال أبو عاصم النبيل ما رأيت','56','0','0');
INSERT INTO [t5848] VALUES (156,'قال نصر بن علي قلت للأصمعي ما تحفظ من كلام العرب في الكذب','56','0','0');
INSERT INTO [t5848] VALUES (157,'ذكر القوم الذين يميزون الرجال وصفتهم','57','0','0');
INSERT INTO [t5848] VALUES (158,'يحمل هذا العلم من كل خلف عدوله ينفون عنه تحريف الغالين وانتحال المبطلين وتأويل الجاهلين وقال إياس بن معاوية إن للحديث فرسانا كفرسان الخيل وقال الوليد بن يزيد لربيعة لم تركت الرواية قال يا أمير المؤمنين تقادم','57','0','0');
INSERT INTO [t5848] VALUES (159,'نهي الرجل أن يأخذ دينه إلا عمن يرضاه لأن العلم دين','58','0','0');
INSERT INTO [t5848] VALUES (160,'إن هذا العلم دين فلينظر أحدكم ممن يأخذ دينه وقال ابن سيرين إن هذا العلم دين فانظروا عمن تأخذون دينكم وروي عن أبي هريرة والضحاك وأيوب مثله وقال ابن عباس إن هذا العلم دين فأجيزوا الحديث ما أسند إلى نبيكم وإلى','58','0','0');
INSERT INTO [t5848] VALUES (161,'روى صالح بن حسان عن محمد بن كعب القرظي عن ابن عباس قال قال رسول الله','58','0','0');
INSERT INTO [t5848] VALUES (162,'صفة من لا يؤخذ عنه العلم','59','0','0');
INSERT INTO [t5848] VALUES (163,'صفة من يؤخذ عنه العلم','60','0','0');
INSERT INTO [t5848] VALUES (164,'سمعناه منه ما سمعناه ومنه ما حدثنا أصحابنا ونحن لا نكذب وكان حميد الطويل يقول عن أنس بن مالك أنه ربما سئل إذا حدث أنت','60','0','0');
INSERT INTO [t5848] VALUES (165,'فيغضب ثم يقول ما كل ما نحدثكم سمعناه من رسول الله','61','0','0');
INSERT INTO [t5848] VALUES (166,'ولو كنت تسند لنا إلى من حدثك فقال الحسن إنا والله ما كذبنا ولا كذبنا ولقد غزوت إلى خراسان','61','0','0');
INSERT INTO [t5848] VALUES (167,'إذا رأيت من أخيك ثلاث خصال فارجه الحياء والأمانة والصدق وإذا لم ترها منه فلا ترجه تمت المقدمة والله ولي الإعانة','62','0','0');
INSERT INTO [t5848] VALUES (168,'حرف الهمزة من اسمه أحمد','63','0','0');
INSERT INTO [t5848] VALUES (169,'في الهميان للمحرم فقال لا أعرفه','63','0','0');
INSERT INTO [t5848] VALUES (170,'الدين النصيحة وقال أبو داود ليس هو كما يتوهمون الناس يعني ليس بذلك الجلالة وقال أبو نعيم الفضل بن دكين ما قدم علينا أحد أعلم بحديث أهل الحجاز من هذا الفتى يريد أحمد هذا وقال أبو زرعة الدمشقي قدمت العراق فسألني أحمد','68','0','0');
INSERT INTO [t5848] VALUES (171,'فجعلا يتذاكران ولا يغرب أحدهما على الآخر وقال موسى بن سهل قدم أحمد هذا الرملة فحدثنا بألوف من حفظه وقال ابن عدي وأحمد بن صالح من حفاظ الحديث وبخاصة حديث الحجاز ومن المشهورين بمعرفته حدث عنه البخاري مع شدة استقصائه ومحمد','68','0','0');
INSERT INTO [t5848] VALUES (172,'قال لعلي أنت سيد في الدنيا سيد في الآخرة قال ابن عدي وأبو الأزهر هذا شبيه بصورة أهل الصدق عند الناس وقد','71','0','0');
INSERT INTO [t5848] VALUES (173,'إذا روى ويكذب في حديث الناس إذا حدث عنهم قال وحدث عن حرملة عن الشافعي بحكايات بواطيل وروى أحاديث مناكير قال وهو كذوب','73','0','0');
INSERT INTO [t5848] VALUES (174,'من اسمه إبراهيم','77','0','0');
INSERT INTO [t5848] VALUES (175,'وهذا عن عمر وقال ابن المثنى سمعت يحيى بن سعيد يحدث عن سفيان عن إبراهيم الهجري وكان عبد الرحمن يحدث عن سفيان عنه وقال ابن معين ضعيف ليس بشيء وقال النسائي ضعيف وقال ابن عدي حدث عنه شعبة والثوري وغيرهما وأحاديثه','78','0','0');
INSERT INTO [t5848] VALUES (176,'قال إن الله قرأ طه ويس قبل أن يخلق آدم بألف عام فلما سمعت الملائكة القرآن قالت طوبى لأمة ينزل هذا عليها وطوبى لأجواف تحمل هذا وطوبى لألسن تكلم بهذا','80','0','0');
INSERT INTO [t5848] VALUES (177,'كلكم راع وكلكم مسئول وهو وهم وكان ابن عيينة يرويه مرسلا قال ابن عدي لا أعلم أنكر عليه إلا هذا وباقي حديثه عن ابن عيينة وأبي معاوية وغيرهما من الثقات مستقيم وهو عندنا من أهل الصدق','93','0','0');
INSERT INTO [t5848] VALUES (178,'فكذبه الناس وواجهوه به وإبراهيم أحاديثه مستقيمة سوى هذا الحديث وقد فتشت في حديثه الكثير فلم أر له حديثا منكرا يكون','96','0','0');
INSERT INTO [t5848] VALUES (179,'من اسمه إسماعيل','97','0','0');
INSERT INTO [t5848] VALUES (180,'أول من صنع الحمامات لا يتابع عليه قاله البخاري وقال ابن عدي يعرف بحديث الحمامات','100','0','0');
INSERT INTO [t5848] VALUES (181,'كان يقرأ بسم الله الرحمن الرحيم ومرة قال معتمر حدثني إسماعيل بن حماد عن أبي خالد عن ابن عباس أن رسول الله','105','0','0');
INSERT INTO [t5848] VALUES (182,'بامرأة وخلى سبيلها ولم يصح وقال أبو داود رأيت إسماعيل بن زكريا يجلس بين يدي الأعمش ونحن جلوس ناحية وقال ابن عدي ولإسماعيل من الحديث صور صالح وهو حسن الحديث يكتب حديثه','108','0','0');
INSERT INTO [t5848] VALUES (183,'قال الرهن بما فيه وقال ابن عدي لا أعرفه إلا بهذا الحديث وهو حديث معضل بهذا الإسناد','109','0','0');
INSERT INTO [t5848] VALUES (184,'من اسمه إسحاق','111','0','0');
INSERT INTO [t5848] VALUES (185,'لعلي بن أبي طالب كلها في الجماع وكيف يجامع إذا جامع وذلك من وضعه وإسحاق بين الأمر في الضعفاء وهو ممن يضع الحديث','112','0','0');
INSERT INTO [t5848] VALUES (186,'يقول من أعتق مملوكا فليس للمملوك من ماله شيء قاله البخاري قال ابن عدي إسحاق هذا يعرف بهذا الحديث وليس له فيما أعرف إلا حديثان أو ثلاثة','113','0','0');
INSERT INTO [t5848] VALUES (187,'بأحاديث لا يتابع عليها وهو أشهر من أبيه إسحاق وأكثر رواية','113','0','0');
INSERT INTO [t5848] VALUES (188,'مقدار عشرين حديثا كلها غير محفوظة وله أحاديث صالحة','114','0','0');
INSERT INTO [t5848] VALUES (189,'وعامتها غير محفوظة قاله ابن عدي','115','0','0');
INSERT INTO [t5848] VALUES (190,'كل معروف صدقة وبسنده قال رسول الله','115','0','0');
INSERT INTO [t5848] VALUES (191,'كان يطوف على نسائه بغسل واحد قال ابن عدي لا أعرفه إلا بهذا الحديث ومتنه مشهور إلا أني أرتاب في لقيه حميدا','116','0','0');
INSERT INTO [t5848] VALUES (192,'بهذا الإسناد أحاديث موضوعة وضعها هو','117','0','0');
INSERT INTO [t5848] VALUES (193,'قال ابن عدي لم أجد له غير هذا الحديث الواحد وهو من هذا الطريق لا يتابع عليه','120','0','0');
INSERT INTO [t5848] VALUES (194,'في الدعاء لا يتابع عليه رواه حماد بن زيد قاله البخاري','121','0','0');
