INSERT INTO [t5848] VALUES (196,'من اسمه أشعث','123','0','0');
INSERT INTO [t5848] VALUES (197,'من اسمه أبان','126','0','0');
INSERT INTO [t5848] VALUES (198,'من دعي فلم يجب فقد عصى الله ورسوله ومن دخل من غير دعوة دخل سارقا وخرج مغيرا قال ابن عدي لا يعرف إلا بهذا الحديث وهذا الحديث معروف به وله غير هذا الحديث لعله حديثين أو ثلاثة وليس له أنكر من هذا','128','0','0');
