INSERT INTO [t5848] VALUES (236,'وقال أحمد سألت يحيى بن معين عن جرير فقال ليس به بأس قلت إنه يحدث عن قتادة عن أنس أحاديث مناكير قال ليس بشيء هو عن قتادة ضعيف وقال الدارمي قلت ليحيى كيف حديث جرير بن حازم قال هو ثقة وقال أبو سلمة موسى بن','180','0','0');
