INSERT INTO [t5880] VALUES (1,'ذكر خلافة عبد الله بن هارون الرشيد المأمون','1','0','0');
INSERT INTO [t5880] VALUES (2,'يوم دخل المدينة وكنا حين تذكر منك نعمي يجل الوصف عن وصف المقال بحمد الله حين حللت فينا بنورك نجتلي ظلم الضلال وكنت كرامة نزلت علينا بأسعد طائر وبخير حال','5','0','0');
INSERT INTO [t5880] VALUES (3,'وأذكروا الفرائض والشرائع في الأسلام وتناظروا بعد ذلك فأعاد محمد لعلى بمثل المقالة الأولى فقال على والله لولا جلالة مجلسة وما وهب الله من خلافته','14','0','0');
INSERT INTO [t5880] VALUES (4,'ذكر خروج عبد الله بن طاهر إلى مضر لمحاربة نصر بن شبث واستخلافه إسحاق بن إبراهيم على مدينة السلام','17','0','0');
INSERT INTO [t5880] VALUES (5,'والمثابرة على فرائضه خلائقه واقتفاء آثار السلف الصالح من بعده وإذا ورد عليك أمر فاستعن عليه باستخارة الله وتقواه ولزوم ما أنزل الله في كتابه من أمره ونهيه وحلاله وحرامه وائتمام ما جاءت به الآثار عن النبي','18','0','0');
INSERT INTO [t5880] VALUES (6,'سيرة المأمون ببغداد وطرائف من أخباره وأخبار أصحابه وقواده وكتابه وحجابه','28','0','0');
INSERT INTO [t5880] VALUES (7,'مع إجماعنا على أصل التنزيل واتفاقنا على عين الخبر فإن كان الذي أوحشك هذا حتى أنكرت كتابنا فقد ينبغي أن يكون اللفظ بجميع ما في التوارة والإنجيل متفقا على تأويله كالاتفاق على تنزيله ولا يكون بين الملتين من اليهود والنصارى اختلاف في شيء','30','0','0');
INSERT INTO [t5880] VALUES (8,'الطاهرة وصانوا أحسابهم','36','0','0');
INSERT INTO [t5880] VALUES (9,'أو قد وضع يده عليه أو شرب فيه أو مسه وما هو عندي بثقة ولا دليل على صدق الرجل إلا أني بفرط النية والمحبة أقبل ذلك فاشتريه بألف دينار وأقل وأكثركم أضعه على وجهي وعيني وأتبرك بالنظر إليه وبمسه فأستشفي به عند المرض يصيبني أو يصيب من أهتم به','37','0','0');
INSERT INTO [t5880] VALUES (10,'وشك الآخر واحتج في كسره وإبطاله من الأحكام في الفروج والدماء والأموال التي النظر فيها أوجب من النظر في التفضيل فيغلظ في مثل هذا أحد يعرف شيئا أو له رؤية أو حسن نظر أو يدفعه من له عقل أو معاند يريد الإلطاط أو متبع لهواه ذاب عن رئاسة','38','0','0');
INSERT INTO [t5880] VALUES (11,'فتكلموا في الكفر والإيمان قال قلت وفقك الله يا أمير المؤمنين أما إن مطهرا البابي أخبرني قال أخبرني أبو الزبير عن جابر ابن عبد الله قال قال رسول الله','39','0','0');
INSERT INTO [t5880] VALUES (12,'بخلاف كتاب الله وبكتاب الله هدى الله نبيه','39','0','0');
INSERT INTO [t5880] VALUES (13,'كل قوم أولى رتبة من أمرهم ومصلحة من أنفسهم يردون على من سواهم ويتبين الحق من ذلك بالملابسة بالعدل عند ذوي الألباب قال والهاشمي علي بن أبي طالب رحمة الله عليه قال المكي فقلت هل تذكر شيئا تعرف به صحيح القياس من متناقضة قال','39','0','0');
INSERT INTO [t5880] VALUES (14,'قال كلفهم أن يعلموا أنه','40','0','0');
INSERT INTO [t5880] VALUES (15,'سيبعث هو مؤمن قال فلست إذا من المرجئة إن لم أقل هو مؤمن قلت فإن سمع بعد ذلك بمحمد ولقي محمدا عليه السلام هل أصاب الإقرار به إيمانا لم يكن أصابه قبل ذلك فعلم أنه ليس له حيلة فقال يا أمير المؤمنين على في الوضوء شدة فأذن له قال','41','0','0');
INSERT INTO [t5880] VALUES (16,'فقال مصعب ابن عبد الله الزبيري أنا يا أمير المؤمنين قال فأنشدنا فأنشد منع الرقاد بلابل وهموم والليل معتلج الرواق بهيم مما أتاني أن أحمد لامني فيه فبت كأنني محموم يا خير من حملت على أوصالها عيرانة','45','0','0');
INSERT INTO [t5880] VALUES (17,'ذكر حلم المأمون ومحاسن أفعاله ومكارم أخلاقه','47','0','0');
INSERT INTO [t5880] VALUES (18,'اللهم وصاحب البرذون الأشهب فالعنة فقال المأمون أنا صاحب البرذون الأشهب وسكت عليها فلما دخل عليه بشر قال له بعد أن ساء له يا أبا عبد الرحمن متى عهدك بلعن صاحب الأشهب فطأطأ بشر رأسه ثم لم يعد بعد ذلك إلى ذكره ولا التعرض به','49','0','0');
INSERT INTO [t5880] VALUES (19,'ومن أخبار طاهر بن الحسين','54','0','0');
INSERT INTO [t5880] VALUES (20,'ومن كلام طاهر بن الحسين وتوقيعاته','62','0','0');
INSERT INTO [t5880] VALUES (21,'ذكر وفاة طاهر بن الحسين وولاية طلحة ابنه','65','0','0');
INSERT INTO [t5880] VALUES (22,'بما أصلحت به أولياءك وأكفها مؤونة من بغى فيها وحسد عليها من لم الشعث وحقن الدماء وأصلاح ذات البين قال فقلت في نفسي أنا أول مقتول لأني لا أكتم الخبر فأنصرفت وأغتسلت بغسل الموتى وائتزرت بازار ولبست قميصا وارتديت رداء وطرحت السواد','66','0','0');
INSERT INTO [t5880] VALUES (23,'ومن أخبار ابن طاهر بن الحسين','69','0','0');
INSERT INTO [t5880] VALUES (24,'وضمانه لك في دينه وذمته الصفح عن سوالف جرائمك ومتقدمات جرائرك وإنزالك ما تستأهل من منازل العز والرفعة إن أتيت وراجعت إن شاء الله والسلام أبو إسحاق أحمد ابن إسحاق قال حدثني بشر السلماني قال سمعت أحمد بن أبي خالد يقول كان','70','0','0');
INSERT INTO [t5880] VALUES (25,'ذكر توجيه توجيه عبد الله بن طاهر إلى عبيد الله بن السري','73','0','0');
INSERT INTO [t5880] VALUES (26,'ومن أخبار طلحة بن طاهر بن الحسين','85','0','0');
INSERT INTO [t5880] VALUES (27,'ذكر وفاة طلحة بن طاهر','87','0','0');
INSERT INTO [t5880] VALUES (28,'ذكر أخبار من أخبار المأمون عن عبد الله بن طاهر','88','0','0');
INSERT INTO [t5880] VALUES (29,'ذكر أخبار ابن عائشة ومقتله في أيام المأمون','89','0','0');
INSERT INTO [t5880] VALUES (30,'ذكر أمر إبراهيم بن المهدي وظفر المأمون به بعد دخوله بغداد وعفوه عنه','93','0','0');
INSERT INTO [t5880] VALUES (31,'أسوة سنة فإنه عزى عن ابنته رقية فقال موت النبات من المكرمات فأمر له المأمون بمائة ألف درهم وأمر أن لا يكتب شيء بعد تعزيته وقال إسحاق الموصلى دخل إبراهيم بن المهدي على المأمون بعد صفحة عنه وعنده أبو إسحاق المعتصم والعباس بن','98','0','0');
INSERT INTO [t5880] VALUES (32,'ذكر بناء المأمون ببوران بنت الحسن بن سهل','105','0','0');
INSERT INTO [t5880] VALUES (33,'ذكر اتصال أحمد بن أبي خالد بالمأمون واستوزاره اياه بعد الفضل بن سهل','110','0','0');
INSERT INTO [t5880] VALUES (34,'ذكر وفاة أحمد بن أبي خالد','117','0','0');
INSERT INTO [t5880] VALUES (35,'ذكر اتصال أحمد بن يوسف بالمأمون','120','0','0');
INSERT INTO [t5880] VALUES (36,'قال فقال المأمون سريتها عني يا أحمد وأمر له بخمسمائة ألف درهم وحدثني عن أحمد بن يوسف بن القاسم الكاتب قال أمرني المأمون أن أكتب إلى جميع العمال في اخذ الناس بالاستكثار من المصابيح في شهر رمضان وتعريفهم ما في ذلك من الفضل فما دريت ما','121','0','0');
INSERT INTO [t5880] VALUES (37,'أخبار أبي دلف القاسم بن عيسى بن أدريس','124','0','0');
INSERT INTO [t5880] VALUES (38,'ذكر اتصال يحيى بن أكثم بالمأمون والسبب الذي له استوزره','131','0','0');
INSERT INTO [t5880] VALUES (39,'أخبار عبد الرحمن بن إسحاق القاضي وبدئ امره وذكر اتصاله بالسلطان','133','0','0');
INSERT INTO [t5880] VALUES (40,'ذكر شخوص المأمون إلى الشام لغزو الروم','134','0','0');
INSERT INTO [t5880] VALUES (41,'أخبار المأمون بالشام','136','0','0');
INSERT INTO [t5880] VALUES (42,'من مضر ولم يخرج اثنان إلا خرج أحدهما شاريا أعزب فعل الله بك فلما كان سنة سبع عشرة ومائتين رحل أمير المؤمنين من مصر ووافي دمشق يوم الخميس لعشر بقين من شهر ربيع الأول','137','0','0');
INSERT INTO [t5880] VALUES (43,'قال أحمد بن أبي طاهر دخل عجيف بن عنبسة بعلى بن هشام بغداد لثلاث بقين من شهر ربيع الأول وخرج به إلى عسكر المأمون لست خلون من شهر ربيع الآخر وقرئ فتح البيضاء من مصر لليلة بقيت من شهر ربيع الآخر وقتل على بن هشام وأخاه الحسين بن هشام في','137','0','0');
INSERT INTO [t5880] VALUES (44,'أخبار المأمون بدمشق','139','0','0');
INSERT INTO [t5880] VALUES (45,'لكم قال فأريته قال فقال إني لأشتهى أن أدري أي شيء هذا الغشاء الذي على هذا الخاتم قال فقال له أبو إسحاق المعتصم حل العقد حتى تدري ما هو قال فقال ما أشك أن النبي','139','0','0');
INSERT INTO [t5880] VALUES (46,'ثم قال للواثق خذه فضعه على عينك لعل الله أن يشفيك قال وجعل المأمون يضعه على عينه ويبكي قال أبو طالب الجعفري قال أخبرني العيشى صاحب إسحاق بن إبراهيم قال كنت مع المأمون بدمشق قال وكان قد قل المال عنده حتى ضاق وشكا','139','0','0');
INSERT INTO [t5880] VALUES (47,'أختتن قال لا أدري وما سمعت في هذا شيئا قال فأخبرني عنه أكان يشهد إذا تزوج أو زوج قال لا أدري قال أخرج قبح الله من قلدك دينه قال حدثني مخارق قال كنا عند المأمون أنا والمغنون بدمشق وعريب معنا فقال غنى يا','142','0','0');
INSERT INTO [t5880] VALUES (48,'أخبار الشعراء في أيام المأمون ومن وفد عليه منهم وذكر ما امتدح به من الشعر','146','0','0');
INSERT INTO [t5880] VALUES (49,'أخبار المغنين أيام المأمون','164','0','0');
INSERT INTO [t5880] VALUES (50,'وصفيه محمد','176','0','0');
INSERT INTO [t5880] VALUES (51,'لا تحرك به لسانك لتعجل به وقال','176','0','0');
INSERT INTO [t5880] VALUES (52,'ذكر من مات في أيام المأمون ببغداد وغيرها من سنة أربع ومائتين وما بعدها من السنين إلى آخر أيامه وولايته من الفقهاء','179','0','0');
