INSERT INTO [t5907] VALUES (829,'مسألة إذا حال دون مطلع الهلال غيم أو قتر ليلة الثلاثين من شعبان فعن أحمد ثلاث روايات إحداهن أنه يجب صوم الثلاثين بنية من رمضان وهذا مذهب عمر وعلي وابن عمر ومعاوية وعمرو بن العاص وأنس وأبي هريرة وعائشة وأسماء وقال به من كبار التابعين طاوس ومجاهد','544','827','0');
INSERT INTO [t5907] VALUES (830,'الحديث الثاني','547','829','0');
INSERT INTO [t5907] VALUES (831,'مسألة يجب صوم رمضان بشاهد واحد وقال مالك وداود لا يجب وعن الشافعي كالمذهبين وقال أبو حنيفة إن كان في السماء علة قبل شاهد وإن لم يكن لم يقبل إلا الجم الغفير لنا أربعة أحاديث','550','827','0');
INSERT INTO [t5907] VALUES (832,'مسألة يجب على المطاوعة على الوطء في نهار رمضان كفارة الجماع وعنه لا يجب وعن الشافعي كالروايتين','553','827','0');
