INSERT INTO [t5907] VALUES (835,'الحديث الرابع','556','832','0');
INSERT INTO [t5907] VALUES (836,'مسألة إذا أكل ناسيا لم يبطل صومه وقال مالك يبطل لنا حديثان الحديث الأول','557','832','0');
