INSERT INTO [t5907] VALUES (845,'الحديث الثالث','564','843','0');
INSERT INTO [t5907] VALUES (846,'فصل فإن صام في السفر صح وقال داود لا يصح لنا أحاديث أخبرنا هبة الله بن محمد قال أنبأنا الحسن بن علي أنبأنا أحمد بن جعفر قال حدثنا عبد الله بن أحمد قال حدثني أبي قال حدثنا أبو المغيرة حدثنا سعيد بن عبد العزيز قال حدثني إسماعيل بن عبيد الله عن أم','565','845','0');
INSERT INTO [t5907] VALUES (847,'مسألة إذا نوى الصوم ثم سافر أبيح له أن يفطر وبه قال داود والمزني وعنه لا يباح كقول أكثرهم','566','827','0');
