INSERT INTO [t5907] VALUES (849,'مسألة إذا أخر قضاء رمضان لغير عذر حتى جاء رمضان آخر وجبت عليه الفدية مع القضاء وقال أبو حنيفة لا يجب إلا القضاء','567','827','0');
