INSERT INTO [t5907] VALUES (860,'مسألة آكد ليلة يلتمس فيها ليلة القدر ليلة سبع وعشرين وقال الشافعي ليلة إحدى وعشرين وقال مالك العشر كله سواء لنا أحاديث','577','827','0');
INSERT INTO [t5907] VALUES (861,'مسألة يستحب أن يتبع رمضان بست من شوال وقال أبو حنيفة ومالك لا يستحب','578','827','0');
