INSERT INTO [t5907] VALUES (864,'مسألة إذا شرط في اعتكافه الخروج إلى القرب كعيادة المرضى وصلاة الجنازة وزيارة العلماء جاز وقال مالك لا يجوز اشتراط هذه الأشياء احتج بأصحابنا بحديثين ضعيفين','582','863','0');
INSERT INTO [t5907] VALUES (865,'كتاب الحج','583','0','0');
INSERT INTO [t5907] VALUES (866,'مسألة إذا كان للمعضوب مال لزمه أن يستنيب من يحج عنه وقال مالك وداود لا يلزمه','583','865','0');
INSERT INTO [t5907] VALUES (867,'مسألة يجوز لمن لا مال له أن يستنيب في الحج ويقع عن المحجوج عنه وقال أبو حنيفة لا يجوز ذلك وإنما يستنيب من له مال ليحصل ثواب النفقة فحسب لنا حديث الخثعمية وقد سبق','584','866','0');
INSERT INTO [t5907] VALUES (868,'مسألة لا يسقط الحج بكون البحر بينه وبين مكة إذا كان غالبه السلامة وقال الشافعي في أحد قوليه يسقط','585','865','0');
INSERT INTO [t5907] VALUES (869,'مسألة من عليه فرض الحج لا يصح أن يحج عن غيره وعنه يجوز كقول أبي حنيفة ومالك لنا حديثان','585','868','0');
