INSERT INTO [t5907] VALUES (871,'مسألة فإذا أحرم الصرورة بحجة نفل انعقدت عن فرضه وعن أحمد أنها تقع نفلا','585','865','0');
INSERT INTO [t5907] VALUES (872,'مسألة يصح إحرام الصبي وعليه الكفارة بالمحظورات وقال أبو حنيفة لا يصح','586','865','0');
INSERT INTO [t5907] VALUES (873,'مسألة يجب الحج على الفور وقال الشافعي لا يجب على الفور لنا أربعة أحاديث','587','865','0');
