INSERT INTO [t5907] VALUES (878,'مسألة يستحب لمن أراد الإحرام أن يتطيب وقال مالك يكره','590','865','0');
INSERT INTO [t5907] VALUES (879,'مسألة الأفضل أن يحرم عقيب ركعتين وعنه أن الإحرام عقيب الصلاة وحين تستوي به راحلته على البيداء سواء وقال مالك الأفضل حين تستوي به راحلته عن البيداء وعن الشافعي كقولنا الأول وعنه إذا سارت به راحلته لنا ما','590','865','0');
