INSERT INTO [t5907] VALUES (881,'الحديث الثاني','591','879','0');
INSERT INTO [t5907] VALUES (882,'مسألة لا يستحب الزيادة على تلبية رسول الله صلى الله عليه وسلم وقال أبو حنيفة يستحب لنا أن جماعة رووا صفة تلبيته وقد قال خذوا عني مناسككم','591','865','0');
INSERT INTO [t5907] VALUES (883,'مسألة يقطع الحاج التلبية عند رمي جمرة العقبة وقال مالك في إحدى روايتيه يقطعها بعد الزوال من يوم عرفة','591','865','0');
INSERT INTO [t5907] VALUES (884,'مسألة ويقطع المعتمر التلبية إذا شرع في الطواف وقال مالك إذا أحرم من الميقات قطع إذا دخل الحرم وإن أحرم من أدنى الحل قطع إذا رأى البيت','592','865','0');
INSERT INTO [t5907] VALUES (885,'مسألة العمرة واجبة وقال أبو حنيفة ومالك لا تجب وعن الشافعي كالمذهبين لنا خمسة أحاديث','592','865','0');
