INSERT INTO [t5907] VALUES (919,'طريق آخر','602','918','0');
INSERT INTO [t5907] VALUES (920,'مسألة لا ينقطع حكم الإحرام بالموت وقد ذكرنا هذه المسألة في كتاب الجنائز فلينظر من ثم','603','865','0');
INSERT INTO [t5907] VALUES (921,'مسألة إذا عدم الإزار ولبس السراويل فلا فدية عليه لنا حديثان','603','920','0');
