INSERT INTO [t5907] VALUES (933,'مسألة الدال على الصيد يلزمه الجزاء إذا كان محرما وقال مالك والشافعي لا يلزمه لنا ما روى أبو بكر الجوزقي في كتابه المخرج على الصحيحين من حديث أبي قتادة أنه كان مع أناس من أصحاب رسول الله صلى الله عليه وسلم وهم محرمون وأبو قتادة ليس بمحرم فصرع حمار وحش','608','929','0');
INSERT INTO [t5907] VALUES (934,'مسألة إذا اشترك جماعة محرومون في قتل صيد فعليهم جزاء واحد وقال أبو حنيفة','608','865','0');
INSERT INTO [t5907] VALUES (935,'مسألة يحرم على المحرم أكل ما صيد لأجله وقال أبو حنيفة لا يحرم لنا ثلاثة أحاديث','609','865','0');
