INSERT INTO [t5907] VALUES (948,'مسألة لا يكره تلفيق الأسابيع وقال أبو حنيفة والشافعي يكره وصفة التلفيق أنه يؤخر ركعتي الطواف حتى إذا فرغ صلى لكل أسبوع ركعتين','615','865','0');
INSERT INTO [t5907] VALUES (949,'مسألة السعي ركن لا ينوب عنه الدم وعنه أنه سنة لا يجب بتركه دم وقال أبو حنيفة هو واجب ينوب عنه الدم','615','865','0');
