INSERT INTO [t5907] VALUES (968,'مسألة فإن طاف ولم يعقبه بالخروج لزمته الإعادة وقال أبو حنيفة لا يلزمه لنا الحديث المتقدم','621','966','0');
INSERT INTO [t5907] VALUES (969,'مسألة وقت الوقوف من طلوع الفجر الثاني يوم عرفة إلى طلوع الفجر الثاني من يوم النحر وقال أبو حنيفة والشافعي أول الوقت بعد الزوال من يوم عرفة وقال مالك وقت الإجزاء ليلة النحر فقط','621','865','0');
INSERT INTO [t5907] VALUES (970,'مسألة إذا دفع من عرفات قبل غروب الشمس فعليه دم خلافا لأحد قولي الشافعي لازم عليه','621','865','0');
INSERT INTO [t5907] VALUES (971,'مسألة يجوز الدفع من مزدلفة بعد نصف الليل وقال أبو حنيفة لا يجوز حتى يطلع الفجر','622','865','0');
INSERT INTO [t5907] VALUES (972,'مسألة فإن دفع قبل نصف الليل فعليه دم وقال أبو حنيفة لا دم عليه وعن الشافعي كالروايتين','622','865','0');
INSERT INTO [t5907] VALUES (973,'مسألة يجوز رمي جمرة العقبة بعد نصف الليل من ليلة النحر وقال أبو حنيفة ومالك لا يجوز حتى يطلع الفجر','622','865','0');
INSERT INTO [t5907] VALUES (974,'مسألة لا يجوز الرمي إلا بالحجارة وقال أبو حنيفة يجوز بجميع جنس الأرض','623','865','0');
INSERT INTO [t5907] VALUES (975,'مسألة ولا يرمى حجر فقد رمي به وقال أكثرهم يجوز','623','865','0');
INSERT INTO [t5907] VALUES (976,'مسألة إذا نكس الرمي فرمى جمرة العقبة ثم الوسطى ثم الأولى لم يجزه وقال أبو حنيفة يجزيه لنا أن النبي صلى الله عليه وسلم رمى مرتبا وقال خذوا عني','623','865','0');
INSERT INTO [t5907] VALUES (977,'مسألة في النفر الأول خطبة وقال أبو حنيفة ومالك لا خطبة فيه لنا أن النبي صلى الله عليه وسلم خطب من ثاني أيام التشريق وقال خذوا عني مناسككم','623','865','0');
INSERT INTO [t5907] VALUES (978,'مسألة إذا ترك المبيت بمنى ليالي منى لزمه دم وعنه لا دم عليه كقول أبي حنيفة','624','865','0');
INSERT INTO [t5907] VALUES (979,'مسألة لا يجزيه في التحلل حلق بعض الرأس وقال أبو حنيفة يجزيه ما يجزي مسحه في الطهارة','624','865','0');
INSERT INTO [t5907] VALUES (980,'مسائل الإحصار','624','979','0');
INSERT INTO [t5907] VALUES (981,'مسألة يجوز للمتمتع والقارن أن يقدما الحلاق على الذبح والرمي ولا دم عليهما في ذلك وعنه إن تعمدا ذلك فعليهما دم وقال أبو حنيفة عليهما دم بكل حال','624','865','0');
INSERT INTO [t5907] VALUES (982,'مسألة يجب الهدي في حق المحصر وقال مالك لا يجب لنا حديث جابر','625','981','0');
INSERT INTO [t5907] VALUES (983,'مسألة ويذبح الهدي حيث أحصر وقال أبو حنيفة لا يذبحه إلا في الحرم لنا ما تقدم من الحديث وأنهم نحروا بالحديبية وهي حل','625','981','0');
INSERT INTO [t5907] VALUES (984,'مسألة إذا شرط أنه متى مرض تحلل أو إن أحصره عدو أو إن أخطأ العدد كان شرطا صحيحا يستفيد به التحلل ولا دم عليه وقال أبو حنيفة ومالك وجود هذا الشرط كعدمه فعند أبي حنيفة لا يتحلل إلا بالهدي وعند مالك لا يتحلل إذا أخطأ العدد لنا ما','625','865','0');
INSERT INTO [t5907] VALUES (985,'مسألة المحصر بالمرض لا يباح له التحلل إلا أن يكون قد اشترط في ابتداء إحرامه أنه إن مرض تحلل وقال أبو حنيفة حكم الإحصار بالمرض حكم الإحصار بالعدو','626','865','0');
INSERT INTO [t5907] VALUES (986,'مسألة لا يجوز للمرأة أن تحج من غير محرم وقال مالك والشافعي يجوز إذا كان معها نساء ثقات','626','865','0');
INSERT INTO [t5907] VALUES (987,'مسألة ولا فرق بين قليل السفر وطويله وقال أبو حنيفة لا يعتبر المحرم إلا في السفر الطويل الذي يبيح الرخص وعن أحمد نحوه لنا ما تقدم من الحديث','626','986','0');
INSERT INTO [t5907] VALUES (988,'مسائل الفوات','627','987','0');
INSERT INTO [t5907] VALUES (989,'مسائل الهدي','627','987','0');
INSERT INTO [t5907] VALUES (990,'مسألة وصفة الإشعار شق صفحة سنامها الأيمن وعنه الأيسر كقول أبي يوسف ومحمد لنا الحديث المتقدم','628','986','0');
INSERT INTO [t5907] VALUES (991,'مسألة يجوز النحر في جميع الحرم وقال مالك لا ينحر الحاج إلا بمنى والمعتمر إلا بمكة','628','865','0');
INSERT INTO [t5907] VALUES (992,'مسألة لا يأكل من الدماء الواجبة إلا من هدي التمتع والقرآن وقال الشافعي لا يأكل منها شيء لنا ما روى عبد الرحمن بن أبي حاتم في سننه من حديث علي عليه السلام قال أمرني رسول الله صلى الله عليه وسلم بهدي التمتع أن أتصدق بلحومها سوى ما يؤكل احتجوا بما','628','991','0');
INSERT INTO [t5907] VALUES (993,'مسألة إذا نذر بدنة وأطلق فهو مخير بن الجزور والبقرة وعنه لا ينتقل إلى البقرة إلا عند عدم الجزور كقول الشافعي','629','865','0');
INSERT INTO [t5907] VALUES (994,'مسألة يجوز أن يشترك سبعة في بدنة وبقرة على الإطلاق وقال أبو حنيفة إن كان بعضهم يريد اللحم وبعضهم يريد القربة لم يصح الاشتراك وقال مالك لا يصح الاشتراك في الهدي الواجب لنا حديث جابر المتقدم','629','993','0');
INSERT INTO [t5907] VALUES (995,'مسائل الأضاحي','630','994','0');
INSERT INTO [t5907] VALUES (996,'الحديث الأول','630','993','0');
INSERT INTO [t5907] VALUES (997,'الحديث الثاني','630','993','0');
