INSERT INTO [t5907] VALUES (1000,'مسألة ويكره لمن أراد أن يضحي إذا دخل العشر أن يحلق شعره أو يقلم أظفاره ومن أصحابنا من قال يحرم عليه وقال أبو حنيفة لا يكره دليلنا حديث أم سلمة المتقدم','632','993','0');
INSERT INTO [t5907] VALUES (1001,'مسألة لا يجوز أن يضحي بعضباء القرن والأذن وقال أبو حنيفة يجوز بعضباء القرن وقال مالك إن كان قرنها يدمى لم يجز وإلا جاز فأما المقطوعة الأذن فيجوز','632','865','0');
INSERT INTO [t5907] VALUES (1002,'مسألة لا يجوز ذبح الأضحية قبل صلاة الإمام ويجوز بعدها وإن لم يكن الإمام قد ذبح وقال أبو حنيفة في أهل الأمصار كقولنا في أهل القرى يجوز أن يذبحوا بعد طلوع الفجر من يوم النحر وقال مالك وقت الذبح إذا صلى الإمام وذبح وقال الشافعي وقت الذبح أن يمضي بعد دخول','632','865','0');
INSERT INTO [t5907] VALUES (1003,'مسألة لا يجوز بيع جلود الأضاحي وقال أبو حنيفة يجوز','633','865','0');
INSERT INTO [t5907] VALUES (1004,'مسألة العقيقة مستحبة وقال أبو حنيفة لا تستحب وقال داود واجبة ونقلها أبو بكر بن عبد العزيز عن أحمد لنا أربعة أحاديث','633','865','0');
