INSERT INTO [t5907] VALUES (1010,'مسائل الخيار','637','1009','0');
INSERT INTO [t5907] VALUES (1011,'مسألة يجوز الخيار أكثر من ثلاث لنا قوله عليه السلام المؤمنون عند شروطهم وسيأتي مسندا في مسائل الشروط احتجوا بحديثين','637','1010','0');
