INSERT INTO [t5907] VALUES (1018,'مسألة لا يجوز بيع تمرة بتمرتين ولا حفنة بحفنتين وقال أبو حنيفة يجوز لنا قوله عليه السلام إلا مثلا بمثل وقد سبق الحديث','639','1010','0');
INSERT INTO [t5907] VALUES (1019,'مسألة لا يجوز التفرق في بيع ما يجري فيه الربا لعلة واحدة قبل القبض كالمكتل بالمكتل والموزون بالموزون وقال أبو حنيفة يجوز لنا أحاديث منها حديث عبادة يدا بيد وقد سبق بإسناده','639','1010','0');
INSERT INTO [t5907] VALUES (1020,'مسألة ما لا يدخله الربا لا يحرم فيه لنسا وهو غير المكيل والموزون وعنه يحرم إذا كان جنسا واحدا كقول أبي حنيفة وقال مالك يحرم النسا في الجنس الواحد إذا كان متفاضلا فأما الجنسان فلا','640','1009','0');
INSERT INTO [t5907] VALUES (1021,'الحديث الثاني','640','1020','0');
INSERT INTO [t5907] VALUES (1022,'الحديث الثالث','641','1020','0');
