INSERT INTO [t5907] VALUES (1024,'مسألة الحنطة والشعير يجوز التفاضل فيهماخلافا لمالك','641','1009','0');
INSERT INTO [t5907] VALUES (1025,'مسألة لا يجوز بيع الحنطة المبلولة باليابسة وقال أبو حنيفة يجوز لنا قوله عليه السلام أينقص الرطب إذا يبس قالوا نعم فنهى عن ذلك وسيأتي هذا الحديث بإسناده إن شاء الله تعالى','641','1024','0');
INSERT INTO [t5907] VALUES (1026,'مسألة لا يجوز بيع الرطب بالتمر وقال أبو حنيفة يجوز لنا حديثان','642','1024','0');
INSERT INTO [t5907] VALUES (1027,'الحديث الثاني','642','1024','0');
INSERT INTO [t5907] VALUES (1028,'مسألة إذا باع جنسا فيه الربا بجنسه ومع أحدهما أو معهما من غير الجنس كمد ودرهم بدرهمين لم يصح وعنه يصح كقول أبي حنيفة لنا ما','643','1009','0');
INSERT INTO [t5907] VALUES (1029,'مسألة لا يجوز بيع اللحم بالحيوان المأكول ويجوز بغير المأكول كالعبد والحمار وقال أبو حنيفة يجوز وقال مالك لا يجوز بيع اللحم بحيوان معد للحم','645','1009','0');
INSERT INTO [t5907] VALUES (1030,'مسائل الشروط في البيع والصبر','645','1029','0');
INSERT INTO [t5907] VALUES (1031,'مسألة يجوز اشتراط منفعة المبيع مدة معلومة مثل أن يبيع دارا يشترط سكانها شهرا أو عبدا ويشترط خدمته سنة أو قلعة ويشترط على البائع حدودها أو جميزة ويشترط حملها خلافا لأكثرهم في أنه لا يجوز ووافقنا أبو حنيفة في القلعة والجمبزة ومالك في الزمان اليسير دون','646','1009','0');
INSERT INTO [t5907] VALUES (1032,'مسائل الثمار','646','1031','0');
INSERT INTO [t5907] VALUES (1033,'مسألة لا يجوز بيع الثمار قبل بدو صلاحها إلا أن يشترط القطع وقال أبو حنيفة يجوز ويؤمر بالقطع','647','1009','0');
INSERT INTO [t5907] VALUES (1034,'مسألة إذا باع بعد بدو الصلاح بشرط التنقية صح وقال أبو حنيفة البيع باطل لنا نهيه عليه السلام في الحديث المتقدم عن بيع الثمرة حتى يبدو صلاحها وحتى للغاية وما بعد الغاية يخالف ما قبلها وقد ثبت أنه لا يجوز البيع قبل الغاية بشرط التنقية فينبغي أن يكون ما','647','1033','0');
INSERT INTO [t5907] VALUES (1035,'مسألة ما تهلكه الجوائح فهو من ضمان البائع وعنه إن كان ذلك الثلث فصاعدا فهو من ضمان البائع وما دون الثلث من ضمان المشتري وبه قال مالك وقال أبو حنيفة والشافعي جميع ذلك من ضمان المشتري لنا ما','647','1009','0');
INSERT INTO [t5907] VALUES (1036,'مسألة يجوز بيع العرايا وهو بيع الرطب على رؤوس النخل بخرصه ثمرا على الأرض وهل يجوز ذلك في سائر الثمار الي لها رطب ويابس على وجهين وقال أبو حنيفة لا يجوز','648','1009','0');
INSERT INTO [t5907] VALUES (1037,'طريق ثالث','648','1036','0');
INSERT INTO [t5907] VALUES (1038,'طريق رابع','648','1037','0');
INSERT INTO [t5907] VALUES (1039,'طريق خامس','649','1037','0');
INSERT INTO [t5907] VALUES (1040,'مسألة ولا يجوز ذلك لسنة وقال مالك يجوز لنا حديث مصعب قال نهى رسول الله صلى الله عليه وسلم عن بيع الرطب بالتمر لسنة وقد ذكرناه والكلام عليه في بيع الرطب بالتمر','649','1036','0');
INSERT INTO [t5907] VALUES (1041,'فصل ولا يجوز إلا فيما دون خمسة أوسق وقال الشافعي يجوز في خمسة أوسق ولا يجوز فيما زاد لنا الحديث المتقدم وهو وارد فيما دون خمسة أوسق بعين وفي الخمسة مشكوك فوجب أن يسقط المشكوك','649','1036','0');
INSERT INTO [t5907] VALUES (1042,'مسألة يجوز للمشتري التصرف في المبيع المتعين قبل قبضه وقال أبو حنيفة والشافعي لا يجوز إلا أن أبا حنيفة وافقنا في العقار لنا ما','649','1009','0');
