INSERT INTO [t5907] VALUES (1053,'الحديث الثاني','652','1050','0');
INSERT INTO [t5907] VALUES (1054,'مسألة الغبن يثبت الفسخ وقال أبو حنيفة والشافعي لا يثبت وقال داود يبطل العقد من أصله','653','1009','0');
INSERT INTO [t5907] VALUES (1055,'مسألة إذا باع سلعة بثمن مؤجل لم يجز أن يعود فيشتريها بالقبض منه حالا وقال الشافعي يجوز','653','1009','0');
INSERT INTO [t5907] VALUES (1056,'مسألة إذا اختلف المتبايعان في قدر الثمن تحالفا إذا كانت السلعة قائمة وإن كانت','653','1009','0');
