INSERT INTO [t5907] VALUES (1058,'مسألة لا يجوز بيع الزيت النجس وقال أبو حنيفة يجوز','656','1009','0');
INSERT INTO [t5907] VALUES (1059,'مسألة لا يجوز بيع الصوف على الظهر وعنه يجوز كقول مالك','657','1009','0');
INSERT INTO [t5907] VALUES (1060,'مسألة لا يجوز بيع السرجين النجس وقال أبو حنيفة يجوز','657','1009','0');
INSERT INTO [t5907] VALUES (1061,'مسألة لا يصح بيع العنب ممن يتخذه خمرا وقال أكثرهم يصح','658','1009','0');
INSERT INTO [t5907] VALUES (1062,'مسألة لا يجوز بيع الكلب وإن كان معلما وقال أبو حنيفة يجوز','658','1009','0');
