INSERT INTO [t5907] VALUES (1068,'مسألة بيع الحاضر للبادي باطل بشرط أن يكون البادي حضر لبيع السلعة بسعر يومه أو يكون بالناس حاجة إلى سلعته وأن يكون البادي جاهلا بالأسعار ويكون الحاضر قصد التاجر','660','1009','0');
INSERT INTO [t5907] VALUES (1069,'مسألة لا يجوز أن يفرق في البيع بين كل ذي رحم محرم وقال مالك لا يفرق بين الأم وولدها خاصة وقال الشافعي لا يفرق بينه وبين أبويه وإن عليا وولده وإن سفل لنا ثلاثة أحاديث','660','1009','0');
