INSERT INTO [t5907] VALUES (1073,'فصل ولا يجوز التفريق بعد البلوغ وعنه يجوز كقول أبي حنيفة لنا مطلق الأخبار المتقدمة وقد احتجوا بما','661','1072','0');
INSERT INTO [t5907] VALUES (1074,'مسألة لا يجوز المعارضة عن عسب الفحل وقال مالك يجوز','662','1009','0');
INSERT INTO [t5907] VALUES (1075,'مسائل القرض','662','1074','0');
INSERT INTO [t5907] VALUES (1076,'مسألة ويجوز قرض الخبز وهل يجوز بالعدد أو يكون بالوزن وقال أبو حنيفة لا يجوز قرضه لنا ما','663','1009','0');
INSERT INTO [t5907] VALUES (1077,'مسألة لا يحل للمقرض أن ينتفع من المقترض منفعة لم تجر عادته بها قبل ذلك وقال الشافعي يجوز ما لم يشترط ذلك وعن أحمد مثله','663','1009','0');
INSERT INTO [t5907] VALUES (1078,'مسائل السلم','664','1077','0');
INSERT INTO [t5907] VALUES (1079,'مسألة يصح السلم في الحيوان وقال أبو حنيفة لا يصح لنا حديث عبد الله بن عمرو بن العاص أمرني رسول الله صلى الله عليه وسلم أن أبتاع البعير بالبعيرين بالأبقرة إلى خروج المصدق وقد سبق هذا بإسناده','665','1077','0');
INSERT INTO [t5907] VALUES (1080,'مسألة يجوز السلم في الخبز خلافا لأكثرهم لنا قوله عليه السلام ووزن معلوم والخبز موزون وقد سبق الحديث بإسناده','665','1077','0');
INSERT INTO [t5907] VALUES (1081,'مسألة إذا أسلم إليه في سلعة ثم تقايلا بعد قبض الثمن لم يجز أن يصرف ذلك الثمن في شيء آخر حتى يقبضه وقال الشافعي يجوز','666','1009','0');
INSERT INTO [t5907] VALUES (1082,'مسألة لا يجوز التسعير وقال مالك يجوز أن تقول لمن حط سعرا إما أن يلحق بالناس أو ينصرف عنهم','666','1009','0');
INSERT INTO [t5907] VALUES (1083,'مسائل الرهن','666','1082','0');
INSERT INTO [t5907] VALUES (1084,'مسألة إذا قال الراهن إن جئتك بالحق في وقت كذا وإلا فالرهن لك بطل الشرط وصح الرهن وكذلك إذا شرط سائر الشروط الفاسدة وقال الشافعي إن كانت الشروط مما تنقص من حق المرتهن قبل أن يشترط أن لا يسلم الرهن إليه أو لا يبيعه في محله فالرهن باطل وإن كان مما يزيد','667','1009','0');
INSERT INTO [t5907] VALUES (1085,'مسألة ما ينفقه المرتهن في غيبة الراهن يكون دينا على الراهن وللمرتهن استيفاؤه من ظهر الرهن ودره وقال أبو حنيفة والشافعي متى أنفق من غير أمر الحاكم كان متطوعا احتجوا بما','668','1009','0');
INSERT INTO [t5907] VALUES (1086,'مسألة ليس للراهن أن ينتفع بالرهن وقال الشافعي له ذلك واحتج بما سبق وقد بينا أن ذلك للمرتهن','669','1009','0');
INSERT INTO [t5907] VALUES (1087,'مسألة إذا أفلس المشتري بالثمن فوجد البائع عين ماله والمفلس حي ولي يقبض من دينه شيئا فهو أحق به من سائر الغرماء وقال أبو حنيفة هو أسوة الغرماء في الموت والحياة وقال الشافعي هو أحق به في الموت والحياة لنا حديثان','669','1086','0');
INSERT INTO [t5907] VALUES (1088,'الحديث الثاني','669','1086','0');
INSERT INTO [t5907] VALUES (1089,'مسألة إذا أفلس وفرق ماله وبقي عليه دين وله حرفة تفصل أجرتها عن كفايته جاز للحاكم إجارته في قضاء دينه وعنه لا يؤجره كقول أكثرهم','670','1009','0');
INSERT INTO [t5907] VALUES (1090,'مسألة إذا امتنع المدين من قضاء دينه حجر الحاكم عليه وباع ماله في قضاء دينه وقال أبو حنيفة لا يباع ماله ويحبس حتى يبيع لنا أن النبي صلى الله عليه وسلم حجر على معاذ وباع ماله في دين','670','1009','0');
INSERT INTO [t5907] VALUES (1091,'مسائل الحجر','671','1090','0');
INSERT INTO [t5907] VALUES (1092,'مسألة حد البلوغ بالسن خمس عشرة سنة وقال أبو حنيفة في حق الغلام ثماني عشرة والدخول في التاسعة عشرة وفي الجارية السابعة عشرة','672','1009','0');
INSERT INTO [t5907] VALUES (1093,'مسألة يحجر على المبذر وقال أبو حنيفة لا يحجر عليه لنا حديث معاذ وقد سبق احتجوا بما','672','1092','0');
INSERT INTO [t5907] VALUES (1094,'مسائل الحوالة','672','1093','0');
INSERT INTO [t5907] VALUES (1095,'مسألة إذا نوى المال على المحال عليه لم يرجع المحال على المحيل وقال أبو حنيفة يرجع في موضعين أحدهما أن يجحد المحال عليه الدين أو يموت مفلسا فأما إن أفلس وهو حي لم يرجع عليه وقال مالك إن أحاله على مفلس والمحتال لا يعلم فله الرجوع لنا حديث حزن جد سعيد','673','1092','0');
INSERT INTO [t5907] VALUES (1096,'مسألة يصح ضمان دين الميت وقال أبو حنيفة لايصح إلا أن يحلف وفاء','673','1009','0');
INSERT INTO [t5907] VALUES (1097,'مسألة لا ينتقل الحق من ذمة المضمون عنه بالضمان وقال داود ينتقل لنا في الخبر المتقدم أنه قال للضامن حين أدى الآن بردت جلده','674','1009','0');
INSERT INTO [t5907] VALUES (1098,'مسألة لا تصح الكفالة ببدن من عليه حد وقال أكثرهم تصح ويجبر على إحضاره','675','1009','0');
INSERT INTO [t5907] VALUES (1099,'مسألة إذا أراق خمرا على ذمي لم يضمنها وكذلك إذا قتل له خنزيرا وقال أبو حنيفة ومالك يضمن','675','1009','0');
INSERT INTO [t5907] VALUES (1100,'مسائل الشركة','676','1099','0');
INSERT INTO [t5907] VALUES (1101,'مسألة دعوة العبد وهديته وعاريته جائزة من غير إذن السيد فأما هبته الدراهم وكسوته فلا يجوز وقال الشافعي لا يجوز جميع ذلك لنا أن رسول الله صلى الله عليه وسلم قبل هدية بربرة وأجاب دعوة العبد','676','1009','0');
INSERT INTO [t5907] VALUES (1102,'مسألة تصرفات الفضولي باطلة وعنه أنها صحيحة وتقف على إجازة المالك كقول أبي حنيفة لنا حديثان أحدهما قوله لحكيم بن حزام لا تبع ما ليس عندك وقد ذكرناه في أول كتاب البيع بإسناده الثاني','677','1101','0');
INSERT INTO [t5907] VALUES (1103,'الحديث الثاني','677','1101','0');
INSERT INTO [t5907] VALUES (1104,'مسألة إذا وكله في شراء شاة بدينار فاشترى شاتين كل واحدة تساوي الدينار فالبيع صحيح فيهما وقال أبو حنيفة يلزم الموكل شاة بنصف دينار ويلزم الوكيل الأخرى بنصف دينار وعن الشافعي كقولنا وعنه يلزمه شاة وهو بالخيار في الأخرى لنا حديث عروة وأنه اشترى شاتين وقد','678','1101','0');
INSERT INTO [t5907] VALUES (1105,'مسألة العارية مضمونة بكل حال وعنه أنها مضمونة إلا أن يشترط إسقاط الضمان وقال أبو حنيفة لا يضمن إلا أن يفرط في حفظها كالوديعة وقال مالك هي كالرهن ما','678','1009','0');
INSERT INTO [t5907] VALUES (1106,'مسألة إذا أعاره أرضه مطلقا ليبني فيها فبنى أو عرش فللمعير أن يسترد الأرض ويضمن قيمة البناء والعرش أو قيمة ما نقص بالقلع وقال أبو حنيفة للمعير أن يسترد الأرض ويقلع البناء والعراش ولا ضمان عليه لنا قوله عليه السلام ليس لعرق ظالم حق وسيأتي مسندا وفيه','680','1009','0');
INSERT INTO [t5907] VALUES (1107,'مسألة إذا مثل بعبده عتق عليه وقال أبو حنيفة لا يعتق لنا ما','680','1009','0');
INSERT INTO [t5907] VALUES (1108,'مسألة إذا غير صفة المغصوب بأن طحن الحنطة أو خبز الدقيق أو شوى الشاة أو قطع الثوب قميصا أو ضرب الزيرة أو أتى لم يزل عنه ملك المالك وقال أبو حنيفة يملكها الغاصب بالبعير ويجب عليه البدل لمالكها','680','1009','0');
INSERT INTO [t5907] VALUES (1109,'مسألة إذا غصب ساحة فبنى عليها أو أجزاء فجعله في أساس حائطه وبنى عليه وجب رده قال أبو حنيفة زال حق المالك عنها وليس له إلا القيمة لنا حديث أنس المتقدم','681','1108','0');
INSERT INTO [t5907] VALUES (1110,'مسألة إذا غصب أرضا فزرعها فصاحبها بالخيار إن شاء أن يقر الزرع إلى وقت الحصاد وإن شاء أن يدفع إليه قيمة الزرع أو ما أنفقه على الزرع على اختلاف الروايتين في ذلك ويكون الزرع له وليس له إجباره على قلعه بغير عوض وقال أكثرهم له إجباره على القلع وليس له','682','1009','0');
INSERT INTO [t5907] VALUES (1111,'الحديث الثاني','682','1110','0');
INSERT INTO [t5907] VALUES (1112,'مسألة إذا كسر آلة اللهو لم يضمن وقال أبو حنيفة والشافعي يضمن','683','1009','0');
INSERT INTO [t5907] VALUES (1113,'مسائل الشفعة','683','1112','0');
INSERT INTO [t5907] VALUES (1114,'طريق آخر','684','1113','0');
INSERT INTO [t5907] VALUES (1115,'الحديث الأول','684','1112','0');
INSERT INTO [t5907] VALUES (1116,'الحديث الثاني','684','1112','0');
INSERT INTO [t5907] VALUES (1117,'الحديث الثالث','684','1112','0');
INSERT INTO [t5907] VALUES (1118,'طريق آخر','684','1117','0');
INSERT INTO [t5907] VALUES (1119,'الحديث الرابع','685','1112','0');
INSERT INTO [t5907] VALUES (1120,'مسألة إذا اشترى أرضا فيها زرع أو شجر مثمر لم تجب الشفعة في الزرع والثمر وقال أبو حنيفة ومالك تجب','685','1009','0');
INSERT INTO [t5907] VALUES (1121,'مسألة لا تثبت الشفعة فيما لا يقسم كالحمام والرحى ونحوه وقال أبو حنيفة تثبت وعن أحمد نحوه وعن مالك كالمذهبين','685','1009','0');
INSERT INTO [t5907] VALUES (1122,'مسألة لا شفعة لذمي على مسلم وهو قول الشافعي خلافا لأكثرهم','686','1009','0');
INSERT INTO [t5907] VALUES (1123,'مسائل الإجارة','686','1122','0');
INSERT INTO [t5907] VALUES (1124,'مسألة لا يجوز أخذ الأجرة على القرب كتعليم القرآن والأذان والصلاة وتعليم الفرائض ورواية الحديث وقال مالك والشافعي يجوز','687','1122','0');
INSERT INTO [t5907] VALUES (1125,'الحديث الأول','688','1122','0');
INSERT INTO [t5907] VALUES (1126,'الحديث الثاني','688','1122','0');
INSERT INTO [t5907] VALUES (1127,'مسألة لا يجوز أخذ الأجرة على الحجامة فإن دفع إليه من غير شرط ولا عقد لم','688','1009','0');
INSERT INTO [t5907] VALUES (1128,'مسألة يجوز استئجار الظئر والخادم بطعامه وكسوته وعنه لا يجوز كقول الشافعي وقال أبو حنيفة يجوز في الظئر دون الخادم','690','1009','0');
INSERT INTO [t5907] VALUES (1129,'مسألة لا يصح الاستئجار لحمل الخمر ومتى حمله لم يستحق أجرة وعنه يصح ويستحق الأجرة كقول أبي حنيفة لنا أن رسول الله صلى الله عليه وسلم قال لعنت الخمر بعينها وحاملها وقد سبق الحديث بإسناده','690','1128','0');
INSERT INTO [t5907] VALUES (1130,'مسألة تجوز المساقاة في النخل والكرم والشجر وكل أصل له ثمر وقال أبو حنيفة لا يجوز بحال وقال الشافعي يجوز في النخل والكرم وبقية الشجر على قولين وقال داود لا يجوز إلا في النخل','690','1009','0');
INSERT INTO [t5907] VALUES (1131,'مسألة تصح المزارعة ببعض ما يخرج من الأرض وقال الشافعي لا يجوز في الأرض البيضاء ويجوز إذا كان في الأرض نخل أو كرم تبعا وقال مالك وأبو حنيفة لا يصح بحال لنا حديث ابن عمر المتقدم في المساقاة واحتجوا بحديث رافع نهى رسول الله صلى الله عليه وسلم عن المخابرة','692','1130','0');
INSERT INTO [t5907] VALUES (1132,'مسألة يجوز كراء الأرض بالثلث والربع وعنه المنع كقول أكثرهم وقد روى أصحابنا من حديث ابن عباس أن النبي صلى الله عليه وسلم قال من كان مكريا أرضا فليكر بالربع والثلث احتجوا بما','692','1130','0');
INSERT INTO [t5907] VALUES (1133,'مسائل إحياء الموتى','693','1132','0');
INSERT INTO [t5907] VALUES (1134,'مسألة لا يفتقر التملك بالإحياء إلى اذن الإمام وقال أبو حنيفة يفتقر وقال مالك ما كان في الفلوات لم يفتقر وما قرب من العمر افتقر','693','1009','0');
INSERT INTO [t5907] VALUES (1135,'مسألة إذا حوط على موات ملكه وقال الشافعي لا يملك أرضا حتى يستخرج لها ما يزرعها ولا دارا حتى يقطعها بيوتا ويسقفها','693','1009','0');
INSERT INTO [t5907] VALUES (1136,'مسألة حريم البئر العادي خمسون ذراعا والبري خمسة وعشرون وقال أبو حنيفة أربعون وقال الشافعي ما يحتاج إليه','694','1009','0');
INSERT INTO [t5907] VALUES (1137,'مسألة ما نبت من الكلأ ونبع من الماء في أرض إنسان فليس بملك له وعنه أنه ملك لصاحب الأرض كقول الشافعي','694','1009','0');
INSERT INTO [t5907] VALUES (1138,'مسألة يلزمه بذل ما فضل عن حاجته من الماء وعنه لا يلزمه ذلك كقول أبي حنيفة والشافعي لنا الحديث المتقدم','695','1137','0');
INSERT INTO [t5907] VALUES (1139,'مسألة يلزم الوقف بغير حكم الحاكم وقال أبو حنيفة لا يصح إلا أن يحكم به حاكم أو يخرجه مخرج الوصية وصاحباه معا','695','1009','0');
INSERT INTO [t5907] VALUES (1140,'مسألة يجوز وقف المنقولات التي ينتفع بها مع بقاء عينها وقال أبو حنيفة لا يصح وقال أبو يوسف لا يصح إلا في الخيل والسلاح وبقر الصنيعة وآلاتها','695','1009','0');
INSERT INTO [t5907] VALUES (1141,'مسألة إذ وقف على غيره واستثنى أن ينفق منه على نفسه حياته صح وقال مالك والشافعي لا يصح لنا حديث عمر المتقدم وأنه لا جناح على من وليها أن يأكل وكان هو واليها','696','1140','0');
INSERT INTO [t5907] VALUES (1142,'مسألة يصح هبة المشاع وقال أبو حنيفة لا يصح فيما يقسم','696','1009','0');
INSERT INTO [t5907] VALUES (1143,'مسألة العمرى تمليك الرقبة وصفتها أن يقول أعمرتك داري أو هي لك مدة حياتك فإن مات من جعلت له انتقلت إلى ورثته فإن لم يكن له وارث فهي لبيت المال وقال مالك هي تمليك المنافع فإن مات رجعت إلى المعمر','696','1009','0');
INSERT INTO [t5907] VALUES (1144,'مسألة وحكم الرقبى حكم العمرى وصفتها أن يقول أرقبتك داري أو يقول الدار لك فإن مت قبلي رجعت إلي وإن مت قبلك فهي لك ولعقبك وقال أبو حنيفة الرقبى باطلة لنا ما تقدم','697','1009','0');
INSERT INTO [t5907] VALUES (1145,'مسألة إذا فضل بعض ولده على بعض في العطية مع تساويهم في الذكورية والأنوثية أساء وأمر بارتجاع ذلك وبالتسوية بينهم وقال أكثرهم لا يرجع','697','1009','0');
INSERT INTO [t5907] VALUES (1146,'مسألة للأب الرجوع في هبته لولده سواء بأن يقع ذلك عليه أو لم يبن وعنه أنه متى بان يقع ذلك عليه مثل أن يستدين على ذلك أو يزوج البنت لأجله لم يكن له الرجوع وهو قول مالك وقال أبو حنيفة لا يجوز له الرجوع بحال','698','1009','0');
INSERT INTO [t5907] VALUES (1147,'مسألة لا يملك الأجنبي الرجوع في هبته وقال أبو حنيفة له الرجوع لما لم يثب منها أو يكون بينهما رحم محرم أو زوجية أو يزيد الموهوب زيادة متصلة لنا ما','699','1009','0');
INSERT INTO [t5907] VALUES (1148,'الحديث الأول','699','1147','0');
INSERT INTO [t5907] VALUES (1149,'الحديث الثاني','699','1147','0');
INSERT INTO [t5907] VALUES (1150,'الحديث الثالث','699','1147','0');
INSERT INTO [t5907] VALUES (1151,'الحديث الرابع','700','1147','0');
INSERT INTO [t5907] VALUES (1152,'مسألة للأب أن يأخذ من مال ولده ما شاء ما لم يجحف بماله خلافا لأكثرهم في أنه لا يأخذ إلا قدر الحاجة','700','1009','0');
INSERT INTO [t5907] VALUES (1153,'مسائل اللقطة','700','1152','0');
INSERT INTO [t5907] VALUES (1154,'مسألة يجوز التقاط الغنم ولا يملكها قبل الحول وقال مالك وداود إذا وجدها بفلاة ولا قرية هناك يضمها إليها جاز أكلها في الحال من غير تعريف لنا قوله في الحديث المتقدم ثم عرفها سنة','701','1152','0');
INSERT INTO [t5907] VALUES (1155,'مسألة إذا عرف اللقطة حولا ملكها إن كانت أثمانا وإن كانت عروضا أو حليا أو ضالة لم يملكها ولم ينتفع بها سواء كانت عينا أو قفيزا وقد قال أبو حنيفة لا يملك شيء من اللقطات بحال ولا ينتفع بها إذا كان عينا فإن كان قفيزا جاز له الانتفاع بها وقال مالك والشافعي','701','1009','0');
INSERT INTO [t5907] VALUES (1156,'طريق آخر','701','1155','0');
INSERT INTO [t5907] VALUES (1157,'الحديث الثاني','702','1155','0');
INSERT INTO [t5907] VALUES (1158,'مسألة لقط الحرم لا تحل إلا لمن يعرفها أبدا وعن أحمد أنها كسائر اللقط وعن أصحاب الشافعي كالروايتين وجه الأولى ما','702','1009','0');
INSERT INTO [t5907] VALUES (1159,'مسألة إذا جاء مدعي اللقطة فأخبر بعددها وعفاصها ووكائها دفعت إليه بغير بينة وقال أبو حنيفة والشافعي لا يدفع إلا ببينة لنا ما تقدم من قوله اعرف عفاصها ووكاءها وعددها ولو كان التسليم موقوفا على البينة لم يكن في معرفة العفاص والوكاء فائدة وإن لم يجد فهو','703','1009','0');
INSERT INTO [t5907] VALUES (1160,'مسألة إذا وقعت دابته فألقاها بأرض مملكة فجاء غيره فأطعمها وسقاها حتى سلمت مهلكة خلافا لأكثرهم','703','1009','0');
INSERT INTO [t5907] VALUES (1161,'مسألة يصح إسلام الصبي وردته وقال الشافعي لا يصح لنا ما روى أحمد أن','703','1009','0');
INSERT INTO [t5907] VALUES (1162,'مسائل الوصايا','704','1161','0');
INSERT INTO [t5907] VALUES (1163,'مسألة إذا أوصى لجيرانه دخل فيه من كل جانب أربعون دارا وقال أبو حنيفة لا يدخل فيه إلا الملاصق','705','1009','0');
INSERT INTO [t5907] VALUES (1164,'مسألة تصح الوصية للعامل وقال أبو حنيفة لا يصح وعن الشافعي كالقولين لنا إطلاق الوصية في قوله تعالى من بعد وصية وللخصم ما','706','1009','0');
INSERT INTO [t5907] VALUES (1165,'مسألة إذا أوصى لرجل بسهم من ماله كان له السدس إلا أن يعول الفريضة فيعطى سدسا عائلا وعنه أنه يعطى أقل سهام الورثة وإن نقص ذلك عن السدس فإن زاد أعطي السدس وعن أبي حنيفة كالرواية الثانية وعنه يعطى أقل نصيب الورثة ما لم ينقص من السدس وقال الشافعي يعطى ما','706','1009','0');
INSERT INTO [t5907] VALUES (1166,'مسألة تصح الوصية بما زاد على الثلث ويقف على تنفيذ الورثة خلافا لأحد قولي الشافعي أنها لا تصح','706','1009','0');
INSERT INTO [t5907] VALUES (1167,'مسائل الفرائض','707','1166','0');
INSERT INTO [t5907] VALUES (1168,'مسألة قاتل الخطأ لا يرث وقال مالك يرث من المال لنا ثلاثة أحاديث','709','1009','0');
