INSERT INTO [t5907] VALUES (1307,'مسألة الطلاق بالرجال فإن كان الرجل حرا فطلاقه ثلاث وإن كان عبدا فاثنتان وقال أبو حنيفة يعتبر بالنساء وقد رويت أحاديث في الطرفين كلها ضعاف','766','1193','0');
INSERT INTO [t5907] VALUES (1308,'مسألة الإطعام في الكفارة لكل مسكين مد من بر أن نصف صاع من شعير أو تمر وقال أبو حنيفة نصف صاع من بر أو صاع من تمر أو شعير وقال الشافعي مد من الجميع','767','1193','0');
