INSERT INTO [t5907] VALUES (1310,'مسألة موجب قذف الزوج الحد وله إسقاطه عنه باللعان وقال أبو حنيفة موجبه اللعان ولا يجب الحد إلا أن يكذب نفسه','768','1193','0');
INSERT INTO [t5907] VALUES (1311,'مسألة لا يصح اللعان على نفي الحمل وقال مالك والشافعي تلاعن لنفي الحمل احتجوا بما','768','1193','0');
INSERT INTO [t5907] VALUES (1312,'مسألة لا تقع فرقة اللعان إلا بلعانهما وتفريق الحاكم وعنه يقع بلعانهما وهو قول مالك وقال الشافعي يقع الفراق بلعان الزوج وحده','768','1193','0');
INSERT INTO [t5907] VALUES (1313,'مسألة فرقة اللعان تقع مؤبدة وعنه إذا لاعن امرأته وأكذب نفسه جلد وردت إليه امرأته وهو قول أبي حنيفة لنا حديث ابن عمر لا سبيل لك عليها وهذا عام أكذب نفسه أو لم يكذب','769','1312','0');
