INSERT INTO [t5907] VALUES (1424,'مسألة إذا اطلع في بيت إنسان على أهله فله أن يرمي عينه فإن فقأها فلا ضمان عليه وقال أبو حنيفة يلزمه الضمان لنا ثلاثة أحاديث','807','1321','0');
