INSERT INTO [t5907] VALUES (1548,'مسألة يمين الغموس لا يوجب الكفارة خلافا للشافعي','849','1321','0');
INSERT INTO [t5907] VALUES (1549,'مسألة لا تنعقد يمين المكره وقال أبو حنيفة تنعقد','850','1321','0');
INSERT INTO [t5907] VALUES (1550,'مسألة ينعقد نذر المعصية وكفارته كفارة يمين وقال أكثرهم لا تنعقد ولا تلزم كفارة لنا حديث عمران بن حصين أن امرأة نجت على العضباء فنذرت لتنحرنها فقال عليه السلام لا وفاء لنذر في معصية الله وقد سبق بإسناده','850','1549','0');
INSERT INTO [t5907] VALUES (1551,'مسألة نذر المباح ينعقد ويكون مخيرا بين الوفاء والكفارة وقال أكثرهم لا ينعقد','850','1321','0');
INSERT INTO [t5907] VALUES (1552,'من مسائل القضاء','850','1551','0');
INSERT INTO [t5907] VALUES (1553,'مسألة لا يجوز أن يولى القضاء خلافا لأبي حنيفة','851','1321','0');
INSERT INTO [t5907] VALUES (1554,'مسألة يصح التحكيم خلافا لأحد قولي الشافعي لنا ما روى أبو بكر عبد العزيز من أصحابنا في حديث عبد الله بن جراد قال قال رسول الله صلى الله عليه وسلم من حكم بين اثنين تحاكما إليه وارتضاه فلم يقل بينهما بالحق فعليه لعنة الله','851','1553','0');
INSERT INTO [t5907] VALUES (1555,'مسألة حكم الحاكم لا يحيل الشيء على صفته وقال أبو حنيفة يحيله في العقود والفسوخ','851','1321','0');
INSERT INTO [t5907] VALUES (1556,'مسألة إذا شهد شاهدان على قضاء الحاكم وهو لا يذكر قبل شهادتهما وقال الشافعي لا يرجع إلى قولهما لنا أن النبي صلى الله عليه وسلم رجع إلى قول غيره في قصة ذي اليدين وقد ذكرناه بإسناده في الظهار وذكرنا في أول النكاح أن جماعة حدثوا ونسوا فكان أحدهم يقول','852','1321','0');
INSERT INTO [t5907] VALUES (1557,'مسألة إذا طلب أحدهما القسمة وفيها ضرر على الآخر لم يقسم وتباع ويقتسمان الثمن وقال أبو حنيفة إذا كان لأحدهما في ذلك منفعة أجبرا على القسمة وقال مالك يجبر على القسمة بكل حال وقال الشافعي إن كان المطالب ينتفع بذلك أجبر وإن كان يستضر فعلى وجهين','852','1321','0');
INSERT INTO [t5907] VALUES (1558,'من مسائل الدعاوى','852','1557','0');
INSERT INTO [t5907] VALUES (1559,'الحديث الأول','853','1557','0');
INSERT INTO [t5907] VALUES (1560,'الحديث الثاني','853','1557','0');
INSERT INTO [t5907] VALUES (1561,'الحديث الثالث','853','1557','0');
INSERT INTO [t5907] VALUES (1562,'مسألة يجوز للجار أن يضع خشبة في جدار جاره عند الحاجة إلى ذلك بشرط أن لا يضر بالحائط فإن امتنع الجار أجبره الحاكم عليه وبه قال الشافعي إلا أنه لا يحكم عليه الحاكم بذلك وقال أكثرهم لا يجوز ذلك إلا بإذن المالك','854','1321','0');
INSERT INTO [t5907] VALUES (1563,'مسألة إذا وطىء امرأة بشبهة فأتت بولد عرض على القافة فإن ألحقوه بأحدهما أو بهما لحق وإن أشكل عليهم وقف حتى يبلغ فينتسب إلى أيهما شاء وقال أبو حنيفة لا يعرض على القافة','854','1321','0');
INSERT INTO [t5907] VALUES (1564,'مسألة لا يرد اليمين في شيء من الدعاوى ويقضي بالنكول وقال مالك','854','1321','0');
INSERT INTO [t5907] VALUES (1565,'الحديث الأول','855','1564','0');
INSERT INTO [t5907] VALUES (1566,'الحديث الثاني','855','1564','0');
INSERT INTO [t5907] VALUES (1567,'الحديث الثالث','855','1564','0');
INSERT INTO [t5907] VALUES (1568,'من مسائل الشهادات','856','1567','0');
INSERT INTO [t5907] VALUES (1569,'مسألة يقبل في الولادة شهادة امرأة واحدة وكذلك في كل ما لا يطلع عليه الرجال وعنه لا يقبل إلا امرأتين كقول مالك وقال الشافعي لا يقبل إلا أربع نسوة','856','1321','0');
INSERT INTO [t5907] VALUES (1570,'مسألة لا تقبل شهادة العدو على عدوه خلافا لأبي حنيفة لنا حديثان','857','1569','0');
INSERT INTO [t5907] VALUES (1571,'الحديث الثاني','857','1569','0');
INSERT INTO [t5907] VALUES (1572,'مسألة لا تقبل شهادة الوالد لولده ولا الولد لوالده وعنه تجوز شهادة الابن لأبيه وعنه تجوز شهادة أحدهما للآخر فيما لا يهمه فيه كالنكاح والطلاق والمال وكل واحد مستغن عن صاحبه وقال داود المزني وأبو ثور تجوز على الإطلاق لنا الحديث المتقدم','857','1569','0');
INSERT INTO [t5907] VALUES (1573,'مسألة لا تقبل شهادة أهل الذمة بعضهم على بعض وقال أبو حنيفة تقبل','858','1321','0');
INSERT INTO [t5907] VALUES (1574,'الحديث الأول','858','1573','0');
