INSERT INTO [t6037] VALUES (1,'وقوت الصلاة','1','0','0');
INSERT INTO [t6037] VALUES (2,'إلا يصلي وتقدير الكلام على مذهب سيبويه إن رسول الله','5','0','0');
INSERT INTO [t6037] VALUES (3,'قال له حافظ على العصرين قال وما كانت من لغتنا وإنما قيل لهما ذلك لأن الغداة والعشي يقال لهما العصران ويقال أيضا لليل والنهار العصران ومعنى غربت الشمس بعدت فلم تدركها الأبصار ومنه سمي الغريب لبعده عن وطنه','9','0','0');
INSERT INTO [t6037] VALUES (4,'بين الخيل وكنت يومئذ فارسا فسبقت الناس وطفف بي الفرس مسجد بني زريق وتوهموه بمعنى جاوز وليس يلزم ما قالوه وإنما أراد أن الفرس وثب به حتى كاد يساور المسجد والمشهور في التطفيف إنما هو النقصان فإن قال قائل إن قوله تعالى ويل','13','0','0');
INSERT INTO [t6037] VALUES (5,'حر الرمضاء فلم يشكنا هذه اللفظة من الأضداد يقال أشكيت الرجل إذا أحوجته إلى أن يشكو وأشكيته إذا اشتكى إليك فأزلت عنه ما يشكوه وقد اختلف في معنى قوله اشتكت النار فجعله قوم حقيقة وقالوا إن الله تعالى قادر على أن ينطق','15','0','0');
INSERT INTO [t6037] VALUES (6,'إذا توضأ أحدكم فليستنشق بمنخره من الماء ثم لينثر فدل هذا على أن الاستنثار غير الاستنشاق وأصل المضمضة الغسل يقال مضمض إناءه ومصمصه بالصاد والضاد إذا غسله وذكر خفض الأرجل في قوله تعالى وأرجلكم إلى الكعبين','19','0','0');
INSERT INTO [t6037] VALUES (7,'يا مقلب القلوب قلب قلبي على طاعتك والوجه الآخر أن العرب قد تشبه إن التي للشرط ب إذا كما تشبه إذا في بعض المواضع ب إن لأن إذا تضارع إن في أنها تحتاج إلى جواب والشيئان إذا تضارعا فقد يحمل كل واحد على','25','0','0');
INSERT INTO [t6037] VALUES (8,'وحانت صلاة العصر المعنى وقد حانت ولا بد من تقدير قد هاهنا لأن الجملة في الحال موضع لأنه إنما أراد رأيت رسول الله','29','0','0');
INSERT INTO [t6037] VALUES (9,'باب الغسل','34','0','0');
INSERT INTO [t6037] VALUES (10,'باب التيمم','37','0','0');
INSERT INTO [t6037] VALUES (11,'لعلك نفست لعل هاهنا ظن وتوقع والمعنى أظنك نفست يقال نفست المرأة إذا حاضت وكذلك في الولادة وحكى ابن الأعرابي نفست ومعنى قولهم نفست المرأة سال نفسها والنفس الدم سمي بذلك لأنه يوجد','38','0','0');
INSERT INTO [t6037] VALUES (12,'كان يعجبه أن يستاك بالضرع والضرع جمع ضريع وهو قضيب من الأراك ينثني فيسقط من الشجر في الظل لا تصيبه الشمس وهو مع ذلك لم ينقطع من الشجرة وذكر أبو حنيفة أنه ألين من الفرع وأطيب ريحا والتهجير البدار إلى الصلاة في أول وقتها','40','0','0');
INSERT INTO [t6037] VALUES (13,'الأذان','42','0','0');
INSERT INTO [t6037] VALUES (14,'فإنه أندى صوتا منك وهو مفتوح الأول مقصور فإذا كسرت أوله مددت حتى يظل الرجل إن يدري بالظاء المشالة أي يقيم ويصير والرجل مرفوع به وإن مكسورة الهمزة وهي حرف نفي بمعنى ما والجملة في موضع نصب على خبر يظل التقدير','43','0','0');
INSERT INTO [t6037] VALUES (15,'فأحرق ويروى فأحرق وهما لغتان أحرقت وحرقت والتشديد أبلغ في المعنى أو مرماتين يروى بكسر الميم وفتحها وفي العين المرماة سهم يتعلم به الرمي والمرماة ما بين ظلفتي الشاة وهو غير معروف والمشهور في المرماة أنها','53','0','0');
INSERT INTO [t6037] VALUES (16,'القنوت','57','0','0');
INSERT INTO [t6037] VALUES (17,'يتعاقبون فيكم ملائكة','57','0','0');
